INSERT INTO `zadaniasprawnosciharcerskich` (`ID`, `Tresc`, `Sprawnosc`, `Stopien`) VALUES
(5826, '﻿Wziąłem/wzięłam udział w obozie lub biwaku pod namiotami. Poznałem/am zasady bezpieczeństwa i przestrzegałem/am ich. Pozostawiłem/am po sobie porządek.', 8, 'Młodzik/Ochotniczka'),
(5827, 'Wyznaczyłem/am strony świata na dwa sposoby. Poprowadziłem/am zastęp podczas wędrówki.', 8, ''),
(5828, 'Samodzielnie rozpaliłem/am ognisko. Przygotowałem/am na nim prosty posiłek (np. upiekłem/am podpłomyki, zagotowałem/am wodę na makaron).', 8, ''),
(5829, 'Znam i zastosowałem/am w różnych sytuacjach co najmniej pięć węzłów.', 8, 'Młodzik/Ochotniczka'),
(5830, 'Rozpaliłem/am ognisko na łonie natury w bezpiecznym miejscu, z zachowaniem zasad bezpieczeństwa, z użyciem wyłącznie naturalnych materiałów (nie dotyczy zapałek); po zgaszeniu doprowadziłem/am miejsce do pierwotnego stanu. Uczestniczyłem/am w przygotowaniu złożonego posiłku na ognisku.', 9, 'Wywiadowca/Tropicielka'),
(5831, 'Wraz z zastępem rozstawiłem/am namiot i zbudowałem/am przydatne urządzenie obozowe (np. wyposażenie namiotu), przez cały obóz utrzymałem/am porządek w swoich rzeczach.', 9, 'Wywiadowca/Tropicielka'),
(5832, 'Zbudowałem/am urządzenie obozowe z wykorzystaniem znanych mi węzłów (np. wyplotłem/am pryczę lub półkę, zbudowałem/am urządzenie obozowe bez użycia gwoździ).', 9, ''),
(5833, 'Wziąłem/wzięłam udział w budowie jednego z urządzeń obozowych wspólnego użytku (np. latryny, bramy, stanowiska na sprzęt ppoż.).', 9, ''),
(5834, 'Pełniłem/am służbę wartowniczą na obozie lub biwaku.', 9, 'Wywiadowca/Tropicielka'),
(5835, 'Wziąłem/wzięłam udział w kilkudniowym rajdzie/wyprawie z nocowaniem na łonie przyrody, podczas którego minimalizowałem/am swój wpływ na środowisko (odpowiednio gospodarując wodą i odpadami, przygotowując nocleg, zabezpieczając ognisko).', 10, 'Odkrywca/Pionierka'),
(5836, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Kierowałem/am służbą kuchenną/zastępem służbowym.\n-Przygotowałem/am młodszych harcerzy do pełnienia wart i dowodziłem/am zastępem wartowniczym.', 10, ''),
(5837, 'Potrafię używać i dbam o sprzęt drużyny/szczepu. Brałem/am udział w jego konserwacji.', 10, 'Odkrywca/Pionierka'),
(5838, 'Samodzielnie ułożyłem/am 3 rodzaje ognisk i rozpaliłem/am je.', 10, 'Odkrywca/Pionierka'),
(5839, 'Zapoznałem/am młodszych harcerzy z zasadami pracy z narzędziami pionierskimi, pokierowałem/am budową przez zastęp prostego urządzenia obozowego.', 10, 'Ćwik/Samarytanka'),
(5840, 'Pełniłem/am funkcję oboźnego, kwatermistrza lub zaopatrzeniowca na wyjeździe drużyny.', 10, 'Ćwik/Samarytanka'),
(5841, 'Zaplanowałem/am i zorganizowałem/am kilkudniową wędrówkę zgodnie \nz zasadami Leave No Trace.', 11, ''),
(5842, 'Wziąłem/wzięłam udział w przygotowaniu obozu, np. tworzeniu preliminarza, przygotowaniu sprzętu, organizacji transportu, zaplanowaniu zakupów żywieniowych. Poznałem/am zasady prowadzenia gospodarczej dokumentacji obozu i pod nadzorem kwatermistrza pomogłem/am ją prowadzić.', 11, 'Harcerz Orli/Harcerka Orla'),
(5843, 'Wziąłem/wzięłam udział w minimum dwudniowej wyprawie w nowej dla mnie formie turystyki (np. obozie wędrownym, rowerowym, rejsie, spływie itp.).', 11, ''),
(5844, 'Noszę mundur zgodny z regulaminem obowiązującym w ZHP.', 19, 'Młodzik/Ochotniczka'),
(5845, 'Poznałem/am najważniejsze tradycje i obrzędy swojej drużyny. Potrafię stanąć w postawie zasadniczej i swobodnej oraz ustawić się w szyku. Wykazałem/am się tą wiedzą i umiejętnościami, uczestnicząc w życiu drużyny.', 19, 'Młodzik/Ochotniczka'),
(5846, 'Mam śpiewnik harcerski. Zaśpiewałem/am przynajmniej 10 piosenek harcerskich.', 19, ''),
(5847, 'Odnalazłem/am kilku funkcyjnych, dowiedziałem/am się, czym się zajmują i zrobiłem/am zdjęcia ich sznurów. Wiem, jak oznacza się funkcje pełnione w zastępie, drużynie, szczepie.', 19, ''),
(5848, 'Dowiedziałem/am się, kto i kiedy założył skauting i harcerstwo.', 19, 'Młodzik/Ochotniczka'),
(5849, 'Dowiedziałem/am się, jakie inne organizacje harcerskie, poza ZHP, działają w Polsce. Nawiązałem/am kontakt z członkiem jednej z nich i poznałem/am kilka różnic między naszymi organizacjami, opowiedziałem/am o tym zastępowi.', 20, ''),
(5850, 'Opowiedziałem/am osobie spoza ZHP (np. koledze, członkowi rodziny) o tym, kto, kiedy i z jakiego powodu utworzył skauting i harcerstwo.', 20, ''),
(5851, 'Przedstawiłem/am w wybranej przez siebie formie przynajmniej trzy postacie z historii harcerstwa.', 20, 'Wywiadowca/Tropicielka'),
(5852, 'Znam nazwy stopni harcerskich i instruktorskich oraz sposób ich oznaczania na mundurze. Kilka razy zameldowałem/am się, używając odpowiednich nazw stopni.', 20, 'Wywiadowca/Tropicielka'),
(5853, 'Odwiedziłem/am jedno z miejsc związanych z ważnymi dla harcerstwa wydarzeniami.', 21, 'Odkrywca/Pionierka'),
(5854, 'Poznałem/am życiorysy ośmiu osób zasłużonych dla rozwoju ruchu harcerskiego. O dwóch spośród nich opowiedziałem/am podczas zbiórki zastępu lub drużyny.', 21, 'Odkrywca/Pionierka'),
(5855, 'Dowiedziałem/am się, jak oznacza się funkcje w hufcu, chorągwi i władzach naczelnych ZHP . Spotkałem/am się z funkcyjnym instruktorem hufca i dowiedziałem się, czym się zajmuje.', 21, 'Ćwik/Samarytanka'),
(5856, 'Przeczytałem/am przynajmniej jedną książkę, która pogłębiła moją wiedzę o dziejach ruchu harcerskiego lub skautowego. Zaprezentowałem/am innym (w zastępie, drużynie) wybrane zagadnienie i znaczącą postać.', 21, 'Ćwik/Samarytanka'),
(5857, 'Przeczytałem/am minimum jedną książkę o tematyce harcerskiej (poza tymi o historii harcerstwa).', 21, ''),
(5858, 'Przekazałem/am w ciekawej formie młodszym członkom mojego środowiska harcerskiego wiedzę o jego tradycjach.', 22, 'Harcerz Orli/Harcerka Orla'),
(5859, 'Utrwaliłem/am historię harcerstwa na obszarze swojej gminy lub powiatu, np. udokumentowałem/am życiorys zasłużonego instruktora lub ważne dla harcerzy wydarzenia. Upowszechniłem/am efekt swojej pracy.', 22, ''),
(5860, 'Wiem, jak są podejmowane najważniejsze decyzje dotyczące ZHP, spotkałem/am się z delegatem/byłym delegatem na zjazd chorągwi lub Zjazd ZHP , dowiedziałem/łam się, jak przebiega taki zjazd, o czym może decydować i jakie są procedury.', 22, ''),
(5861, 'Przyjąłem/am postawy zasadniczą i swobodną. Wykonałem/am zwroty: w lewo, w prawo, w tył.', 141, ''),
(5862, 'Zameldowałem/am się.', 141, ''),
(5863, 'Prawidłowo zachowałem/am się podczas zbiórki, ustawiając się w poleconym szyku.', 141, ''),
(5864, 'Uczestniczyłem/am w uroczystym śpiewaniu Hymnu ZHP, prawidłowo się zachowując.', 141, ''),
(5865, 'Kierowałem/am musztrą zastępu, wykonując: zbiórkę, zmiany szyku, odliczanie.', 142, ''),
(5866, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Prawidłowo zachowałem/am się podczas musztry, pełniąc funkcję proporcowego/proporcowej.\n-Uczestniczyłem/am w poczcie flagowym podnoszącym i opuszczającym flagę (na/z masztu).', 142, ''),
(5867, 'Zameldowałem/am zastęp na apelu.', 142, ''),
(5868, 'Doprowadziłem/am, zameldowałem/am i odprowadziłem/am zastęp lub patrol na punkcie biegu harcerskiego.', 142, ''),
(5869, 'Kierowałem/am apelem drużyny.', 143, ''),
(5870, 'Wykorzystałem/am musztrę do zorganizowanego przemieszczenia drużyny, np. na posiłek.', 143, ''),
(5871, 'Kierowałem/am przemarszem drużyny przez teren miejski na odległość minimum 2 km, sprawdzając stan liczebny i zapewniając bezpieczeństwo podczas przemarszu.', 143, ''),
(5872, 'Przeszkoliłem/am mniej doświadczonych harcerzy w zakresie podstaw musztry, w tym do pełnienia funkcji proporcowego/proporcowej.', 143, ''),
(5873, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Prawidłowo wykonałem/am musztrę z laską skautową.\n-Prawidłowo zachowałem/am się podczas musztry zespołowej, pełniąc funkcję sztandarowego, wykonując chwyty sztandarem, oddając honory itd.\n-Przygotowałem/am poczet sztandarowy do wystąpienia i dowodziłem/am nim podczas uroczystego wydarzenia.', 144, ''),
(5874, 'Kierowałem/am apelem jednostki większej niż drużyna (szczepu, rajdu, turnieju zastępów, hufca).', 144, ''),
(5875, 'Wykonałem/am jedno z poniższych zadań:\n-Przygotowałem/am drużynę reprezentacyjną lub sztandarową do wystąpienia podczas uroczystości i dowodziłem/am nią zgodnie z zasadami ceremoniału.\n-Zaplanowałem/am wystąpienie harcerskiej reprezentacji (pocztu sztandarowego, drużyny reprezentacyjnej itp.) w sytuacji nieopisanej wprost w zasadach harcerskiego ceremoniału, a następnie dowodziłem/am tą reprezentacją w sposób właściwy dla okoliczności.', 144, ''),
(5876, 'Wiem, jakie znaczenie ma służba wartownicza dla bezpieczeństwa osób i mienia znajdującego się na terenie obozu (biwaku). Pełniłem/am wzorowo służbę wartowniczą łącznie przez 10 godzin w nocy i 8 godzin w dzień.', 145, ''),
(5877, 'W czasie ćwiczeń pokazałem/am, że potrafię zachować się w przypadku:\nzauważenia niepożądanych osób na obozie,\npożaru,\ngwałtownej zmiany pogody: ulewy/porywistego wiatru/burzy.', 145, ''),
(5878, 'Brałem/am udział w przynajmniej pięciu grach doskonalących czujność, spostrzegawczość itp. Wykazałem/am się umiejętnością:\nwykrycia za pomocą słuchu, z której strony zbliża się człowiek;\ndostrzeżenia zamaskowanej osoby.', 145, ''),
(5879, 'Zdobyłem/am sprawność Wartownika**.', 146, ''),
(5880, 'Pełniłem/am funkcję dowódcy warty (instruktora służbowego na obozie lub biwaku). Przekazałem/am wartownikom instrukcje niezbędne do prawidłowego zabezpieczania obozu w różnych sytuacjach.', 146, ''),
(5881, 'Zorganizowałem/am co najmniej pięć gier doskonalących czujność oraz spostrzegawczość.', 146, ''),
(5882, 'Opiekowałem/am się harcerzem/harcerką zdobywającym/ą sprawność Wartownika**.', 146, ''),
(5883, 'Przyjmowałem/am gości obozowych i wizytacje zgodnie z właściwymi procedurami.', 147, ''),
(5884, 'Kierowałem/am rozstawianiem obozu w taki sposób, by był maksymalnie bezpieczny (od nieproszonych gości, pożarów i zjawisk pogodowych), a w tym zadbałem/am o:\numieszczenie na każdym obozie/podobozie dobrze wyposażonego punktu ppoż.,\nnawiązanie stałego kontaktu ze Strażą Pożarną,\nregularne otrzymywanie prognoz pogodowych.', 147, ''),
(5885, 'Współprowadziłem/am próby alarmów obozowych związanych z różnymi zagrożeniami.', 147, ''),
(5886, 'Nadzorowałem/am ustalanie grafiku wart w taki sposób, by harcerze mieli zapewnioną odpowiednią długość snu, a długość warty, jej liczebność i pora dnia/nocy jej pełnienia były dostosowane do wieku i możliwości harcerza.', 147, ''),
(5887, 'Kierowałem/am harcerskimi podchodami na obozie tak by były prowadzone zgodnie z zasadami i służyły umacnianiu bezpieczeństwa na obozie (np. przez wzmacnianie czujności wartowników).', 147, ''),
(5888, 'Wziąłem/wzięłam udział w pięciu grach w terenie o różnych zasadach, np. INO, grze ruchowej, zabawie ruchowej, biegu patrolowym. Byłem/am uczciwa w trakcie współzawodnictwa przestrzegałem/am zasad.', 148, ''),
(5889, 'Wziąłem/wzięłam udział w 10 ćwiczeniach rozwijających spostrzegawczość, refleks, pamięć.', 148, ''),
(5890, 'Uczestniczyłem/am razem z zastępem w zawodach, turnieju, meczu lub innej formie współzawodnictwa między zastępami.', 148, ''),
(5891, 'Zaproponowałem/am zastępowi lub znajomym swoją ulubioną grę lub ćwiczenie.', 148, ''),
(5892, 'Kierowałem/am zastępem/drużyną w czasie gry terenowej/zawodów sportowych.', 149, ''),
(5893, 'Przeprowadziłem/am pięć gier terenowych i sportowych, ucząc kolegów zasad w nich obowiązujących. Są pośród nich również takie, które da się zorganizować bez wcześniejszego przygotowania. Wyjaśniłem/am rówieśnikom pojęcie fair play i czemu przestrzeganie zasad współzawodnictwa jest ważne.', 149, ''),
(5894, 'Zorganizowałem/am dla zastępu (drużyny) ćwiczenia typu „kim” w harcówce i w terenie.', 149, ''),
(5895, 'Zorganizowałem/am dowolne zawody między zastępami.', 149, ''),
(5896, 'Zebrałem/am poznane gry do wykorzystania w pomieszczeniu i w terenie w dowolnej formie (notatnik, plik tekstowy itp.) (minimum 10)', 149, ''),
(5897, 'W grze pokierowałem/am zespołem, opracowałem/am taktykę działania, przydzieliłem/am zadania członkom zespołu adekwatne do umiejętności. Zadbałem/am o komunikację i współpracę członków zespołu podczas trwania gry.', 150, ''),
(5898, 'Przeprowadziłem/am po kilkanaście gier terenowych i sportowych w drużynie (szczepie) lub dla dzieci spoza ZHP, w różnych grupach wiekowych.', 150, ''),
(5899, 'Przygotowałem/am trasę i program harcerskiego biegu terenowego, sprawdzającego znajomość technik harcerskich.', 150, ''),
(5900, 'Przedstawiłem/am zestaw gier i ćwiczeń zebranych w czasie próby. Nauczyłem/am innych minimum 10 gier i ćwiczeń do wykorzystania na zbiórkach i w szkole.', 150, ''),
(5901, 'Znam znaczenie ognia w historii ludzkości, opowiedziałem/am w zastępie, jak rozpalano go i korzystano z niego na przestrzeni wieków.', 151, ''),
(5902, 'Dowiedziałem/am się, w jakich miejscach można rozpalić ognisko, a także, jakie jest znaczenie martwego drewna w lesie. Samodzielnie znalazłem/am i przygotowałem materiały na ognisko. Ułożyłem/am stos i rozpaliłem/am ognisko w legalnym, właściwie zabezpieczonym miejscu ogniskowym.', 151, ''),
(5903, 'Przeprowadziłem/am eksperyment sprawdzając, jak palą się różne gatunki drewna – które z nich palą się jasnym płomieniem, które niskim, które szybko, które długo. Swoimi wnioskami podzieliłem/am się z członkami swojego zastępu.', 151, ''),
(5904, 'Zatarłem/am ślady ogniska po jego wygaszeniu. Zabezpieczyłem/am zapałki przed zamoknięciem.', 151, ''),
(5905, 'Skompletowałem/am własny zestaw materiałów do rozpalania ognia zabezpieczonych we właściwy sposób.', 152, ''),
(5906, 'Ułożyłem/am i opiekowałem/am się stosem obrzędowym pełniąc służbę strażnika ognia podczas ogniska drużyny/szczepu lub większej grupy. Układając stos ogniskowy pamiętałem/am o tym, aby ognisko nie było zbyt duże. Pełniąc służbę strażnika ognia umiejętnie dokładałem/am drewno, regulując wysokość płomienia i ograniczając iskrzenie.', 152, ''),
(5907, 'Zabezpieczyłem/am miejsce ogniskowe pod względem przeciwpożarowym. Nauczyłem/am się posługiwać podstawowym sprzętem gaśniczym. Udzieliłem/am lub przećwiczyłem/am udzielanie pierwszej pomocy w razie oparzenia.', 152, ''),
(5908, 'Poprawnie zbudowałem/am co najmniej trzy rodzaje stosów ogniskowych.', 152, ''),
(5909, 'Zorganizowałem/am miejsce ogniskowe. Przeszkoliłem/am grupę harcerzy/harcerek objaśniając im, jak dobierać materiały w zależności od przeznaczenia ogniska i nadzorując ich podczas przygotowywania stosu ogniskowego. Przygotowałem/am ich do pełnienia funkcji strażników ognia oraz nadzorowałem/am w trakcie.', 153, ''),
(5910, 'Poznałem/am obrzędowość związaną z harcerskim ogniskiem. Wygłosiłem/am gawędę na temat ognia i zasad zachowania się przy ognisku harcerskim.', 153, ''),
(5911, 'Rozpaliłem/am ognisko w trudnych warunkach atmosferycznych, np. po deszczu, w śniegu itp.', 153, ''),
(5912, 'Zdobyłem/am wiedzę na temat różnych metod rozpalania ognisk i rozpaliłem/am przynajmniej jedno ognisko bez użycia zapałek lub zapalniczki, korzystając z krzesiwa, soczewek, łuków ogniowych, techniki fire rollingu itp.', 153, ''),
(5913, 'Przeprowadziłem/am w pobliżu obozu w samotności kilkugodzinną obserwację życia lasu o wschodzie Słońca.', 154, ''),
(5914, 'W dowolnej formie udokumentowałem/am swoje spostrzeżenia z przebywania w lesie (zauważone zwierzęta i ich tropy, zjawiska przyrody, występujące rośliny chronione, jadalne itp.), zaprezentowałem/am je na forum zastępu/drużyny.', 154, ''),
(5915, 'Przygotowałem/am razem z kolegą/koleżanką ciepły posiłek, korzystając z darów lasu.', 154, ''),
(5916, 'Wraz z kolegą/koleżanką biwakowałem/am w pobliżu obozu przez 24 godziny. Zbudowałem szałas (najlepiej z wykorzystaniem płachty namiotowej).', 154, ''),
(5917, 'Odbyłem/am samotną wędrówkę po lesie (od świtu do zmroku), żywiąc się wyłącznie owocami lasu, odnalazłem/am wyznaczone przez drużynowego miejsce. Powróciłem/am niepostrzeżenie do ogniska i na podstawie obserwacji oraz przemyśleń z wędrówki opowiedziałem/am gawędę o życiu lasu.', 155, ''),
(5918, 'W czasie tej wędrówki znalazłem/am ciekawe przyrodniczo miejsce. Pokazałem je później harcerzom/harcerkom z mojej drużyny.', 155, ''),
(5919, 'Wykonałem co najmniej jedno z zadań:\nPrzygotowałem/am posiłek dla zastępu korzystając z darów lasu, gotując w kociołku na ognisku i piekąc w żarze.\nPrzygotowałem/am posiłek dla zastępu korzystając z mini-piecyka turystycznego (np. rakietowego) i kettle do grzania wody lub innych urządzeń opartych o spalanie drewna.', 155, ''),
(5920, 'Zdobyłem/am wiedzę na temat roślin i zwierząt, które mogą być niebezpieczne dla zdrowia i życia człowieka. Podzieliłem/am się tą wiedzą z młodszymi uczestnikami w czasie zajęć terenowych.', 155, ''),
(5921, 'Spędziłem/am samotnie co najmniej jedną noc w lesie (poza obozem) będąc niezauważalny/a dla osób postronnych.', 156, ''),
(5922, 'Przygotowałem/am posiłki dla siebie na cały dzień korzystając z darów lasu.', 156, ''),
(5923, 'Pozyskałem/am wodę pitną w terenie – bezpieczną pod względem chemicznym i biologicznym.', 156, ''),
(5924, 'Rozpaliłem/am ogień bez pomocy zapałek.', 156, ''),
(5925, 'Przeprowadziłem/am zajęcia terenowe na temat różnych technik bushcraftowych dla młodszych harcerzy/harcerek.', 156, ''),
(5926, 'Wskazałem/am najkrótszą drogę do ważnych miejsc w swojej miejscowości.', 157, ''),
(5927, 'Dostarczyłem/am ustną wiadomość do adresata w wyznaczonym terminie, przekazując precyzyjnie i w zwięzłej formie powierzoną informację.', 157, ''),
(5928, 'W czasie gry podążałem/am drogą wyznaczoną przez znaki patrolowe. Zapamiętałem/am przebytą drogę i samodzielnie nią powróciłem do punktu wyjścia.', 157, ''),
(5929, 'W czasie gry poruszałem/am się bezszelestnie, czołgałem/am się, zacierałem/am swoje ślady i przygotowałem/am kamuflaż odpowiedni do otoczenia.', 157, ''),
(5930, 'Znalazłem/am w Internecie (lub w inny sposób) adresy i numery telefonów trzech, wyznaczonych przez drużynowego, instytucji, dysponując tylko ich nazwą. Załatwiłam/em wyznaczoną przez drużynowego sprawę.', 158, ''),
(5931, 'W czasie gry:\nSchowałem/am i zamaskowałem/am sprzęt, a następnie odnalazłem/am nienaruszony schowek.\nOpisałem/am przebytą trasę, wykonałem/am jej dokładny szkic i opisałem/am spotkane osoby.', 158, ''),
(5932, 'Obserwowałem/am nieznany mi obóz z dalszej odległości. Dyskretnie stworzyłem/am szkic/plan tego obozu. Plan został sprawdzony pod względem poprawności dowolną metodą (np. przez wykorzystanie w trakcie podchodów przez inną osobę).', 158, ''),
(5933, 'Potrafię posługiwać się środkami łączności (np. CB radio). Znam zasady łączności radiowej. Bezbłędnie przekazałem/am meldunek w czasie gry terenowej, posługując się wybranymi sposobami łączności.', 158, ''),
(5934, 'Przeprowadziłem/am zwiad na określony temat różnymi technikami (wywiad, obserwacja, badanie dokumentów). Zebrałem/am materiały, sporządziłem/am szczegółowy raport z opisem wykonanych zadań i zaobserwowanych/badanych zdarzeń.', 159, ''),
(5935, 'Dotarłem/am w nocy do wyznaczonego miejsca oddalonego o co najmniej 4 kilometry posługując się wyłącznie mapą i busolą (bez wykorzystania elektronicznych środków lokalizacji).', 159, ''),
(5936, 'W nieznanym terenie niedostrzeżenie podszedłem/am wskazany obiekt. Wykonałem/am zadanie i niezauważony/a wycofałem/am się.', 159, ''),
(5937, 'Nawiązałem/am łączność radiową i przekazałem/am informacje zdobyte podczas zwiadu. Przeprowadziłem/am w zastępie/drużynie szkolenie z łączności.', 159, ''),
(5938, 'Wykonałem jedno z zadań:\nNadałem/am i odebrałem/am wiadomość zakodowaną alfabetem Morse’a za pomocą chorągiewek, latarki lub innego przyrządu.\nNadałem/am i odebrałem/am wiadomości za pomocą alfabetu semaforowego.', 161, ''),
(5939, 'Przeprowadziłem/am zajęcia z szyfrów lub sygnalizacji dla zastępu.', 161, ''),
(5940, 'Stworzyłem/am szyfr dla zastępu lub drużyny. Przeprowadziłem/am na zbiórce grę opartą na wykorzystaniu tego szyfru.', 161, ''),
(5941, 'Wiem, jak sprawdzić sygnaturę e-maila i jak powinien być on podpisany. Wiem, jak sprawdzić, czy połączenie jest bezpieczne. Wysłałem/am maila podpisanego lub szyfrowanego i wykonałem co najmniej jedno z zadań:\nPokazałem/am znajomemu lub członkowi rodziny, jak rozpoznać, że połączenie jest szyfrowane/bezpieczne.\nZaprezentowałem/am zastępowi lub członkom rodziny kilka przykładów phishingu bazując na wiedzy z portali takich jak niebezpiecznik.pl lub zaufanatrzeciastrona.pl.', 162, ''),
(5942, 'Wiem, jakie są zasady i sposoby przekazywania oraz gromadzenia poufnych informacji (np. danych osobowych), szczególnie w Internecie. Założyłem/am e-maila w domenie zhp.net.pl i wykorzystuję tę pocztę do załatwiania wszystkich spraw służbowych.', 162, ''),
(5943, 'Znam podstawowe metody łamania szyfrów. Złamałem/am szyfr i odczytałem/am wiadomość napisaną nieznanym mi szyfrem.', 162, ''),
(5944, 'Przeprowadziłem/am zajęcia dla harcerzy/harcerek z dziedziny bezpieczeństwa danych osobowych w Internecie, zademonstrowałem/am, ile można się o kimś dowiedzieć z portali społecznościowych i innych miejsc jego aktywności w sieci.', 162, ''),
(5945, 'Wziąłem/wzięłam udział w konkursie kryptologicznym (np. lamaczeszyfrow.pl) lub we własnym zakresie zapoznałem/am się z rodzajami szyfrów i metodami skutecznego ataku na nie.', 163, ''),
(5946, 'Znam metody Kasiskiego i Friedmana, potrafię skorzystać ze statystyki znaków do określenia rodzaju użytego szyfru oraz przypuszczenia ataku na szyfrogram. Złamałem/am jeden szyfr polialfabetyczny (klucz min. 3 znaki).', 163, ''),
(5947, 'Poznałem/am współczesne metody kryptologiczne stosowane do zabezpieczania danych i podstawowe metody szyfrowania danych. Wykonałem/am co najmniej jedno z zadań:\nNa bazie zdobytej wiedzy napisałem/am prosty program szyfrujący.\nZdobyłem/am wiedzę na temat śledzenia danych przez przeglądarki. Wybrałem/am taką, której używanie w zgodnie ze zdobyta wiedzą zapewnia największy poziom prywatności i bezpieczeństwa. Dobrałem/am dodatkowe metody nawiązywania bezpiecznych połączeń z Internetem.', 163, ''),
(5948, 'Przeprowadziłem/am zajęcia/zbiórkę z łamania szyfrów.', 163, ''),
(5949, 'Udzieliłem/am pomocy w następujących sytuacjach:\nOpatrzyłem/am ranę, zabezpieczyłem/am oparzenie.\nZareagowałem/am właściwie w sytuacji omdlenia.\nZadbałem/am o bezpieczeństwo osoby nieprzytomnej (oceniłem/am przytomność, udrożniłem/am drogi oddechowe i sprawdziłem/am oddech u poszkodowanego nieprzytomnego).', 1, 'Młodzik/Ochotniczka'),
(5950, 'Znam numery alarmowe, skorzystałem/am z nich w sytuacji rzeczywistej lub symulowanej podając dyspozytorowi niezbędne informacje.', 1, 'Młodzik/Ochotniczka'),
(5951, 'Unieruchomiłem/am rękę w przypadku urazu.', 1, ''),
(5952, 'Oceniłem/am czy mogę bezpiecznie udzielić pierwszej pomocy poszkodowanemu. Bezpiecznie zdjąłem/zdjęłam rękawiczki.', 1, ''),
(5953, 'Skompletowałem/am apteczkę osobistą.', 2, ''),
(5954, 'Ułożyłem/am poszkodowanego w pozycji bezpiecznej.', 2, ''),
(5955, 'Wykonałem/am prawidłowo uciśnięcia klatki piersiowej u osoby dorosłej (podczas RKO).', 2, ''),
(5956, 'Udzieliłem/am pierwszej pomocy w przypadku ukąszenia przez owady.', 2, 'Wywiadowca/Tropicielka'),
(5957, 'Prawidłowo postąpiłem/am przy krwotokach:\nUdzieliłem/am pierwszej pomocy w przypadku krwotoku z nosa.\nZałożyłem/am opatrunek uciskowy w przypadku krwotoku z rany.\nUdzieliłem/am pierwszej pomocy w przypadku złamania otwartego.', 2, 'Wywiadowca/Tropicielka'),
(5958, 'Zabezpieczyłem/am poszkodowanego przed utratą ciepła (zastosowałem/am termoizolację).', 2, ''),
(5959, 'Przeprowadziłem/am resuscytację krążeniowo-oddechową na fantomie osoby dorosłej.', 3, 'Odkrywca/Pionierka'),
(5960, 'Rozpoznałem/am objawy udaru słonecznego i udzieliłem/am poszkodowanemu pierwszej pomocy.', 3, 'Odkrywca/Pionierka'),
(5961, 'Rozpoznałem/am sytuacje, w których może dojść do urazu kręgosłupa, prawidłowo postąpiłem/am w przypadku podejrzenia urazu kręgosłupa u poszkodowanego.', 3, ''),
(5962, 'Rozpoznałem/am objawy zawału serca i udaru i udzieliłem/am pierwszej pomocy poszkodowanemu.', 3, 'Ćwik/Samarytanka'),
(5963, 'Udzieliłem/am pierwszej pomocy poszkodowanemu z napadem duszności i w przypadku zadławienia u osoby dorosłej.', 3, 'Ćwik/Samarytanka'),
(5964, 'Pełniłem/am służbę samarytańską, np. na wycieczce, biwaku, rajdzie. Dokonałem/am przeglądu apteczki drużyny i uzupełniłem/am ją. Opiekowałem/am się nią przez określony czas.', 3, ''),
(5965, 'Udzieliłem/am pierwszej pomocy poszkodowanemu podczas napadu drgawek.', 3, ''),
(5966, 'Współprowadziłem/am zbiórkę na temat pierwszej pomocy dla innych harcerzy.', 3, 'Ćwik/Samarytanka'),
(5967, 'Przez minimum 2 tygodnie samodzielnie przygotowywałem/am sobie drugie śniadanie do szkoły zgodnie z zasadami zbilansowanej diety.', 92, ''),
(5968, 'Wziąłem/wzięłam udział w wycieczce pieszej/rowerowej/kajakowej, trwającej około 4–5 godzin. Samodzielnie spakowałem/am plecak odpowiednio do potrzeb i pogody (uwzględniając zdrowe jedzenie, wodę, okrycie na słońce i deszcz, krem z filtrem itd.).', 92, 'Młodzik/Ochotniczka'),
(5969, 'Przez okres próby ćwiczyłem/am wybrany rodzaj sportu lub dziedzinę sprawności fizycznej (np. szybkość, skoczność, celność, utrzymywanie równowagi). Zapisałem/am wynik osiągany na początku (np. czas na 100 m) i poprawiłem/am go na koniec próby.', 92, ''),
(5970, 'Zademonstrowałem/am umiejętność jazdy na 3 różnych sprzętach (np. rower, hulajnoga, wrotki, rolki, łyżwy, sanki, narty).', 92, ''),
(5971, 'Wraz z rodzicami ułożyłem/am dla siebie tygodniowy jadłospis zgodnie z zasadami zbilansowanej diety. Pilnowałem/am jego przestrzegania (np. nie dojadając między posiłkami).', 93, ''),
(5972, 'Wziąłem/wzięłam udział w całodziennej wycieczce pieszej/rowerowej/kajakowej. Dowiedziałem/am się jak i przygotowałem/am na niej obiad niewymagający gotowania.', 93, 'Wywiadowca/Tropicielka'),
(5973, 'Przez okres próby ćwiczyłem/am wybrany sport lub dziedzinę sprawności fizycznej (np. szybkość, skoczność, celność, utrzymywanie równowagi). Wziąłem/wzięłam udział w zawodach sportowych w konkurencji wymagającej posługiwania się ćwiczoną sprawnością (np. wyścigi, skok w dal, rzucanie do celu, wyścig z jajkiem) i zająłem/zajęłam czołowe miejsce lub znacząco poprawiłem/am swój wynik z początku próby.', 93, 'Wywiadowca/Tropicielka'),
(5974, 'Wziąłem/wzięłam udział w meczu rekreacyjnym trzech różnych sportów drużynowych (np. piłki nożnej, piłki siatkowej, piłki ręcznej, dwóch ogni, hokeja na trawie).', 93, ''),
(5975, 'Znam i stosuję (wprowadzam w swoje życie) zasady zdrowego odżywiania się/ zdrowe nawyki żywieniowe. Przez okres min. 2 miesięcy dbałem/am o prawidłowe nawodnienie organizmu, pijąc min. 2 litry wody dziennie, i unikałem/am słodkich przekąsek.', 93, 'Wywiadowca/Tropicielka'),
(5976, 'Przez trzy miesiące spałem/am minimum po siedem godzin dziennie, dbając o to, aby przed snem się wyciszyć. Dowiedziałem/am się, dlaczego używanie urządzeń z wyświetlaczem przed snem jest niekorzystne dla jakości snu, i nie korzystałem/am z nich na min. godzinę przed snem.', 94, 'Odkrywca/Pionierka'),
(5977, 'Po konsultacji z lekarzem wykonałem/am badanie profilaktyczne, wynikające z mojego stylu życia (np. badanie pod kątem HCV czy HBV, jeśli chodzę do kosmetyczki lub mam nowy tatuaż, badanie poziomu glukozy lub hormonów tarczycy, jeśli często jestem zmęczony bez wyraźnego powodu).', 94, ''),
(5978, 'Przez okres trwania próby minimum raz w tygodniu uprawiałem/am wybraną aktywność fizyczną.', 94, 'Odkrywca/Pionierka'),
(5979, 'Przez 3 miesiące spałem/am minimum po 7 godzin dziennie, dbając o to, aby przed snem się wyciszyć.', 94, 'Ćwik/Samarytanka'),
(5980, 'W porozumieniu z rodzicami i ew. dietetykiem dobrałem/am właściwy dla siebie tryb i sposób żywienia, monitorując uważnie reakcje swojego organizmu na wprowadzane nowości.', 94, 'Ćwik/Samarytanka'),
(5981, 'Zaplanowałem/am i zorganizowałem/am długotrwały wysiłek fizyczny o charakterze wyczynu i dobrałem/am dostosowaną do niego dietę. W miarę możliwości uzyskałem/am dla swojego pomysłu akceptację profesjonalisty.', 95, 'Harcerz Orli/Harcerka Orla'),
(5982, 'Dowiedziałem/am się, jak ważny jest dobry sen dla zdrowia człowieka. Poznałem/am zalecenia dla nastolatków związane z długością i jakością snu. Na podstawie raportów z aplikacji mobilnych dokonałem/am przeglądu jakości swojego snu i wprowadziłem/am w swoim życiu zmiany pozwalające na uzyskanie większej liczby godzin snu głębokiego.', 95, ''),
(5983, 'Wziąłem/wzięłam udział w profilaktycznej wizycie u specjalisty (np. u fizjoterapeuty, ginekologa) lub samodzielnie wykonałem/am 3 ekokosmetyki.', 95, ''),
(5984, 'Przez 3 miesiące uprawiałem/am raz w tygodniu wybraną formę aktywności fizycznej.', 95, ''),
(5985, 'Opiekowałem/am się młodszym rodzeństwem lub innym dzieckiem przez kilka godzin (np. pod nieobecność rodziców lub gdy byli bardzo zajęci), umiejętnie je zabawiając i zapewniając bezpieczeństwo.', 223, ''),
(5986, 'Wiem, jak ocenić temperaturę człowieka. Regularnie mierzyłem/am temperaturę chorego, który znajdował się pod moją opieką co najmniej przez trzy dni.', 223, ''),
(5987, 'Prawidłowo zaparzyłem/am herbatę ziołową osobie starszej lub chorej.', 223, ''),
(5988, 'Potrafię opiekować się małym dzieckiem. Pod nadzorem rodziców zadbałem/am o powierzone mi dziecko (np. rodzeństwo, dziecko sąsiadów) przez dłuższy okres (np. miesiąc, wakacje):\nprzygotowywałem/am odpowiednie posiłki,\nzabierałem/am na spacer w ubraniu dostosowanym do warunków,\nzapewniałem/am bezpieczną zabawę dostosowaną do wieku dziecka,\njeżeli była taka potrzeba, towarzyszyłem/am przy toalecie porannej i wieczornej oraz usypiałem/am.', 224, ''),
(5989, 'Dowiedziałem się, jak odpowiednio zaopiekować się osobą chorą. Opiekowałem/am się chorym w domu lub na obozie. Wykonywałem/am następujące czynności:\nodpowiednio przygotowałem łóżko osobie chorej na dzień i na noc,\npodawałem/am posiłki i lekarstwa według zaleceń lekarza.', 224, ''),
(5990, 'Pomagałem/am rodzicom w organizacji co najmniej jednego dużego rodzinnego wydarzenia. Wykonałem/am co najmniej jedno z zadań:\nwykonałem/am drobne prace przy okazji remontu, np. mieszkania (malowanie ściany, gipsowanie dziur itp.),\npomagałem/am ułożyć zastawę stołową i przygotować potrawy na uroczystość rodzinną,\numiejętnie pakowałem i rozpakowywałem różne drobne przedmioty (zastawa kuchenna, bibeloty, elektronika itp.) w czasie przeprowadzki.', 224, ''),
(5991, 'Własnoręcznie przygotowałem/am niespodziankę dla dziecka (własnoręcznie wykonana zabawka, uszyte/przerobione ubranko, ulubiony podwieczorek).', 225, ''),
(5992, 'Opracowałem/am krótki wykaz książek i/lub miejsc w Internecie (np. fora, blogi, wideoblogi, kanały YouTube) dotyczących opieki nad dzieckiem. Zapoznałem/am z nim osoby zainteresowane tym tematem.', 225, ''),
(5993, 'Przygotowałem/am dietetyczny posiłek odpowiedni do rodzaju choroby osoby, którą się opiekowałem/am.', 225, ''),
(5994, 'Wiem, jakie są prawidłowe poziomy ciśnienia krwi i cukru we krwi. Wykonałem co najmniej jedno zadanie:\nzmierzyłem/am osobie chorej lub starszej ciśnienie krwi,\nzmierzyłem/am osobie chorej lub starszej poziom cukru we krwi.', 225, ''),
(5995, 'Wiem, jak się zachowywać w czasie epidemii. Ograniczyłem/am do minimum wyjścia z domu i bezpośrednie kontakty z innymi osobami. Zachowałem/am kontakt telefoniczny/internetowy z moimi bliskimi, z którymi nie mieszkam (np. dziadkami).', 226, ''),
(5996, 'Myłem/am ręce kilka/kilkanaście razy dziennie przez 30 sekund. Obowiązkowo myłem/am ręce po powrocie do domu i po kontakcie z innymi osobami.', 226, ''),
(5997, 'Codziennie uczyłem/am się, niezależnie od tego, czy otrzymywałem/am materiały do nauki (jeżeli nie otrzymałem/am materiałów ze szkoły, czytałem/am lektury i powtarzałem/am sobie wcześniej zrealizowany materiał).', 226, ''),
(5998, 'Wziąłem/am udział w zdalnej zbiórce zastępu.', 226, ''),
(5999, 'Dowiedziałem/am się, czym jest izolacja społeczna. Zastosowałem/am w praktyce jej zasady.', 227, ''),
(6000, 'W czasie pobytu w domu byłem/am aktywny/a: skończyłem/am czytać książkę, posprzątałem/am mieszkanie, dokonałem/am przeglądu i napraw mojego umundurowania i sprzętu harcerskiego.', 227, ''),
(6001, 'Regularnie ćwiczyłem/am, by utrzymać sprawność fizyczną. Dokumentowałem/am ćwiczenia i postępy w nich.', 227, ''),
(6002, 'Codziennie pomagałem/am w przyrządzaniu przynajmniej 1 posiłku dla innych domowników z zachowaniem wszystkich zasad higieny.', 227, ''),
(6003, 'Dowiedziałem/am się, czym różni się bakteria od wirusa, dlatego wiem, na czym polegają działania prewencyjne. Zastosowałem/am się do zaleceń służb sanitarnych.', 228, ''),
(6004, 'Unikałem/am fake newsów - wiedzę czerpałem/am z oficjalnych portali. Przekazywałem/am sprawdzone informacje do swoich znajomych m. in. za pomocą mediów społecznościowych.', 228, ''),
(6005, 'Pomogłem/am rodzeństwu lub kolegom/młodszym harcerzom (najlepiej w trybie internetowym) w nauce wybranego przedmiotu.', 228, ''),
(6006, 'Opracowałem/am samodzielnie lub z inną osobą zbiórkę dla zastępu i przeprowadziłem/am ją z wykorzystaniem łączy internetowych.', 228, ''),
(6007, 'Opracowałem/am zbiórkę dla drużyny i przeprowadziłem/am ją z wykorzystaniem łączy internetowych. Materiały potrzebne do zorganizowania zbiórki udostępniłem/am innym wędrownikom/instruktorom.', 229, ''),
(6008, 'Utrzymywałem/am kontakt (internetowy lub telefoniczny) z osobami, z którymi nie powinienem się spotykać osobiście (np. sąsiedzi, dziadkowie). Zorganizowałem/am dla nich pomoc w funkcjonowaniu w czasie kwarantanny (np. pomoc w zakupach i załatwianiu innych ważnych spraw).', 229, ''),
(6009, 'Realizując dewizę wędrowniczą (Wyjdź w świat, zobacz, pomyśl – pomóż, czyli działaj), podjąłem/am w miarę swoich możliwości służbę zgodną ze społecznymi potrzebami i zaleceniami służb sanitarnych.', 229, ''),
(6010, 'Wiem, jak niebezpieczne w domu jest wspinanie się na meble, parapety itp. Aby sięgnąć po coś z góry lub umyć okna ,używałem/am wyłącznie stabilnych taboretów lub domowych drabinek.', 230, ''),
(6011, 'Dowiedziałem/am się, na czym polega zasada ograniczonego zaufania na drodze. Mimo posiadania pierwszeństwa na pasach zawsze sprawdzałem/am przed wejściem na nie, czy nie nadjeżdża rozpędzony samochód/rower.', 230, ''),
(6012, 'Wykonałem/am co najmniej jedno zadanie:\ndowiedziałem/am się, jak należy zachowywać się w obecności nieznanego psa, jakie zachowania mogą sprowokować jego agresję. Zachowuję bezpieczny dystans wobec obcych zwierząt,\nwziąłem/wzięłam udział w zajęciach lub porozmawiałem/am z rodzicami o tym, jak należy reagować na zaczepki obcych ludzi, ich propozycje i żądania. Ustaliłem/am z rodzicami schemat działań w sytuacji zagrożenia i go stosuję.', 230, ''),
(6013, 'Dowiedziałem się, na czym polega zagrożenie bąblowicą. W czasie próby myłem/am wszystkie owoce zebrane w lesie.', 230, ''),
(6014, 'W czasie wycieczki starałem/am się nie oddalać od grupy. Zawsze w czasie wyjazdu grupowego posiadałem/am numer telefonu do zastępowego/drużynowego/kierownika grupy.', 230, ''),
(6015, 'Nauczyłem/am się zabezpieczać dom przed złodziejami i zdarzeniami losowymi. Sporządziłem/am listę kontrolną czynności, które należy wykonać przed wyjściem z domu/mieszkania (zamknięcie okien i balkonu, wyłączenie kuchenki gazowej, wyłączenie urządzeń elektrycznych, sprawdzenie kranów z wodą itp.). Regularnie ją stosowałem/am przed każdym wyjściem z domu.', 231, ''),
(6016, 'Wykonałem co najmniej jedno z poniższych zadań:\n-poruszając się po drodze poza miastem, zastosowałem/am komplet odblasków zapewniający widoczność mojej sylwetki ze wszystkich stron;\n-poruszając się rowerem po drodze, zastosowałem/am kamizelkę odblaskową i kask rowerowy.', 231, ''),
(6017, 'Poznałem zasady bezpieczeństwa na obozie. Wziąłem udział w rożnego rodzaju alarmach (np. przeciwpożarowym, ewakuacyjnym), sprawnie wykonując polecenia opiekunów.', 231, ''),
(6018, 'Opracowałem/am trasę dotarcia do wyznaczonego punktu, wykorzystując m.in. komunikację publiczną (np. miejską). Sprawdziłem rozkład jazdy, wybrałem odpowiednie połączenie i zakupiłem bilet MPK/PKS/PKP. Zapoznałem/am się z podstawowymi zasadami korzystania z komunikacji publicznej (np. kiedy i jak odbijamy bilety, które miejsca w autobusach i tramwajach są przeznaczone dla mnie, dla starszych i chorych, jak używamy rączek i uchwytów podczas podróży na stojąco, do czego służą dzwonki alarmowe i gdzie się znajdują).', 231, ''),
(6019, 'Sprawdziłem/am w mieszkaniu/domu, gdzie znajdują się zawory: gazu, wody i wyłącznik prądu. Nauczyłem/am się je obsługiwać. Dowiedziałem/am się, kiedy i w jakich okolicznościach należy je zamknąć.', 232, ''),
(6020, 'Poznałem/am zasady bezpiecznego poruszania się w miejscach publicznych pieszo, na rowerze, rolkach, hulajnodze itp. (zgodnie z tym, z czego korzystam). Zastosowałem/am się do tych zasad w czasie mojego codziennego korzystania z chodnika, jezdni, ścieżki rowerowej.', 232, ''),
(6021, 'Na obozie pomagałem/am w przeprowadzeniu dla młodszych instruktażu dotyczącego bezpieczeństwa (np. zapoznając z zasadami posługiwania się ogniem, wartowania, ewakuowania się z obozu). Pomagałem/am w przeprowadzeniu alarmów np. przeciwpożarowego, ewakuacyjnego.', 232, ''),
(6022, 'Dowiedziałem/am się, jak rozpoznać kleszcza. Zapoznałem/am się z poprawnymi technikami wyciągania kleszczy i prawidłowo usuwałem/am te pasożyty. Zaopatrzyłem/am się w narzędzia umożliwiające usuwanie kleszczy: pęseta, pętla, kleszczołapki itp.', 232, ''),
(6023, 'Wybrałem/am się na samodzielną wycieczkę do innego miasta/innej części regionu mojego zamieszkania. Przed wyjazdem skompletowałem/am i zabezpieczyłem/am przed zgubieniem i kradzieżą dokumenty oraz pieniądze. Sprawdziłem/am prognozy pogody i przygotowałem/am odpowiedni ubiór na wyjazd. Skorzystałem/am z komunikacji publicznej, zakupiłem/am odpowiedni bilet (zniżka) na przejazd pociągiem lub autobusem. Podczas przejazdu dostosowałem/am się do zasad bezpieczeństwa obowiązujących wszystkich podróżnych.', 232, ''),
(6024, 'Dowiedziałem się, na czym polega w domu zagrożenie tlenkiem węgla i pożarem. Wykonałem/am co najmniej jedno zadanie:\nzainstalowałem/am w domu czujnik CO,\nzaopatrzyłem/am dom w podręczną gaśnicę dostosowaną do gaszenia drobnych pożarów, które najczęściej występują w mieszkaniach, a których nie należy gasić wodą.', 233, ''),
(6025, 'Zapoznałem/am młodszych z regulaminem/zasadami poruszania się po drogach. Uwzględniłem/am przy tym zagrożenia wynikające z analizy potencjalnych niebezpieczeństw w mojej okolicy jak np. niebezpieczne przejścia przez jezdnię, ulice bez chodników, miejsca na drogach ze słabą widocznością dla kierowcy (zakręty, wzniesienia). Przeprowadziłem/am bezpiecznie grupę (np. zastęp, drużynę) po różnych rodzajach dróg/ulic na dystansie kilku kilometrów.', 233, ''),
(6026, 'Uczestniczyłem/am w działaniach profilaktyczno-edukacyjnych dotyczących bezpieczeństwa (np. handlu ludźmi, przemocy w szkole itp.). Przeprowadziłem/am dla swojej drużyny/klasy warsztaty dotyczące bezpieczeństwa.', 233, ''),
(6027, 'Dowiedziałem/am się, jakie niebezpieczeństwa grożą osobom poruszającym się po lesie - szczególnie jednoosobowo. Na obozie przeprowadziłem/am instruktaż, a następnie nadzorowałem/am wyjścia do lasu (np. na sprawność Leśnego człowieka) mniej doświadczonych harcerzy.', 233, ''),
(6028, 'Dowiedziałem/am się, jakie niebezpieczeństwa mogą na mnie czekać w czasie podróży zagranicznej lub podróży po Polsce w odległe miejsce. Wykonałem/am następujące zadania:\nustaliłem/am i pozostawiłem/am domownikom informacje o tym, dokąd jadę i pod jakim adresem/adresami będę przebywał/a,\nustaliłem/am sposób i częstotliwość kontaktowania się z osobami, które zostały w domu (wszelkie zakłócenia mogą stanowić sygnał alarmowy),\nnie rozstawałem/am się ze swoimi dokumentami. Pamiętałem/am, że mam OBOWIĄZEK mieć je przy sobie i nikt poza mną nie ma prawa ich przetrzymywać.', 233, ''),
(6029, 'Znam zasady segregacji odpadów w swoim miejscu zamieszkania. Segreguję zgodnie z nimi odpady w domu, szkole i harcówce.', 4, 'Młodzik/Ochotniczka'),
(6030, 'Podczas zakupów korzystam wyłącznie z wielorazowych toreb.', 4, ''),
(6031, 'Przez okres trwania próby raz w tygodniu podejmowałem/am przynajmniej półgodzinną aktywność na łonie przyrody (w parku, lesie, na łące itd.), stosując się do zasad właściwego zachowania w terenie (m.in. zostaw to, co znajdziesz, w stanie nienaruszonym, zachowaj ciszę, niech natura ma głos, rozpalaj ogień tylko tam, gdzie jest to dozwolone).', 4, ''),
(6032, 'Nauczyłem/am się dokonywać obserwacji dzikich zwierząt w sposób bezpieczny i nieingerujący w ich życie. Wykazałem/am się tymi umiejętnościami podczas wycieczki, rajdu lub biwaku.', 4, ''),
(6033, 'Korzystałem/am regularnie z wielorazowej butelki na wodę, nie kupując wody w jednorazowych, plastikowych butelkach, o ile nie było to absolutnie konieczne, przynajmniej przez 3 miesiące.', 5, ''),
(6034, 'Korzystałem/am wyłącznie z wielorazowych naczyń i sztućców na biwakach, imprezach szkolnych i harcerskich przez minimum 3 miesiące.', 5, 'Wywiadowca/Tropicielka'),
(6035, 'Przez okres trwania próby odpowiednio pozbywałem/am się swoich odpadów podczas wędrówek w terenie – korzystałem/am z wielorazowej torby na odpady oraz zakopałem/am we właściwy sposób nadające się do tego odpady biologiczne.', 5, ''),
(6036, 'Opowiedziałem/am innym (np. członkom swojego zastępu) o tym, kiedy można spotkać podloty oraz młode innych gatunków (np. zające, sarny) oraz dlaczego należy pozostawić je w spokoju tam, gdzie są. Wyjaśniłem/am, w jakich sytuacjach należy pomóc rannemu lub zagrożonemu zwierzęciu oraz zademonstrowałem/am, jak należy wezwać pomoc. Zapisałem/am w telefonie komórkowym numer do najbliższego ośrodka rehabilitacji dzikich zwierząt (lub analogicznej instytucji).', 5, ''),
(6037, 'Zapoznałem/am się z ideą domowych/balkonowych ogrodów. Wyhodowałem/am jadalną roślinę i użyłem/am jej do przyrządzenia posiłku dla rodziny.', 5, 'Wywiadowca/Tropicielka'),
(6038, 'Wziąłem/wzięłam udział w kilkudniowej wyprawie z nocowaniem na łonie przyrody. Nauczyłem/am się odpowiednio załatwiać swoje potrzeby fizjologiczne, będąc w terenie.', 6, ''),
(6039, 'Wiem, co to jest rezerwat przyrody, park narodowy i park krajobrazowy. W czasie harcerskich wędrówek odwiedziłem/am jeden z parków narodowych lub krajobrazowych.', 6, 'Odkrywca/Pionierka'),
(6040, 'Dowiedziałem/am się, czym jest globalne ocieplenie, jakie są jego przyczyny oraz prognozowane konsekwencje. Poznałem/am pojęcie śladu węglowego, opowiedziałem/am o stosowanych we własnym życiu działaniach na rzecz jego zmniejszania (np. dojeżdżanie do szkoły rowerem lub komunikacją miejską, stosowanie różnych sposobów oszczędzania energii). W trakcie trwania próby wyrobiłem/am w sobie nowy nawyk zmniejszający emisję gazów cieplarnianych.', 6, 'Ćwik/Samarytanka'),
(6041, 'Dowiedziałem/am się, jaki wpływ na środowisko wywieram podczas wypraw, wycieczek, obozów, biwaków na łonie przyrody. Poznałem/am podstawowe techniki minimalizacji tego wpływu. Kompletuję własny ekwipunek, uwzględniając w nim przynajmniej jedną pomocną w tym rzecz (np. sprzęt do hamakowania, wielorazowy worek na resztki/odpady, worki lub pojemniki zabezpieczające żywność przed dostępem dzikich zwierząt, sprzęt do gotowania, składana miska do mycia).', 6, 'Ćwik/Samarytanka'),
(6042, 'Wykonałem jedno z poniższych zadań:\n-Znalazłem/am ulubioną formę spędzania czasu na łonie natury. W trakcie trwania próby poświęcałem/am jej w sumie minimum 4 godziny w miesiącu.\n-Zaangażowałem/am się w projekt dotyczący ochrony przyrody.', 6, ''),
(6043, 'Przeanalizowałem/am ilość i rodzaj generowanych przez siebie śmieci. Zmniejszyłem o założony procent ilość generowanych odpadów lub wyeliminowałem/am z moich powtarzalnych zakupów jeden rodzaj ￹nieekologicznego opakowania.', 6, 'Ćwik/Samarytanka'),
(6044, 'Poznałem/am podstawowe problemy związane z dostępem do wody pitnej (w tym problem jej niedoboru) na świecie i w Polsce. Wykonałem/am jedno z poniższych zadań:\n-Poznałem/am różne sposoby pozyskania lub uzdatniania wody pitnej w terenie (destylacja, filtry osobiste, filtracja i uzdatnianie chemiczne itp.). Podczas wędrówki zastosowałem/am jeden z nich.\n-Przeprowadziłem/am akcję szerzenia dobrych praktyk w zakresie gospodarowania wodą, w tym:oszczędzania wody, gromadzenia deszczówki do podlewania roślin,\nniekoszenia trawników, tworzenia łąk kwietnych zamiast trawników, niepodlewania ogrodu/trawnika w ciągu dnia (w największym słońcu).', 7, ''),
(6045, 'Dowiedziałem/am się, czym jest i skąd bierze się mikroplastik oraz oceaniczne „wyspy śmieci”. Wprowadziłem/am w swoim życiu przynajmniej jedną wymierną zmianę wpływającą na zmniejszanie ilości mikroplastiku, np. niekupowanie produktów zawierających mikrogranulki (m.in. past do zębów, peelingów i chemii gospodarczej) lub stosowanie się do wskazówek prania ubrań ze sztucznych włókien.', 7, ''),
(6046, 'Dowiedziałem/am się, gdzie znaleźć rzetelne informacje na temat ekologii i ochrony środowiska. Nauczyłem/am się korzystać z tych źródeł w razie potrzeby i poleciłem/am je innym.', 7, ''),
(6047, 'Znam i stosuję zasady ekologicznego obozowania. Wdrażam w nie młodszych od siebie harcerzy, pomagając im w ekologicznym obozowaniu/biwakowaniu.', 7, 'Harcerz Orli/Harcerka Orla'),
(6048, 'Rozpoznałem/am podstawowe jadalne rośliny leśne (np. borówka, poziomka, malina, jeżyna, orzechy laskowe).', 78, ''),
(6049, 'Rozpoznałem/am zwierzęta dziko żyjące w Polsce: sarny, jelenie, dziki, łosie, żubry, bobry, wilki. Co najmniej dwa z nich widziałem na własne oczy.', 78, ''),
(6050, 'Podczas spaceru po lesie lub innym terenie zielonym rozpoznałem/am po sylwetkach, korze i liściach 8 rodzajów drzew (np. dąb, brzozę, klon, leszczynę, wierzbę, topolę, lipę, sosnę, świerk, jodłę, modrzew).', 78, ''),
(6051, 'Przez przynajmniej 2 miesiące hodowałem/am własną roślinę, obserwując jej wzrost.', 78, ''),
(6052, 'Rozpoznałem/am przynajmniej 5 roślin i grzybów niebezpiecznych lub trujących (np. konwalia majowa, barszcz sosnowskiego, wawrzynek wilczełyko, pokrzyk, wilcza jagoda, trzmielina). Opowiedziałem/am innym jak postępować i pomóc komuś w przypadku spożycia/kontaktu z nimi.', 79, ''),
(6053, 'Rozpoznałem/am żmiję, odróżniając ją od innych polskich węży oraz jaszczurek beznogich (padalców).', 79, ''),
(6054, 'Wykonałem/am jedno z poniższych zadań:\n-Przesadziłem/am 5 roślin doniczkowych.\n-Wyhodowałem/am roślinę z nasiona.\n-Założyłem/am ogród dla owadów zapylających.\n-Zasadziłem/am 5 drzew.', 79, ''),
(6055, 'Zbudowałem/am oraz zainstalowałem/am bezpieczną oraz odpowiednich wymiarów budkę lęgową lub karmnik, lub poidełko dla ptaków bądź innych zwierząt lub hotel dla owadów.', 79, ''),
(6056, 'Rozpoznałem/am i opowiedziałem innym osobom o życiu co najmniej 10 występujących w moim otoczeniu roślin i zwierząt:\nprzynajmniej 2 gatunki drzew lub krzewów,\nprzynajmniej 1 ptaka, 1 płaza, 1 owada, 1 pajęczaka, 1 skorupiaka i 1 mięczaka.', 80, ''),
(6057, 'Spędziłem/am dzień na łonie natury, dokonując obserwacji przyrodniczych samodzielnie (ewentualnie pod opieką osoby dorosłej) i dokumentując ten fakt w dowolnej formie. Swoimi wrażeniami podzieliłem/am się z resztą zastępu.', 80, ''),
(6058, 'Wyjaśniłem/am harcerzom z zastępu rolę martwego drewna w lesie. Nauczyłem/am ich, co z tego wynika i jakiego drewna należy używać na ogniska.', 80, ''),
(6059, 'Zorganizowałem/am co najmniej jedną wycieczkę zastępu do jednego z miejsc chroniących przyrodę (obszar Natura 2000, park krajobrazowy, rezerwat, park narodowy). Zaprezentowałem/am walory przyrodnicze wybranego miejsca.', 80, ''),
(6060, 'Skompletowałem/am biblioteczkę (książki, artykuły, foldery, adresy stron internetowych) o tematyce ekologicznej. Przeprowadziłem/am zbiórkę na temat znaczenia przyrody dla zdrowia człowieka oraz potrzeby jej ochrony przed zagrożeniami cywilizacyjnymi.', 81, ''),
(6061, 'Poznałem/em kilka gatunków roślin i zwierząt zagrożonych wyginięciem w Polsce. Wziąłem/wzięłam udział w akcji na rzecz ochrony gatunków rodzimych (np. akcje reintrodukcji, akcje ochrony płazów migrujących na wiosnę, wyznaczania/monitorowania stref ochronnych, akcji liczenia raków).', 81, ''),
(6062, 'Nauczyłem/am się reagować na typowe działania szkodliwe dla środowiska naturalnego (np. wypalanie traw, wyrzucanie odpadów do lasu, zabieranie dzikich zwierząt, rozpalanie ognia w miejscach zabronionych, niszczenie środowiska, wycinka drzew dziuplastych). Wykazałem/am się przynajmniej raz czynną postawą w obliczu szkodliwych zachowań.', 81, ''),
(6063, 'Zapoznałem/am się z celami i działaniami kilku organizacji ekologicznych. Przez co najmniej pół roku współpracowałem/am z jedną z nich.', 81, ''),
(6064, 'Wyhodowałem/am z nasion, pestek lub cebulek wybraną przez siebie roślinę, opiekowałem/am się nią i udokumentowałem/am np. na zdjęciach etapy jej wzrastania i przedstawiłem/am je na zbiórce.', 203, ''),
(6065, 'Odwiedziłem/am ogród botaniczny (lub byłem/am na łące, w lesie, w polu itp.) i opowiedziałem/am na zbiórce o swoich obserwacjach dotyczących występującej tam roślinności.', 203, ''),
(6066, 'Opiekowałem/am się roślinami w domu, w harcówce, w szkolnej pracowni lub na działce przez cały okres próby na sprawność.', 203, ''),
(6067, 'Odnalazłem/am i odwiedziłem/am w swojej okolicy co najmniej dwa drzewa/grupy drzew – pomniki przyrody.', 204, ''),
(6068, 'Opowiedziałem/am w drużynie o interesujących roślinach występujących w rezerwacie przyrody w mojej okolicy.', 204, ''),
(6069, 'Przygotowałem/am w ciekawej formie informację o wybranych 15 roślinach chronionych występujących w Polsce i przedstawiłem/am ją na zbiórce zastępu, drużyny lub w klasie. Celem mojej prezentacji było zapobieżenie niszczeniu roślin chronionych wynikającemu z niewiedzy, dlatego co najmniej połowa prezentowanych roślin to takie, na które możemy natrafić w swoim otoczeniu.', 204, ''),
(6070, 'Przedstawiłem/am na zbiórce drużyny w ciekawy sposób wybrane gatunki roślin występujących na świecie, zagrożonych wyginięciem („czerwona księga”).', 205, ''),
(6071, 'Poprowadziłem/am wycieczkę zastępu/drużyny w celu poznania środowiska roślinnego najbliższej okolicy. Ciekawie opowiedziałem o co najmniej 8 roślinach, które napotkaliśmy (np. na łące, w lesie). Przygotowując wyprawę, korzystałem ze swoich zbiorów: albumów, atlasów, zielników, zbiorów cyfrowych itp. na temat roślin.', 205, ''),
(6072, 'Zorganizowałem/am i poprowadziłem/am wyprawę zastępu/drużyny do ogrodu botanicznego, parku narodowego, rezerwatu przyrody albo muzeum przyrodniczego.', 205, '');
INSERT INTO `zadaniasprawnosciharcerskich` (`ID`, `Tresc`, `Sprawnosc`, `Stopien`) VALUES
(6073, 'Znalazłem/am w lesie i na łące co najmniej 6 roślin jadalnych, które zebrałem w celu wykorzystania.', 200, ''),
(6074, 'Przygotowałem/am napar (herbatę) z pokrzywy, mięty, rumianku lub innego zioła.', 200, ''),
(6075, 'W czasie wędrówki zastępu pokazałem/am innym harcerzom/harcerkom miejsce występowania co najmniej 3 roślin użytkowych (opowiedziałem/am o ich zastosowaniu) i co najmniej 3 trujących (wyjaśniłem/am dlaczego należy ich unikać).', 200, ''),
(6076, 'Samodzielnie wysuszyłem/am i odpowiednio spakowałem/am przyprawy, które następnie wziąłem/wzięłam na biwak – np. bazylia, natka pietruszki itp., w tym co najmniej jedną przyprawę samodzielnie znalezioną w terenie.', 201, ''),
(6077, 'Przyrządziłem/am ziołowy środek przydatny w leczeniu, kosmetyce, w gospodarstwie domowym.', 201, ''),
(6078, 'Prowadziłem/am zbiór roślin (zgodnie z kalendarzem ich zbierania). Zebrałem/am, zasuszyłem/am i przechowałem/am co najmniej 3 wybrane rośliny, tak aby nie straciły swoich właściwości.', 201, ''),
(6079, 'Zorganizowałem/am i poprowadziłem/am wyprawę zastępu/drużyny na łąkę lub do lasu w celu zbierania ziół i innych roślin jadalnych.', 201, ''),
(6080, 'Przygotowałem co najmniej 8 potraw (minimum 4 ciepłe) z wykorzystaniem samodzielnie zebranych, dziko rosnących owoców i ziół (przy wykorzystaniu dodatkowo takich produktów jak kasza, mąką i miód).', 202, ''),
(6081, 'Potrafię zebrać „w terenie” rośliny jadalne o każdej porze roku. Przygotowałem/am posiłek leśny na wypadzie zastępu/drużyny wiosną, latem, jesienią i zimą.', 202, ''),
(6082, 'Przygotowałem/am do apteczki drużyny zestaw ziół oraz krótki opis sposobu ich stosowania.', 202, ''),
(6083, 'Przeprowadziłem zbiórkę w zastępie/drużynie na temat stosowania ziół w domowym leczeniu.', 202, ''),
(6084, 'Dowiedziałem/am się, jakie kwiaty można uprawiać w Polsce, a jakie sprowadza się z zagranicy. Ułożyłem/am 3 bukiety składające się wyłącznie z polskich kwiatów.', 83, ''),
(6085, 'Pielęgnowałem/am w czasie próby rzadki lub trudny w uprawie okaz kwiatu. Swoje doświadczenia z uprawy przedstawiłem/am w zastępie.', 83, ''),
(6086, 'Wykonałem/am jedno z poniższych zadań:\n-Zastosowałem/am suszone kwiaty i inne rośliny do wykonania różnych ozdób (np. kartek urodzinowych, zakładek do książki, kolczyków).\n-Dowiedziałem/am się co to jest „ikebana”. Wykonałem/am kwiatową dekorację na wybraną uroczystość w rodzinie, szkole lub drużynie.', 83, ''),
(6087, 'Rozpoznałem/am co najmniej 15 różnych kwiatów w tym kwiaty ogrodowe, doniczkowe i polne.', 82, ''),
(6088, 'Pielęgnowałem/am w czasie próby własny ogródek kwiatowy (np. na klombie, w doniczkach, na działce) składający się co najmniej z kilku gatunków.', 82, ''),
(6089, 'Dowiedziałem/am się, w jakich miesiącach i porach roku kwitną w Polsce poszczególne kwiaty. Ułożyłem/am 3 bukiety, w tym jeden wyłącznie z roślin sezonowych.', 82, ''),
(6090, 'Rozpoznałem/am nasiona popularnych warzyw np. marchwi, pietruszki, sałaty, ogórków, buraczków, groszku i dyni.', 196, ''),
(6091, 'Wyhodowałem/am samodzielnie co najmniej 5 warzyw z nasion lub rozsady. Starałem/am się nie korzystać z nawozów sztucznych oraz oszczędnie gospodarowałem/am wodą (np. łapałem/am deszczówkę).', 196, ''),
(6092, 'Przygotowałem/am wybrane warzywa do przechowania podczas zimy.', 196, ''),
(6093, 'Rozpoznałem/am nasiona rzadziej hodowanych warzyw np. karczochów, jarmużu, rzepy, szpinaku i rukoli.', 197, ''),
(6094, 'Zaplanowałem/am i przeprowadziłem/am siew i zbieranie plonów na działce lub w ogrodzie. Nie korzystałem/am z nawozów sztucznych oraz oszczędnie gospodarowałem/am wodą (np. łapałem/am deszczówkę).', 197, ''),
(6095, 'Przygotowałem/am kompost z odpadów ogrodniczych. Nawoziłem/am swoje uprawy bez stosowania środków chemicznych. Zdobyłem/am wiedzę o tym, jak sąsiedztwo niektórych roślin odstrasza pasożyty i zapobiega chorobom moich upraw. Zdobytą wiedzę zastosowałem/am w moich uprawach.', 197, ''),
(6096, 'Prowadziłem/am mały ogródek permakulturowy, np. grządkę podwyższoną lub ogród w pojemniku drenażowym na balkonie.', 197, ''),
(6097, 'Rozpoznałem/am po pniach i liściach popularne drzewa owocowe np. jabłonie, grusze, śliwy, wiśnie, czereśnie oraz krzewy owocowe np. maliny, porzeczki, winorośl.', 84, ''),
(6098, 'Wziąłem/wzięłam udział w sadzeniu drzew owocowych lub krzewów jagodowych.', 84, ''),
(6099, 'Wziąłem/wzięłam udział w całorocznej pielęgnacji sadu np. przycinaniu drzew i krzewów, szczepieniu drzewek, zabezpieczeniu go przed szkodnikami itp.', 84, ''),
(6100, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Wziąłem/wzięłam udział w zbiorze owoców. Prawidłowo ułożyłem/am je w skrzynce/pojemniku i przygotowałem/am do transportu.\n-Wziąłem/wzięłam udział w przygotowaniu owoców do składowania przez zimę.', 84, ''),
(6101, 'Obserwowałem/am życie lasu o świcie, o zmierzchu i w nocy. Wytropiłem/am zwierzę i obserwowałem/am jego życie. Poszerzyłem swoją wiedzę o zwyczajach tego zwierzęcia w książkach, telewizji lub Internecie. Przedstawiłem swoje obserwacje i zdobytą wiedzę w dowolnej formie (opowieść przy ognisku, wpis w Internecie itp.).', 198, ''),
(6102, 'Odbyłem/am obchód lasu z leśnikiem lub z harcerzem posiadającym sprawność****. Wykazałem/am się znajomością drzew i umiejętnością rozpoznawania ich wieku.', 198, ''),
(6103, 'Zorganizowałem/am dla swojego zastępu w porozumieniu z leśnikiem całodzienną służbę na rzecz lasu (prace w szkółce, przy oczyszczaniu pasów przeciwpożarowych, sadzeniu drzew itp.).', 198, ''),
(6104, 'Brałem/am udział w całodziennych pracach leśnych – w co najmniej w 3 rodzajach: zbieranie nasion, siew nasion, dołowanie sadzonek, sadzenie, pielęgnowanie upraw, sprzątanie zrębu.', 199, ''),
(6105, 'Podczas indywidualnych wędrówek (lub z przyjacielem) poznałem/am najbliższe lasy. Poprowadziłem/am zastęp/drużynę/patrol na wyprawę do lasu. Pokazałem/am różne gatunki drzew i krzewów oraz rośliny będące pod ochroną; miejsca, gdzie można znaleźć grzyby, jagody i zioła oraz szlaki wędrówek zwierząt leśnych.', 199, ''),
(6106, 'Zorganizowałem/am zbiórkę w terenie dla zastępu/drużyny/patrolu poświęconą lasom. Pokazałem/am różnice między lasami naturalnymi (puszczami) a lasem gospodarczym (uprawą leśną) i zaprezentowałem/am korzyści płynące z jednego i drugiego rodzaju lasów. Zapoznałem/am uczestników zbiórki z rodzajami drewna pozyskiwanymi z lasu i dalszym jego wykorzystaniem.', 199, ''),
(6107, 'Rozpoznałem/am co najmniej 5 grzybów jadalnych (np. borowika szlachetnego, maślaka zwyczajnego, podgrzybka brunatnego, czubajkę kanię, pieprznika jadalnego), 5 grzybów niejadalnych (np. gąskę białawą, gąskę siarkową, aksamitówkę złotą, gołąbka krwistego, lisówkę pomarańczową) oraz 5 grzybów trujących (np. muchomora sromotnikowego, muchomora czerwonego, borowika szatańskiego, borowika purpurowego, strzępiaka ceglastego). Uwieczniłem/am je na zdjęciach.', 87, ''),
(6108, 'Wziąłem/wzięłam udział w grzybobraniu w 3 różnych lokalizacjach. Zbierałem/am grzyby nie niszcząc grzybni i ściółki.', 87, ''),
(6109, 'Wziąłem/wzięłam udział w myciu grzybów po zebraniu, a także przygotowaniu ich do suszenia lub w marynowaniu.', 87, ''),
(6110, 'Pod nadzorem osoby dorosłej przygotowałem/am posiłek z wykorzystaniem grzybów (np. sos do mięsa, zupę).', 87, ''),
(6111, 'Przygotowałem/am dla zastępu quiz, grę planszową itp. wymagającą rozpoznania na zdjęciach lub rysunkach minimum 15 gatunków zwierząt żyjących dziko w Polsce.', 209, ''),
(6112, 'Wykonałem/am jedno z poniższych zadań:\n-Obserwowałem/am przez miesiąc życie mojego zwierzęcia domowego, zapisywałem/am najciekawsze spostrzeżenia, opowiedziałem/am o nich na zbiórce zastępu.\n-Umówiłem/am się z kimś na regularne wyprowadzania psa lub opiekę nad czyimś zwierzęciem przez miesiąc.', 209, ''),
(6113, 'Dowiedziałem/am się i opowiedziałem/am zastępowi, jakie dzikie zwierzęta (w tym ptaki) zamieszkują najbliższe tereny i jak można im pomagać. Pomogłem/am zwierzętom sam/a lub w ramach zorganizowanej akcji np. w zimowym dokarmianiu, budowaniu budek lęgowych, przenoszeniu skrzeku, budowaniu płotków dla płazów.', 209, ''),
(6114, 'Wiem, jakie niebezpieczne zwierzęta żyją w Polsce. Opowiedziałem/am członkom/członkiniom mojego zastępu przed wyprawą do lasu jak należy postępować w przypadku spotkania z nimi.', 210, ''),
(6115, 'Wiem, jak odpowiednio zachowywać się w lesie, aby nie płoszyć zwierzyny i nie tworzyć dla niej zagrożenia. Podczas wycieczki do lasu lub na łąkę wykonałem/am co najmniej jedno z zadań:\nRozpoznałem/am tropy kilku dzikich zwierząt (np. dzika, sarny, zająca) i utrwaliłem/am je (np. wykonałem/am zdjęcie, odlew).\nNagrałem/am odgłosy ptaków, sfotografowałem/am je, zebrałem/am ich pióra.\nRozpoznałem/am i nagrałem/am głosy pięciu żyjących tam zwierząt.\nZaprezentowałem/am moje zdobycze na zbiórce zastępu.', 210, ''),
(6116, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Przeczytałem/am poradnik dotyczący opieki nad wybranym zwierzęciem. Wybrałem/am z niego 5 najciekawszych według mnie informacji i przedstawiłem/am je w zastępie.\n-Poszedłem/poszłam ze swoim zwierzęciem do weterynarza lub samodzielnie sprawdziłem/am stan zdrowia zwierzęcia na podstawie wiarygodnego poradnika.', 210, ''),
(6117, 'Podczas samodzielnej wyprawy do lasu obserwowałem/am wybrane zwierzę i podążałem/am jego śladem, dokumentując jego życie (w sposób bezpieczny dla niego i dla mnie). Przedstawiłem/am jego zwyczaje na forum zastępu/drużyny.', 211, ''),
(6118, 'Zorganizowałem/em dla zastępu wyprawę do lasu, muzeum przyrodniczego lub zoo. Opowiedziałem/am o wybranych gatunkach zwierząt.', 211, ''),
(6119, 'Przedstawiłem/am na forum zastępu/drużyny wybrane gatunki zwierząt żyjących na całym świecie, zagrożonych wyginięciem („czerwona księga”). Opowiedziałem/am o produktach pochodzących z nielegalnego handlu zagrożonymi gatunkami, zwracając uwagę na wyroby popularnie przywożone jako pamiątki z wakacji. Opowiedziałem/am na zbiórce zastępu lub w klasie szkolnej o tym, jakie zwierzęta można hodować w domu w Polsce zgodnie z obowiązującym prawem.', 211, ''),
(6120, 'Przez 2 tygodnie doradzałem/am młodszemu rodzeństwu lub członkowi drużyny w opiece nad zwierzęciem domowym. Zrobiłem/am dla niego listę dobrych praktyk w opiece nad zwierzętami (np. niepozostawianie w samochodzie, zapewnianie wody do picia itp.).', 211, ''),
(6121, 'Zorganizowałem/am dla swojego zastępu/drużyny służbę na rzecz zwierząt.', 212, ''),
(6122, 'Nawiązałem/am współpracę z wybranym leśnictwem, parkiem narodowym lub organizacją zajmującą się ochroną zwierząt. Wziąłem/wzięłam udział jako wolontariusz w akcji organizowanej przez te instytucje np. liczeniu zwierząt, obrączkowaniu ptaków itp.', 212, ''),
(6123, 'Dokonałem/am przeglądu używanych przez siebie produktów (np. kosmetyków, produktów spożywczych). Wyeliminowałem te, których produkcja powoduje szkody w świecie zwierząt (np. testowane na zwierzętach, zawierające olej palmowy, pochodzenie z niezrównoważonych hodowli).', 212, ''),
(6124, 'Przed założeniem hodowli dowiedziałem/am się z rzetelnego źródła wiedzy, w jaki sposób należy dbać o interesujące mnie zwierzęta. Dowiedziałem/am się, które zwierzęta hodowlane są obce/inwazyjne i jakie są konsekwencje ich umyślnego lub przypadkowego uwolnienia do środowiska naturalnego. Oceniłem/am swoje możliwości i po uzyskaniu zgody rodziców/opiekunów dokonałem/am wyboru zwierząt do hodowli.', 85, ''),
(6125, 'Przygotowałem/am dla swoich zwierząt właściwe pomieszczenia i przynajmniej od 2 miesięcy utrzymuje je w stałej czystości.', 85, ''),
(6126, 'W trakcie trwania próby (przynajmniej 2 miesiące) samodzielnie karmiłem/am swoje zwierzęta. W przypadku własnej niedyspozycji zapewniłem/am sobie zastępstwo.', 85, ''),
(6127, 'Zaprezentowałem/am na zbiórce zastępu wyhodowane przez siebie zwierzęta, opowiedziałem/am o ich zwyczajach, sposobach żywienia i zapobiegania chorobom.', 85, ''),
(6128, 'Dbam o zdrowie moich zwierząt, wykonując jedno z poniższych zadań:\n-Zapewniłem/am moim zwierzętom wizytę weterynarza,\n-zaszczepiłem/am moje zwierzęta,\n-zweryfikowałem/am swoje dotychczasowe praktyki hodowlane w fachowych książkach, czasopismach, portalach.', 86, ''),
(6129, 'Przygotowałem/am dla swoich zwierząt wyposażenie zwiększające komfort miejsca ich codziennego przebywania (np. zabawkę, wybieg).', 86, ''),
(6130, 'Dostosowałem/am dietę moich zwierząt do ich indywidualnych potrzeb. W trakcie trwania próby (przynajmniej 4 miesiące) samodzielnie karmiłem/am swoje zwierzęta. W przypadku własnej niedyspozycji zapewniłem/am sobie zastępstwo.', 86, ''),
(6131, 'Zabrałem/am członków mojego zastępu na wizytę, podczas której oglądaliśmy moją hodowlę.', 86, ''),
(6132, 'Przygotowałem/am plakat/prezentację na temat różnic w wyglądzie między pszczołami, osami, trzmielami i szerszeniami. Zaprezentowałem/am go na zbiórce zastępu/drużyny i opowiedziałem/am o tym, co pożytecznego robią pszczoły i trzmiele.', 206, ''),
(6133, 'Obserwowałem/am pracę pszczół na łące lub w ogrodzie. Opowiedziałem/am członkom mojego zastępu, co zaobserwowałem/am.', 206, ''),
(6134, 'Posmakowałem/am co najmniej 3 różne rodzaje miodu (np. rzepakowy, lipowy, gryczany, spadziowy, wielokwiatowy, manuka) i podjąłem/jęłam próbę opisania różnic w ich smakach.', 206, ''),
(6135, 'Wybrałem/am się do sklepu pszczelarskiego w swojej okolicy lub zajrzałem/ma do sklepu internetowego dla pszczelarzy. Opowiedziałem/am, co ciekawego tam widziałem/am.', 206, ''),
(6136, 'Wziąłem/wzięłam udział w kręceniu miodu.', 207, ''),
(6137, 'Rozpoznałem/am co najmniej 5 rodzajów roślin miododajnych występujących w Polsce. Obserwowałem/am jak pszczoły zbierają z nich nektar i pyłek kwiatów.', 207, ''),
(6138, 'Przygotowałem/am plakat/prezentację o co najmniej 3 produktach pszczelich i ich właściwościach.', 207, ''),
(6139, 'Zbudowałem/am hotel dla owadów zapylających, zademonstrowałem/am go innym wyjaśniając, czemu praca tych owadów jest ważna.', 207, ''),
(6140, 'Od wiosny do jesieni pracowałem/am w pasiece i stworzyłem/am relację ze swoich działań w dowolnej, atrakcyjnej formie (np. zdjęcia, pamiętnik, filmy, relacja w mediach społecznościowych).', 208, ''),
(6141, 'Wziąłem/wzięłam udział w zabezpieczaniu pszczół przed zimą.', 208, ''),
(6142, 'Poznałem/am właściwości lecznicze produktów wytwarzanych przez pszczoły. Skorzystałem z dwóch z takich produktów odpowiednio do okoliczności.', 208, ''),
(6143, 'Opisałem/am i spopularyzowałem właściwości co najmniej 5 produktów pszczelich (na zbiórkach lub w mediach społecznościowych).', 208, ''),
(6144, 'Zorganizowałem/am wycieczkę zastępu/patrolu/drużyny do pasieki. Pokazałem/am jak zabezpieczyć się przed użądleniem podczas pracy w pasiece. Przedstawiłem/am zasady zachowania podczas pracy przy pszczołach.', 208, ''),
(6145, 'Zaoszczędziłem/am pieniądze na wybrany przez siebie cel .', 15, 'Młodzik/Ochotniczka'),
(6146, 'Właściwie (bezpiecznie) przechowuję pieniądze.', 15, ''),
(6147, 'Sporządziłem/am wraz z rodzicami listę potrzebnych codziennych produktów. Samodzielnie zrobiłem/am zakupy zgodnie z listą, sprawdzając ceny i cechy tych produktów i nie przekraczając założonej kwoty.', 15, 'Młodzik/Ochotniczka'),
(6148, 'Dowiedziałem/am się, jaki wpływ na środowisko ma to, co jem oraz kupuję. Przez co najmniej tydzień prowadziłem/am zestawienie swoich wydatków. Następnie określiłem/am, które były niezbędne, a z których mógłbym/mogłabym zrezygnować.', 15, ''),
(6149, 'Robiąc zakupy na rzecz drużyny, uzyskałem/am od sprzedającego właściwie wystawioną fakturę. Odbierając ją, sprawdziłem/am poprawność wszystkich niezbędnych danych.', 16, ''),
(6150, 'Wziąłem/wzięłam udział w pozyskiwaniu środków w formie np. akcji zarobkowej, zbiórki publicznej, loterii, crowdfundingu, akcji 1% itp.', 16, 'Wywiadowca/Tropicielka'),
(6151, 'Przez miesiąc prowadziłem/am zestawienie swoich wydatków. Następnie określiłem/am, które były niezbędne, a z których mógłbym/mogłabym zrezygnować.', 16, ''),
(6152, 'Zapoznałem/am się z zasadą 4R (refuse, reduce, reuse, recycle), i stosując ją, ograniczyłem/am swoją konsumpcję. W trakcie trwania próby całkowicie zrezygnowałem/am z kupowania jednego z produktów nieodpowiedzialnych środowiskowo np. słodyczy z olejem palmowym, napojów w butelkach PET itp.', 16, ''),
(6153, 'Wykorzystując więcej niż jeden sposób, pozyskałem/am środki finansowe lub materialne na wybrany przez siebie cel.', 17, 'Odkrywca/Pionierka'),
(6154, 'Wziąłem/wzięłam udział w organizowaniu akcji zarobkowej (np. w formie zbiórki publicznej, sprzedaży towarów lub usług, loterii, crowdfundingu), będąc osobą odpowiedzialną za ustalony obszar jej realizacji bądź rozliczenia (pod nadzorem instruktora).', 17, ''),
(6155, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Dokonałem/am płatności przy pomocy karty płatniczej, wypłaciłem/am pieniądze z bankomatu oraz zapłaciłem/am przelewem bankowym np. składki.\n-Zaprezentowałem/am zastępowi/drużynie w formie graficznej informacje o drodze produkcji i podziale zysków z produkcji i sprzedaży wybranego produktu (w tym o sposobie ich opodatkowania).', 17, ''),
(6156, 'Zebrałem/am informacje o dostępnych dla młodzieży ofertach różnych banków i przeanalizowałem/am oferowane w ramach nich usługi i produkty. Na podstawie dokonanej analizy założyłem/am konto bankowe lub porównałem/am warunki banku, w którym posiadam rachunek, z innymi zebranymi ofertami.', 17, 'Ćwik/Samarytanka'),
(6157, 'Przygotowałem/am plan finansowy biwaku, rajdu, wycieczki lub innego przedsięwzięcia harcerskiego. Rozliczyłem/am go w ramach uzyskanej zaliczki, jednocześnie weryfikując w ramach tego rozliczenia realizację zakładanego preliminarza.', 17, 'Ćwik/Samarytanka'),
(6158, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Przeprowadziłem/am w drużynie grę ekonomiczną umożliwiającą uczestnikom poznanie pojęć: popyt, podaż, koszt, przychód, marża, zysk, wykorzystując udostępnione w ramach programów edukacyjnych materiały wychowania ekonomicznego. Odpowiedzialnie zadbałem/am o jakość materiałów, czerpiąc je ze stron instytucji finansowych, np. NBP, GPW, Związek Banków Polskich.\n-Pokierowałem/am akcją zarobkową drużyny (w formie np. zbiórki publicznej, sprzedaży towarów lub usług, loterii, crowdfundingu), która przyniosła wymierny zysk, i rozliczyłem/am ją.', 18, ''),
(6159, 'Zidentyfikowałem/am swoje predyspozycje zawodowe, korzystając z pomocy doradcy zawodowego lub wybranego kwestionariusza.', 18, 'Harcerz Orli/Harcerka Orla'),
(6160, 'Poznałem/am różne rodzaje ochrony i pomnażania oszczędności. Pomnożyłem/am swoje środki i przedstawiłem/am osiągnięte efekty wraz z wnioskami podczas zbiórki dla zastępu/drużyny/rodziców. Podczas prezentacji efektów tego działania wykorzystałem/am narzędzie analizy portfelowej.', 18, ''),
(6161, 'Przygotowałem/am własne CV oraz napisałem/am list motywacyjny do wybranej oferty pracy.', 18, ''),
(6162, 'Poznałem/am różnicę pomiędzy pensją brutto a netto. Wyliczyłem/am z kwoty pensji brutto wszystkie składowe zarówno po stronie pracownika, jak i pracodawcy: kwotę netto, podatek, ubezpieczenie zdrowotne, ubezpieczenie emerytalne, ubezpieczenie chorobowe, ubezpieczenie rentowe, ubezpieczenie wypadkowe, PPK itp. Potrafię wyjaśnić, na co są przeznaczone poszczególne składowe pensji brutto.', 18, ''),
(6163, 'Skompletowałem/am sprzęt zastępu potrzebny na obozie. Dbałem/am o niego w czasie obozu.', 217, ''),
(6164, 'Brałem/am udział w pracach konserwatorsko-naprawczych sprzętu drużyny/szczepu/hufca po akcji letniej.', 217, ''),
(6165, 'Opiekowałem/am się harcówką przez okres próby: dokonałem/am w niej drobnych napraw lub zaproponowałem/am nowe funkcjonalne i estetyczne rozmieszczenie wyposażenia.', 217, ''),
(6166, 'W okresie trwania próby prowadziłem/am magazyn sprzętu podczas obozu drużyny lub w ciągu roku wraz z dokumentacją.', 218, ''),
(6167, 'Wiem na czym polega inwentaryzacja. Brałem/am udział w spisie z natury.', 218, ''),
(6168, 'Byłem/am odpowiedzialny/a za organizacyjną stronę biwaku lub rajdu drużyny/szczepu.', 218, ''),
(6169, 'Nauczyłem/am młodszych konserwować sprzęt pionierski i porządkować magazyn sprzętu oraz posługiwać się narzędziami zgodnie z podstawowymi zasadami bezpieczeństwa.', 218, ''),
(6170, 'Przeprowadziłem/am szkolenie dla kwatermistrzów zastępów/drużyn.', 219, ''),
(6171, 'W okresie trwania próby prowadziłem/am dokumentację sprzętową drużyny/szczepu/hufca.', 219, ''),
(6172, 'Przeprowadziłem/am inwentaryzację sprzętu drużyny/szczepu lub pomagałem/am w przeprowadzeniu inwentaryzacji sprzętu hufca.', 219, ''),
(6173, 'Uczestniczyłem/am w organizowaniu obozu drużyny/szczepu pod względem kwatermistrzowskim (udział w zwiadzie przedobozowym, przygotowanie sprzętu do obozu, nadzór nad sprzętem w czasie obozu i prowadzenie niezbędnej dokumentacji, przegląd sprzętu po obozie i zmagazynowanie go).', 219, ''),
(6174, 'Wiem na co są zbierane składki w ZHP. Zapoznałem/am z tym tematem członków mojego zastępu.', 220, ''),
(6175, 'Wspierałem/am zbieranie składek w moim zastępie (przypominanie o terminach, zrobienie listy zebranych składek dla zastępowego) co najmniej przez jeden kwartał.', 220, ''),
(6176, 'Zorganizowałem/am akcje zarobkową zastępu i rozliczyłem ją zgodnie z zasadami obowiązującymi w mojej drużynie.', 220, ''),
(6177, 'Poznałem/am przepisy finansowe ZHP obowiązujące drużynę.\nPod nadzorem drużynowego zebrałem/am i rozliczyłem/am składki drużyny za jeden kwartał.\nCo najmniej raz rozliczyłem/am w hufcu fakturę za wydatkowanie środków na śródroczną działalność programową.', 221, ''),
(6178, 'Co najmniej dwa razy zarządzałem/am finansami biwaku/imprezy drużyny (osobiście lub wspierając innych członków drużyny):\nopracowałem/am preliminarz (czyli plan finansowy);\nzarządzałem finansami w trakcie trwania przedsięwzięcia;\nrozliczyłem/am imprezę zgodnie z obowiązującymi w hufcu zasadami.', 221, ''),
(6179, 'Opracowałem/am plan finansowy drużyny na rok harcerski i przedstawiłem/am swoją koncepcję radzie drużyny.', 221, ''),
(6180, 'Przeprowadziłem/am szkolenie dla skarbników zastępów i drużyn.', 222, ''),
(6181, 'Uczestniczyłem/am w pracach zespołu przygotowującego oraz rozliczającego pod względem finansowym obóz mojego środowiska.', 222, ''),
(6182, 'Wziąłem/wzięłam udział w opracowywaniu wniosku grantowego i rozliczaniu grantu po jego wykonaniu.', 222, ''),
(6183, 'Wziąłem/wzięłam udział w opracowywaniu miesięcznego rozliczenia finansowego hufca.', 222, ''),
(6184, 'Przygotowałem/am zasady bycia odpowiedzialnym konsumentem i przedstawiłem/am je zastępowi/drużynie wraz z informacją o zasadach fair trade.', 65, ''),
(6185, 'Sporządziłem/am mapę miejsc, w których można kupić produkty lokalne oraz produkty fair trade. Przygotowaną mapę zaprezentowałem/am na zbiórce zastępu/drużyny lub umieściłem/am w mediach społecznościowych wykorzystywanych przez lokalną społeczność.', 65, ''),
(6186, 'Przeanalizowałem/am swoje miesięczne zakupy, określiłem/am, które były spowodowane realną, a które wykreowaną potrzebą (zachcianką/ pragnieniem).', 65, ''),
(6187, 'Przygotowałem/am posiłek wyłącznie z produktów dostępnych aktualnie w domu, wykorzystując te, dla których data przydatności była najkrótsza (aby marnowało się jak najmniej jedzenia).', 65, ''),
(6188, 'Przeanalizowałem/am skład kilku często kupowanych produktów pod kątem zdrowych/ szkodliwych składników, a także mikroplastików (acrylate), jednocześnie zweryfikowałem/am ich pochodzenie i sposób wytworzenia pod kątem kwestii odpowiedzialności za środowisko, jak ślad wodny oraz ślad węglowy. Z analizy wyciągnąłem/wyciągnęłam wnioski, którymi podzieliłem/am się z minimum jedną osobą, podejmując wspólnie z nią konkretne postanowienia co do ułożenia na nowo swoich konsumenckich nawyków. Efekty przyjętych postanowień śledziłem/am przez minimum 2 miesiące, porównując je z tą osobą.', 66, ''),
(6189, 'Poznałem/am sposoby na zmniejszenie kosztów gospodarstwa domowego, minimum jeden wprowadziłem/am u siebie w domu.', 66, ''),
(6190, 'Wybrałem/am kilka rodzajów najczęściej używanych w swoim domu produktów i porównałem/am dostępne oferty kilku marek pod kątem stosunku jakości do ceny, przedstawiłem/am rodzinie swoje rekomendacje.', 66, ''),
(6191, 'Zapoznałem/am się z minimum jedną pozycją literatury lub podcastem na temat fair trade oraz zrównoważonej konsumpcji. Wnioski zawarte w przeczytanej/wysłuchanej publikacji włączyłem/am do swojej codziennej rutyny jako konkretne rozwiązania.', 66, ''),
(6192, 'Zidentyfikowałem/am swoje predyspozycje zawodowe korzystając z pomocy doradcy zawodowego lub wybranego kwestionariusza. Na podstawie zidentyfikowanych predyspozycji zawodowych przygotowałem/am swoje CV.', 63, ''),
(6193, 'Przeprowadziłem/am wywiad z osobą wykonującą interesujący mnie zawód, dowiedziałem/am się, jakie umiejętności/wykształcenie są potrzebne do jego wykonywania.', 63, ''),
(6194, 'Zapoznałem/am się z ofertami pracy dostępnymi w różnych źródłach, wybrałem/am kilka interesujących mnie i pozyskałem/am na ich podstawie pracę adekwatną do aktualnych możliwości (praca tymczasowa, wakacyjna praktyka itp.).', 63, ''),
(6195, 'Założyłem/am profil na dowolnym portalu specjalizującym się w kontaktach zawodowo-biznesowych (takich jak np.: linkedin, goldenline). Zbudowałem/am sieć kontaktów.', 63, ''),
(6196, 'Poznałem/am obowiązki pracownika i pracodawcy wynikające z umowy zlecenia. Wiem, do czego zobowiązuje mnie jako zleceniobiorcy umowa o dzieło. Znam aktualną minimalną stawkę godzinową.', 63, ''),
(6197, 'Znalazłem/am interesujące mnie oferty pracy, do których dostosowałem/am swoje CV oraz list motywacyjny (o ile był potrzebny) i wysłałem/am w ramach złożonej aplikacji. Przygotowałem/am się do rozmowy o pracę, pozyskując z dostępnych źródeł informacje o potencjalnym pracodawcy.', 64, ''),
(6198, 'Przystąpiłem/am do procesu rekrutacji i uzyskałem/am na tej podstawie wybraną ofertę pracy na co najmniej 1 miesiąc. Podjąłem pracę w sposób świadomy, dbając o swoje prawa poprzez weryfikację, czy otrzymane wynagrodzenie jest zgodne z obowiązującą minimalną stawką godzinową / pensją minimalną, a także jakie inne prawa i obowiązki przysługują mi w danym trybie zatrudnienia, w szczególności w kwestiach dni wolnych, odpowiedzialności materialnej oraz urlopu. Swoje warunki porównałem z inną osobą zatrudnioną na podstawie umowy o pracę.', 64, ''),
(6199, 'Przeprowadziłem/am na zbiórce ćwiczenia/warsztaty z technik autoprezentacji i sposobów przygotowania się do zdobycia pierwszej pracy, w tym kontaktów z Urzędem Pracy oraz innymi organizacjami zajmującymi się zatrudnieniem w kraju i za granicą.', 64, ''),
(6200, 'Poznałem/am różne formy zatrudnienia. Znam różnicę pomiędzy pensją brutto a netto. Wyliczyłem/am z kwoty pensji brutto wszystkie składowe składniki zarówno po stronie pracownika jak i pracodawcy: kwotę netto, ZUS, US, Ubezpieczenie Zdrowotne, Ubezpieczenie Emerytalne, Ubezpieczenie Chorobowe, Ubezpieczenie Rentowe, Ubezpieczenie Wypadkowe, PPK itp.', 64, ''),
(6201, 'Przeanalizowałem/am kilka reklam wybranego produktu oceniając, na ile odnoszą się do rzeczywistych jego cech użytkowych.', 61, ''),
(6202, 'Przedstawiłem/am w dowolnej formie zabiegi marketingowe stosowane przez sklepy, zachęcające do zrobienia jak największych zakupów.', 61, ''),
(6203, 'Stworzyłem/am plakat przedstawiający korzyści i zagrożenia płynące z reklam.', 61, ''),
(6204, 'Przygotowałem/am dwie reklamy produktu lokalnego produkowanego w moim regionie, określając cel przekazu i grupę docelową z wykorzystaniem takich technik jak: plakat, film, nagranie audio.', 61, ''),
(6205, 'Zaprezentowałem/am członkom drużyny wywiad, jaki przeprowadziłem/am z profesjonalistą (psychologiem reklamy, pracownikiem firmy reklamowej bądź działu marketingu) na temat mechanizmów i technik stosowanych dla maksymalizacji skuteczności przekazu reklamowego oraz różnych rodzajów reklam, a w szczególności różnic pomiędzy reklamą komercyjną a społeczną.', 62, ''),
(6206, 'Przygotowałem/am infografikę bądź podcast na dowolnie wybrany temat, wykorzystujący zasady stosowane podczas tworzenia skutecznych przekazów reklamowych i umieściłem/am go w social mediach. Następnie zmierzyłem/am skuteczność przygotowanego przekazu.', 62, ''),
(6207, 'Przygotowałem/am i przeprowadziłem/am kampanię reklamową dla wybranej jednostki harcerskiej, wykorzystując podstawowe mechanizmy tworzenia przekazu reklamowego, sprecyzowania potrzeb reklamodawcy, określania odbiorcy oraz właściwego doboru form przekazu (np. reklama w ramach akcji naborowej, kampanii 1%, etc.).', 62, ''),
(6208, 'Przeprowadziłem/am debatę na wybrany temat z obszaru etyki reklamy.', 62, ''),
(6209, 'Dowiedziałem/am się, z jakiego banku korzystają moi rodzice/opiekunowie, gdzie się znajduje i jakie usługi oferuje. Wspólnie z nimi przedyskutowałem/am powody, dla których korzystają z oferty akurat tego banku.', 57, ''),
(6210, 'Poznałem/am dostępne dla siebie sposoby oszczędzania pieniędzy i przedstawiłem/am je na zbiórce zastępu.', 57, ''),
(6211, 'Odszukałem/am dostępne w sieci edukacyjne materiały multimedialne, dzięki którym poznałem/am definicję pieniądza oraz zapoznałem/am się z zabezpieczeniami na banknotach, jak również nietypowymi monetami w obiegu w Polsce. Wziąłem/wzięłam udział w minimum jednej grze oraz quizie, dzięki którym zapamiętałem/am powyższe informacje (np. na portalu NBP).', 57, ''),
(6212, 'Dowiedziałem/am się, co to jest RRSO i jaką rolę pełni. Wyjaśniłem/am to innym, porównując poziom RRSO w kilku ofertach finansowych.', 57, ''),
(6213, 'Poznałem/am pojęcia takie jak: cechy pieniądza, funkcje pieniądza oraz jestem świadomy/a ewolucji form pieniądza. Jedno z tych pojęć wyjaśniłem/am na forum zastępu przygotowując graficzną prezentację.', 58, ''),
(6214, 'Wykonałem/am jedno z poniższych zadań:\n-Wziąłem/wzięłam udział w grze dostępnej na Portalu Edukacji -Ekonomicznej Narodowego Banku Polskiego (NBP).\n-Odwiedziłem/am Centrum Pieniądza NBP.', 58, ''),
(6215, 'Obliczyłem/am odsetki, jakie uzyskałbym/uzyskałabym w wypadku założenia rocznej lokaty w wysokości 100 zł, przy użyciu aktualnej stopy oprocentowania, jaką proponuje wybrany przeze mnie bank i przy założeniu, że nie uwzględniona zostaje kapitalizacja (procent prosty).', 58, ''),
(6216, 'Poznałem/am różnice pomiędzy przelewem bądź przekazem bankowym i pocztowym. Dokonałem/am jednego z nich.', 58, ''),
(6217, 'Wyjaśniłem/am innym, jak wygląda obieg pieniądza w gospodarce. Przygotowałem/am na ten temat dowolną prezentację.', 59, ''),
(6218, 'Obliczyłem/am odsetki, jakie uzyskałbym/uzyskałabym w wypadku założenia rok temu rocznej lokaty w walucie (kwota w EUR) w wysokości 100 zł (przeliczonych na EUR), przy użyciu aktualnej stopy oprocentowania, jaką proponuje wybrany przeze mnie bank oraz kursu waluty sprzed roku i dzisiejszego oraz przy założeniu, że nie uwzględniona zostaje kapitalizacja (procent prosty).', 59, ''),
(6219, 'Wziąłem/wzięłam udział w grze ekonomicznej zawierającej elementy inwestowania (forma inwestycji, czas, płynność) oraz oceny ryzyka oraz rozwiązałem/am quiz lub krzyżówkę z dziedziny finansów i bankowości, dostępnych na Portalu Edukacji Ekonomicznej NBP.', 59, ''),
(6220, 'Poznałem/am różne rodzaje ochrony i pomnażania oszczędności, przeprowadziłem/am na ten temat zbiórkę dla członków drużyny oraz dla ich rodziców. Podczas zbiórki uwzględniłem/am kwestie poziomu ryzyka, zabezpieczeń BFG oraz specyfiki poszczególnych instrumentów i wynikających stąd różnic (np. różnica między kredytem a pożyczką).', 59, ''),
(6221, 'Wyliczyłem/am ratę równą i malejącą z kredytu oraz zaprezentowałem/am, jakie są między nimi różnice. Uwzględniłem/am w takich obliczeniach element ryzyka kursu waluty oraz podałem/am stopę nominalnego oraz realnego jego oprocentowania, z uwzględnieniem wpływu stopy inflacji.', 60, ''),
(6222, 'Przygotowałem/am i ustawiłem/am odpowiednie zabezpieczenia dla transakcji zakupu dokonywanych poprzez Internet.', 60, ''),
(6223, 'Przygotowałem/am i przeprowadziłem/am grę ekonomiczną zawierającą elementy inwestowania (forma inwestycji, czas, płynność), oszczędzania oraz oceny ryzyka.', 60, ''),
(6224, 'Posługuję się biegle bankowością elektroniczną, realizując następujące działania:\ndokonałem/am przelewu,\nużyłem/am karty płatniczej,\nustanowiłem/am lokatę,\nzapłaciłem/am przy użyciu dostępnego elektronicznego serwisu transakcyjnego (np. BLIK) wykorzystując do tego telefon.\nPrzeanalizowałem/am na podstawie systemu bankowego strukturę swoich wydatków.', 60, ''),
(6225, 'Przygotowałem/am infografikę przedstawiającą strukturę rynku finansowego \nw Polsce oraz podmiotów na nim występujących. Infografika pozwoli zrozumieć zainteresowanym znaczenie podstawowych pojęć jak: rynek, giełda, notowanie, akcja, obligacja, dywidenda, dematerializacja oraz rolę podstawowych instytucji: KNF, GPW, KDPW, NBP, itp. \nNa podstawie infografiki przedstawiłem/am funkcjonowanie tego rynku swojej drużynie/zastępowi.', 55, ''),
(6226, 'Poznałem/am podstawowe indeksy giełdowe oraz rynki funkcjonujące na GPW oraz na głównych giełdach na świecie. W ciągu miesiąca prowadziłem/am analizę notowań wybranych spółek z tych rynków i oceniłem/am różnice poziomu opłacalności oraz ryzyka pomiędzy nimi wykorzystując do tego między innymi narzędzie, jakim jest mapa ryzyka.', 55, ''),
(6227, 'Sporządziłem/am mapę ryzyka oraz rentowności dla wybranych instrumentów finansowych (np. akcje vs. obligacje vs. jednostki uczestnictwa funduszy). Na jej podstawie podczas zbiórki drużyny/zastępu przeprowadziłem/am dyskusję na temat wyborów inwestycyjnych w kontekście osobistych preferencji co do akceptowanego poziomu ryzyka oraz oczekiwanych zysków. Wnioski dyskusji w ramach podsumowania uczestnicy odnieśli do innych sytuacji życiowych, poza rynkiem finansowym.', 55, ''),
(6228, 'Odwiedziłem/am portal wiedzy ekonomicznej z dziedziny finansów oraz giełdy, na którym wziąłem/wzięłam udział w minimum dwóch grach edukacyjnych z dziedziny inwestowania na rynku finansowym. Zdobytą wiedzę wykorzystałem/am, biorąc udział w grze giełdowej.', 55, ''),
(6229, 'Na podstawie dostępnych w Internecie materiałów przeprowadziłem/am dla członków drużyny test oceny skłonności do ryzyka inwestycyjnego.', 56, ''),
(6230, 'Dokonałem/am analizy ofert domów maklerskich i na jej podstawie otworzyłem/am własny rachunek inwestycyjny.', 56, ''),
(6231, 'Przeprowadziłem/am grę, dzięki której mogłem/am przedstawić uczestnikom podstawowe mechanizmy inwestowania oraz działania giełdy.', 56, ''),
(6232, 'Poznałem/am techniki gry na wybranej przez siebie części rynku finansowego i wybrałem/am najlepszą dla siebie. Następnie po zapoznaniu się z typami zleceń oraz dodatkowymi istotnymi warunkami transakcji podjąłem/podjęłam decyzje inwestycyjne składając zlecenia kupna/sprzedaży. Oceniłem/am efekt swoich inwestycji w horyzoncie minimum trzech miesięcy, porównując go z wybranymi alternatywnymi możliwościami inwestycji oraz dokonując szacunku efektywności w horyzoncie długoterminowym.', 56, ''),
(6233, 'Wykonałem/am jedną z podstawowych czynności na komputerze:\nNapisałem/am prosty tekst w edytorze tekstu.\nPrzygotowałem/am prostą grafikę w edytorze grafiki.\nDostosowałem/am do swoich potrzeb jeden z programów, np. do oglądania multimediów lub przeglądania sieci. Znam jego nazwę i podstawowe ustawienia.', 23, ''),
(6234, 'Znalazłem/am informacje o wybranym zagadnieniu za pomocą dowolnej wyszukiwarki internetowej.', 23, ''),
(6235, 'Zapoznałem/am się z zasadami zdrowego korzystania z komputera (postawa, ergonomia, oświetlenie, przerwy itp.). Porównałem/am je z moimi przyzwyczajeniami i skorygowałem/am (w razie potrzeby) swoje nawyki.', 23, 'Młodzik/Ochotniczka'),
(6236, 'Poznałem/am zasady bezpiecznego korzystania z Internetu (ochrona prywatności, rozmowy z nieznajomymi, niebezpieczne strony) i przedstawiłem/am je osobom z mojego otoczenia (rodzina/znajomi/klasa/zastęp).', 23, ''),
(6237, 'Korzystając z wybranego pakietu biurowego, wykonałem/am jedno z poniższych zadań:\n-Przygotowałem/am prezentację multimedialną na wybrany temat.\n-Przygotowałem/am dokument tekstowy z wykorzystaniem podstawowych możliwości edytora tekstu (wypunktowanie i numeracja, wyrównanie tekstu, formatowanie tekstu, podział na akapity, wstawianie obrazów, tabel i wykresów, edycja marginesów i układu strony, druk dwustronny).\n-Przygotowałem/am arkusz kalkulacyjny z wykorzystaniem funkcji takich jak: tabele, formatowanie warunkowe, wykresy oraz kilku prostych formuł takich jak sumowanie, liczenie warunkowe.', 24, ''),
(6238, 'Zapoznałem/am się z netykietą, czyli zbiorem zasad zachowania się w Internecie. Stosuję je podczas codziennego użytkowania sieci.', 24, 'Wywiadowca/Tropicielka'),
(6239, 'Korzystam z komputera zarówno do spędzania wolnego czasu czy komunikacji ze znajomymi, jak i zdobywania wiedzy i umiejętności. Przygotowałem/am zestawienie, ile czasu poświęcam na korzystanie z komputera i w jakich celach. Jeśli ten czas był nieodpowiedni, wprowadziłem/am potrzebne zmiany.', 24, ''),
(6240, 'Utworzyłem/am konto e-mailowe. Wykorzystuję podstawowe funkcje klienta pocztowego, m.in. ustawiłem/am podpis w wiadomości, przy wysyłaniu e-maili do wielu osób korzystam z opcji UDW.', 25, 'Odkrywca/Pionierka'),
(6241, 'Wykonałem/am co najmniej jedno z zadań:\nDokonałem/am komputerowej edycji zdjęć (zalecane bezpłatne oprogramowanie), np. naniesienia logo, wykadrowania zdjęcia, korekcji błędów, zmiany jasności/ kontrastu/ nasycenia kolorów. Moje harcerskie projekty są zgodne z Katalogiem Identyfikacji Wizualnej ZHP.\nDokonałem/am komputerowego montażu filmu (zalecane bezpłatne oprogramowanie).\nPrzygotowałem/am dokumenty z wykorzystaniem zaawansowanych funkcji edytora tekstu (np. nagłówek i stopka, interlinia, zapis równania i symboli matematycznych, przypisy, kolumny, wstawianie komentarzy, opracowanie własnego stylu, korzystanie z tzw. twardych spacji i podziałów strony).', 25, 'Ćwik/Samarytanka'),
(6242, 'Podczas korzystania z sieci nie udostępniam swoich danych innym osobom lub portalom, jeśli nie ma uzasadnionej potrzeby. Zapoznałem/am się z zasadami ochrony moich danych i moimi prawami w sieci.', 25, ''),
(6243, 'Odpowiedzialnie korzystam z możliwości, które daje Internet, wykonałem jedno z poniższych zadań:\n-Poznałem/am zasady umożliwiające odróżnienie prawdziwych informacji od tzw. fake newsów i zaprezentowałem/am minimum 3 konkretne przykłady.\n-Szanuję prawa autorskie, w swoich pracach zawieram informacje o autorach i źródłach materiałów. Poznałem/am rodzaje licencji – zaprezentowałem/am minimum 3 przykłady.', 25, ''),
(6244, 'Zainstalowałem/am i skonfigurowałem/am specjalistyczny program, który wykorzystuję w mojej pracy lub do realizacji moich zainteresowań.', 26, ''),
(6245, 'Zrealizowałem/am samodzielnie lub w zespole dowolny projekt informatyczny, np. zaprojektowałem/am stronę WWW, opracowałem/am urządzenie korzystające z systemu wbudowanego, stworzyłem/am aplikację internetową lub mobilną.', 26, ''),
(6246, 'Znam pojęcie hejtu i uczestniczyłem/am w projekcie/kampanii, która miała wpływ na ograniczenie tego zjawiska w sieci.', 26, ''),
(6247, 'Przygotowałem/am pod nadzorem rodzica śniadanie lub kolację dla domowników, w tym jedno wybrane danie na ciepło, np. owsiankę, jajecznicę itp.', 12, 'Młodzik/Ochotniczka'),
(6248, 'Przygotowałem/am podwieczorek na biwaku lub obozie drużyny, serwując zdrową przekąskę według własnego pomysłu, np. jogurt z owocami, szaszłyki z suszonych lub świeżych owoców, kaszę mannę, batoniki musli, sałatkę owocową.', 12, ''),
(6249, 'Obrałem/am kilka różnych warzyw (np. ziemniaki, marchewkę), które posłużyły do przygotowania posiłku podczas 5 różnych okazji.', 12, ''),
(6250, 'Przyrządziłem/am pod nadzorem rodzica 2 potrawy:\nsałatkę składającą się z co najmniej 4 różnych składników,\nkanapki z 3 różnymi dodatkami.', 12, ''),
(6251, 'Ugotowałem/am zupę i drugie danie (niekoniecznie jednego dnia) dla mojej rodziny z pomocą przepisu (z Internetu, książki kucharskiej).', 13, ''),
(6252, 'Nakryłem/am do stołu do przygotowanego przez siebie obiadu.\nPrzyrządziłem/am zdrowe (np. niskokaloryczne, warzywne lub owocowe) danie lub deser, do którego przygotowania wykorzystałem/am mikser, blender lub inne urządzenie kuchenne.', 13, ''),
(6253, 'Nauczyłem/am się przygotowywać ulubioną potrawę z rodzinnego przepisu.', 13, 'Wywiadowca/Tropicielka'),
(6254, 'Przyrządziłem/am 3 potrawy różnych typów, np.:\nomlet z dodatkami,\npastę na kanapki,\ndeser bez pieczenia.', 13, ''),
(6255, 'Przygotowałem/am potrawę tradycyjną dla wybranego regionu Polski (np. proziaki z Podkarpacia, babkę ziemniaczaną z Podlasia).', 195, 'Odkrywca/Pionierka'),
(6256, 'Wykonałem co najmniej jedno z poniższych zadań:\n-Wybrałem/am miejsce na kuchnię na biwaku drużyny. Urządziłem/am ją funkcjonalnie. -Pokierowałem/am gotowaniem obiadu dla drużyny.\n-Ułożyłem/am urozmaicony jadłospis na kilkudniowy biwak, rajd lub kilka dni obozu dla całej drużyny. Nadzorowałem/am przygotowanie posiłków.', 195, 'Odkrywca/Pionierka'),
(6257, 'Ugotowałem/am co najmniej po dwie różne zupy, drugie dania i desery (niekoniecznie jednego dnia), wykorzystując różne sposoby przygotowania, np. pieczenie, smażenie, duszenie, gotowanie. Nakryłem/am do stołu z okazji uroczystego posiłku, korzystając z zastawy, sztućców, serwetek, wybranych elementów dekoracyjnych, np. świec, kwiatów, obrusu.', 195, 'Ćwik/Samarytanka'),
(6258, 'Upiekłem/am chleb lub ciasto drożdżowe (np. placek drożdżowy, pizza).', 195, ''),
(6259, 'Przyrządziłem/am 4 różne potrawy:\nsałatkę z gotowanych jarzyn,\nzapiekankę mięsną lub warzywną,\nmakaron z własnoręcznie przygotowanym sosem lub zupę,\npierogi / uszka / pyzy / kołduny itp.', 195, ''),
(6260, 'Kierowałem/am co najmniej przez dwa dni służbą kuchenną na obozie drużyny lub na biwaku, układając jadłospisy i przygotowując dania mięsne i wegetariańskie.', 14, ''),
(6261, 'Przyrządziłem/am 5 pełnowartościowych posiłków dostosowanych do różnych diet i upodobań, w tym: wegetariański, niskowęglowodanowy, bezglutenowy, wegański, mięsny.', 14, ''),
(6262, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Zorganizowałem/am, ugotowałem/am i podałem/am uroczysty obiad dla rodziny lub przyjaciół, podczas którego zaserwowałem/am co najmniej 5 potraw przygotowanych według wybranych przez siebie przepisów komponujących się w spójną całość.\n-Opracowałem/am i przetestowałem/am co najmniej 5 własnych przepisów na potrawy, które zyskały uznanie mojej rodziny lub przyjaciół.', 14, ''),
(6263, 'W trakcie trwania próby nie mniej niż 1/3 posiłków (śniadań, obiadów i kolacji) w każdym tygodniu przygotowałem/am samodzielnie.', 14, ''),
(6264, 'Przez okres próby utrzymywałem/am porządek w pokoju, w którym mieszkam.', 88, 'Młodzik/Ochotniczka'),
(6265, 'Wykonałem/am za pomocą noża jedną z poniższych rzeczy z drewna lub kory:\nużyteczny przedmiot do własnego pokoju lub harcówki,\nprosty ekwipunek biwakowy, np. kubek, sztućce,\ndrewnianą ozdobę lub figurkę.', 88, ''),
(6266, 'Mocno i estetycznie przyszyłem/am łatę lub naszywkę oraz guzik.', 88, 'Młodzik/Ochotniczka'),
(6267, 'Wykonałem/am jedno z poniższych zadań:\n-Oczyściłem/am i skleiłem/am lub zaimpregnowałem/am własne buty, np. wypastowałem/am stałą pastą skórzane powierzchnie.\n-Samodzielnie pod opieką dorosłych wymieniłem/am żarówkę z zachowaniem zasad bezpieczeństwa.', 88, ''),
(6268, 'Mam stały obowiązek związany z obsługą sprzętów lub urządzeń domowych (np. pralka, zmywarka, odkurzacz) i wywiązuję się z niego.', 89, 'Wywiadowca/Tropicielka'),
(6269, 'Potrafię posługiwać się śrubokrętem, wykonałem/am jedno z poniższych zadań:\n-Pomogłem/am skręcać meble.\n-Wymieniłem/am baterie w zabawce, która ma klapkę na baterie zamkniętą na śrubki.\n-Pod nadzorem osoby dorosłej rozkręciłem/am, wyczyściłem/am i skręciłem/am urządzenie, np. laptop.', 89, ''),
(6270, 'Wykonałem/am jedno z poniższych zadań:\n-Zbudowałem/am według schematu prosty obwód elektryczny z użyciem połączeń lutowanych.\n-Zainstalowałem/am lub wymieniłem/am gniazdko elektryczne lub włącznik światła z zachowaniem zasad bezpieczeństwa i pod nadzorem.\n-W obecności dorosłych samodzielnie złożyłem/am mebel na podstawie dołączonej instrukcji.', 89, ''),
(6271, 'Używając materiałów pochodzących z recyklingu, uszyłem/am jedną z poniższych rzeczy:\nelement ubioru lub akcesorium, z którego później korzystałem/am, np. torba na zakupy, pokrowiec na tablet/telefon, worek na WF,\nzabawkę.', 89, ''),
(6272, 'Rzetelnie wykonuję swoje obowiązki domowe. Zdobyłem/am nową umiejętność przydatną w gospodarstwie domowym (np. umiejętność obsługi pralki, ostrzenia noży, wiercenia otworów wiertarką, mycia okien itp.).', 90, 'Odkrywca/Pionierka'),
(6273, 'Wykonałem/am jedno z poniższych zadań:\n-Zbudowałem/am z własnoręcznie docinanych elementów drewnianą konstrukcję z użyciem wkrętów i gwoździ (np. regał do piwnicy, skrzynię z wiekiem).\n-Zbudowałem/am z własnoręcznie docinanych elementów drewnianą konstrukcję z drewna naturalnego bez użycia wkrętów, gwoździ i innych metalowych elementów (np. obozową tablicę ogłoszeń, stanowisko ppoż., regał do namiotu).', 90, ''),
(6274, 'Wykonałem/am jedno z poniższych zadań:\n-Zakonserwowałem/am (zabezpieczyłem przed szybszym zużyciem/zniszczeniem) jeden ze swoich sprzętów, który używam na co dzień.\n-Zakonserwowałem/am swój rower na zimę lub dokonałem/am prostej naprawy (np. nasmarowanie łańcucha, zmiana opony, naklejenie łaty na dętkę).', 90, ''),
(6275, 'Wykonałem/am jedno z poniższych zadań:\n-Dokonałem/am modyfikacji krawieckiej ubioru, który następnie normalnie nosiłem/am (np. skrócenie nogawki spodni, zwężenie bluzki/koszuli).\n-Samodzielnie przytwierdziłem/łam do murowanej ściany np. półkę lub obraz, nawiercając otwory i prawidłowo stosując kołek.', 90, ''),
(6276, 'Wykonałem/am lub naprawiłe/am przedmiot użyteczny w działaniach drużyny, szczepu lub gospodarstwa domowego.', 90, 'Ćwik/Samarytanka'),
(6277, 'Wykonałem/am jedno z poniższych zadań:\n-Zaprojektowałem/am i wykonałem/am drewnianą konstrukcję pionierską z drewna naturalnego utrzymującą ciężar kilku osób (np. wartownię obozową, pomost kąpielowy) bez użycia wkrętów, gwoździ i innych metalowych elementów.\n-Zaprojektowałem/am i wykonałem/am z surowych materiałów solidny i estetyczny mebel (np. stolik, regał na wymiar z półkami, ławkę do harcówki) wykorzystywany następnie w domu/mieszkaniu/harcówce.\n-Zaprojektowałem/am i wykonałem/am metalową konstrukcję, wykorzystując połączenia śrubowe i spawane (np. metalowy stolik, stojak na sztandar, wyspecjalizowane narzędzie do warsztatu, stojak na rower do komórki na zimę).', 91, ''),
(6278, 'Korzystając ze wskazówek fachowca, samodzielnie naprawiłem/am niesprawne urządzenie domowe (np. zmywarkę, odkurzacz), pamiętając o tym, by nie utracić gwarancji producenta.', 91, ''),
(6279, 'Zaprojektowałem/am i uszyłem/am solidny i estetyczny element ubioru (bluzkę, spodnie, spódnicę) lub akcesorium (torbę, plecak itp.), które następnie wykorzystywałem/am w codziennym życiu.', 91, ''),
(6280, 'Wstawiłem/am pranie, prawidłowo posegregowałem/am rzeczy, dobrałem/am odpowiedni program, wyciągnąłem/am i rozwiesiłem/am.', 123, ''),
(6281, 'Zrobiłem/am listę zakupów oraz zakupy dla rodziny, uwzględniając zakup jedzenia i drobnych elementów higieny i chemii domowej (np. papier toaletowy, mydło, pastę do zębów).', 123, ''),
(6282, 'Poznałem/em i wypróbowałem/am minimum 3 domowe (niezawierające środków chemicznych) sposoby i środki stosowane do czyszczenia, mycia, zabezpieczania przed owadami.', 123, ''),
(6283, 'Wiem, gdzie w moim mieszkaniu znajdują się liczniki, odczytałem/am ich stan.', 123, ''),
(6284, 'Przygotowałem/am strój na ważne wydarzenie, estetycznie wyprasowałem/am koszulę.', 124, ''),
(6285, 'Znalazłem/am (np. w Internecie) i przetestowałem/am minimum 6 domowych sposobów czyszczenia, mycia itp., których wcześniej nie znałem/am. Najciekawsze/najskuteczniejsze z nich wykorzystałem/am na biwaku lub zbiórce.', 124, ''),
(6286, 'Dowiedziałem/am się, jakie są miesięczne domowe opłaty, w porozumieniu z rodzicami/opiekunami wykonałem/am część z nich.', 124, ''),
(6287, 'Skompletowałem/am lub uzupełniłem/am domową apteczkę (w porozumieniu z rodzicami/opiekunami).', 124, ''),
(6288, 'Zorganizowałem/am uroczystość rodzinną lub przyjęcie dla znajomych na kilka osób, przygotowałem/am potrawy, zadbałem o odpowiednią oprawę przyjęcia (np. muzyka, nakrycia, wystrój).', 125, ''),
(6289, 'Przygotowałem/am dom/mieszkanie do dłuższej nieobecności, np. wyłączyłem/am wodę, gaz, zabezpieczyłem/am przed kradzieżą.', 125, ''),
(6290, 'Przez minimum dwa tygodnie zajmowałem/am się prowadzeniem domu:\nutrzymywałem/am czystość i porządek w mieszkaniu,\nzorganizowałem/am tygodniowe zakupy według wcześniej przygotowanej listy,\nprzyrządzałem/am wszystkie posiłki zgodnie z opracowanym przez siebie tygodniowym jadłospisem.', 125, ''),
(6291, 'Opracowałem/am tygodniowy plan zajęć, w którym uwzględniłem/am wszystkie swoje zajęcia (lekcje w szkole, odrabianie zadań domowych, zajęcia pozalekcyjne, obowiązki domowe, czas odpoczynku).', 126, ''),
(6292, 'Zorganizowałem/am sobie miejsce do nauki w domu (oświetlenie, ustawienie krzesła itp.).', 126, ''),
(6293, 'Poznałem/am co najmniej jedną metodę umożliwiającą zapamiętywanie różnych informacji. Użyłem/am tej metody do przygotowania się na sprawdzian w szkole.', 126, ''),
(6294, 'Ucząc się do przynajmniej jednego sprawdzianu, wymyśliłem/am po 2 sposoby praktycznego zastosowania wiedzy, którą zdobyłem/am.', 126, ''),
(6295, 'Poznałem/am kilka metod zapamiętywania i efektywnego uczenia się – wykorzystuję je w mojej codziennej nauce. Nauczyłem/am jednej metody członków mojego zastępu.', 127, ''),
(6296, 'Rozumiem wpływ snu na porządkowanie zdobytych informacji. Zaplanowałem/am swoją tygodniową naukę tak, aby przed wykorzystaniem zdobytej wiedzy móc się dobrze wyspać.', 127, ''),
(6297, 'Przedstawiłem/am członkom swojego zastępu zestaw ćwiczeń odprężających, jakie stosuję podczas przerw w nauce.', 127, ''),
(6298, 'Znam kilka metod prowadzenia notatek w tym w formie rysunkowej, wybrałem/am taką, która najlepiej pomaga mi się uczyć.', 127, ''),
(6299, 'W ramach zbiórki zorganizowałem/am formę przyswajania wiedzy, która była jednocześnie dobrą zabawą.', 128, '');
INSERT INTO `zadaniasprawnosciharcerskich` (`ID`, `Tresc`, `Sprawnosc`, `Stopien`) VALUES
(6300, 'Wykonałem jedno z poniższych zadań:\n-zorganizowałem/am i poprowadziłem w zastępie zbiórkę/zbiórki poświęcone metodom zapamiętywania i efektywnego uczenia się,\n-zorganizowałem/am zajęcia dla osób starszych zainteresowanych ćwiczeniami mnemotechnicznymi.', 128, ''),
(6301, 'Wiem, co oznacza termin neuroplastyczność mózgu. Przez czas trwania próby stosowałem/am dietę, która pomaga mojemu mózgowi lepiej działać.', 128, ''),
(6302, 'Wiem, jak endorfiny pomagają w zapamiętywaniu. Wypracowałem/am metody zwiększenia poziomu endorfin w trakcie nauki, które działają na mnie (np. słucham ulubionej muzyki, robię notatki rysunkowe, bo rysowanie sprawia mi przyjemność, jem ulubione przekąski, uczę się w ruchu).', 128, ''),
(6303, 'Rozumiem, dlaczego należy zarządzać swoim czasem. Planując zadania na tydzień, dopilnowałem/am, aby nie przeładować swojego tygodnia i zostawić sobie czas na odpoczynek i odpowiednią ilość snu.', 129, ''),
(6304, 'Poznałem/am trzy różne metody zarządzania czasem. Zaplanowałem/am jedno zadanie wybraną metodą.', 129, ''),
(6305, 'Planując swój czas, korzystałem/am z kalendarza/terminarza (papierowego lub elektronicznego).', 129, ''),
(6306, 'Korzystając z wiedzy o planowaniu, pomogłem/am zastępowi stworzyć harmonogram tropu.', 129, ''),
(6307, 'Założyłem/am własny bullet journal, dobierając najwygodniejsze i najpotrzebniejsze dla siebie elementy. Prowadziłem/am go przez co najmniej miesiąc, a następnie podzieliłem/am się dobrymi praktykami z zastępem lub znajomymi.', 130, ''),
(6308, 'W trakcie trwania próby planowałem/am każdy swój tydzień. Pod koniec każdego tygodnia dokonywałem/am podsumowania realizacji planu i brałem/am pod uwagę efekty przy planowaniu kolejnego tygodnia.', 130, ''),
(6309, 'Poznałem/am co najmniej dwie metody planowania zadań i zwiększenia produktywności (np. diagram Gantta, pomodoro, macierz Eisenhowera, kanban). Zastosowałem/am je przy dowolnym zadaniu w trakcie próby.', 130, ''),
(6310, 'Współorganizowałem/am biwak drużyny: brałem/am udział w podziale zadań, byłem/am odpowiedzialny/a za wybrane zadania (np. lista zakupów, dojazdy, zamówienie autokaru).', 130, ''),
(6311, 'Poznałem/am podstawy zarządzania projektami, wiem czym jest agile i waterfall. Do prowadzonego przez siebie projektu dobrałem/am takie narzędzia z tych metodyk projektowych, które pasowały do charakteru grupy i prowadzonego projektu tak, by wspierały efektywność pracy, a nie ją utrudniały.', 131, ''),
(6312, 'Poznałem/am kilka narzędzi do planowania i zarządzania pracą zespołu lub własną (np. Trello, Asana, Microsoft Planner, Habitica). Wybrałem/am jedno i zastosowałem/am w pracy własnej lub zespołu, konsekwentnie używając go w trakcie trwania zadania.', 131, ''),
(6313, 'Wiem, czym jest ścieżka krytyczna, zidentyfikowałem/am ją w prowadzonym przez siebie w projekcie, zidentyfikowałem/am ryzyka w projekcie i nimi zarządziłem/am.', 131, ''),
(6314, 'Zaproponowałem/am co najmniej jedną aktywność do planu pracy drużyny, do pomysłu przekonałem/am organ demokracji drużyny, a po zatwierdzeniu kierowałem/am realizacją projektu i podsumowałem/am go.', 131, ''),
(6315, 'Z pomocą innej osoby, książek lub Internetu poznałem/am i wypróbowałem co najmniej dwie z aktywności: szydełkowanie, robienie na drutach lub ramie dziewiarskiej, makramę.', 132, ''),
(6316, 'Nauczyłem/am się trzech ściegów szydełkowych, na druty lub węzłów makramowych.', 132, ''),
(6317, 'Samodzielnie lub z drobną pomocą wykonałem/am 2 proste przedmioty/ozdoby w wybranej technice. Mogą to być np. szalik, opaska, kwiatek na gumkę do włosów, zakładka do książek, podstawka pod kubek, bransoletka. Zaprezentowałem/am je na zbiórce zastępu lub drużyny. Opowiedziałem/am, jakiego ściegu (węzła) użyłem/am.', 132, ''),
(6318, 'Samodzielnie wykonałem/am co najmniej 8 przedmiotów w wybranej przeze mnie technice dziewiarskiej. Wykorzystałem/am co najmniej 6 różnych ściegów podstawowych i ażurowych (szydełko, druty, obręcz) lub węzłów makramowych.\nZaprezentowałem/am je (co najmniej dwa przedmioty) na zbiórce zastępu lub drużyny i opowiedziałem/am na czym polega wykonywane przeze mnie rękodzieło.', 133, ''),
(6319, 'Nauczyłem/am inną osobę wykonania prostej makramy lub ozdoby na szydełku, drutach.', 133, ''),
(6320, 'Wykonałem/am prezent lub ozdobę do harcówki w wybranej przeze mnie technice.', 133, ''),
(6321, 'Zgromadziłem/am warsztat przydatnych narzędzi i materiałów oraz bazę pomysłów. Zaprezentowałem/am go (w naturze lub na zdjęciach) zastępowi lub drużynie.', 133, ''),
(6322, 'Regularnie doskonalę swoje umiejętności w wybranej dziedzinie dziewiarstwa (uczestniczę w warsztatach, korzystam z tutoriali w Internecie). W swoich pracach zastosowałem/am co najmniej 15 ściegów lub węzłów. Nawiązałem/am regularny kontakt z co najmniej jedną osobą mającą takie same zainteresowania, z którą wymieniamy się pomysłami.', 134, ''),
(6323, 'Wykonałem jedno z poniższych zadań:\n-zorganizowałem/am i poprowadziłem w zastępie lub drużynie cykl 2-3 zbiórek/zajęć dla chętnych podczas których, wykorzystując swój warsztat narzędzi, nauczyłem/am podstawowych technik dziewiarskich, a moi podopieczni wykonali co najmniej dwa różne przedmioty;\n-prowadzę swój kanał na YouTube lub bloga, na którym zamieściłem/am co najmniej 5 tutoriali.', 134, ''),
(6324, 'Urządziłem/am wystawę lub aukcję wykonanych przez siebie ozdób oraz przedmiotów użytkowych, na których znajdzie się co najmniej 15 eksponatów.\nCo najmniej 5 przedmiotów wykonałem/am na dużym poziomie zaawansowania, tzn. w przypadku:\nszydełka: poncho, obrus ażurowy, zestaw serwetek, rękawiczki, figurki amigurumi, itp.,\ndrutów: sweter, poncho, sukienka, szal ażurowy, skarpety, rękawiczki, koc, itp.,\nmakrama: makatka w kształcie sowy, torba, firanka, prycz, półka obozowa, żyrandol, itp.', 134, ''),
(6325, 'Na lekcji techniki, zajęciach w domu kultury, na zbiórce lub z pomocą książek i Internetu poznałem/am i wypróbowałem/am różne rodzaje DIY.', 135, ''),
(6326, 'Samodzielnie lub z drobną pomocą wykonałem/am przedmioty/ozdoby w 5 różnych technikach. Zaprezentowałem/am je na zbiórce zastępu lub drużyny.', 135, ''),
(6327, 'Wykonałem/am prezent lub ozdobę do harcówki w wybranej przeze mnie technice.', 135, ''),
(6328, 'Wziąłem/wzięłam udział w profesjonalnych warsztatach rękodzielniczych np. podczas imprezy harcerskiej, festiwalu rękodzieła, w domu kultury. Poznałem/am podstawowe techniki (np. ściegi hafciarskie) potrzebne w danym rękodziele.', 136, ''),
(6329, 'Samodzielnie wykonałem/am co najmniej 8 przedmiotów w wybranej przeze mnie technice DIY (lub dwóch). Zaprezentowałem/am je na zbiórce zastępu lub drużyny i opowiedziałem, na czym polega wykonywane przeze mnie rękodzieło.', 136, ''),
(6330, 'Nauczyłem/am innych harcerzy wykonania prostego przedmiotu w wybranej technice DIY (np. prezentu mikołajkowego).', 136, ''),
(6331, 'Gromadzę warsztat przydatnych narzędzi i materiałów oraz bazę pomysłów. Zaprezentowałem/am go (w naturze lub na zdjęciach) zastępowi lub drużynie.', 136, ''),
(6332, 'Regularnie doskonalę swoje umiejętności w wybranej dziedzinie rękodzieła (uczestniczę w warsztatach, korzystam z tutoriali w Internecie). Nawiązałem/am regularny kontakt z co najmniej jedną osobą mającą takie same zainteresowania, z którą wymieniamy się pomysłami.', 137, ''),
(6333, 'Wykonałem jedno z poniższych zadań:\n-zorganizowałem/am i poprowadziłem w zastępie lub drużynie cykl 2-3 zbiórek/zajęć dla chętnych podczas których, wykorzystując swój warsztat narzędzi, nauczyłem podstawowych technik potrzebnych w danym rękodziele, a moi podopieczni wykonali co najmniej dwa różne przedmioty.\n-Prowadzę swój kanał na YouTube lub bloga DIY, na którym zamieściłem/am co najmniej 5 tutoriali.', 137, ''),
(6334, 'Urządziłem/am wystawę lub aukcję wykonanych przez siebie ozdób oraz przedmiotów użytkowych, na których znalazło się co najmniej 15 eksponatów.', 137, ''),
(6335, 'Wykonałem/am praktyczny element do wyposażenia harcówki.', 137, ''),
(6336, 'Nawlokłem/am nitkę na igłę. Rozpocząłem/rozpoczęłam i zakończyłem/am ścieg. Zastosowałem/am co najmniej dwa różne ściegi (np. fastryga, ścieg za igłą, ścieg przed igłą).', 138, ''),
(6337, 'Wykonałem/am co najmniej dwa z poniższych zadań:\nprzyszyłem/am guziki do koszuli,\nobrębiłem/am chusteczkę,\nnaszyłem/am łatę na spodnie,\nzacerowałem/am dziurę w swetrze,\nwyhaftowałem/am swój monogram,\ndoszyłem/am linki do namiotu.', 138, ''),
(6338, 'Uszyłem/am ręcznie lub na maszynie drobny przedmiot, np. łapki do garnków, kieszeń na grzebienie w łazience, fartuszek kuchenny, worek na kapcie itp.', 138, ''),
(6339, 'Naszyłem/am prawidłowo wszystkie plakietki na mundur.', 138, ''),
(6340, 'Rozpoznałem/am po dotyku i wyglądzie podstawowe rodzaje tkanin (np. bawełna, wełna, jedwab, len, sztuczne tworzywa) i wytłumaczyłem/am, jaki wpływa ma rodzaj tkaniny na technikę szycia i co można uszyć z wymienionych tkanin.', 139, ''),
(6341, 'Za pomocą maszyny do szycia wykonałem/am co najmniej dwa zadania:\nuszyłem/am według gotowego wykroju fartuch, spódnicę lub szorty,\nobrębiłem/am obrus,\nwszyłem/am zamek błyskawiczny,\nuszyłem/am poszewkę na poduszkę,\nnaprawiłem/am lub przerobiłem/am używaną cześć odzieży.', 139, ''),
(6342, 'Konserwowałem maszynę do szycia.', 139, ''),
(6343, 'Uszyłem/am chusty dla drużyny lub zastępu lub inny element mundurowy/obrzędowy.', 139, ''),
(6344, 'Zaprezentowałem/am własny styl ubierania się, szyjąc samodzielnie lub adaptując gotowe ubrania do własnych potrzeb (co najmniej 8 projektów).', 140, ''),
(6345, 'Uszyłem/am samodzielnie bluzkę, mundur, szorty lub spodnie, pobierając uprzednio miarę i przygotowując wykrój.', 140, ''),
(6346, 'Zaprojektowałem/am i uszyłem/am element dekoracyjny do domu lub harcówki (firankę, zasłonę, dekorację, itp.).', 140, ''),
(6347, 'Przeprowadziłem/am w drużynie naukę podstaw szycia.', 140, ''),
(6348, 'Wyznaczyłem/am kilkakrotnie kierunki świata w terenie, za pomocą gwiazd lub słońca.', 165, ''),
(6349, 'Narysowałem/am prosty szkic sytuacyjny na potrzeby gry lub rozplanowania obozowiska.', 165, ''),
(6350, 'Poznałem/am znaki topograficzne. Poprowadziłem/am zastęp/patrol w terenie leśnym, posługując się busolą i mapą. Dotarłem/am bezbłędnie do miejsca oznaczonego na mapie.', 165, 'Wywiadowca/Tropicielka'),
(6351, 'Wyznaczałem/am azymuty za pomocą busoli. Prawidłowo przeszedłem/am trasę zapisaną za pomocą azymutów.', 165, ''),
(6352, 'Wziąłem udział w Imprezie na Orientację (INO), uzyskując wynik zapewniający klasyfikację. Trasa InO składała się z co najmniej 8 punktów kontrolnych.', 165, ''),
(6353, 'Wyznaczyłem/am kierunki świata w terenie z pomocą gwiazd oraz leśnych słupków oddziałowych.', 166, ''),
(6354, 'Przy pomocy busoli wykonałem/am szczegółowy plan obozu.', 166, ''),
(6355, 'Posługiwałem/am się różnymi rodzajami map: np. leśnymi, samochodowymi, turystycznymi. Trafiłem/am do odległego o kilka kilometrów obiektu, wykonując w czasie marszu szkic drogi z zastosowaniem właściwej skali.', 166, ''),
(6356, 'W działaniach topograficznych korzystałem/am z różnych przyrządów ułatwiających pracę (busola, krzywomierz, koraliki zliczające „pace count beads”, krokomierz, GPS, itp.)', 166, 'Ćwik/Samarytanka'),
(6357, 'Wykreśliłem/am mapę wybranego terenu (lub zaktualizowałem/am starą mapę) na potrzeby obozu, gry terenowej, rajdu, INO itp.', 167, ''),
(6358, 'Na podstawie archaicznej mapy (np. z lat 30. XX wieku) odnalazłem/am w terenie miejsca, które uległy zmianom (np. wgłębienie po wyschniętym oczku wodnym, miejsce po zarośniętej lasem drodze, pozostałości nieistniejących już zabudowań itp.).', 167, ''),
(6359, 'Dokonałem/am pomiaru terenu nadającego się na urządzenie obozu i wykonałem/am dokładny plan (lub inny plan przydatny do pełnienia służby).', 167, ''),
(6360, 'Przeprowadziłem/am dla młodszych harcerzy szkolenia z zakresu terenoznawstwa oparte na praktycznych ćwiczeniach i grach terenowych.', 167, ''),
(6361, 'Zorganizowałem/am imprezę na orientację, przygotowując trasę o długości co najmniej 5 kilometrów.', 167, ''),
(6362, 'Wyznaczyłem/am kilkakrotnie kierunki świata w terenie, za pomocą kompasu oraz bez jego użycia, wykorzystując elementy terenowe.', 164, ''),
(6363, 'W czasie wędrówki w terenie zorientowałem/am mapę zgodnie z kierunkami świata. Poprowadziłam/em zastęp podczas wędrówki.', 164, 'Młodzik/Ochotniczka'),
(6364, 'Zapamiętałem/am drogę przebytą w mieście i z pamięci wykonałem/am szkic drogi (plan ulic, przez które przechodziłem/am).', 164, 'Młodzik/Ochotniczka'),
(6365, 'W Imprezie Na Orientację (INO) składającej się z co najmniej 5 punktów kontrolnych pokonałem cała trasę, odnajdując wszystkie punkty.', 164, ''),
(6366, 'Doskonale znam swoją okolicę (np. miejscowość, dzielnicę). Wskazałem/am w terenie, jak dojść do wybranych ulic i charakterystycznych miejsc w mojej dzielnicy/miejscowości lub wskazałem/am je na mapie (np. ośrodek zdrowia, aptekę, pocztę, straż pożarną, posterunek policji, urząd gminy, bibliotekę, dom kultury, kino, dworzec).', 168, ''),
(6367, 'Wyszukałem/am ciekawostki historyczne o swojej dzielnicy/miejscowości w rozmowie ze starszą osobą lub w przewodniku lub Internecie. Dotarłam/em do opisywanych miejsc. Wyniki poszukiwań przedstawiłem/am swojemu zastępowi.', 168, ''),
(6368, 'Przeprowadziłem/am terenową zbiórkę zastępu na temat dzielnicy/miejscowości (uwzględniając zabytek historyczny, muzeum, osobliwość przyrodniczą itp.).', 168, ''),
(6369, 'Ciekawie opowiedziałem swojemu zastępowi, skąd wzięła się nazwa rodzinnej miejscowości oraz przedstawiłem jej herb (jeśli miejscowość go posiada) i wyjaśniłem/am dlaczego tak wygląda.', 169, ''),
(6370, 'Poznałem/am kilka (6–10) obiektów swojej dzielnicy/miejscowości. Poznałem/am ich aktualne i dawne funkcje. Odwiedziłem je i zrobiłem/am zdjęcia.', 169, ''),
(6371, 'Wziąłem/wzięłam udział w miejscowym wydarzeniu (np. dniach miasta/dzielnicy). Zachęciłem do wspólnego udziału członków mojego zastępu/drużyny.', 169, ''),
(6372, 'Nauczyłem/am się podstawowych zwrotów w języku obcym, przydatnych do udzielania informacji zagranicznym turystom.', 170, ''),
(6373, 'Korzystając z różnych źródeł (np. internetowe mapy, rozkłady jazdy) oraz własnego doświadczenia, opracowałem na potrzeby przedsięwzięcia drużyny/zastępu wariant drogi (najkrótsza, najszybsza, najtańsza, najwygodniejsza itd.) dostosowany do potrzeb drużyny/zastępu .', 170, 'Ćwik/Samarytanka'),
(6374, 'Pogłębiłem/am wiedzę na temat co najmniej 10 wybranych obiektów w swojej dzielnicy/miejscowości. Poznałem/am kontekst historyczny ich powstania oraz zmian. Zorganizowałem/am dla zastępu/drużyny lub znajomych/rodziny wycieczkę/spacer po okolicy opracowaną przez siebie trasą zawierającą charakterystyczne obiekty, zabytki itp.', 170, ''),
(6375, 'Udzielałem/am się w życiu społeczności związanej z dzielnicą/miejscowością. Samodzielnie lub z zastępem brałem/am udział w organizacji miejscowego wydarzenia (np. dni dzielnicy/miejscowości).', 170, ''),
(6376, 'Poznałem/am historię swojego regionu. Korzystałem/am z publikacji na temat dzielnicy/miejscowości. Poznałem/am strony i społeczności internetowe związane z dzielnicą/ miejscowością. Opracowałem/am minimum jeden wpis na tych stronach dotyczący walorów przyrodniczych, historycznych lub kulturowych mojej miejscowości/regionu.', 171, ''),
(6377, 'Pogłębiłem/am wiedzę związaną z konkretnym tematem/obiektem związanym z moją miejscowością/regionem. Powiększyłem/am swoją biblioteczkę tematyczną dotyczącą tego tematu/obiektu.', 171, ''),
(6378, 'Opracowałem/am trasę kilkugodzinnej wycieczki i wykonałem/am projekt folderu/miniprzewodnika (w formie strony www/aplikacji/artykułu/broszury itp.).', 171, ''),
(6379, 'Przeprowadziłem/am dla kolegów/koleżanek ze swojego środowiska lub przyjezdnych co najmniej jedną wycieczkę opracowaną przez siebie trasą po swojej miejscowości (okolicy), wykazując się znajomością jego historii, zabytków i ciekawych obiektów.', 171, ''),
(6380, 'Poznałem/am podstawowe problemy swojej dzielnicy/miejscowości. Wybrałem/am jeden z nich i zwróciłem/am się do odpowiedniej instytucji (np. urzędu gminy, zarządu dróg, straży miejskiej) z prośbą o zaradzenie sytuacji lub samodzielnie (lub z moją jednostką harcerską) rozwiązałem/am problem.', 171, ''),
(6381, 'Dowiedziałem/am się, w jakim regionie mieszkam, skąd się wzięła jego nazwa i z jakimi regionami graniczy. Zaopatrzyłem/am się w mapę regionu. Zaprezentowałem ją na zbiórce zastępu i pokazałem granice mojego regionu.', 172, ''),
(6382, 'Znam najważniejsze wydarzenia z historii mojej okolicy/regionu. Odwiedziłem/am miejsce z nimi związane. Opowiedziałem/am o nich swojemu zastępowi.', 172, ''),
(6383, 'Wziąłem/wzięłam udział w wycieczce zastępu do miejsca ważnego dla historii lub kultury mojego regionu.', 172, ''),
(6384, 'Poznałem/am przyrodę regionu: krajobraz, występujące zwierzęta i rośliny, miejsca ochrony przyrody. Z zastępem odwiedziłem/am co najmniej jeden obiekt cenny przyrodniczo w moim regionie.', 173, ''),
(6385, 'Byłem/am w muzeum/skansenie itp. przedstawiającym fragment kultury/historii mojego regionu. Wziąłem/wzięłam udział w przygotowanych przez muzeum/skansen aktywnościach np. grze, queście, zajęciach manualnych itp.', 173, ''),
(6386, 'Odwiedziłem/am kilka zabytków ważnych dla regionu. Przed lub po wycieczce wyszukałem/am informacje na temat odwiedzanych miejsc.', 173, ''),
(6387, 'Poznałem/am i rozpropagowałem/am wiedzę o wybranej wybitnej osobie związanej z moim regionem (najlepiej taką, która jest mniej znana).', 174, ''),
(6388, 'Poznałem/am kulturę regionu, dowiedziałem/am się, jakie grupy etnograficzne/mniejszości narodowe mieszkały w nim kiedyś, a jakie mieszkają teraz. Poznałem/am gwarę/dialekt regionu. Powiedziałem/am w nim kilka zdań.', 174, ''),
(6389, 'Przygotowałem/am i przeprowadziłem/am dla zastępu (przy pomocy osoby dorosłej biegłej w tym temacie) warsztaty kulinarne dotyczące kuchni regionalnej.', 174, ''),
(6390, 'Odwiedziłem/am co najmniej jedno mniej znane, ale ważne dla regionu miejsce. Rozpropagowałem je np. przez prezentację zdjęć z mojej wyprawy w mediach społecznościowych.', 174, ''),
(6391, 'Poznałem/am najważniejsze wydarzenia z historii regionu. Wykonałem/am na ten temat prezentację multimedialną, którą wykorzystałem/am na zbiórce lub udostępniłem/am w mediach społecznościowych.', 175, ''),
(6392, 'Poznałem/am instytucje działające na rzecz regionu. Wziąłem/wzięłam udział w wydarzeniu regionalnym.', 175, ''),
(6393, 'Zaplanowałem/am i odbyłem/am wycieczkę tematyczną po regionie. Relację fotograficzną z mojej wyprawy przedstawiłem/am drużynie lub w mediach (np. społecznościowych).', 175, ''),
(6394, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Oprowadziłem/am po wybranym obiekcie w regionie opowiadając o jego historycznym i gospodarczym/kulturowym znaczeniu w kontekście zmieniających się czasów.\n-Pokierowałem/am sporządzeniem informacji o walorach turystycznych okolicy/regionu na podstawie zajęć terenowych i literatury (geografia i historia okolicy, system połączeń komunikacyjnych, przemysł i rolnictwo, obiekty historyczne i przyrodnicze, sztuka, zwyczaje ludowe itp.).', 175, ''),
(6395, 'Poznałem/am najważniejsze regiony Polski. Wskazałem/am je na mapie.', 176, ''),
(6396, 'Poznałem/am różnice przyrodnicze między regionami w Polsce. Wymieniłem/am kilka parków narodowych i opowiedziałem/am, jakie elementy przyrody chronią, charakterystyczne dla regionu, w którym się znajdują.', 176, ''),
(6397, 'Odwiedziłem/am region inny niż mój region zamieszkania. Poznałem/am jego najciekawsze miejsca. Po powrocie przygotowałem pokaz zdjęć i przedstawiłem go w rodzinie lub zastępie.', 176, ''),
(6398, 'Wskazałem/am różnice między mapami drogowymi, krajobrazowymi i tematycznymi. Skorzystałem/am z różnych map w praktyce, dostosowując mapę do potrzeb.', 177, ''),
(6399, 'Uczestniczyłem/am w wydarzeniu związanym z kulturą mojego regionu. Poznałem/am jego gwarę/dialekt, kuchnię i folklor.', 177, ''),
(6400, 'Uczestniczyłem/am w co najmniej trzech wycieczkach krajoznawczych w różnych regionach Polski (w tym co najmniej jedną w swoim regionie).', 177, ''),
(6401, 'Zlokalizowałem/am na mapie miasta wojewódzkie w Polsce, największe rzeki, jeziora, pasma górskie. Zaznaczyłem na mapie miejsca, w których już byłem/am i pokazałem ją rodzinie celem weryfikacji.', 177, ''),
(6402, 'Korzystałem/am z Kanonu Krajoznawczego Polski lub innych źródeł rzetelnych informacji o krajoznawstwie. W oparciu o nie zaproponowałem/am miejsca warte odwiedzenia podczas rodzinnego wypadu lub biwaku/rajdu/obozu drużyny.', 178, ''),
(6403, 'Zaplanowałem/am i odbyłem/am wycieczkę (wraz z rodziną/drużyną/zastępem) do miejsc ciekawych pod względem historycznym, przyrodniczym lub geograficznym w innym regionie, posługując się mapami i przewodnikami. Osobiście pilotowałem/am przez minimum jeden dzień na wybranej lub ułożonej przez siebie trasie.', 178, ''),
(6404, 'Uczestniczyłem/am w wydarzeniu związanym z kulturą innego niż mój regionu. Poznałem/am jego gwarę/dialekt, kuchnię i folklor.', 178, ''),
(6405, 'Korzystałem/am z literatury/Internetu na temat wybranego regionu Polski. Dowiedziałem/am się, jakie aktywności można uprawiać w różnych regionach Polski. Poznałem/am najważniejsze trasy z nimi związane. Przedstawiłem/am je podczas planowania wyjazdu (biwaku/obozu)..', 179, ''),
(6406, 'Zorganizowałem/am z drużyną/zastępem wyjazd, odpowiadałem za dobór trasy, bogatej w miejsca cenne pod względem historycznym, kulturowym i przyrodniczym.', 179, ''),
(6407, 'Odwiedziłem/am kilka miejsc na szlaku tematycznym w danym regionie np. Szlak Zabytków Techniki, Zamków Gotyckich, Cysterski, Piastowski.', 179, ''),
(6408, 'Przeszedłem/przeszłam samodzielnie lub z zastępem zaplanowaną trasę o długości co najmniej 6 km w terenie pozamiejskim, ubierając się stosownie do warunków terenowych i pogodowych, zabezpieczając się przed przegrzaniem, przemoczeniem oraz otarciem stóp.', 180, ''),
(6409, 'Dotarłem/am pieszo do wskazanego punktu, korzystając z mapy.', 180, ''),
(6410, 'Skompletowałem/am niezbędna część ekwipunku turystycznego (dobre buty do wędrówki w każdą pogodę, kurtkę przeciwdeszczową, plecak, kompas).', 180, ''),
(6411, 'Przed wyruszeniem na wędrówkę przypomniałem/am pozostałym uczestnikom zasady bezpiecznego poruszania się po drogach.', 180, ''),
(6412, 'Uczestniczyłem/am w co najmniej dwóch kilkudniowych wycieczkach pieszych. Nauczyłem/am się korzystać z oznakowanych szlaków turystycznych. Co najmniej raz zaplanowałem całodzienną trasę uwzględniając szlaki.', 181, ''),
(6413, 'Prawidłowo spakowałem/am plecak, zabierając tylko rzeczy przydatne na wyprawie.', 181, ''),
(6414, 'Wskazałem/am na mapie trasy trzech ostatnich wędrówek przebytych z zastępem/drużyną.', 181, ''),
(6415, 'Uczestnicząc w wycieczkach i rajdach, zachowałem/am pogodę ducha, mimo trudów wędrowania.', 181, ''),
(6416, 'Poznałem/am zasady bezpiecznego marszu (tempo, odpoczynek, maksymalne obciążenie, przepisy ruchu drogowego). Pilnowałem/am przestrzegania tych zasad przez zastęp.', 182, ''),
(6417, 'Spędziłem/am co najmniej dziesięć dni na różnych pieszych wycieczkach.', 182, ''),
(6418, 'Współorganizowałem/am wędrówkę/biwak zastępu (drużyny) pilnując zachowania zasad Leave No Trace. Kierowałem noclegiem, przygotowując go, a następnie likwidując bez pozostawienia śladów.', 182, ''),
(6419, 'Przeszedłem/przeszłam samodzielnie lub na czele patrolu założoną trasę o długości minimum 5 km po zmroku, orientując się według mapy i kompasu.', 182, ''),
(6420, 'Odbyłem/am co najmniej pięć samodzielnych wypraw w promieniu kilku kilometrów od miejsca zamieszkania oraz co najmniej jedną dalszą wyprawę poza swój region (niekoniecznie samodzielną).', 183, ''),
(6421, 'Nocowałem/am w lesie bez namiotu, a także w nieznanej miejscowości, korzystając np. ze schroniska.', 183, ''),
(6422, 'Zorganizowałem/am dla drużyny wycieczki o różnej tematyce, m.in. krajoznawcze, przyrodnicze, historyczne. Zorganizowałem/am i poprowadziłem/am dwie co najmniej dwudniowe wędrówki grupy po zaplanowanym przez siebie szlaku, pod opieką pełnoletniego opiekuna.', 183, ''),
(6423, 'Skompletowałem/am ekwipunek przydatny w długich wędrówkach pieszych (np. namiot, tarp, hamak, lekki śpiwór, plecak, sprzęt do gotowania, ubiór, uniwersalne oświetlenie, itp.).', 183, ''),
(6424, 'Dowiedziałem/am się: jak bezpiecznie wędrować po szlaku, co zrobić w przypadku nagłego załamania pogody (nagła burza, mgła, itd.), spotkania dzikich zwierząt (niedźwiedzia, żmii) i jak wezwać pomoc w górach. Przedstawiłem/am to swojemu zastępowi.', 184, ''),
(6425, 'Zdobyłem/am co najmniej 2 szczyty górskie, scharakteryzowałem/am je z uwzględnieniem elementów przyrody oraz mieszkających tam zwierząt.', 184, ''),
(6426, 'Spakowałem/am plecak na całodniową wycieczkę górską, pamiętając o zmienności pogody i aktualnej porze roku. Mój strój odpowiadał aktualnym warunkom pogodowym.', 184, ''),
(6427, 'Poruszałem/am się po górach z uwzględnieniem szlaków. Wyznaczyłem/am drogę szlakiem górskim na podstawie mapy.', 184, ''),
(6428, 'Zapoznałem/am się z publikacjami dotyczącymi wybranych pasm górskich (np. przewodniki, strony internetowe). Zachęciłem/am członków mojej drużyny do wspólnego lub indywidualnego wyjazdu w te miejsca.', 185, ''),
(6429, 'Zdobyłem/am co najmniej 7 szczytów górskich, poznałem ich specyfikę przyrodniczą.', 185, ''),
(6430, 'Skompletowałem/am część ubrań oraz sprzętu turystycznego (np. kijki trekkingowe, raczki, stuptuty, buff, softshell, odpowiednie spodnie, kurtkę, buty, plecak, kompas).', 185, ''),
(6431, 'Uczestniczyłem/am w wędrówce górskiej trwającej minimum 2 dni. Spędziłem/am co najmniej dziesięć dni na całodniowych wyprawach górskich. Podczas wędrówki zapoznałam się z tłem historycznym i kulturą odwiedzanego regionu.', 185, ''),
(6432, 'Wykonałem/am jedno z poniższych zadań:\n-uczestniczyłem/am w co najmniej jednym obozie wędrownym w górach;\n-nocowałem/am w trzech różnych schroniskach górskich w czasie jednej wędrówki.', 187, ''),
(6433, 'Zdobyłem/am co najmniej 2 szczyty (w różnych pasmach górskich) powyżej 1300 m n.p.m.', 187, ''),
(6434, 'Zaplanowałem/am dwudniową wycieczkę turystyczną w góry z uwzględnieniem mapy, noclegu górskiego, czasu przejścia oraz możliwości fizycznych uczestników, a także minimum dwóch miejsc istotnych historycznie bądź kulturowo, o których zostanie, podczas wycieczki, opowiedziana gawęda.', 187, ''),
(6435, 'Zorganizowałem/am prelekcję na temat swojej wędrówki/wędrówek w górach dla drużyny/klasy/klubu wspinaczkowego, itp. z wykorzystaniem zdjęć, map, fachowej literatury itp. (ewentualnie dłuższa relacja w mediach np. społecznościowych).', 187, ''),
(6436, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-spędziłem/am minimum 1 noc w warunkach zimowych w górach w namiocie, igloo, jamie śnieżnej odpowiednio zabezpieczając się przed zimnem,\n-sprawnie chodziłem/am w rakach w różnym terenie (mikstowym, śnieżnym, lodowym) oraz posługiwałem/am się czekanem przy hamowaniu upadku oraz samoasekuracji.', 188, ''),
(6437, 'Zdobyłem/am co najmniej 3 szczyty powyżej 1300 m n.p.m. w tym co najmniej jeden zimą i co najmniej jeden powyżej 1500 m n.p.m.', 188, ''),
(6438, 'Zdobyłem/am 3 szczyty górskie w warunkach zimowych. Zapoznałem/am się z komunikatem lawinowym przed wyruszeniem na wędrówkę, jeśli była taka potrzeba, oraz dobrałem/am właściwy sprzęt turystyczny.', 188, ''),
(6439, 'Wykonałem/am jedno z poniższych zadań:\n-uczestniczyłem/am w festiwalu górskim lub spotkaniu, np. Przeglądzie Filmów Górskich, Kolosach, itd.\n-zorganizowałem/am spotkanie z taternikiem/alpinistą/himalaistą dla wędrowników z mojego środowiska.', 188, ''),
(6440, 'Poznałem/am i zademonstrowałem/am podstawowe zasady bezpiecznego poruszania się pojazdu jednośladowego po drodze (prawostronny ruch, zakaz jazdy po chodnikach, prawidłowe oświetlenie roweru oraz właściwy ubiór rowerzysty).', 191, ''),
(6441, 'Rozróżniłem/am poszczególne części roweru, umyłem/am go. Dowiedziałem/am się, gdzie znaleźć pomoc w razie awarii. Przygotowałem/am rower i ekwipunek do wyprawy (światła, koła, apteczka, mapa).', 191, ''),
(6442, 'Wziąłem/wzięłam udział w przynajmniej 2 wycieczkach rowerowych.', 191, ''),
(6443, 'Poznałem/am ścieżki/szlaki rowerowe w pobliżu miejsca mojego zamieszkania, zaplanowałem/am i wykonałem/am bezpieczny przejazd za pomocą tych ścieżek/szlaków do punktu odległego o kilka kilometrów.', 191, ''),
(6444, 'Poznałem/am i zademonstrowałem/am różnice pomiędzy wymijaniem, omijaniem i wyprzedzaniem oraz znam zasady pierwszeństwa przejazdu (m.in. na skrzyżowaniach).', 192, ''),
(6445, 'Przygotowywałem/am się do wypraw rowerowych na podstawie ustalonego harmonogramu, regularnie trenując jazdę. Przygotowywałem/am rower np. wyregulowałem/am hamulce, nasmarowałem łańcuch, zmieniłem/am dętkę/oponę itp.', 192, ''),
(6446, 'Wziąłem/am udział w przynajmniej 3 rajdach rowerowych lub wycieczkach trwających przynajmniej 6 godzin.', 192, ''),
(6447, 'Wyregulowałem/am przerzutki i szprychy oraz zakonserwowałem/am łańcuch.', 193, ''),
(6448, 'Wziąłem/am udział w przynajmniej 1 dwudniowej wyprawie rowerowej z nocowaniem.', 193, ''),
(6449, 'Przedstawiłem/am dziennik swoich wypraw lub inne materiały (fotografie, szkice, rysunki, itp.) dotyczące historii, osobliwości przyrodniczych i geologicznych zwiedzanych na rowerze miejscowości.', 193, ''),
(6450, 'Zorganizowałem/am zbiórkę dla zastępu lub drużyny popularyzującą turystykę rowerową, najlepiej w formie wycieczki całodniowej lub z biwakowaniem, uwzględniając różnice wzniesień i nawierzchni oraz przygotowanie turystyczne uczestników wyprawy rowerowej.', 193, ''),
(6451, 'Wraz z innymi wędrownikami współorganizowałem/am otwarty warsztat rowerowy, dokonując drobnych napraw, sprawdzania sprzętu przed sezonem oraz edukując na temat zasad bezpiecznego kolarstwa.', 194, ''),
(6452, 'Wziąłem/wzięłam udział w 3 zawodach/rajdach rowerowych.', 194, ''),
(6453, 'Współorganizowałem/am kilkudniową (przynajmniej 4 dni) wyprawę rowerową. Byłem/am odpowiedzialny/a za jeden z kluczowych aspektów wyprawy (nocleg, nawigacja, wyżywienie, zaplecze techniczne, pomoc przedmedyczna).', 194, ''),
(6454, 'Wykonałem/am jedno z zadań:\nWziąłem/wzięłam udział w konsultacjach prowadzonych przez samorządy na temat ścieżek rowerowych i ruchu rowerowego.\nPrzez co najmniej pół roku współpracowałem/am z organizacją społeczną zajmującą się promocją ruchu rowerowego.', 194, ''),
(6455, 'Nauczyłem/am się grać na wybranym instrumencie. Przygotowałem/am wybrany utwór, który zaprezentowałem/am przed większym gronem (drużyną, rodziną).', 96, ''),
(6456, 'Zebrałem/am zbiór przynajmniej 5 ulubionych utworów, które potrafię zagrać na instrumencie (może być w postaci śpiewnika bądź zeszytu/segregatora nutowego).', 96, ''),
(6457, 'Zapisałem/am w formie nut gamę w tonacji C, nazwałem/am jej dźwięki. Powiedziałem/am czym są i do czego służą: klucz wiolinowy, krzyżyk, bemol, kasownik.', 96, ''),
(6458, 'W duecie (bądź w większym zespole) przygotowałem/am wybrany utwór muzyczny, który zaprezentowałem/am przed większym gronem (drużyną, rodziną).', 33, ''),
(6459, 'Przygotowałem/am zestaw piosenek związanych z tematem ogniska/kominku drużyny. Akompaniowałem/am podczas ich wykonywania.', 33, ''),
(6460, 'Rozpoznałem/am przynajmniej 5 utworów różnych kompozytorów – podałem/am nazwiska autorów.', 33, ''),
(6461, 'Wziąłem/wzięłam udział w konkursie/przeglądzie muzycznym.', 97, ''),
(6462, 'Zorganizowałem/am wyjście zastępu lub drużyny na koncert (np. do filharmonii/opery/klubu muzycznego).', 97, ''),
(6463, 'Wziąłem/wzięłam udział w przygotowaniu uroczystości harcerskiej, organizując oprawę muzyczną.', 97, ''),
(6464, 'Skomponowałem/am własny utwór.', 34, ''),
(6465, 'Zorganizowałem/am spotkanie z muzyką dla drużyny bądź chętnych, podczas którego\nuczestnicy zapoznali się z dziejami muzyki oraz przykładowymi gatunkami bądź muzykiem go reprezentującym (np. spotkanie z jazzmanem).', 34, ''),
(6466, 'Zaangażowałem/am się w działalność muzyczną w moim otoczeniu (orkiestra symfoniczna, zespół muzyczny).', 34, ''),
(6467, 'Akompaniowałem/am drużynie przy wykonaniu minimum 5 piosenek (np. w czasie ogniska, na zbiórce).', 98, ''),
(6468, 'Nastroiłem/am gitarę przy pomocy stroika.', 98, ''),
(6469, 'Grając na gitarze wykorzystałem/am akordy barowe. Płynnie przechodziłem/am z nich na zwykłe i odwrotnie.', 98, ''),
(6470, 'Akompaniowałem/am drużynie przy wykonaniu minimum 15 piosenek. Poprowadziłem/am muzycznie co najmniej 3 ogniska/kominki.', 99, ''),
(6471, 'Akompaniowałem/am drużynie/zespołowi przy wykonaniu piosenki na scenie (np. na festiwalu, przeglądzie piosenki harcerskiej).', 99, ''),
(6472, 'Nastroiłem/am gitarę ze słuchu przy pomocy podanego dźwięku. Zestroiłem/am z sobą dwie gitary.', 99, ''),
(6473, 'Dobrałem tonację wykonywanego utworu do konkretnej grupy wykonawców. W tym celu przetransponowałem akordy z jednej tonacji do drugiej.', 99, ''),
(6474, 'Rozpocząłem/am śpiew kilku piosenek na zbiórce zastępu i zaśpiewałem piosenkę w duecie.', 100, ''),
(6475, 'Nauczyłem/am zastęp 1 nowej piosenki.', 100, ''),
(6476, 'Systematycznie w czasie próby wykonywałem/am przed lustrem ćwiczenia techniczne ust, języka, brody ułatwiające poprawne artykułowanie i śpiewanie (np. szerokie otwieranie ust, odpowiednie układanie języka i brody).', 100, ''),
(6477, 'Zaśpiewałem/am przynajmniej 10 piosenek wybranych ze śpiewnika, z którego korzystam.', 100, ''),
(6478, 'Wystąpiłem/am jako solista np. w szkolnym konkursie talentów, podczas szkolnego apelu czy festiwalu obozowego.', 101, ''),
(6479, 'Zaśpiewałem/am 10 piosenek z pamięci.', 101, ''),
(6480, 'Nauczyłem/am się poprawnego oddychania przeponą, umiem odpowiednio podeprzeć dźwięk korzystając z przepony oraz śpiewać tak, aby nie obciążać strun głosowych.', 101, ''),
(6481, 'Nauczyłem/am drużynę śpiewać 1 nową piosenkę (dobrą okazją są przygotowania do festiwalu obozowego lub innego występu).', 101, ''),
(6482, 'Wystąpiłem/am z przygotowanym wcześniej utworem/utworami w konkursie poza swoim najbliższym środowiskiem np. w konkursie międzyszkolnym, gminnym, ogólnopolskim lub na chorągwianym festiwalu harcerskim.', 102, ''),
(6483, 'Przygotowałem/am i wykonałem/am własną interpretację wokalną znanej piosenki harcerskiej.', 102, ''),
(6484, 'Brałem/am udział przynajmniej 3 razy w zajęciach z emisji głosu.', 102, ''),
(6485, 'Pokierowałem/am drużyną podczas przygotowania do wspólnego występu: dobrałem/am repertuar do możliwości drużyny, rozdzieliłem/am role, prowadziłem/am próby.', 102, ''),
(6486, 'Jestem solistą/solistką lub wokalistą\\tką zespołu muzycznego czy chóru. Przynajmniej od 3 miesięcy uczestniczę w regularnych próbach i występach.', 103, ''),
(6487, 'Przez przynajmniej 6 miesięcy uczęszczałem/am na lekcje śpiewu/emisji głosu z profesjonalistą (mogą odbywać się w ramach przygotowań zespołu z zadania 1).', 103, ''),
(6488, 'Przeprowadziłem/am zajęcia wokalne dla harcerek i harcerzy z mojego szczepu lub hufca.', 103, ''),
(6489, 'Nauczyłem/am się i zaśpiewałem/am publicznie piosenkę spoza swojego obszaru zainteresowań muzycznych – w stylu, którego zwykle nie słucham, nie śpiewam.', 103, ''),
(6490, 'Zaprezentowałem/am się przed publicznością w jednej z form aktorskich: monolog, dialog, pantomima, teatr radia, teatr lalek, teatr tańca.', 104, ''),
(6491, 'Wykonałem/am jedno z poniższych zadań:\n-Wziąłem/wzięłam udział w warsztacie teatralnym.\n-Wziąłem/wzięłam udział w przedstawieniu /nagraniu filmu /słuchowisku przygotowanym przez drużynę.', 104, ''),
(6492, 'Wybrałem/am jedną z ról mojego ulubionego aktora/aktorki i odtworzyłem/am jej kilkuminutowy fragment przed członkami zastępu. Zadbałem/am o odpowiedni kostium i charakteryzację.', 104, ''),
(6493, 'Zaprezentowałem/am się przed publicznością w trzech różnych formach aktorskich np. monolog, dialog, pantomima, teatr radia, teatr lalek, teatr tańca.', 105, ''),
(6494, 'Wykonałem/am jedno z poniższych zadań:\n-uczęszczałem/am na zajęcia/warsztaty teatralne przynajmniej przez 3 miesiące;\n-wziąłem/wzięłam udział w poprzedzonym próbami przedstawieniu/nagraniu filmu/słuchowisku przygotowanym przez drużynę.', 105, ''),
(6495, 'Wybrałem/am jedną z klasycznych ról (np. z dzieł Sofoklesa, Szekspira, Mickiewicza, Słowackiego, itp.) i odtworzyłem/am jej kilkuminutowy fragment przed członkami zastępu. Zadbałem/am o odpowiedni kostium i charakteryzację.', 105, ''),
(6496, 'Obejrzałem/am kilka spektaklów w teatrze lalek lub lalkowych programów telewizyjnych poznając teatr cieni i teatr przedmiotu, a także różne rodzaje lalek (np. kukły, pacynki, marionetki, jawajki, bunkaku, maski, lalki cieniowe).', 106, ''),
(6497, 'Zorganizowałem/am wraz z zastępem przedstawienie lalkowe dla rodziców, zuchów, kolegów z drużyny lub szkoły. Wykonałem/am jedno z poniższych zadań:\nwykonałem/am lalki;\nurządziłem/am scenę: przygotowałem/am dekoracje lub instalację oświetleniową.', 106, ''),
(6498, 'Zagrałem/am rolę w teatrzyku lalkowym, animując lalką np. kukłą, pacynką, marionetką, jawajką, bunkaku, maską, lalką cieniową.', 106, ''),
(6499, 'Wywołałem/am w czasie występu efekty dźwiękowe (deszcz, burza, odgłosy zwierząt).', 106, ''),
(6500, 'Poprowadziłem/am kilka ognisk i kominków. Ustaliłem/am scenariusz, umiejętnie dobrałem/am repertuar, zaintonowałem/am piosenki.', 107, ''),
(6501, 'Przewodziłem/am w śpiewie drużyny podczas zbiórek i w czasie wycieczek.', 107, ''),
(6502, 'Wykonałem/am jedno z poniższych zadań:\n-poprowadziłem jako konferansjer festiwal piosenki, w czasie którego wprowadziłem/am ciekawe przerywniki;\n-zorganizowałem/am z zespołem bal harcerski lub zabawę taneczną, w czasie których prowadziłem/am ciekawe konkursy i zabawy.', 107, ''),
(6503, 'Zaprezentowałem/am członkom zastępu podstawowe ćwiczenia doskonalące dykcję.', 108, ''),
(6504, 'Obejrzałem/am na żywo lub na nagraniu trzy interpretacje prozy lub wierszy w wykonaniu profesjonalistów. Zinterpretowałem/am utwór poetycki lub prozatorski, dobierając barwę głosu, modulację, tempo i rytm.', 108, ''),
(6505, 'Wziąłem/wzięłam udział w konkursie recytatorskim.', 108, ''),
(6506, 'Uczestniczyłem/am w przygotowaniu wieczoru poezji, prezentując swoje ulubione utwory.', 108, ''),
(6507, 'Przeprowadziłem/am dla kilku osób ćwiczenia dykcji w trakcie prób przed występem.', 109, ''),
(6508, 'Zinterpretowałem/am dwa utwory poetyckie oraz dwa utwory prozatorskie, dobierając barwę głosu, modulację, tempo i rytm.', 109, ''),
(6509, 'Pomogłem/am przygotować się do występu mniej doświadczonemu/ej recytatorowi/recytatorce np. nadzorując próby.', 109, ''),
(6510, 'Zorganizowałem/am wieczór poezji w szkole lub swoim środowisku harcerskim. Samodzielnie dokonałem/am wyboru utworów i rekomendowałem/ am recytatorów.', 109, ''),
(6511, 'Opowiedziałem/am kilka gawęd podczas ogniska lub kominka zawierających morał lub przysłowie.', 27, ''),
(6512, 'Uwzględniłem/am w przynajmniej jednej gawędzie poznane wcześniej legendy lub opowieści ludowe.', 27, ''),
(6513, 'Opowiedziałem/am jedną z przeczytanych w czasie próby książek, zyskując zainteresowanie słuchaczy losami bohaterów.', 27, ''),
(6514, 'Opowiedziałem/am 3 gawędy: jedną na wskazany temat, drugą na podstawie zaobserwowanego wydarzenia i trzecią dostosowaną do sytuacji, w której się aktualnie znalazłem/am.', 27, ''),
(6515, 'Wraz z zastępem lub grupą kolegów przygotowałem/am do wystawienia (pełniąc rolę reżysera) sztukę lalkową, teatrzyk harcerski, program kabaretowy itp.', 111, ''),
(6516, 'Opracowałem/am scenariusz przedstawienia (własny lub adaptowany) i prowadziłem/am próby z zespołem.', 111, ''),
(6517, 'Dobrałem/am do przedstawienia rekwizyty i nadzorowałem/am powstanie dekoracji.', 111, ''),
(6518, 'Przygotowałem/am do wystawienia sztukę teatru dramatycznego (komedię lub tragedię), teatru lalek, teatru tańca lub pantomimę. Dobrałem/am aktorów i przeprowadziłem/am próby.', 112, ''),
(6519, 'Opracowałem/am reżyserię wybranej przez siebie sztuki lub programu, planując wykorzystanie sceny, ruch sceniczny, rolę muzyki i tańca w przedstawieniu i oczekiwania wobec scenografa.', 112, ''),
(6520, 'Udzieliłem/am wskazówek aktorom prezentującym się przed publicznością w jednej z form aktorskich: monologu, dialogu, pantomimie, teatrze radia, teatrze lalek, teatrze tańca.', 112, ''),
(6521, 'Wykonałem jedno z poniższych zadań:\n-opracowałem/am scenografię do przedstawienia w sali i w plenerze, opracowałem/am rzuty do scenografii;\n-zorganizowałem/am wykonanie / pokierowałem/am zespołami wykonującymi dekorację wystawy lub innej imprezy kulturalnej.', 30, ''),
(6522, 'Wykonałem/am detal scenograficzny, wykorzystując różne materiały.', 30, ''),
(6523, 'Wykonałem/am kilka różnych kostiumów do szkolnego lub harcerskiego przedstawienia.', 30, ''),
(6524, 'Poznałem/am podstawowe kadry filmowe (np. amerykański, pełny, bliski itp.) oraz ich zastosowanie. Wykorzystałem/am różne rodzaje kadrów kręcąc materiały do filmu.', 29, ''),
(6525, 'Zorganizowałem/am plan filmowy stosowny do danej okoliczności. Podzieliłem funkcje na planie i skoordynowałem pracę nad powstaniem harcerskiego filmu.', 29, ''),
(6526, 'Poznałem/am podstawowe zasady montowania filmu i wykorzystałem je do zmontowania nagranych materiałów.', 29, ''),
(6527, 'Opublikowałem/am film na wybranym portalu internetowym odpowiednio ustawiając jego kategorię, licencję i jakość.', 29, ''),
(6528, 'Wykonałem/am co najmniej 3 ozdobne strony do kroniki (np. stronę tytułową, wpisy zawierające ozdobne inicjały, rysunki itp.) trzema różnymi stylami i technikami lub trzy ilustracje na potrzeby drużyny, do swojego śpiewnika czy na potrzeby szkoły lub domu.', 113, ''),
(6529, 'Wykonałem/am drobne przedmioty ozdobne z prostych materiałów (karton, tworzywo leśne, sznurek owoce itp.) - na przykład zdobnictwo obozowe, rekwizyty do zbiórki, ozdoby na choinkę itd.', 113, ''),
(6530, 'Umiem posługiwać się farbami, wykorzystując resztki farb do końca zrealizowałem jedno z poniższych zadań:\n-wykonałem/am ozdobny napis właściwie zestawiając kolory;\n-namalowałem/am obraz wypróbowując różne metody nakładania farby (mokry pędzel/suchy pędzel, kawałek kartonu/gąbka lub dowolne inne).\nWażne: w zadaniu chodzi o kreatywność, nie o technikę!', 113, ''),
(6531, 'Dowiedziałem/am się jak zrobić dobry plakat (Youtube, Google) i wykonałem/am co najmniej jeden plakat reklamowy na potrzeby zastępu lub drużyny wykorzystując zdobyte wskazówki.', 114, ''),
(6532, 'Poznałem/am zasady kompozycji oraz kompozycji kolorów. Zastosowałem/am je w dowolnej pracy. Przynajmniej jedno z dzieł powinno przedstawiać martwą naturę.', 114, ''),
(6533, 'Wyszukałem/am informacje na temat różnych technik artystycznych (pointylizm, makrama, asamblaż, kolaż, mozaika, sitodruk). Stworzyłem/am co najmniej 5 prac różnymi technikami - rysunki, plakaty i obrazy, loga, napisy (5 rodzajów) – wykorzystując to, czego się dowiedziałem/am.', 114, ''),
(6534, 'Wykonałem/am ozdobę do harcówki lub totem obozowy.', 114, ''),
(6535, 'Szukam informacji o dziełach sztuki – rzeźbie i malarstwie polskim. Wybrałem/am 10 dzieł, które podobają mi się najbardziej i zaprezentowałem/am je na zbiórce, wycieczce itp. w dowolnej formie.', 115, ''),
(6536, 'Wykonałem/am co najmniej 10 prac dowolną techniką, starając się zastosować takie, których jeszcze nie próbowałem/am (akwarela, tempera, tusz, papieroplastyka, kolaż, rysunek, olej, linoryt, gipsoryt, batik, monotypia, rzeźba w drewnie, w glinie, grafika komputerowa lub inne)', 115, ''),
(6537, 'Stworzyłem/am koncepcję/projekt dekoracji i kierowałem dekorowaniem obozu/sceny/harcówki na uroczystość itp.', 115, ''),
(6538, 'Zapoznałem/am się z podstawową terminologią dotyczącą typografii oraz zasadami operowania pustą przestrzenią (white space). Wiem, jaki wpływ na kompozycję ma używany krój pisma. Pozyskane informacje wykorzystałem/am tworząc materiał dla środowiska (np. plakat, ulotkę).', 115, ''),
(6539, 'Skompletowałem/am zestaw podstawowych narzędzi do pracy: farby, paleta i pędzle. Opowiedziałem/am członkom zastępu, jak o nie dbam.', 116, ''),
(6540, 'Namalowałem/am 3 obrazy o dowolnej tematyce dowolną techniką.', 116, ''),
(6541, 'Odwiedziłem/am wystawę malarską/artystyczną.', 116, ''),
(6542, 'Wymieniłem/am barwy podstawowe, trzy kolory ciepłe i trzy kolory zimne.', 116, ''),
(6543, 'Odpowiednio wymieszałem/am farby, aby uzyskać kolor: pomarańczowy, fioletowy, zielony, brązowy, szary. Wykorzystałem/am, w tym celu tylko barwy podstawowe.', 39, ''),
(6544, 'Namalowałem/am minimum 5 obrazów, posługując się co najmniej dwiema technikami malarskimi np. akwarela, tempera, akryl.', 39, ''),
(6545, 'Odwiedziłem/am wystawę malarską, wybrałem/am jeden obraz i go opisałem/am (Co przedstawia dzieło? Co znajduje się na pierwszym, a co na drugim planie? Jaka jest kompozycja: statyczna czy dynamiczna, otwarta, czy zamknięta, diagonalna, horyzontalna, czy wertykalna? Jaką technikę zastosował artysta? Jaką paletą barw posłużył się artysta?)', 39, ''),
(6546, 'Wybrałem/am 3 dzieła ulubionego malarza. Porównałem/am je, wskazując po 3 podobieństwa i różnice.', 39, ''),
(6547, 'Wziął/wzięła udział w plenerze malarskim.', 117, ''),
(6548, 'Namalowałem/am minimum 7 obrazów na różnych materiałach (np. płótno, drewno, szkło), dopasowując odpowiednią technikę.', 117, ''),
(6549, 'Odwiedziłem/am wystawę malarską. Wybrałem/am element jednego obrazu, który mnie szczególnie zaciekawił. Namalowałem/am swój własny obraz zainspirowany wybranym fragmentem.', 117, ''),
(6550, 'Przygotowałem/am infografikę przedstawiającą dwie epoki malarskie. Infografika zawiera krótką charakterystykę każdej epoki, sylwetki co najmniej 4 artystów (po dwie dla każdej epoki), ciekawostki.', 117, ''),
(6551, 'Samodzielnie przygotowałem/am podłoże malarskie, np. naciągnąłem/am i przymocowałem/am płótno do blejtramu, przygotowałem/am drewno.', 118, ''),
(6552, 'Namalowałem/am co najmniej 4 cykle obrazów poruszające różną tematykę. Zaprezentowałem/am je na wystawie w miejscu publicznym.', 118, ''),
(6553, 'Odwiedziłem/am minimum 5 wystaw malarskich (dopuszczalne są wizyty w muzeach online). Stworzyłem/am kopię wybranego dzieła.', 118, ''),
(6554, 'Prowadziłem/am szkicownik zawierający inspiracje i pomysły na kolejne obrazy.', 118, ''),
(6555, 'Zobaczyłem/ am co najmniej 5 pomników. Dowiedziałem/am się, kto jest ich autorem i jaką techniką zostały wykonane.', 119, ''),
(6556, 'Korzystając z narzędzi rzeźbiarskich do drewna (np. dłuta, rylce, tarniki) wykonałem/am na obozie element zdobnictwa obozowego rzeźbiony w drewnie.', 119, ''),
(6557, 'Wykonałem/am gipsowy model jednego z symboli harcerskich lub skautowych.', 119, ''),
(6558, 'Odwiedziłem/am co najmniej 3 muzea lub obiekty zabytkowe, w których odnalazłem rzeźby/płaskorzeźby reprezentujące co najmniej 3 okresy w sztuce. Dowiedziałem/am się, kto jest ich autorem i jaką techniką zostały wykonane.', 120, ''),
(6559, 'Wykonałem/am samodzielnie model obiektu z natury z odwzorowaniem charakterystycznych cech i proporcji wykorzystując do tego glinę, gips, drewno, itp.', 120, ''),
(6560, 'Wyrzeźbiłem/am przedmiot przydatny w pracy swojego środowiska harcerskiego (np. totem zuchowy, lilijkę lub krzyż harcerski na ścianę harcówki, popiersie patrona itp.).', 120, ''),
(6561, 'Rozpoznaję 6 podstawowych nowożytnych stylów w architekturze. Odwiedziłem/am znajdujące się w okolicy budowle charakterystyczne dla tych stylów i wypromowałem/am je w swoim środowisku poprzez fotorelację.', 121, ''),
(6562, 'Zorganizowałem/am wyprawę zastępu lub drużyny do ciekawego obiektu, opowiedziałem/am o cechach charakterystycznych tej budowli, jej stylu i historii.', 121, ''),
(6563, 'Wykonałem/am jedno z poniższych zadań:\n-korzystając z programu do planowania przestrzeni lub odręcznie zaprojektowałem/am optymalne umeblowanie dla jednego pomieszczenia (np. swojego pokoju);\n-zaaranżowałem/am przestrzeń w jednym pomieszczeniu (np. harcówce) dobierając kolory ścian i elementy wystroju.', 121, ''),
(6564, 'Poznałem/am dzieła przynajmniej 5 twórców stylu, który lubię najbardziej. Wypromowałem/am je w swoim środowisku poprzez fotorelację z własnych podróży.', 523, ''),
(6565, 'Zorganizowałem/am wyprawę zastępu lub drużyny do innej miejscowości w celu zwiedzenia ciekawych budowli jednego ze swoich ulubionych architektów. Opowiedziałem/am zarówno o cechach charakterystycznych tej budowli i jej historii, jak i o innych dziełach tego twórcy, które podziwiam.', 523, ''),
(6566, 'Wykonałem/am jedno z poniższych zadań:\n-korzystając z programu do planowania przestrzeni lub odręcznie zaprojektowałem/am kompleksowe umeblowanie dla mieszkania/domu;\n-zaaranżowałem/am przestrzeń w mieszkaniu/domu dobierając kolory ścian i elementy wystroju.', 523, ''),
(6567, 'Wykonałam/em portret osoby lub zjawiska przyrody.', 35, ''),
(6568, 'Wykonałam/em zdjęcie tematyczne, odpowiednio używając właściwych funkcji.', 35, ''),
(6569, 'Na biwaku lub obozie robiłam/em zdjęcia drużynie, następnie wybrałam/em najlepsze do publikacji.', 35, ''),
(6570, 'Posiadam własną kolekcję zdjęć. Wiem, jak o nią dbać (katalogowanie, opisywanie, bezpieczne przechowywanie).', 35, ''),
(6571, 'Poznałem/am urządzenia, dzięki którym mogę wykonać zdjęcia. Dowiedziałem/am się, na jakie parametry zwracać uwagę. Fotografując, stosowałam/em prawidłowe ustawienia.', 36, ''),
(6572, 'Wykonałem/am zdjęcia używając różnych kadrów dobranych do sytuacji.', 36, ''),
(6573, 'Fotografowałam/em podczas co najmniej 3 wydarzeń dotyczących drużyny lub szkoły. Zrobione i wyselekcjonowane zdjęcia udostępniałam/em.', 36, ''),
(6574, 'Przedstawiłam/em w drużynie/zastępie album lub galerię zdjęć przez siebie wykonanych.', 36, ''),
(6575, 'Wykonałem/am zdjęcia w trudnych warunkach np. w nocy, obiektów w ruchu.', 37, ''),
(6576, 'Poznałam/em budowę oraz rodzaje aparatów fotograficznych. Wiem, czym jest ogniskowa obiektywu. Dostosowałem/am jej długość do sytuacji.', 37, ''),
(6577, 'Pełniłem/am funkcję fotografa podczas wydarzenia harcerskiego.', 37, ''),
(6578, 'Posiadam własną kolekcję zdjęć i udostępniam ją publicznie.', 37, ''),
(6579, 'Wykonałem/am udane zdjęcia w każdych warunkach (np. w ciemności, w ruchu, pod słońce) – za każdym razem dobierając ustawienia aparatu.', 38, ''),
(6580, 'Koordynowałam/am zespół fotografów podczas wydarzenia harcerskiego.', 38, ''),
(6581, 'Posiadam sprzęt niezbędny do realizacji skomplikowanych ujęć np. statyw, blendę.', 38, ''),
(6582, 'Mam swoich ulubionych fotografów. Zebrałem/am własne portfolio zdjęć inspirowanych ich twórczością.', 38, '');
INSERT INTO `zadaniasprawnosciharcerskich` (`ID`, `Tresc`, `Sprawnosc`, `Stopien`) VALUES
(6583, 'Zaprojektowałem/am prostą grafikę w dowolnym programie graficznym np. plakat, zaproszenie.', 40, ''),
(6584, 'Wykonałem/am ilustrację do kroniki drużyny używając dowolnego programu graficznego.', 40, ''),
(6585, 'Obrobiłem/am fotografię używając dowolnego programu graficznego lub aplikacji na telefon.', 40, ''),
(6586, 'Przygotowałem/am grafikę promującą ważne wydarzenie: plakat i grafiki do mediów społecznościowych np. Facebook, Instagram. Zapoznałem/am się z pojęciem white space i wykorzystałem/am tę wiedzę podczas tworzenia projektów.', 41, ''),
(6587, 'Zaprojektowałem/am dowolny gadżet harcerski (np. przypinkę, kubek, naszywkę).', 41, ''),
(6588, 'Zaprojektowałem/am prostą infografikę, która porusza ważny, aktualny temat.', 41, ''),
(6589, 'Przygotowałem/am grafikę na potrzeby drużyny (np. ulotkę) w oparciu o Katalog Identyfikacji Wizualnej ZHP.', 41, ''),
(6590, 'Przygotowałem/am listę lub repozytorium legalnych źródeł grafik, fotografii i krojów pisma (również tych zawierających polskie znaki). Wiem jak się nimi posługiwać zgodnie z obowiązującym prawem autorskim.', 42, ''),
(6591, 'Zaprojektowałem/am identyfikację wizualną obejmującą logo/logotyp, papier firmowy, dwa gadżety i księgę znaku.', 42, ''),
(6592, 'Stworzyłem/am grafikę np. cyfrowy obraz, zaawansowaną grafikę wektorową, pattern, cyfrowy collage, broszurę informacyjną, rozbudowaną infografikę.', 42, ''),
(6593, 'Wykonałem zadanie „a”, w przypadku gdyby było to niemożliwe wykonałem zadanie „b”:\na) poprawie zinterpretowałem/am wytyczne od drukarni i prawidłowo przygotowałem/am pliki do produkcji ( rozdzielczość, spad, przestrzeń kolorów, odpowiedni format zapisu, przygotowanie tekstu);\nb) wyjaśniłem/am różnicę pomiędzy programami do grafiki rastrowej, wektorowej, CMYK i RGB, wyjaśniłem/am pojęcie spadu drukarskiego poprawnie stosując go w tworzonych projektach.', 42, ''),
(6594, 'Znam historię godła i barw narodowych. Wiem, co oznaczają i potrafię się wobec nich zachować. Wywiesiłem/am flagę biało-czerwoną w dniu święta państwowego.', 238, ''),
(6595, 'Razem z zastępem/drużyną zaśpiewałem/am hymn państwowy w czasie uroczystości.', 238, ''),
(6596, 'Posiadam mapę Polski, na której oznaczam miejsca, w których byłem/am (może być konturówka z magnesami, pocztówkami itp.).', 238, ''),
(6597, 'Zapoznałem/am się z częścią statutu szkoły, zawierającą prawa i obowiązki ucznia. Angażuje się w życie swojej szkoły/klasy. Wziąłem/Wzięłam udział w choć jednym przedsięwzięciu szkolnym przygotowywanym przez klasę/samorząd szkolny.', 238, ''),
(6598, 'Poznałem/am daty polskich świąt narodowych. Wiem, na pamiątkę jakich wydarzeń zostały ustanowione. Przygotowałem/am z zastępem zbiórkę z okazji jednego z nich.', 239, ''),
(6599, 'Wskazałem/am na mapie krainy geograficzne Polski. Jedną z nich zaprezentowałem/am na zbiórce zastępu.', 239, ''),
(6600, 'Poznałem/am zasady demokratycznych wyborów i etapy podejmowania decyzji. Wykonałem/am co najmniej jedno z zadań:\nPrzedstawiłem/am swoja sprawę na Radzie Drużyny.\nPo wcześniejszym poznaniu programów wyborczych kandydatów, wziąłem/wzięłam udział w głosowaniu w wyborach do samorządu uczniowskiego.', 239, ''),
(6601, 'Znam obszar swojej gminy. Posługując się planem swojej okolicy, dotarłem/am do urzędu gminy/miasta i dowiedziałem/am się, jakie sprawy można w nim załatwić.', 239, ''),
(6602, 'Przeprowadziłem/am zwiad tematyczny w mojej „małej ojczyźnie” (np. poznając kulturę, historię, współczesne życie społeczne i gospodarcze, poznając ciekawe osoby, odkrywając mało znane miejsca, zapomniane pamiątki historyczne). Sporządziłem/am dokumentację zwiadu (np. opisy, wywiady, pamiątki, fotografie, spis lektur na wybrany temat). W interesujący sposób przedstawiłem/am wyniki zwiadu w drużynie.', 240, ''),
(6603, 'Wziąłem/am udział w pracach samorządu klasowego lub szkolnego.', 240, ''),
(6604, 'Przygotowałem/am się i podjąłem/podjęłam funkcję w zastępie, drużynie lub szczepie.', 240, ''),
(6605, 'Dowiedziałem/am się, dlaczego ważne jest wyrażanie swojego zdania we wspólnych sprawach, zachęciłem/am swoich rodziców lub innych członków rodziny do uczestnictwa w wyborach lub głosowaniu w budżecie obywatelskim/partycypacyjnym.', 240, ''),
(6606, 'Poznałem/am najważniejsze fakty z najnowszej historii Polski. Odwiedziłem/am miejsce związane z takim wydarzeniem, by w pełni zrozumieć, jakie płyną z niego dla nas przesłania. Na wyprawę zabrałem/am drużynę/zastęp/przyjaciela/rodzinę.', 241, ''),
(6607, 'We współpracy z samorządem uczniowskim, przygotowałem/am szkolną akcję promującą aktywność społeczną i obywatelską.', 241, ''),
(6608, 'Poznałem/am podział polskiej sceny politycznej i programy wyborcze partii posiadających mandaty w parlamencie. Dowiedziałem/am się, jakie uprawnienia posiada Sejm i Senat RP. Zaprezentowałem/am członkom zastępu/drużyny narzędzie do badania preferencji politycznych (np. Latarnik wyborczy).', 241, ''),
(6609, 'Wykonałem/am jedno z zadań:\nPrzeprowadziłem/am analizę lokalnych potrzeb i przygotowałem/am projekt do budżetu obywatelskiego/partycypacyjnego.\nZorganizowałem/am spotkanie z lokalnym radnym poświęcone potrzebom lokalnej młodzieży.', 241, ''),
(6610, 'Wiem, czym są organizacje typu watchdog. Nawiązałem/am kontakt z wybraną z nich i upowszechniłem/am w swoim środowisku efekty jej pracy.', 241, ''),
(6611, 'Dowiedziałem/am się, czym jest Unia Europejska i od kiedy należy do niej Polska. Zdobyłem/am różne ciekawostki dotyczące państw należących do UE. Zaprezentowałem/am je w ciekawej formie rówieśnikom.', 242, ''),
(6612, 'Poznałem/am najważniejsze symbole Unii Europejskiej. Wykonałem/am jedno z zadań:\nOdśpiewałem/am hymn Unii Europejskiej.\nZagrałem/am hymn Unii Europejskiej na instrumencie.\nWykonałem/am flagę europejską.', 242, ''),
(6613, 'Wiem, jakie kraje z najbliższego otoczenia mojego kraju są członkami Unii Europejskiej. Przygotowałem/am mapę Polski z zaznaczonymi zewnętrznymi granicami UE, które pokrywają się z granicami Polski.', 242, ''),
(6614, 'Przedstawiłem/am członkom zastępu dewizę/motto Unii Europejskiej. Przygotowałem/am z rówieśnikami (np. w zastępie) plakat prezentujący UE i jej motto.', 243, ''),
(6615, 'Poznałem/am ważne informacje dotyczące Unii Europejskiej oraz jej najważniejsze instytucje. Zorganizowałem/am konkurs wiedzy o UE i jej krajach członkowskich dla rówieśników.', 243, ''),
(6616, 'Znalazłem/am w swoim otoczeniu kilka inwestycji dofinansowanych z funduszy Unii Europejskiej. Na zbiórce zastępu przedstawiłem/am ich listę i wspólnie stworzyliśmy listę rankingową inwestycji najważniejszych dla naszej wspólnoty lokalnej.', 243, ''),
(6617, 'Dowiedziałem/am się, czym była Deklaracja Schumana i jakie miała znaczenie w pokojowym jednoczeniu kontynentu. Wcieliłem/am się w rolę historyka i stworzyłem/am krótką notatkę przedstawiającą główne etapy tworzenia Unii Europejskiej. Dowiedziałem/am się, jakie kraje starają się obecnie przystąpić do Unii Europejskiej i jakie jeszcze warunki muszą spełnić. Wyniki swojej pracy zaprezentowałem/am publicznie.', 244, ''),
(6618, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Zapoznałem/am się z różnymi kulturami krajów należących do Unii Europejskiej uwzględniając zwyczaje, obrzędy i stroje ludowe. -Przygotowałem/am na ten temat materiał, który został opublikowany.\n-Przeprowadziłem/am warsztaty wymiany międzykulturowej z drużyną z innego kraju UE.\n-Brałem/am udział w wydarzeniu propagującym kulturę państw członkowskich UE (np. Festiwal Kultury Europy).', 244, ''),
(6619, 'Dowiedziałem/am się, jakie wymogi należy spełnić, aby podróżować w Strefie Schengen. Zaplanowałem/am wycieczkę do jednego kraju ze strefy Schengen, uwzględniając walory turystyczne.', 244, ''),
(6620, 'Poznałem/am zasady deklaracji bolońskiej. Skorzystałem/am z Europejskiej Sieci Informacji o Edukacji – EURYDICE. Na tej podstawie zebrałem/am informacje dotyczące możliwości i warunków podejmowania przez młodych Polaków nauki w wybranym kraju Unii Europejskiej. Zebrane materiały przedstawiłem/am swoim rówieśnikom.', 245, ''),
(6621, 'Dowiedziałem/am się, w jaki sposób Unia Europejska prowadzi swoją politykę migracyjną i azylową oraz jak wpływają one na bezpieczeństwo obywateli państw członkowskich. Zorganizowałem/am zbiórkę/spotkanie wędrownicze/instruktorskie na ten temat.', 245, ''),
(6622, 'Dowiedziałem/am się, jaka jest procedura podejmowania decyzji w Unii Europejskiej oraz jaka jest waga polskiego głosu. Przygotowałem/am dla drużyny/kręgu/wspólnoty wędrowniczej debatę nad przyszłością Europy, dotykając wyzwań, z jakimi współcześnie musi się ona zmagać.', 245, ''),
(6623, 'Nawiązałem/am kontakt z drużyną skautową z terenu Unii Europejskiej. Dowiedziałem/am się o ich zwyczajach, obrzędach i tradycjach. Zorganizowałem/am wspólną akcję, np. wzajemne odwiedziny, wspólny wyjazd, wymianę listów.', 245, ''),
(6624, 'Dowiedziałem/am się, jak nazywa się świątynia i duchowny w wyznaniach chrześcijańskich działających w Polsce. Rozpoznałem/am ich stroje liturgiczne.', 246, ''),
(6625, 'Dowiedziałem/am się, jakie Kościoły i wspólnoty chrześcijańskie działają w mojej okolicy/regionie. Odwiedziłem/am (np. z rodzicami lub zastępem) świątynię, miejsce modlitwy lub cmentarz należące do jednego z nich (innego niż moje własne). Zastosowałem/am się do obowiązujących zasad ubioru i zachowania w miejscu kultu lub podczas ceremonii religijnej.', 246, ''),
(6626, 'Na zbiórce zastępu przedstawiłem/am tradycje i zwyczaje związane ze Świętami Wielkiejnocy lub Bożego Narodzenia w rzymskim katolicyzmie, prawosławiu i Kościołach ewangelickich w Polsce i innych krajach Europy.', 246, ''),
(6627, 'Poznałem/am typowe elementy architektury i wyposażenia oraz ich znaczenie w świątyniach wyznań chrześcijańskich tradycji wschodniej i zachodniej (np. ikonostas, carskie i diakońskie wrota, ikony, obrazy lub ich brak, ambona, osobne przestrzenie dla kobiet i mężczyzn). Przedstawiłem/am je na zbiórce zastępu.', 247, ''),
(6628, 'Pomogłem/am zorganizować wycieczkę zastępu lub drużyny do świątyni wyznawców wybranego wyznania lub obrządku (innych niż większości uczestników) i przedstawiłem/am informacje o niej. Przygotowałem/am członków zastępu lub drużyny pod względem właściwego ubioru i zachowania.', 247, ''),
(6629, 'Poznałem/am trzy wybrane inicjatywy ekumeniczne (np. Wigilijne Dzieło Pomocy Dzieciom, Tydzień Modlitw o Jedność Chrześcijan, Spotkania Młodych Taizé, Betlejemskie Światło Pokoju, Ekokościół, stworzenie ekumenicznego przekładu Pisma Świętego, Znaki Pamięci). Rozpropagowałem/am wiedzę o nich wśród rówieśników.', 247, ''),
(6630, 'Opowiedziałem/am mojemu zastępowi/grupie znajomych o Spotkaniach Młodych Taizé. Nauczyliśmy się trzech piosenek z Taizé, np. wykorzystując nagrania dostępne w Internecie.', 247, ''),
(6631, 'Na zbiórce zastępu lub drużyny zaprezentowałem/am w ciekawy sposób ideę i najważniejsze działania ruchu ekumenicznego, bazując na dokumentach Światowej Rady Kościołów lub Polskiej Rady Ekumenicznej. Przedstawiłem/am idee i sposób działania Wspólnoty z Taizé.', 248, ''),
(6632, 'Wziąłem/wzięłam udział w nabożeństwie w ramach Światowego Tygodnia Modlitw o Jedność Chrześcijan, Europejskiego Spotkania Młodych lub w innym nabożeństwie ekumenicznym. Porozmawiałem/am z kilkorgiem jego uczestników o powodach, dla których zdecydowali się na udział.', 248, ''),
(6633, 'W odniesieniu do trzech Kościołów działających w Polsce (prawosławnego, rzymskokatolickiego i jednego z Kościołów ewangelickich), odszukałem/am najważniejsze informacje dotyczące zasad ich wiary: problematyki usprawiedliwienia, relacji człowieka z Bogiem i Kościołem, najważniejszych świąt, struktury, sposobu działania, największych ośrodków i sposobów prowadzenia działalności dobroczynnej. Zrobiłem/am to, zapoznając się z ich stronami internetowymi. Zastanowiłem/am się nad tym, jakie elementy są dla nich wspólne i pozwalają na współpracę.', 248, ''),
(6634, 'Wykonałem/am jedno z zadań:\nPoznałem/am główne spory teologiczne w obrębie chrześcijaństwa. Zastanowiłem/am się nad ich konsekwencjami dla funkcjonowania społeczeństw. Poznałem/am najważniejsze inicjatywy na rzecz jedności chrześcijan. Przeprowadziłem/am w szczepie lub hufcu dyskusję na ten temat.\nPoznałem/am ideę i historię ruchu ekumenicznego, w tym trudności, które w nim występują (np. relacje między Kościołami prawosławnymi a Kościołami ewangelickimi, negatywne nastawienie do idei ekumenizmu, problemy z różnym rozumieniem pojęć teologicznych). Podjąłem/podjęłam refleksję na temat ich źródeł, w tym uwarunkowań kulturowych i społecznych. Przeprowadziłem/am w szczepie lub hufcu dyskusję na temat trudności i przyszłości ekumenizmu.', 248, ''),
(6635, 'Poznałem/am terminy: negocjacja, opinia, fakt, pogłoska, dane, przypuszczenie. Wyjaśniłem/am zastępowi relacje i różnice między nimi. W działaniach zastępu staram się by informacje były oparte na faktach i danych, a nie na pogłoskach i przypuszczeniach, i aby każdy miał okazje do wygłoszenia swojej opinii.', 253, ''),
(6636, 'Odnalazłem/am i przedstawiłem/am kilka przykładów opinii, faktu, pogłoski, danej, przypuszczenia znalezionych w mediach (źródła drukowane i elektroniczne).', 253, ''),
(6637, 'Rozpoznałem/am i nazwałem/am emocje, które pojawiły się podczas żywej rozmowy.', 253, ''),
(6638, 'Podczas emocjonującej debaty, zapanowałem/am nad swoimi emocjami, wybierając rzeczowy argument zamiast docinku.', 253, ''),
(6639, 'Poznałem/am fazy negocjacji prowadzące do porozumienia. Przedstawiłem/am zastępowi podstawowe zasady negocjacji.', 254, ''),
(6640, 'Przeprowadziłem/am dwa rodzaje debat dla zastępu, drużyny lub innej grupy. Przed debatami przygotowałem/am do nich uczestników.', 254, ''),
(6641, 'Ćwiczyłem/am rozpoznawanie emocji pojawiających się w gorących dyskusjach. Poprawnie nazwałem/am lub pomogłem/am nazwać je osobom rozmawiającym w trakcie dyskusji.', 254, ''),
(6642, 'Łagodziłem/am konflikty w otoczeniu różnymi sposobami, w ramach podsumowania omówiłem/am analizę przypadku dla kilku (min. 3) pozytywnie rozwiązanych sytuacji (nie identyfikując osób biorących w nich udział i dbając o ich anonimowość), identyfikując metody negocjacji, których użyłem/am do rozwiązania konfliktu.', 254, ''),
(6643, 'Doprowadziłem/am do porozumienia między stronami konfliktu.', 255, ''),
(6644, 'Znam pojęcie erystyki. Zapoznałem/am się z dialektyką erystyczną Schopenhauera i potrafię zidentyfikować minimum 5 forteli w dyskusji i właściwie na nie zareagować, rozbrajając oponenta.', 255, ''),
(6645, 'Zauważam u siebie odruchowe zagrania erystyczne, zastępując je rzeczową, merytoryczną argumentacją. W czasie podsumowania sprawności wskazałem/am, jakie chwyty erystyczne najczęściej musiałem u siebie ograniczać.', 255, ''),
(6646, 'Wziąłem/wzięłam udział w warsztatach z zakresu Porozumienia Bez Przemocy lub przeczytałem/am książkę na ten temat.', 255, ''),
(6647, 'Poznałem/am różne rodzaje środków masowego przekazu. Za pomocą jednego z nich, uzyskałem/am bieżące informacje dotyczące mojego kraju. Podzieliłem/am się nimi z rówieśnikami, pozyskując ich opinie na ważne dla mnie tematy.', 256, ''),
(6648, 'Poznałem/am różne rodzaje mediów społecznościowych, za pomocą jednego z nich przedstawiłem/am wydarzenie z życia drużyny szerszej publiczności.', 256, ''),
(6649, 'Sporządziłem/am relacje z wydarzeń na podstawie opinii świadków i własnych doświadczeń. Wykorzystałem/am je do napisania wpisu do kroniki, gazetki szkolnej/bloga/fanpage lub strony drużyny.', 256, ''),
(6650, 'Napisałem/am dwa artykuły, dobierając odpowiednie ilustrujące je rysunki lub zdjęcia. Przedstawiłem/am je szerszej publiczności (np. w drużynie, klasie).', 256, ''),
(6651, 'Rozpoznałem/am fałszywą informacje, tzw. fake news. Postępując między innymi według następujących zasad: zapytałem/am eksperta, sprawdziłem/am datę, źródło oraz autora, przemyślałem/am własne uprzedzenia.', 256, ''),
(6652, 'Podczas zbiórki zastępu zaprezentowałem/am cechy, jakimi powinni charakteryzować się dziennikarze oraz zasady, jakie obowiązują w ich zawodzie. Zaprezentowałem/am przykład rzetelnego dziennikarza/reportera.', 257, ''),
(6653, 'Przygotowałem/am relacje z biwaku, obozu drużyny lub innego dłuższego wyjazdu. Upubliczniłem/am je na blogu/fanpage lub stronie drużyny.', 257, ''),
(6654, 'Wykonałem/am jedno z poniższych zadań:\n-Zapoznałem/am się z czterema reportażami (np. wojennymi, podróżniczymi).\n-Zapoznałem/am się z sześcioma felietonami na interesujący mnie temat.', 257, ''),
(6655, 'Wykonałem/am jedno z poniższych zadań:\n-Przeprowadziłem/am wywiad z nauczycielem/specjalistą dotyczący jego pracy/pasji. -Omówiłem/am swoje wyniki w zastępie/drużynie.\n-Wcieliłem/am się w rolę komentatora sportowego podczas meczu drużyny lub szkoły.', 257, ''),
(6656, 'Wykonałem/am jedno z poniższych zadań:\n-Poznałem/am zasady edycji i korekty tekstów, stosując je w swoich artykułach. -Napisałem/am artykuł do gazetki szkolnej lub hufcowej.\n-Wykonałem/am fotoreportaż, publikując go w gazetce szkolnej lub hufcowej.', 257, ''),
(6657, 'Przeprowadziłem/am debatę na temat znaczenia środków masowego przekazu dla zachowania wolności słowa. Przeanalizowałem/am sytuację prawną dziennikarzy. W czasie debaty wziąłem/am pod uwagę represje wobec dziennikarzy ze strony różnych państw.', 258, ''),
(6658, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Napisałem/am minimum dwa artykuły do gazetki hufcowej lub prasy lokalnej.\n-Nakręciłem/am reportaż, który zaprezentowałem/am na forum szczepowym/hufcowym o problemach dotyczących mojego najbliższego środowiska lokalnego.\n-Sprawdziłem/am na YouTube kanały prowadzone przez harcerzy. Udało mi się nawiązać z nimi współpracę, tworząc wspólny film.', 258, ''),
(6659, 'Porównałem/am wiadomości dotyczące mojego kraju i świata na podstawie dwóch mediów o przeciwnych liniach programowych. Swoje przemyślenia przedstawiłem/am na forum drużyny.', 258, ''),
(6660, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Znalazłem/am minimum 10 przykładów działań public relations, wykorzystujące, m.in. technikę spinu (socjotechnika) i czarny PR. Przedstawiłem/am własne spostrzeżenia na forum drużyny/szczepu.\n-Przeanalizowałem/am rolę plakatów, memów oraz motywatorów jako środków masowego przekazu. -Przedstawiłem/am własne spostrzeżenia na forum drużyny/szczepu.\n-Porównałem/am gazety z XIX w., np. „Kurier Warszawski”, „Czas”, „Dziennik Wileński” ze współczesnymi gazetami. Znalezione podobieństwa i różnice przedstawiłem/am na forum drużyny/szczepu.\n-Porównałem/am twórczość kilku różnych youtuberów, m.in. jak tworzą kontent i wchodzą w kontakt ze swoimi odbiorcami. Przedstawiłem/am własne spostrzeżenia na forum drużyny/szczepu.', 258, ''),
(6661, 'Wyszukałem/am zadane słowa w słowniku języka obcego. Znalazłem/am definicje zadanych słów w słowniku języka polskiego, stosując zarówno słowniki książkowe, jak i cyfrowe.', 67, ''),
(6662, 'Poznałem/am podstawowe zwroty w wybranych 4 językach i 20 słówek związanych z harcerstwem w dowolnym języku obcym.', 67, ''),
(6663, 'Przez okres trwania próby uczyłem/am się wybranego języka obcego raz w tygodniu, we własnym zakresie (poza lekcjami szkolnymi).', 67, ''),
(6664, 'Zidentyfikowałem/am błędy językowe, które powtarzam w mowie i piśmie, wybrałem/am dwa i się ich pozbyłem/am.', 67, ''),
(6665, 'Pogłębiłem/am znajomość języka, którego się uczę, podczas kontaktów ze skautami z zagranicy pomagałem/am się z nimi porozumieć.', 68, ''),
(6666, 'Poznałem/am słownictwo harcerskie w stopniu pozwalającym na opowiedzenie o organizacji harcerstwa w Polsce oraz typowych dla mojego środowiska aktywnościach harcerskich. Zaprezentowałem/am to podczas zajęć z danego języka, np. podczas omawiania hobby.', 68, ''),
(6667, 'Znalazłem/am darmowe metody poszerzania znajomości języka i przez okres próby przynajmniej trzy razy w tygodniu korzystałem/am z jednego z nich (np. Duolingo, khan academy, kanały i zasoby sieciowe).', 68, ''),
(6668, 'Wiem, czym są frazeologizmy i jak wpływają na trafność tłumaczeń. Przedstawiłem/am dowolnej grupie, jak złe dobranie tłumaczenia sprawia, że pojęcie traci sens (np. „Thank you from the mountain” i „z góry dziękuję”, „without garden” i „bez ogródek”, itp.), wymyślając kilka własnych przykładów.', 68, ''),
(6669, 'Opanowałem/am na poziomie podstawowym co najmniej dwa języki obce. Nauczyłem/am innych podstawowych zwrotów w tych językach.', 69, ''),
(6670, 'Przetłumaczyłem/am z języka obcego materiały pomocne w pracy harcerskiej, upowszechniając je w środowisku.', 69, ''),
(6671, 'Znalazłem/am słowa, które w języku polskim i obcym pochodzą od jednego rdzenia, znalazłem/am jego pochodzenie i znaczenie. Opowiedziałem/am o nich członkom zastępu i wskazałem/am, jak obce rdzenie mogły znaleźć się w tych językach.', 69, ''),
(6672, 'Nawiązałem/am kontakt ze skautami z innego kraju poprzez dowolne medium. Wymieniłem/am informacje o ciekawych formach pracy, którymi podzieliłem/am się w swoim środowisku.', 69, ''),
(6673, 'Zapoznałem/am się z różnymi możliwościami wprowadzania zapytań i narzędziami wyszukiwania oferowanymi przez przeglądarki, za ich pomocą wyszukałem/am informacje dotyczące wybranego tematu i porównałem/am wiarygodność treści pochodzących z różnych źródeł, co pokazałem/am na dwóch przykładach.', 70, ''),
(6674, 'Zidentyfikowałem/am jeden obszar wiedzy, który jest dla mnie trudny do zrozumienia. Dobierając odpowiednie publikacje lub zapytania w wyszukiwarce internetowej, znalazłem/am wyjaśnienie, które pozwoliło mi opanować i zrozumieć trudny temat.', 70, ''),
(6675, 'Dowiedziałem/am się, dlaczego poznawanie argumentów z dwóch lub więcej punktów widzenia jest ważne. Do dwóch tematów znalazłem/am argumenty za obiema stronami problemu i zaprezentowałem/am je na zbiórce, dowiadując się, co myślą inni.', 70, ''),
(6676, 'Zebrałem/am informacje, które wykorzystałem/am na zbiórce/wyjeździe drużyny (np. ciekawostki o wybranej miejscowości, które opowiedziałem/am rówieśnikom).', 70, ''),
(6677, 'Na zbiórce zaprezentowałem/am wiarygodne źródła wiedzy w wybranym temacie i opowiedziałem/am o mechanizmach „zakotwiczenia i dostosowania”.', 71, ''),
(6678, 'Poznałem/am portale/publikacje związane z moim obszarem zainteresowań prezentujące wiarygodne informacje. Poznałem/am portale zajmujące się sprawdzaniem pochodzenia informacji i ich wiarygodnością. Zaprezentowałem/am je w drużynie lub zastępie.', 71, ''),
(6679, 'Nauczyłem/am się wychwytywać i odróżniać opinię, hipotezę, dane, a także wyłapywać, kiedy informacja jest podawana w sposób tendencyjny lub stronniczy bądź jej część nie znajduje oparcia w faktach. Na zbiórce zademonstrowałem/am po 2 stwierdzenia z każdego z wymienionych rodzajów i pomogłem/am innym uczestnikom zbiórki je zaklasyfikować.', 71, ''),
(6680, 'Na podstawie dogłębnych informacji z wybranej przez siebie dziedziny przeprowadziłem/am dowolną aktywność dla drużyny lub zastępu (zwiad, grę z fabułą opartą na wydarzeniach historycznych, warsztaty naukowe).', 71, ''),
(6681, 'Odwiedziłem/am w mojej miejscowości lub podczas wyjazdu dwa muzea poświęcone dawnym czasom. Opowiedziałem/am o jednej z obejrzanych wystaw.', 75, ''),
(6682, 'Na podstawie różnych (minimum dwóch) dostępnych źródeł (pamiętniki, dokumenty, encyklopedia, przekaz ustny, itd.) sporządziłem/am notatkę o patronie drużyny/szkoły/ulicy. Wykorzystałem/am ją na zbiórce lub w szkole.', 75, ''),
(6683, 'Stworzyłem/am plansze historyczne ukazujące epoki historyczne lub okresy z historii harcerstwa (wraz z ich ramami czasowymi i charakterystycznymi elementami). Udostępniłem/am je w szkole/harcówce/Internecie lub ozdobiłem/am swój pokój.', 75, ''),
(6684, 'Przeczytałem/am beletrystyczną historyczną książkę dla dzieci (np. z serii: „Kroniki Archeo”, „Wojna oczami dzieci”, „Pan Samochodzik”, „Polscy superbohaterowie”, „Strażnicy historii”, „Zdarzyło się w Polsce”), najciekawszymi fragmentami podzieliłem/am się z zastępem.', 75, ''),
(6685, 'Wykonałem/am jedno z zadań:\nStworzyłem/am albumy starych fotografii mojej miejscowości lub okolicy i porównałem/am je z aktualnymi fotografiami na zasadzie „wczoraj i dziś”, dopisałem/am do nich merytoryczne komentarze/opisy. Zaprezentowałem/am je zainteresowanym osobom.\nObejrzałem/am film, w którym wskazałem/am niehistoryczne elementy na podstawie przeczytanej literatury. Przedstawiłem/am swoje obserwacje na forum drużyny.', 76, ''),
(6686, 'Przeczytałem/am minimum dwie książki historyczne (naukowe lub popularnonaukowe). Zachęciłem/am innych do zapoznania się z nimi lub napisałem recenzję i udostępniłem/am ją w mediach społecznościowych.', 76, ''),
(6687, 'Wykonałem/am jedno z zadań:\nZrekonstruowałem/am urządzenie/przedmiot/element stroju z dawnych czasów. Wykorzystałem/am go do przygotowania rekonstrukcji/gry/zbiórki.\nWykonałem/am grę planszową lub karcianą na temat interesujących mnie wydarzeń historycznych. Przeprowadziłem/am ją w drużynie lub klasie.', 76, ''),
(6688, 'Odwiedziłem/am minimum cztery instytucje, w których poszerzyłem/am swoją wiedzę historyczną (np. muzeum, archiwum, biblioteka, zamek). Najciekawsze informacje przedstawiłem/am zastępowi/drużynie w atrakcyjny sposób, osadzając je w odpowiedniej epoce historycznej.', 76, ''),
(6689, 'Wykonałem/am jedno z zadań:\nNawiązałem/am współpracę z grupą rekonstrukcyjną odtwarzającą interesujący mnie okres historyczny.\nNawiązałem/am współpracę z instytucją krzewiącą historię.', 77, ''),
(6690, 'Wykonałem/am jedno z zadań:\nZorganizowałem/am rajd/wycieczkę/wyjście do muzeum lub spacer historyczny, podczas którego pełniłem/am funkcję przewodnika.\nZorganizowałem grę lub rajd historyczny dla patroli harcerskich (zastępów/drużyn).', 77, ''),
(6691, 'Wykonałem/am jedno z zadań:\nWraz z zespołem przygotowałem/am wystawę historyczną (np. na temat historii mojej miejscowości lub wybranego jej epizodu, historii mojego środowiska harcerskiego).\nZainicjowałem/am i przeprowadziłem/am inscenizację wybranego wydarzenia historycznego.', 77, ''),
(6692, 'Wykonałem/am jedno z zadań:\nOdkryłem/am i poznałem/am zapomniane/mało znane miejsca historyczne (np. z działań wojennych z , wojny polsko-bolszewickiej, czasów zaborów i powstań, z historii harcerstwa), z pomocą wszystkich dostępnych źródeł historycznych opisałem/am miejsce, zaopiekowałem/am się nim. Swoje osiągnięcia i wnioski upubliczniłem/am dla większej społeczności (np. na portalu Otwarte Zabytki, Harcerska mapa Niepodległej itp.).\nNapisałem/am artykuł dotyczący interesującego mnie wydarzenia historycznego, korzystając z wybranej nauki pomocniczej historii (np. paleografia, numizmatyka, archiwistyka, heraldyka) oraz minimum czterech różnych źródeł. Opublikowałem/am artykuł w wybrany sposób.', 77, ''),
(6693, 'Odwiedziłem/am planetarium, wziąłem/wzięłam udział w pokazie tematycznym.', 213, ''),
(6694, 'Wskazałem/am na nocnym niebie gwiazdozbiór Wielkiej i Małej Niedźwiedzicy wraz z Gwiazdą Polarną. Nauczyłem członków mojego zastępu znajdować ją.', 213, ''),
(6695, 'Zaobserwowałem/am na niebie przynajmniej jedną planetę Układu Słonecznego.', 213, ''),
(6696, 'Korzystałem/am z mapy nieba, aby zidentyfikować gwiazdozbiory i najjaśniejsze gwiazdy. Za jej pomocą wyznaczyłem/am również układ gwiazd o różnych porach roku i nocy.', 214, ''),
(6697, 'Wskazałem/am na niebie minimum 8 podstawowych gwiazdozbiorów, w tym jeden charakterystyczny dla nieba letniego i jeden dla nieba zimowego.', 214, ''),
(6698, 'Pogłębiłem/am swoją wiedzę z zakresu astronomii, np.: wiem, jakie planety tworzą układ słoneczny, co oznaczają pojęcia: galaktyka, droga mleczna, mgławica, kometa, meteor, rok świetlny, satelita, zaćmienie słońca i księżyca itp. Dla zastępu przeprowadziłem/am zbiórkę (lub blok zajęć na biwaku/obozie) z astronomii, podczas której zapoznałem/am innych z podstawowymi zjawiskami astronomicznymi i zasadami obserwacji nieba.', 214, ''),
(6699, 'Skorzystałem/am z programów komputerowych do nauki astronomii i obserwacji nieba (np. Stellarium, Stark Walk 2, Sky Safari czy NASA itp.), by na bieżąco śledzić zjawiska astronomiczne.', 215, ''),
(6700, 'Zorganizowałem/am dla harcerzy „noc pod gwiazdami”, podczas której zapoznałem/am ich z podstawowymi konstelacjami nieba i ciałami niebieskimi.', 215, ''),
(6701, 'Zgromadziłem/am biblioteczkę popularnonaukową. Na bieżąco śledziłem/am portale astronomiczne. Podczas realizacji sprawności śledziłem/am wydarzenia/zjawiska astronomiczne: np. zaćmienie księżyca, przelot stacji kosmicznej, przelot komety, fazy księżyca.', 215, ''),
(6702, 'Wykonałem/am co najmniej jedno z poniższych zadań:\nZorganizowałem/am dla drużyny przegląd kina „kosmicznego”: np. Apollo 13, Grawitacja, Interstaller, Marsjanin.\nZorganizowałem/am wyjazd zastępu lub drużyny do planetarium lub obserwatorium astronomicznego.', 215, ''),
(6703, 'Dowiedziałem/am się, jak są zbudowane teleskop oraz radioteleskopy. Posługiwałem/am się teleskopem do obserwacji nieba (wiem do czego służą jego poszczególne części, jak przygotować go do obserwacji i przechowywać). Obserwowałem przez teleskop ciała niebieskie (np. bliskie galaktyki, powierzchnię Księżyca, księżyce Jowisza) i zjawiska astronomiczne (np. fazy Wenus).', 216, ''),
(6704, 'Systematycznie obserwowałem/am niebo przez jeden kwartał. Regularnie prowadziłem/am dziennik obserwacji nieba.', 216, ''),
(6705, 'Przeprowadziłem/am zbiórkę, na której przy pomocy teleskopu obserwowaliśmy różne obiekty kosmiczne i ciała niebieskie.', 216, ''),
(6706, 'Nawiązałem/am kontakt z placówkami naukowymi zajmującymi się badaniem kosmosu. Wziąłem/wzięłam udział w programie obserwacji lub wykrywania obiektów astronomicznych.', 216, ''),
(6707, 'Przygotowałem/am minimum 3 wydarzenia z życia zastępu/drużyny, umieszczając wpis w kronice zastępu/drużyny. W ramach wpisów uwzględniłem/am ważne dla środowiska elementy obrzędowe i anegdotyczne. Wykazałem/am się kreatywnością w ich tworzeniu.', 72, ''),
(6708, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Skontaktowałem/am się z kimś, kto prowadził kronikę w swoim środowisku i przeprowadziłem/am z tą osobą wywiad, uzyskując następujące informacje: sposób prowadzenia kroniki, zbierania informacji, opieki nad kroniką i wykorzystanie jej do pracy z zastępem/drużyną.\n-Przejrzałem/am kroniki swojego środowiska i spisałem/am swoje wnioski dotyczące sposobów prowadzenia kroniki, które przedstawiłem/am na forum drużyny.', 72, ''),
(6709, 'Poszukałem/am w Internecie informacji na temat kaligrafii, akcesoriów i materiałów wykorzystywanych w kaligrafii. Wybrałem/am jeden krój, z pomocą którego stworzyłem/am wpis w kronice.', 72, ''),
(6710, 'Przygotowałem/am wpis do kroniki drużyny, opisując dłuższy wyjazd drużyny, tzn. biwak, obóz, zimowisko i umieszczając zdjęcia, rysunki, itd. podnoszące wartość artystyczną relacji.', 72, ''),
(6711, 'Prowadziłem/am w okresie próby kronikę, opisując minimum 7 wydarzeń dotyczących drużyny. Oprawę graficzną opisów dopasowałem/am do klimatu/treści wydarzenia.', 73, ''),
(6712, 'Stworzyłem/am jeden wpis do kroniki metodą scrapbookingu.', 73, ''),
(6713, 'Wykonałem/am co najmniej jedno z poniższych zadań:\n-Stworzyłem/am oprawę kroniki według własnego pomysłu lub zmodyfikowałem/am istniejącą tak, aby pasowała do istniejącej obrzędowości drużyny.\n-Stworzyłem/am bogato zdobioną okazjonalną kartę (np. na rozpoczęcie roku harcerskiego/szkolnego) w kronice oraz wprawiłem/am ją we właściwe miejsce w kronice, nie uszkadzając innych wpisów.\n-Zreperowałem/am uszkodzoną kartę (np. podarta strona) w kronice z zastosowaniem właściwych technik i materiałów uzupełniających ubytek.', 73, ''),
(6714, 'Przeprowadziłem/am dla innych harcerzy warsztaty doskonalące umiejętności prowadzenia kronik.', 73, ''),
(6715, 'Dowiedziałem/am się, jaki pozytywny wpływ na nasz organizm ma wysiłek fizyczny i gimnastyka. Zdobytą wiedzę przedstawiłem/am klasie lub swojemu zastępowi.', 51, ''),
(6716, 'Opracowałem/am własne zestawy ćwiczeń w domu i na powietrzu.', 51, ''),
(6717, 'Przeprowadziłem/am gimnastykę zastępu lub grupy dzieci.', 51, ''),
(6718, 'Przez co najmniej miesiąc brałem/am udział regularnie we wszystkich lekcjach WF-u i oprócz tego raz w tygodniu uprawiałem/am dowolną aktywność sportową.', 51, ''),
(6719, 'Nauczyłem/am się wykonywać jeden zestaw ćwiczeń z wybranego rodzaju treningu(aerobik, gimnastyka sportowa, gimnastyka artystyczna, kalistenika, tabata, zumba, fitness, joga, crossfit itp.).', 52, ''),
(6720, 'Poznałem/am zasady, dzięki którym podczas ćwiczeń nie zaszkodzę swojemu ciału. Na ich podstawie wykonałem/am poprawną rozgrzewkę.', 52, ''),
(6721, 'W okresie trwania próby co najmniej raz w tygodniu wykonywałem/am dowolny zestaw ćwiczeń trwający minimum 60 minut.', 52, ''),
(6722, 'Przeprowadziłem/am zestaw ćwiczeń z ulubionego przeze mnie rodzaju gimnastyki dla zastępu lub drużyny na obozie lub biwaku, zwracając uwagę na dokładność wykonywania ćwiczeń.', 52, ''),
(6723, 'Pod okiem trenera opracowałem/am własny zestaw ćwiczeń z wybranego rodzaju treningu (aerobik, gimnastyka sportowa, gimnastyka artystyczna, kalistenika, tabata, zumba, fitness, joga, crossfit itp.).', 53, ''),
(6724, 'Poznałem/am zasady, dzięki którym podczas ćwiczeń nie zaszkodzę swojemu ciału. Na ich podstawie wykonałem/am poprawne rozciąganie po treningu.', 53, ''),
(6725, 'W okresie trwania próby co najmniej dwa razy w tygodniu wykonywałem/am dowolny zestaw ćwiczeń trwający minimum 60 minut.', 53, ''),
(6726, 'Zorganizowałem/am wyjście zastępu/drużyny na siłownię/do klubu fitness. Pokazałem/am, jak prawidłowo się rozgrzać przed treningiem i prawidłowo rozciągnąć po treningu.', 53, ''),
(6727, 'W ciągu miesiąca nauczyłem/am się wykonywać ćwiczenie, którego wcześniej nie byłem/am w stanie wykonać.', 54, ''),
(6728, 'Zorganizowałem/am lub wziąłem/wzięłam udział w dowolnej akcji popularyzującej dany rodzaj treningu (zawody, dni otwarte, festiwal zumby/gimnastyki/kalisteniki/jogi itp..) wśród okolicznych mieszkańców.', 54, ''),
(6729, 'W okresie trwania próby co najmniej trzy razy w tygodniu wykonywałem/am dowolny zestaw ćwiczeń trwający minimum 60 minut.', 54, ''),
(6730, 'Wzięłam/wziąłem udział w jednej wybranej imprezie dotyczącej mojej ulubionej dyscypliny gimnastyki:\nzawodach sportowych,\ngrupowym pokazie.', 54, ''),
(6731, 'Wziąłem/am udział w wydarzeniu kulturalnym bądź sportowym związanym z tańcem, którym się interesuję.', 43, ''),
(6732, 'Poprowadziłem/am kilka pląsów lub zabaw z muzyką na zbiórce zastępu bądź drużyny.', 43, ''),
(6733, 'Obejrzałem/am film na temat tańca i wskazałem/am występujące w nim elementy taneczne, jak na przykład: postaci znanych tancerzy, konkretne figury, rodzaje tańca, specyficzna rytmika.', 43, ''),
(6734, 'Rozpoznałem/am kroki i melodie minimum 5 różnych rodzajów tańców.', 43, ''),
(6735, 'Zatańczyłem/am publicznie minimum dwa wybrane tańce z różnych gatunków, z których minimum jeden to taniec tańczony w parze. Skompletowałem/am strój taneczny do wybranego rodzaju tańca, który zaprezentowałem/am.', 44, ''),
(6736, 'Wziąłem/wzięłam udział w zajęciach nauki tańca.', 44, ''),
(6737, 'Przeprowadziłem/am rozgrzewkę przed treningiem tanecznym oraz zakończyłem/am trening rozciąganiem.', 44, ''),
(6738, 'Przygotowałem/am materiał promujący historię i specyfikę wybranego tańca bądź dokonania konkretnego tancerza i przedstawiłem/am go w atrakcyjnej formie (materiał multimedialny, artykuł, wystąpienie publiczne).', 44, ''),
(6739, 'Wziąłem/am udział w konkursie tanecznym, starając się uzyskać jak najlepszą ocenę.', 45, ''),
(6740, 'Zatańczyłem/am publicznie trzy choreografie prezentujące wybrany taniec: towarzyski, taniec ludowy lub taniec nowoczesny.', 45, ''),
(6741, 'Nauczyłem/am tańczyć co najmniej dwie osoby podstaw dwóch wybranych tańców.', 45, ''),
(6742, 'Zaprezentowałem/am pięć podstawowych pozycji tańca klasycznego (baletu).', 45, ''),
(6743, 'Zatańczyłem/am publicznie, prezentując co najmniej trzy wybrane choreografie, które przedstawią minimum trzy różne rodzaje tańca, np.: taniec ludowy, towarzyski, nowoczesny, klasyczny.', 46, ''),
(6744, 'Zatańczyłem/am taniec, którego choreografia zawierała minimum jeden element akrobatyczny.', 46, ''),
(6745, 'Zatańczyłem/am w parze wybrany taniec z zupełnie nowym partnerem (bez treningu i wcześniejszego przygotowania).', 46, ''),
(6746, 'Przeprowadziłem/am rozgrzewkę przed treningiem oraz rozciągałem/am się po treningu.', 47, ''),
(6747, 'Poznałem/am podstawowe komendy używane w trakcie wspinaczki i stosowałem/am je za każdym razem, kiedy się wspinałem/am.', 47, ''),
(6748, 'Nałożyłem/am uprząż oraz przywiązałem/am się do niej (wiążąc podwójną ósemkę).', 47, ''),
(6749, 'Wykonałem/am jedno z poniższych zadań:\n-zrobiłem/am drogę o wycenie 4b na wędkę na sztucznej ścianie;\n-wspinałem/am się na wędce na sztucznej ściance (min. 5 razy).', 47, ''),
(6750, 'Nauczyłem/am się dwóch ćwiczeń poprawiających technikę wspinaczkową i stosuję je w praktyce.', 48, ''),
(6751, 'Wykonałem/am jedno z poniższych zadań:\n-zrobiłem/am drogę o wycenie V0 na boulderach;\n-zrobiłem/am drogę na wędkę o wycenie 5a na sztucznej ściance;\n-Zrobiłem/am drogę na wędkę o wycenie IV w skałkach.', 48, ''),
(6752, 'Zwinąłem/zwinęłam i przeniosłem/am linę wspinaczkową.', 48, ''),
(6753, 'Wymieniłem/am różne formy uprawiania wspinaczki, style wspinania, skale trudności. Dowiedziałem/am się, do czego służą przewodniki wspinaczkowe oraz zorientowałem/am się, gdzie mogę jechać na skałki. Opowiedziałem/am o tym w zastępie.', 48, ''),
(6754, 'Poprowadziłem/am drogę o wycenie 5 (asekuracja dolna).', 49, ''),
(6755, 'Skompletowałem/am podstawowy sprzęt wspinaczkowy (np. uprząż, buty, kask, przyrząd do asekuracji, itd.).', 49, ''),
(6756, 'Wykonałem/am jedno z poniższych zadań:\n-zrobiłem/am drogę na wędkę o wycenie 6a;\n-zrobiłem/am drogę o wycenie V0 na boulderach;\n-zrobiłem/am drogę na wędkę o wycenie V w skałkach.', 49, ''),
(6757, 'Zorganizowałem/am wyjście zastępu/drużyny na ściankę wspinaczkową. Pokazałem/am, jak prawidłowo się rozgrzać przed treningiem i prawidłowo rozciągnąć po treningu.', 49, ''),
(6758, 'Asekurowałem/am drugiego wspinacza.', 49, ''),
(6759, 'Poprowadziłem/am drogę o wycenie 6b.', 50, ''),
(6760, 'Wykonałem/am jedno z poniższych zadań:\n-zrobiłem/am drogę na wędkę o wycenie 6c;\n-zrobiłem/am drogę o wycenie V2 na boulderach;\n-zrobiłem/am drogę na wędkę o wycenie VI w skałkach.', 50, ''),
(6761, 'Wykonałem/am jedno z poniższych zadań:\n-zorganizowałem/am warsztaty wspinaczkowe w drużynie, korzystając z pomocy instruktora posiadającego odpowiednie kwalifikacje, np. instruktora PZA;\n-zorganizowałem/am min. trzydniowy wyjazd na skałki.\nW obu zadaniach przedstawiłem/am uczestnikom normy etyczne obowiązujące w środowisku wspinaczy.', 50, ''),
(6762, 'Wykonałem/am jedno z poniższych zadań:\n-uczestniczyłem/am w obozie wspinaczkowym;\n-uczestniczyłem/am w kurskie skałkowym;\n-wspinałem się w skałkach min. 2 dni za granicą.', 50, ''),
(6763, 'Uczestniczyłem/am w co najmniej trzech spływach kajakowych.', 189, ''),
(6764, 'Wiosłowałem/am na wprost i wstecz oraz zawróciłem/am kajakiem.', 189, ''),
(6765, 'Potrafię czytać locję rzeki. Podczas spływu sterowałem/am kajakiem tak, aby nie wpłynąć na przeszkody. Pokazałem/am innym, na jakie miejsca na rzece należy uważać.', 189, ''),
(6766, 'Poprawnie spakowałem/am się na spływ i wymieniłem/am elementy ekwipunku na spływ jednodniowy.', 189, ''),
(6767, 'Wszedłem/weszłam do kajaka z wody płytkiej.', 189, ''),
(6768, 'Uczestniczyłem/am w załadunku i wyładunku kajaków.', 189, ''),
(6769, 'Uczestniczyłem/am w co najmniej pięciu spływach kajakowych w tym przynajmniej raz z noclegiem.', 190, ''),
(6770, 'Pokazałem/am, jak piórkować.', 190, ''),
(6771, 'W czasie wiosłowania wykorzystałem/am kontrę sterującą.', 190, ''),
(6772, 'Skompletowałem/am sprzęt ratowniczy na spływ, podałem/am rzutkę ratowniczą (na ziemi).', 190, ''),
(6773, 'Brałem/am udział w pozorowanej wywrotce kajaka.', 190, ''),
(6774, 'Znam zasady rozłożenia ładunku w kajaku na spływ wielodniowy, zaprezentowałem/am je podczas pakowania i opowiedziałem/am, jak rozłożyć ciężar.', 190, ''),
(6775, 'Ukończyłem/am odpowiednie szkolenie przygotowawcze (instruktaż) do latania w tunelu aerodynamicznym', 261, ''),
(6776, 'Wykazałem/am się wiedzą na temat bezpieczeństwa w trakcie latania w tunelu (ubiór, zachowanie w tunelu).', 261, ''),
(6777, 'Wykazałem/am się znajomością podstawowych sygnałów obowiązujących w tunelu i potrafiłem/am się do nich zastosować podczas latania (wydłuż nogi, głowa do góry, rozluźnij się).', 261, ''),
(6778, 'Zachowałem/am poprawną sylwetkę podczas latania w tunelu (nogi wyprostowane, ręce ugięte pod kątem 90 stopni, głowa do góry, wzrok skierowany na wprost, wygięcie całego tułowia w kształcie banana).', 261, ''),
(6779, 'Wykonałem/am minimum dwa loty z instruktorem w tunelu aerodynamicznym.', 261, ''),
(6780, 'Zdobyłem/am wiedzę i umiejętności na poziomie sprawności (*)', 262, ''),
(6781, 'Przez okres jednego miesiąca przed lataniem regularnie wykonywałem/am ćwiczenia wzmacniające mięśnie ramion, tułowia oraz gibkość.', 262, ''),
(6782, 'Ukończyłem/am odpowiednie szkolenie przygotowawcze (instruktaż) przed samodzielnym lataniem w tunelu aerodynamicznym.', 262, ''),
(6783, 'Opanowałem/am neutralną sylwetkę podczas latania w tunelu stosując wszystkie sygnały pokazane przez instruktora.', 262, ''),
(6784, 'Wykonałem/am minimum 10 minutowy trening w tunelu aerodynamicznym.', 262, ''),
(6785, 'Opowiedziałem/am w zastępie o sporcie tunelowym, konkurencjach i osiągach Polaków.', 262, ''),
(6786, 'Zdobyłem/am wiedzę i umiejętności na poziomie sprawności (**)', 263, ''),
(6787, 'Przez okres trzech miesięcy regularnie wykonywałem/am ćwiczenia znacząco poprawiając siłę mięśni ramion, tułowia i gibkość.', 263, ''),
(6788, 'Ukończyłem/am odpowiednie szkolenie przygotowawcze (instruktaż) przed lataniem w tunelu aerodynamicznym na poziomie kursu AFF.', 263, ''),
(6789, 'Wykazałem/am się umiejętnością samodzielnego wejścia i wyjścia z tunelu aerodynamicznego.', 263, ''),
(6790, 'Wykazałem/am się umiejętnością przemieszczania się we wszystkich płaszczyznach, wykonywania obrotów o 360 stopni w prawo i w lewo, przechodzenia do pozycji delta oraz inicjowania otwarcia spadochronu głównego.', 263, ''),
(6791, 'Przeprowadziłem/am dla zastępu lub drużyny zajęcia popularyzujące latanie tunelowe.', 263, ''),
(6792, 'Zdobyłem/am wiedzę i umiejętności na poziomie sprawności (***)', 264, ''),
(6793, 'Osiągnąłem/osiągnęłam sprawność fizyczną i techniczną pozwalającą na pełną kontrolę nad ciałem podczas latania w tunelu w wybranej sylwetce.', 264, ''),
(6794, 'W sposób biegły wykonałem/am cztery różne formacje Random (FS-2 lub VFS-2) wchodząc w każdą z ról.', 264, ''),
(6795, 'Wziąłem/wzięłam udział w zawodach tunelowych.', 264, ''),
(6796, 'Pomogłem/am instruktorowi w przeprowadzeniu szkolenia przygotowawczego dla osób początkujących.', 264, ''),
(6797, 'Rozpoznałem/am i rozróżniłem/am samoloty cywilne i wojskowe. Rozpoznałem/am znaki sił powietrznych i linii lotniczych przynajmniej trzech krajów.', 265, ''),
(6798, 'Wykazałem/am się znajomością zasad bezpiecznego poruszania się po lotniskach.', 265, ''),
(6799, 'Wykazałem/am się wiedzą, gdzie w Polsce znajdują się lotniska pasażerskie. Wymieniłem/am przynajmniej pięć cywilnych portów lotniczych. Odwiedziłem/am port lotniczy.', 265, ''),
(6800, 'Wykazałem/am się znajomością alfabetu fonetycznego ICAO. Poprawnie podałem/am znaki boczne obserwowanych samolotów.', 265, ''),
(6801, 'Wykazałem/am się wiedzą, jakim sprzętem posługują się spotterzy.', 265, ''),
(6802, 'Wykazałem/am się znajomością internetowych serwisów tematycznych poświęconych planespottingowi (namierzaniu samolotów).', 265, ''),
(6803, 'Rozpoznałem/am i rozróżniłem/am samoloty cywilne i wojskowe. Rozpoznałem/am znaki sił powietrznych i linii lotniczych przynajmniej dziesięciu krajów.', 266, ''),
(6804, 'Rozpoznałem/am modele i typy obserwowanych samolotów.', 266, ''),
(6805, 'Wykazałem/am się znajomością zasad bezpiecznego poruszania się po lotniskach. Wykazałem/am się wiedzą, jak przygotować się do poruszania w pobliżu lotniska.', 266, ''),
(6806, 'Wykazałem/am się wiedzą, gdzie w Polsce znajdują się lotniska pasażerskie. Wymieniłem/am wszystkie porty lotnicze w Polsce. Odwiedziłem/am port lotniczy. Wykazałem/am się znajomością jego topografii.', 266, ''),
(6807, 'Wykazałem/am się wiedzą, jakim sprzętem posługują się spotterzy. W czasie próby kompletowałem/am podstawowy sprzęt do obserwacji.', 266, ''),
(6808, 'Wykazałem/am się wiedzą, jakie funkcje na lotnisku może pełnić spotter. Uzasadniłem/am społeczne zaufanie dla obserwatorów.', 266, ''),
(6809, 'Nawiązałem/am kontakt ze stowarzyszeniem spotterów. Dowiedziałem/am się, na czym polega to hobby. Swoją wiedzą podzieliłem/am się z zastępem.', 266, ''),
(6810, 'Rozpoznałem/am i rozróżniłem/am samoloty cywilne i wojskowe. Rozpoznałem/am znaki sił powietrznych i linii lotniczych przynajmniej dwudziestu krajów.', 267, ''),
(6811, 'Rozpoznałem/am modele i typy obserwowanych samolotów. Opowiedziałem/am o danych technicznych wybranych modeli samolotów.', 267, ''),
(6812, 'Przeprowadziłem/am zajęcia z zasad bezpiecznego poruszania się po lotniskach.', 267, ''),
(6813, 'Wykazałem/am się wiedzą, gdzie w Polsce znajdują się lotniska pasażerskie. Wymieniłem/am wszystkie porty lotnicze w Polsce. Odwiedziłem/am port lotniczy, wykazałem/am się znajomością jego topografii. Przedstawiłem/am jego historię.', 267, ''),
(6814, 'Wykazałem/am się wiedzą, jakim sprzętem posługują się spotterzy. Skompletowałem/am sprzęt do obserwacji. Za jego pomocą prowadziłem/am własne obserwacje i dokumentację', 267, ''),
(6815, 'Zorganizowałem/am wycieczkę zastępu lub drużyny na lotnisko pod opieką doświadczonego spottera.', 267, ''),
(6816, 'Rozpoznałem/am i rozróżniłem/am samoloty cywilne i wojskowe. Rozpoznałem/am znaki sił powietrznych i linii lotniczych przynajmniej dwudziestu krajów.', 268, ''),
(6817, 'Rozpoznałem/am modele i typy obserwowanych samolotów. Opowiedziałem/am o danych technicznych wybranych modeli samolotów.', 268, ''),
(6818, 'Wykazałem/am się wiedzą, jakim sprzętem posługują się spotterzy. Skompletowałem/am sprzęt do obserwacji i sprzęt fotograficzny. Za jego pomocą prowadziłem/am własne obserwacje i dokumentację.', 268, ''),
(6819, 'Wykazałem/am się znajomością internetowych serwisów tematycznych poświęconych planespottingowi. Korzystałem/am z nich w realizacji swoich zainteresowań.', 268, ''),
(6820, 'Zorganizowałem/am w szkole lub w hufcu wystawę własnych fotografii wykonanych podczas obserwacji.', 268, ''),
(6821, 'Na podstawie nazewnictwa ICAO stworzyłem/am nazwę klubu planespottingowego działającego na wybranym lotnisku.', 268, ''),
(6822, 'Poprawnie wykonałem/am manewry startu i lądowania.', 269, ''),
(6823, 'Wykonałem/am trening lotu dronem w symulatorze.', 269, ''),
(6824, 'Sprawdziłem/am stan baterii i oszacowałem/am czas możliwego lotu.', 269, ''),
(6825, 'Poprawnie skalibrowałem/am żyroskop w statku powietrznym.', 269, ''),
(6826, 'Podczas lotu poprawnie instruowałem/am operatora drona o wymaganych manewrach.', 269, ''),
(6827, 'Wyjaśniłem/am zasadę działania śmigła.', 269, ''),
(6828, 'Zdobyłem/am wiedzę i umiejętności na poziomie sprawności (*)', 270, ''),
(6829, 'Poprawnie wykonałem/am manewry dronem stale kontrolując parametry lotu\nstart i lądowanie;\nheading na kierunek świata lub podany w stopniach;\npodlecenie do obszaru i uchwycenie go w kadrze;\nprzelot nad przeszkodą.', 270, ''),
(6830, 'Sprawdziłem/am stan techniczny i przygotowałem/am drona do lotu. (np. poprawność działania silników, gimbala i jego kontroli, prawidłowość przykręcenia śmigieł oraz działanie systemów pomocniczych: GPS i żyroskop).', 270, ''),
(6831, 'Nagrałem/am krótki filmik z wykorzystaniem drona.', 270, ''),
(6832, 'Obejrzałem/am kilka wideorelacji z zawodów dronów sportowych. Zaobserwowałem/am różnice w budowie pomiędzy dronami filmującymi i sportowymi.', 270, ''),
(6833, 'Wykazałem/am się znajomością zagrożeń związanych z lotem dronem.', 270, ''),
(6834, 'Opowiedziałem/am kolegom i koleżankom z zastępu o działaniu drona.', 270, ''),
(6835, 'Zdobyłem/am wiedzę i umiejętności na poziomie sprawności (**)', 271, ''),
(6836, 'Przez co najmniej 30 sekund kołowałem/am nad obiektem poruszającym się w sposób nieregularny (np. rower, biegnąca osoba) z obiektywem skierowanym w jego kierunku bez wcześniejszej znajomości trasy.', 271, ''),
(6837, 'Z użyciem drona wykonałem/am wideorelację dowolnego wydarzenia z życia drużyny lub zastępu.', 271, ''),
(6838, 'Wykazałem/am się wiedzą z zakresu warunków prawnych prowadzenia operacji dronem.', 271, ''),
(6839, 'Poprawnie oceniłem/am warunki meteorologiczne do wykonywania lotów dronem.', 271, ''),
(6840, 'Przeprowadziłem/am zdjęcia popularyzujące loty dronami w drużynie lub zastępie.', 271, ''),
(6841, 'Wykazałem/am się znajomością wymagań stawianym kandydatom na żołnierzy zawodowych w jednostkach powietrzno-desantowych. Osiągnąłem/osiągnęłam sprawność pozwalającą na zaliczenie egzaminu z wychowania fizycznego według norm określonych w takich jednostkach (co najmniej na poziomie dostatecznym).', 260, ''),
(6842, 'Wykazałem/am się wiedzą, na czym polegają różnice pomiędzy wykonywaniem skoków sportowych i wojskowych.', 260, ''),
(6843, 'Omówiłem/am rodzaje skoków spadochronowych wykonywanych przez skoczków wojskowych (desantowe, HALO, HAHO, LALO).', 260, ''),
(6844, 'Wykazałem/am się znajomością warunków nadawania tytułu i Odznaki Skoczka Spadochronowego Wojsk Powietrzno–Desantowych.', 260, ''),
(6845, 'Ukończyłem/am kurs samoobrony bądź rozwija w sposób ciągły swoje umiejętności w dziedzinie sportów lub sztuk walki.', 260, ''),
(6846, 'Omówiłem/am budowę i działanie broni palnej krótkiej i długiej na wybranych egzemplarzach. Wykazałem/am się znajomością zasad bezpiecznego posługiwania się bronią. Wykazałem/am się wiedzą, jakiego rodzaju uzbrojenie znajduje się na wyposażeniu jednostek powietrzno-desantowych.', 260, ''),
(6847, 'Ukończyłem/am szkolenie strzeleckie z broni palnej.', 260, ''),
(6848, 'Zorganizowałem/am dla zastępu lub drużyny zawody strzeleckie z broni pneumatycznej przestrzegając przepisów strzeleckich i zasad bezpieczeństwa. Wyjaśniłem/am zasady bezpieczeństwa podczas działań strzeleckich oraz omówiłem/am podstawy prawidłowego strzelania.', 260, ''),
(6849, 'Przeprowadziłem/am dla drużyny lub kilku środowisk grę terenową o tematyce obronnej, rozwijającą sprawność fizyczną, umiejętności orientacji w terenie w dzień i w nocy oraz umiejętności strzeleckie.', 260, '');
INSERT INTO `zadaniasprawnosciharcerskich` (`ID`, `Tresc`, `Sprawnosc`, `Stopien`) VALUES
(6850, 'Poznałem/am budowę zewnętrzną, maści i odmiany koni. Wiedzę tę przekazałem/am swojemu zastępowi podczas zbiórki.', 272, ''),
(6851, 'Potrafiłem/am prawidłowo podejść do konia, odczytać i zinterpretować jego reakcje. Wskazałem/am najważniejsze zasady obowiązujące w stajni oraz na ujeżdżalni.', 272, ''),
(6852, 'Poznałem/am budowę siodła wszechstronnego oraz ogłowia. Prawidłowo wyczyściłem/am i założyłem/am rząd jeździecki na konia.', 272, ''),
(6853, 'Poruszałem/am się w zastępie konnym w równowadze w stępie, kłusie anglezowanym oraz galopie w dosiadzie odciążającym.', 272, ''),
(6854, 'Skompletowałem/am odpowiedni i bezpieczny strój do jazdy konnej.', 273, ''),
(6855, 'Rozróżniłem/am i opisałem/am podstawowy sprzęt jeździecki.', 273, ''),
(6856, 'Poznałem/am podstawowe figury na ujeżdżalni, prawidłowo je wyjeżdżałem/am: wyjeżdżanie narożników, koła, wolty, półwolty, zmiany kierunków na 3 różne sposoby, wężyki, serpentyny.', 273, ''),
(6857, 'Poruszałem/am się w równowadze w trzech podstawowych chodach w dosiadzie ujeżdżeniowym oraz odciążającym.', 273, ''),
(6858, 'Wytłumaczyłem/am zastępowi podstawy teorii jazdy konnej, a także czym jest dosiad i jakie są jego rodzaje.', 274, ''),
(6859, 'Zaprezentowałem/am na zbiórce różne pomoce używane w jeździectwie, wskazałem/am ich dobre i złe strony.', 274, ''),
(6860, 'Prawidłowo wykonałem/am najazd na przeszkodę, skok oraz lądowanie. Pokonałem/am przeszkody do 60cm.', 274, ''),
(6861, 'Przygotowałem/am pokaz ujeżdżeniowy dla zastępu/drużyny. Pokazałem/am następujące elementy ujeżdżenia: stęp pośredni, kłus roboczy anglezowany, koło w kłusie ćwiczebnym, galop roboczy (na prawo i na lewo), zatrzymanie z kłusu, nieruchomość 5’.', 274, ''),
(6862, 'Pokazałem/am położenie liter na małym i dużym czworoboku. Wykonałem/am szkic – oznaczając je.', 275, ''),
(6863, 'Rozrysowałem/am podstawowe figury ujeżdzeniowe. Zaprezentowałem/am je na zbiórce.', 275, ''),
(6864, 'Wykonałem/am płynne przejścia we wszystkich trzech chodach.', 275, ''),
(6865, 'Wymieniłem/am i rozpoznałem/am sprzęt użytkowany w tej dyscyplinie.', 275, ''),
(6866, 'Wykonałem/am prawidłowo koło o średnicy 20 m we wszystkich trzech chodach.', 276, ''),
(6867, 'Wymieniłem/am wszystkie figury ujeżdzeniowe i rodzaje chodów konia.', 276, ''),
(6868, 'Prawidłowo dobrałem/am strój dla konia i jeźdźca charakterystyczny dla tej dyscypliny. Przygotowałem/am przykładową stylizację (może być w formie projektu) – zaprezentowałem/am ją na zbiórce.', 276, ''),
(6869, 'Opiekowałem/am się koniem, czyszcząc go, zaplatając grzywę (koreczki), karmiąc i ścieląc boks.', 276, ''),
(6870, 'Założyłem/am samodzielnie owijki z podkładem.', 277, ''),
(6871, 'Założyłem/am i dopasowałem/am ogłowie munsztukowe.', 277, ''),
(6872, 'Wykonałem/am serpentynę w  kontrgalopie, zaprezentowałem/am kłus wyciągnięty.', 277, ''),
(6873, 'Wskazałem/am reprezentantów Polski w tej dyscyplinie i wymieniłem/am ich sukcesy.', 277, ''),
(6874, 'Pracowałem/am z koniem na lonży, przygotowując go do pracy ujeżdżeniowej.', 277, ''),
(6875, 'Utrzymałem/am równowagę w skoku, podążałem/am za koniem, zarówno z kłusu, jak i z galopu.', 278, ''),
(6876, 'Pokonałem/am parkur złożony z 4 przeszkód o wysokości min. 50-60cm (duże konie) i 40-50 cm (kucyki).', 278, ''),
(6877, 'Pokonałem/am szereg gimnastyczny ułożony z co najmniej trzech przeszkód ustawionych na zasadzie skok-wyskok.', 278, ''),
(6878, 'Po skoku oceniłem/am, a w razie potrzeby zmieniłem/am nogę, na którą koń galopuje.', 278, ''),
(6879, 'Przejechałem/am parkur złożony z 8 różnych przeszkód o wysokości 70-80cm (duże konie) i 60-70 cm (kucyki).', 279, ''),
(6880, 'Odliczyłem/am foule w linii.', 279, ''),
(6881, 'Pokonałem/am szereg gimnastyczny zróżnicowany wysokościowo.', 279, ''),
(6882, 'Co najmniej raz wziąłem/wzięłam  udział w zawodach towarzyskich.', 279, ''),
(6883, 'Przejechałem/am parkur złożony z więcej niż 8 przeszkód o wysokości powyżej 80 cm (duże konie) i 70 cm (kucyki).', 280, ''),
(6884, 'Skompletowałem/am prawidłowy ubiór na zawody jeździeckie.', 280, ''),
(6885, 'Wziąłem/Wzięłam udział w różnych zawodach (towarzyskich i regionalnych) przynajmniej 2 razy.', 280, ''),
(6886, 'Zaprezentowałem/am innym harcerzom swoją wiedzę na temat jazdy skokowej i zawodów.', 280, ''),
(6887, 'Wykonałem/am własnego Hobby Horsa. Zaprezentowałem/am go na zbiórce. Nadałem/am mu imię oraz przedstawiłem/am jego dane (rasa, maść, wiek). Opowiedziałem/am o cechach charakterystycznych dla tej rasy.', 282, ''),
(6888, 'Ustawiłem/am i przejechałem/am parkur złożony z co najmniej 6 przeszkód o wysokości 60 cm.', 282, ''),
(6889, 'Stworzyłem/am układ ujeżdżeniowy do dowolnej piosenki. Zastosowałem/am figury z poziomu SOJ (Srebrna Odznaka Jeździecka Polskiego Związku Jeździeckiego).', 282, ''),
(6890, 'Zorganizowałem/am zawody Hobby Horsing dla własnego zastępu: ujeżdżeniowe, skokowe lub Pony Games. Nagrałem/am filmik z tego wydarzenia i zaprezentowałem/am na zbiórce drużyny.', 282, ''),
(6891, 'Wykonałem/am co najmniej trzy Hobby Horses. Powiedziałem/am, jakiej są rasy i maści. Regularnie na nich trenuję.', 283, ''),
(6892, 'Przeprowadziłem/am zbiórkę o tym, jak zrobić własnego Hobby Horsa.', 283, ''),
(6893, 'Przygotowałem/am zawody „Potęgi Skoku”.', 283, ''),
(6894, 'Zorganizowałem/am Dzień Hobby Horsing dla całej drużyny. Wymyśliłem/am dla drużyny choreografię do kadryla jeździeckiego.', 283, ''),
(6895, 'Wykonałem/am stajnię dla moich Hobby Horses. Uszyłem/am derki i kantary.', 284, ''),
(6896, 'Zorganizowałem/am dla drużyny zajęcia o tym, jak zrobić własną stajnię Hobby Horses i jak dbać o swoje Hobby Horses.', 284, ''),
(6897, 'Zorganizowałem/am Zawody Hobby Horses dla innej drużyny. Przygotowałem/am zawody skokowe, ujeżdżeniowe i Pony Games.', 284, ''),
(6898, 'Prawidłowo wsiadałem/am i zsiadałem/am z konia, zarówno z prawej, jak i lewej strony (dopuszczalna jest pomoc asystenta).', 285, ''),
(6899, 'Prawidłowo wykonałem/am następujące ćwiczenia:\nA) na koniu gimnastycznym: wskok, siad podstawowy, klęk, flaga, młynek, nożyce, stanie;\nB) na koniu stojącym: siad podstawowy, flaga, młynek, rybka w przód i tył;\nC) na koniu chodzącym stępem na lonży: flaga, młynek, jazda bokiem i tyłem, martwy Indianin.', 285, ''),
(6900, 'Wymieniłem/am i opisałem/am podstawowy sprzęt używany do woltyżerki.', 285, ''),
(6901, 'Poznałem/am zasady prawidłowego lonżowania konia i zastosowałem/am je w praktyce.', 285, ''),
(6902, 'Utrzymywałem/am równowagę w stępie, kłusie i galopie.', 286, ''),
(6903, 'Prawidłowo wykonałem/am następujące ćwiczenia:\nA) na koniu stojącym: młynek, stanie, nożyce, zeskok odwrócony, podpór przodem;\nB) na koniu chodzącym w trzech chodach na lonży: wskok, flaga, młynek, jazda bokiem i tyłem.', 286, ''),
(6904, 'Prawidłowo założyłem/am i dopasowałem/am sprzęt woltyżerski (ogłowie, wypinacze, pas).', 286, ''),
(6905, 'Przygotowałem/am pokaz woltyżerski dla swojego zastępu.', 286, ''),
(6906, 'Przedstawiłem/am swojemu zastępowi w dowolny sposób regulamin dyscypliny woltyżerka.', 287, ''),
(6907, 'Prawidłowo wykonałem/am następujące ćwiczenia:\nA) na koniu stojącym: wskok trzema sposobami bez asystenta;\nB) na koniu chodzącym w trzech chodach na lonży: wskok, zeskok, zeskok-wskok, rybka, nożyce, stanie, podpór przodem.', 287, ''),
(6908, 'Zaprezentowałem/am 5 figur wymyślonych przez siebie w układzie choreograficznym.', 287, ''),
(6909, 'Nauczyłem/am mniej doświadczone osoby podstawowych ćwiczeń na koniu gimnastycznym.', 287, ''),
(6910, 'Skompletowałem/am odpowiedni i bezpieczny strój do jazdy konnej, a także wytłumaczyłem/am, jak wygląda strój jeźdźca zgodny z kanonem tej dyscypliny.', 288, ''),
(6911, 'Rozpoznałem/am podstawowe elementy rzędu jeździeckiego związane z tą dyscypliną.', 288, ''),
(6912, 'Wsiadałem/am i zsiadałem/am na konia „po strzemieniu”.', 288, ''),
(6913, 'Poruszałem/am się w kłusie i stępie, wykonałem/am zatrzymania z obu chodów, cofałem/am.', 288, ''),
(6914, 'Opisałem/am jedną z konkurencji rozgrywanych w stylu western.', 289, ''),
(6915, 'Rozpoznałem/am kiełzna charakterystyczne dla tej dyscypliny.', 289, ''),
(6916, 'Dowiedziałem/am się, jakie są korzenie tej dyscypliny, przedstawiłem/am je innym harcerzom.', 289, ''),
(6917, 'Poruszałem/am się w trzech chodach – wykonywałem/am płynne przejścia pomiędzy nimi, zatrzymania, cofanie.', 289, ''),
(6918, 'Wymieniłem/am i opisałem/am najważniejsze klasy pokazowe w stylu western.', 290, ''),
(6919, 'Samodzielnie przygotowałem/am konia do jazdy.', 290, ''),
(6920, 'Nauczyłem/am się, jak trzyma się wodzę w stylu western. Zaprezentowałem/am, jak wygląda to w różnych kategoriach.', 290, ''),
(6921, 'Rozpoznałem/am typowe rasy koni użytkowane w stylu westernowym.', 290, ''),
(6922, 'Poruszałem/am się w trzech chodach – wykonywałem/am płynne przejścia pomiędzy nimi, zatrzymania, cofanie, ruchy boczne oraz zmiany nogi w galopie, spin.', 290, ''),
(6923, 'Rozpoznałem/am kiełzna wykorzystywane w zaprzęgach.', 291, ''),
(6924, 'Dowiedziałem/am się, czym różni uprząż szorowa od chomątowej. Opowiedziałem/am o tym.', 291, ''),
(6925, 'Opiekowałem/am się koniem, czyszcząc go, zaplatając grzywę i ogon, karmiąc i ścieląc boks.', 291, ''),
(6926, 'Poznałem/am zasady bezpiecznego zaprzęgania i stosowałem/am je w praktyce.', 291, ''),
(6927, 'Wymieniłem/am rodzaje uprzęży i typy pojazdów, rozpoznając je na rysunkach lub zdjęciach.', 292, ''),
(6928, 'Zademonstrowałem/am i opowiedziałem/am o tym, jaką funkcję pełni luzak w dyscyplinie powożenia.', 292, ''),
(6929, 'Poznałem/am budowę uprzęży szorowej lub chomątowej. Zakładałem/am i dopasowywałem/am je, poznając przy tym zasady zaprzęgania koni w pojedynkę i w parze.', 292, ''),
(6930, 'Brałem/am udział w przygotowaniach do zawodów zaprzęgów, pełniąc funkcję luzaka.', 292, ''),
(6931, 'Poznałem/am budowę uprzęży. Dowiedziałem/am się, co to jest munsztuk zaprzęgowy, liverpool, fasculce, blendysz, duga itp. oraz opowiedziałem/am o tym na zbiórce zastępu lub drużyny.', 293, ''),
(6932, 'Przedstawiłem/am za pomocą multimediów, jak najłatwiej rozróżnić style zaprzęgów: angielski, węgierski, bałagulski i inne.', 293, ''),
(6933, 'Prawidłowo wsiadałem/am i zsiadałam/em z pojazdu, odmierzyłem/am lejce.', 293, ''),
(6934, 'Prowadziłem/am zastęp jednokonny lub parokonny po placu po liniach prostych i w zakrętach zgodnie z zatwierdzonym schematem.', 293, ''),
(6935, 'Znalazłem/am informacje na temat reprezentantów Polski w tej dyscyplinie i opowiedziałem/am o ich sukcesach.', 293, ''),
(6936, 'Poruszałem/am się płynnie i w równowadze w trzech podstawowych chodach.', 294, ''),
(6937, 'Poprawnie pokonałem/am przeszkody w stępie: slalom między tyczkami, trzy beczki, bramka, gliniany garnek, przeniesienie kubka z jednej tyczki na drugą.', 294, ''),
(6938, 'Wykonałem/am i przedstawiłem/am pracę dotyczącą Working Equitation, a dokładniej: co to jest, obowiązujące zasady i skojarzenia.', 294, ''),
(6939, 'Prawidłowo wyjeżdżałem/am: woltę, półwolty, zatrzymanie, nieruchomość 6s, koło.', 294, ''),
(6940, 'Wziąłem/Wzięłam udział w zawodach regionalnych lub towarzyskich na poziomie L, osiągnąłem/osiągnęłam wynik min. 55%.', 295, ''),
(6941, 'Poprawnie pokonałem/am przeszkody w kłusie: ósemka między beczkami, skok przez kostki słomy, gliniany garnek, objeżdżanie tyczek lub przeszkód, przeniesienie kubka z jednej tyczki na drugą.', 295, ''),
(6942, 'Aktywnie monitorowałem/am rozwój dyscypliny Working Equitation w Polsce, znalazłem/am informacje na temat aktualnych zawodów i szkoleń, a także wskazałem/am jeźdźca, na którym się wzoruję.', 295, ''),
(6943, 'Przygotowałem/am pokaz dla zastępu/drużyny, na którym pokazałem/am, jak pokonuję podane przeszkody. Wykonałem/am je w dwóch formach: na szybkość oraz na dokładność.', 295, ''),
(6944, 'Współorganizowałem/am u siebie w stajni zawody z elementami Working Equitation  dla kolegów/zastępu.', 296, ''),
(6945, 'Prawidłowo wykonałem/am: cofnięcie 6 kroków, łopatką do wewnątrz w stępie, ustępowanie od łydki w stępie, półpiruet, serpentynę o 3 łukach ze zmianami nogi na linii środkowej (w galopie), zatrzymanie z kłusa.', 296, ''),
(6946, 'Poprawnie pokonałem/am przeszkody (w kłusie lub galopie): wyciągnięcie tyczki z beczki, użycie tyczki do zdjęcie pierścienia, włożenie tyczki do beczki, chód boczny przez kłodę (stęp), bramka.', 296, ''),
(6947, 'Nauczyłem/am mniej doświadczonych jeźdźców poprawnie technicznego pokonywania podstawowych przeszkód.', 296, ''),
(6948, 'Zorganizowałem/am tor przeszkód składający się z minimum 7 elementów i pokonałem/am go w jak najszybszym tempie.', 296, ''),
(6949, 'Wytłumaczyłem/am zastępowi, czym są zawody TREC PTTK, oraz wyjaśniłem/am skróty Faza PTV, MA, POR.', 297, ''),
(6950, 'Posługiwałem/am się mapą topograficzną. Wziąłem/Wzięłam udział w rozgrywkach INO.', 297, ''),
(6951, 'Wziąłem/Wzięłam udział w fazie MA (kontrola tempa). Osiągnąłem/Osiągnęłam wyniki: galop - powyżej 27 sekund, stęp - poniżej 95 sekund.', 297, ''),
(6952, 'Poprawnie pokonałem/am przeszkody z fazy PTV: niskie gałęzie, korytarz, nieruchomość w siodle, prowadzenie konia po literze \"S\", dosiadanie konia.', 297, ''),
(6953, 'Zaprezentowałem/am zastępowi w dowolnej formie regulamin TREC PTTK.', 298, ''),
(6954, 'Uczestniczyłem/am w wycieczce konnej, podczas której zaznaczałem/am na mapie topograficznej przejechaną trasę.', 298, ''),
(6955, 'Wziąłem/Wzięłam udział w fazie MA (kontrola tempa). Osiągnąłem/am wyniki: galop - powyżej 30 sekund, stęp - poniżej 80 sekund.', 298, ''),
(6956, 'Poprawnie pokonałem/am przeszkody z fazy PTV: przejazd na koniu po literze \"S\", nieruchomość, bramka, kłoda, ósemka.', 298, ''),
(6957, 'Wziąłem/Wzięłam udział w zawodach TREC PTTK.', 299, ''),
(6958, 'Zorganizowałem/am zawody z elementami TREC dla znajomych ze stajni lub/i harcerstwa.', 299, ''),
(6959, 'Wziąłem/Wzięłam udział w fazie MA (kontrola tempa). Osiągnąłem/am wyniki: galop - powyżej 33,5 sekund, stęp - poniżej 70 sekund.', 299, ''),
(6960, 'Poprawnie pokonałem/am przeszkody z fazy PTV: mostek przejechany na koniu, cofanie, wprowadzanie konia do przyczepy, szereg (cross), zjazd z pochyłości.', 299, ''),
(6961, 'Za pomocą dowolnej formy przedstawiłem/am podstawowe informacje na temat chowu i hodowli koni: typy i rasy koni, maści koni, podstawowe części ciała, ogólne wiadomości o zasadach żywienia koni.', 300, ''),
(6962, 'Poznałem/am zasady bezpiecznego popasu koni, prawidłowo uwiązałem/am konia.', 300, ''),
(6963, 'Prawidłowo wykonywałem/am podstawowe ćwiczenia rozluźniające w siodle.', 300, ''),
(6964, 'W równowadze i harmonii poruszałem/am się stępem, kłusem i galopem.', 300, ''),
(6965, 'Wziąłem/Wzięłam udział w wycieczce konnej, podczas której poznałem/am walory przyrodniczo-krajoznawcze okolic ośrodka.', 300, ''),
(6966, 'Posiadałem/am i potrafiłem/am przedstawić w ciekawy sposób: anatomię konia, zasady stosowania pomocy jeździeckich, powodowanie koniem w podstawowych chodach.', 301, ''),
(6967, 'Poruszałem/am się konno w trzech chodach, zachowując płynność chodów. Potrafiłem/am pokonać drążki i cavaletti w tych chodach podstawowych.', 301, ''),
(6968, 'Zaprezentowałem/am swojemu zastępowi zasady bezpieczeństwa obchodzenia się z końmi, wyjazdów w teren oraz rajdów konnych.', 301, ''),
(6969, 'Nauczyłem/am się jazdy w terenie otwartym i zadrzewionym z pokonywaniem przeszkód naturalnych oraz podjazdów i zjazdów, a także przekraczaniem przeszkód wodnych. Przy pokonywaniu przeszkód zachowywałem/am odpowiedni kontakt z koniem oraz dosiad w równowadze.', 301, ''),
(6970, 'Brałem/am udział w dwudniowym rajdzie konnym, podczas którego poznałem/am walory przyrodniczo-krajoznawcze w promieniu 30 km. W praktyce poznałem/am zasady oznaczeń szlaków konnych, rozróżniałem/am szlaki konne, piesze i rowerowe.', 301, ''),
(6971, 'Nauczyłem/am się podstawowych zasady z prawa o ruchu drogowym dotyczących poruszania się po drogach publicznych, nauczyłem/am się, jak przygotować konia do wyjazdu oraz jak zachować się w terenie i na postojach.', 302, ''),
(6972, 'Za pomocą dowolnej formy przedstawiłem/am i nauczyłem/am młodszych kolegów: jak koń się porusza (jak prawidłowo stawia nogi w różnych chodach), jakie są główne wady budowy koni.', 302, ''),
(6973, 'Prawidłowo stosowałem/am pomoce jeździeckie, jeździłem/am chodami zebranymi i wyciągniętymi, wykonywałem/am poprawnie wszystkie podstawowe figury na ujeżdżalni.', 302, ''),
(6974, 'Pokonywałem/am w równowadze przeszkody do 40 cm zarówno na ujeżdżalni, jak i w terenie.', 302, ''),
(6975, 'Brałem/am udział w rajdzie czterodniowym oraz w 4 wycieczkach konnych po różnych trasach o różnych celach krajoznawczych.', 302, ''),
(6976, 'Posiadłem/am wiedzę na temat różnych grup rekonstrukcyjnych, znalazłem/am informacje o tym, jakie grupy działają w okolicy mojego zamieszkania.', 303, ''),
(6977, 'Opowiedziałem/am o danej epoce historycznej związanej z zakresem rekonstrukcji – podstawowe daty, bitwy, wydarzenia, umundurowanie (ubiór), itp.', 303, ''),
(6978, 'Wskazałem/am, z jakich elementów składa się strój rekonstruktora w wybranej przeze mnie epoce.', 303, ''),
(6979, 'Poruszałem/am się konno w pełnym stroju w trzech chodach.', 303, ''),
(6980, 'Zorganizowałem/am pokaz jazdy dla swojej drużyny, opowiedziałem/am szczegółowo o danej epoce historycznej.', 304, ''),
(6981, 'Zaprezentowałem/am posługiwanie się dodatkowymi atrybutami historycznymi (broń biała, palna, lasso, itp.) związanymi z rekonstrukcją.', 304, ''),
(6982, 'Opowiedziałem/am o różnych rasach koni wykorzystywanych w danej rekonstrukcji.', 304, ''),
(6983, 'Odwiedziłem/am w swojej okolicy zamieszkania grupę rekonstrukcyjną, na zbiórce opowiedziałem/am, jak wygląda takie spotkanie oraz jakie warunki trzeba spełnić, aby zostać rekonstruktorem.', 304, ''),
(6984, 'Poznałem/am podstawowe zasady bezpieczeństwa pracy przy koniach. Stosowałem/am je w praktyce, a także w dowolnej formie przedstawiłem/am je swojemu zastępowi.', 305, ''),
(6985, 'Rozróżniłem/am stanowisko i boks, omówiłem/am ich wady i zalety, prawidłowo je pościeliłem/am.', 305, ''),
(6986, 'Dowiedziałem/am się, czym odżywia się koń. Wymieniłem/am podstawowe pasze objętościowe i treściwe. W obecności osoby odpowiedzialnej przygotowałem/am i zadałem/am paszę koniom w stajni.', 305, ''),
(6987, 'Prawidłowo złapałem/am konia, prowadziłem/am go, wprowadzałem/am i wyprowadzałem/am z boksu.', 305, ''),
(6988, 'Rozpoznałem/am na pastwisku, czy wszystkie rośliny są jadalne dla koni, wymieniłem/am 5 roślin trujących.  Sprawdziłem/am, czy siano i pasza treściwa są zdatne do zjedzenia dla konia (np. brak pleśni).', 306, ''),
(6989, 'Brałem/am udział w przygotowaniu pastwiska do wypasu: sprzątałem/am odchody po koniach, sprawdzałem/am, czy ogrodzenie jest całe, przygotowałem/am wraz z opiekunem miejsca poboru siana przez zwierzęta, sprawdziłem/am stan poideł, czyszcząc je.', 306, ''),
(6990, 'Prawidłowo założyłem/am derkę i owijki/ochraniacze stajenne. Rozróżniłem/am rodzaje derek i ochraniaczy, nauczyłem/am się, kiedy stosować każdy z rodzajów.', 306, ''),
(6991, 'Prawidłowo odczytałem/am mowę ciała konia (ogólny wygląd, ustawienie zadu, głowa, mowa nóg, mowa ogona). Na podstawie odczytanych sygnałów konia bezpiecznie podszedłem/podeszłam do konia. Sygnały mowy końskiej wskazałem/am zastępowi.', 306, ''),
(6992, 'Kierowałem/am pracą wachty stajennej na obozie lub w klubie.', 307, ''),
(6993, 'Przedstawiłem/am zastępowi/drużynie 5 zasad dobrostanu zwierząt, w swojej pracy z końmi zastosowałem/am te zasady w praktyce.', 307, ''),
(6994, 'Opisałem/am poprawne warunki utrzymania koni w stajni, posiłkując się najnowszym rozporządzeniem Ministra Rolnictwa i Rozwoju Wsi w sprawie minimalnych warunków utrzymywania zwierząt gospodarskich', 307, ''),
(6995, 'Przygotowałem/am dyskusję dla zastępu/drużyny o rodzajach systemu utrzymania koni, przedstawiłem/am zalety i wady każdego systemu.', 307, ''),
(6996, 'Rozróżniałem/am pojęcia chów i hodowla, przedstawiłem/am za pomocą wizualizacji (np. mapy myśli), jak prawidłowo definiować te pojęcia.', 308, ''),
(6997, 'Poznałem/am typy użytkowe i rasy koni. Opowiedziałem/am zastępowi o typach użytkowych koni, wymieniłem/am kilka ras koni, które należą do danych grup. Poznałem/am podział koni na gorącokrwiste, zimnokrwiste i prymitywne, podałem/am przykładowe rasy koni należące do konkretnego rodzaju.', 308, ''),
(6998, 'Wskazałem/am na koniu poszczególne elementy jego budowy, wymieniłem/am główne wady budowy u koni.', 308, ''),
(6999, 'Uczestniczyłem/am w pokazie hodowlanym koni.', 308, ''),
(7000, 'Wskazałem/am na koniach u siebie w stajni zadane maści i odmiany.', 308, ''),
(7001, 'Opowiedziałem/am zastępowi/drużynie o okresie trwania ciąży u konia, terminie przeprowadzania stanówki i odsadzaniu źrebiąt.', 309, ''),
(7002, 'Zaprezentowałem/am w dowolny sposób charakterystyczne cechy przedstawicieli koni hodowlanych w Polsce podzielonych na konie zimnokrwiste, gorącokrwiste, półkrwi oraz prymitywne.', 309, ''),
(7003, 'Poznałem/am, na jakiej podstawie ocenia się eksterier u koni, oceniłem/am budowę min. 3 koni, wskazując prawidłowości i wady postawy.', 309, ''),
(7004, 'Uczestniczyłem/am w próbie dzielności koni i wiem, na czym polega ocena wartości hodowlanej.', 309, ''),
(7005, 'Opisałem/am poszczególne etapy rozwoju konia od źrebaka do konia w podeszłym wieku, zwracałem/am uwagę na stan psychiczny i fizyczny. Odróżniłem/am na pastwisku, czy koń jest młody czy w podeszłym wieku.', 309, ''),
(7006, 'Prawidłowo korzystałem/am z Ksiąg Stadnych, wyszukałem/am konkretnego konia po ustalonych wcześniej założeniach. Wykonałem/am teoretyczny rodowód konia do 3 pokoleń wstecz.', 310, ''),
(7007, 'Rozpoznałem/am  narowy i złe nawyki u koni. Potrafiłem/am je wskazać oraz im zapobiegać.', 310, ''),
(7008, 'Korzystałem/am ze strony bazakoni.pl , stworzyłem/am na niej przykładowy rodowód, zwracając szczególną uwagę na imbred. Wytłumaczyłem/am, co to jest imbred, czemu tak ważne jest jego obliczanie i kontrola w hodowli.', 310, ''),
(7009, 'Prawidłowo oceniałem/am wiek trzech różnych (wiekowo) koni na podstawie obecności rejestrów i kształtu powierzchni trącej siekaczy w szczęce górnej i żuchwie.', 310, ''),
(7010, 'Poznałem/am zasady przyznawania klas ogierom rozpłodowym, przygotowałem/am na ten temat zajęcia dla zastępu.', 310, ''),
(7011, 'Zaprojektowałem/am i postawiłem/am rozbudowaną stronę internetową w dowolnej technologii z możliwością logowania się oraz aktywnego działania przez odwiedzających (komentarze, polubienia, itp.)', 311, ''),
(7012, 'Stworzyłem/am aplikację w wybranej technologii back-end (PHP, .NET, Java, Python itp.).', 311, ''),
(7013, 'Poznałem/am sposoby zabezpieczania stron internetowych. Zapoznałem/am się z protokołami HTTP oraz HTTPS. Zabezpieczyłem/am swoją stronę internetową/aplikację webową protokołem HTTPS.', 311, ''),
(7014, 'Stworzyłem/am prostą aplikację webową w wybranym języku backendowym z wykorzystaniem relacyjnych baz danych (np. MySQL, PostgreSQL, SQLite, Firebird).', 312, ''),
(7015, 'Stworzyłem/am stronę WWW z zawartością dynamiczną, wykorzystując języki HTML i JavaScript oraz arkusze stylów CSS z zachowaniem zasad RWD.', 312, ''),
(7016, 'Postawiłem/am serwer WWW na komputerze lub maszynie wirtualnej, zabezpieczyłem/am go i udostępniłem/am w sieci.', 312, ''),
(7017, 'Poznałem/am podstawy algorytmów. Stworzyłem/am schemat blokowy na podstawie wybranego przez siebie procesu (np. proces zbierania i opłacania składek, proces decyzyjny w przypadku udzielania pierwszej pomocy, itp.).', 313, ''),
(7018, 'Zapoznałem/am się z definicją rekurencji oraz iteracji. Stworzyłem/am algorytm zawierający iterację i rekurencję.', 313, ''),
(7019, 'Napisałem/am prosty program w dowolnym języku programowania, który wyświetla tekst, wykonuje proste operacje oraz umożliwia wprowadzenie danych, np. z klawiatury.', 313, ''),
(7020, 'Napisałem/am użyteczny w pracy harcerskiej program, wykorzystując wybrany paradygmat programowania (proceduralny, obiektowy, funkcyjny, logiczny itd.). Zaprezentowałem/am go wybranej grupie docelowej (zastępowi, zespołowi kwatermistrzowskiemu, zespołowi promocji itp.) i udostępniłem/am do wykorzystywania. Uwzględniłem/am informację zwrotną, ulepszając program.', 314, ''),
(7021, 'Napisałem prosty program w języku programowania innym niż wykorzystany w zadaniu 1.', 314, ''),
(7022, 'Zapoznałem/am się z wybranym systemem kontroli wersji, np. Git. Utworzyłem/am profil na platformie pozwalającej na prowadzenie zdalnej kontroli wersji (np. GitHub). Umieściłem/am na niej co najmniej jeden swój program.', 314, ''),
(7023, 'Stworzyłem lub istotnie ulepszyłem dokumentację programu przeznaczoną dla użytkownika.', 314, ''),
(7024, 'Napisałem/am złożony program pomocny w pracy harcerskiej, wykorzystując wybrany framework (np. .NET, Symphony, Flask) w celu uniknięcia konieczności samodzielnego implementowania powszechnie wykorzystywanych funkcjonalności.', 315, ''),
(7025, 'Napisałem/am użyteczny w pracy harcerskiej program, wykorzystując wybrany paradygmat programowania (proceduralny, obiektowy, funkcyjny, logiczny itd.) inny niż w przypadku zadania 1.', 315, ''),
(7026, 'Pisząc program, użyłem bibliotek rozszerzeń spoza standardu wykorzystanego języka (np. systemy kolejkowania, bazy NoSQL).', 315, ''),
(7027, 'Zaprezentowałem/am stworzony program wybranej grupie docelowej (zastępowi, zespołowi kwatermistrzowskiemu, zespołowi promocji itp.) i udostępniłem/am do wykorzystywania. Uwzględniłem/am informację zwrotną, ulepszając program.', 315, ''),
(7028, 'Poznałem/am budowę komputera (stacjonarnego lub laptopa). Zapoznałem/am się z najważniejszymi podzespołami komputera (zasilacz, płyta główna, pamięć, karta graficzna, procesor, itp.) i wiem, do czego służą. Sprawdziłem konfigurację używanego komputera, wykorzystując funkcjonalność systemu operacyjnego lub narzędzia dodatkowe. Przygotowałem/am opis swojego komputera, prezentując w nim najważniejsze informacje o każdym istotnym jego podzespole.', 316, ''),
(7029, 'Zapoznałem/am się z najważniejszymi pojęciami dotyczącymi sprzętu komputerowego. Zapoznałem/am się z rodzajami pamięci (RAM, ROM, pamięć masowa, cache), rodzajami pamięci masowej (HDD, SSD, pamięci Flash itp.). Doradziłem/am znajomemu/znajomej dobór odpowiedniej pamięci masowej dopasowany do jego/jej potrzeb.', 316, ''),
(7030, 'Zapoznałem/em się z podstawowymi rodzajami złącz (HDMI, DisplayPort, USB 2.0, USB 3.0, DVI, D-SUB itd.).  Podłączyłem/am i skonfigurowałem/am dodatkowy monitor, drukarkę oraz inne dowolne urządzenie peryferyjne.', 316, ''),
(7031, 'Zapoznałem/am się z podstawowymi oznaczeniami najbardziej znanych modeli procesorów. Ręcznie zmieniłem/am taktowanie procesora w komputerze (np. w BIOS/UEFI), zaobserwowałem efekty (stabilność, szybkość działania, zużycie energii, temperatura procesora) i wyjaśniłem/am je.', 318, ''),
(7032, 'Zapoznałem/am się z rodzajami kart graficznych i ich oznaczeniami oraz wiem, do czego mogą zostać one wykorzystane. Wyjaśniłem parametry techniczne wybranej karty graficznej. Dobrałem/am kartę graficzną dopasowaną do wskazanych potrzeb, uwzględniając określony budżet (np. kartę przystosowaną do obróbki grafiki, gier, kryptowalut, itp.).', 318, ''),
(7033, 'Podłączyłem/am w komputerze stacjonarnym dodatkową kartę (np. graficzną), pamięć RAM oraz dysk pamięci masowej.', 318, ''),
(7034, 'Poznałem/am zależności wynikające ze współpracy poszczególnych podzespołów komputerowych. Złożyłem/am komputer stacjonarny od podstaw, samodzielnie montując takie rzeczy jak: płyta główna, procesor, karta graficzna, pamięć RAM oraz inne konieczne podzespoły.', 320, ''),
(7035, 'Zaprojektowałem/am zestaw komputerowy lub komputer przenośny spełniający wskazane wymagania i mieszczący się w określonym (realnym) budżecie. Dobrałem podzespoły (CPU, RAM, pamięć masową, karty rozszerzeń itd.), biorąc pod uwagę parametry współpracujących elementów (np. taktowanie pamięci) i dostępne do wykorzystania złącza.', 320, ''),
(7036, 'Poznałem/am budowę przenośnych komputerów osobistych (laptopów, notebooków, ultrabooków, itp.). Rozłożyłem/am laptopa i wykonałem/am co najmniej jedno z zadań:\nWyczyściłem/am podzespoły komputera, wymieniłem/am pastę termoprzewodzącą pod procesorem i/lub kratą graficzną;\nWymieniłem/am w nim dysk i/lub pamięć RAM;\nWymieniłem/am matrycę wyświetlacza.\nPo wykonaniu tych działań, prawidłowo złożyłem/am komputer z powrotem.', 320, ''),
(7037, 'Dowiedziałem/am się, na czym polega druk 3D i jakie może mieć zastosowania. Skonfigurowałem/am drukarkę do prostego wydruku. Dowiedziałem/am się, czym są filament, hotend, extruder.', 321, ''),
(7038, 'Poznałem/am dowolny program do modelowania trójwymiarowego (np. Skrimarket Playground, Tinkercad). Wykonałem/am w nim model przedmiotu, który może się przydać w mojej drużynie (np. element wyposażenia harcówki albo harcerską ozdobę).', 321, ''),
(7039, 'Dowiedziałem/am się, jakie materiały mogą zostać wykorzystane jako filament. Sprawdziłem/am, które z dostępnych filamentów są produkowane z surowców wtórnych.', 321, ''),
(7040, 'Zaprojektowałem/am i uszyłem/am solidny i estetyczny element ubioru (bluzkę, spodnie, spódnicę) lub akcesorium (torbę, plecak itp.), które następnie wykorzystywałem/am w codziennym życiu.', 91, ''),
(7041, 'Przez minimum tydzień podjąłem/am się dodatkowego obowiązku, ustalonego z rodzicami, na rzecz domu, np. zmywałem/am naczynia, wynosiłem/am śmieci.', 122, ''),
(7042, 'W czasie próby segregowałem/am śmieci. Jeśli dotąd go nie było, zorganizowałem/am w domu miejsce na różne rodzaje surowców wtórnych.', 122, ''),
(7043, 'Zabezpieczyłem/am przed zepsuciem się różne artykuły spożywcze, np. wędliny, masło, pieczywo, płatki itp.', 122, ''),
(7044, 'Poodkurzałem/am całe mieszkanie, stosując właściwy sprzęt i środki do podłóg, mebli i dywanów.', 122, ''),
(7045, 'Poznałem/am alfabet polskiego języka migowego. Przeliterowałem/am swoje imię i nazwisko.', 324, ''),
(7046, 'Wyjaśniłem/am zainteresowanej osobie różnicę między polskim językiem migowym a systemem językowo-migowym.', 324, ''),
(7047, 'Poznałem/am podstawowe zwroty w polskim języku migowym. Przywitałem/am się i pożegnałem/am, przedstawiłem/am się.', 324, ''),
(7048, 'Zademonstrowałem/am 20 znaków w polskim języku migowym, w tym przynajmniej 5 znaków o tematyce harcerskiej. Zamigałem/am 3 zdania dotyczące mego harcerskiego życia.', 324, ''),
(7049, 'Zapoznałem/am się z kulturą i tradycją Głuchych. Wyjaśniłem/am zainteresowanej osobie, co oznacza znak „ILY” oraz symbol motyla w kulturze Głuchych.', 324, ''),
(7050, 'Poznałem/am metody badawcze, potrafię odróżnić metody ilościowe i jakościowe oraz właściwie dobierać je do badanej cechy. Rozumiem, czym jest próba reprezentatywna, pozyskałem/am wiadomości o metodach jej doboru. Przeprowadziłem/am badania na temat ważny dla mojego środowiska harcerskiego, prezentując wyniki w formie raportu.', 325, ''),
(7051, 'Przeprowadziłem/am lub wspierałem/am wydarzenie nakierowane na poszerzenie wiedzy w wybranym zakresie (np. noc naukową, wszechnicę, debatę naukową).', 325, ''),
(7052, 'Oceniłem/am, czy próba badawcza przedstawiona w artykule lub opisie badań była reprezentatywna i w jakim stopniu na jej podstawie można rozszerzyć wnioski z badania na różne populacje.', 325, ''),
(7053, 'Wiem, czym są uprzedzenia poznawcze oraz modele mentalne, nauczyłem/am innych harcerzy korzystania z moich ulubionych modeli.', 325, ''),
(7054, 'Przeprowadziłem/am wywiad z rodzicami na temat ich rodziców i rodzeństwa. Na tej podstawie stworzyłem/am proste drzewo genealogiczne uwzględniające najbliższych krewnych.', 326, ''),
(7055, 'W albumie rodzinnym (lub elektronicznym zbiorze zdjęć) odnalazłem/am co najmniej 3 zdjęcia wykonane przed moim urodzeniem. Opisałem/am dokładnie, kto się na nich znajduje i jakiego wydarzenia dotyczą.', 326, ''),
(7056, 'Zdobyłem/am informacje na temat krewnych z mojego drzewa genealogicznego: kiedy się urodzili, gdzie się urodzili/wychowali, gdzie pracowali/uczyli się.', 326, ''),
(7057, 'Odtworzyłem/am drzewo genealogiczne swojej rodziny przynajmniej trzy pokolenia wstecz, korzystając np. z dokumentów rodzinnych.', 327, ''),
(7058, 'Wykonałem/am co najmniej jedno zadanie:\nUporządkowałem/am album rodzinny - opisałem/am zdjęcia i ułożyłem/am je chronologicznie.\nZe zdjęć rodziny zrobiłem/am prezentację multimedialną i pokazałem/am na uroczystości rodzinnej.', 327, ''),
(7059, 'Prowadziłem/am kronikę rodziny. Zapisałem/am w niej np. kilka ciekawych rodzinnych przygód, wywiady przeprowadzone ze starszymi członkami rodziny, słowniczek pierwszych słów młodszego rodzeństwa.', 327, ''),
(7060, 'Drzewo genealogiczne swojej rodziny (co najmniej trzy pokolenia wstecz) uzupełniłem/am o opis i w miarę możliwości zdjęcie każdej osoby umieszczonej na nim.', 328, ''),
(7061, 'Poznałem/am najważniejsze wydarzenia z historii mojej rodziny. Umieściłem/am je na osi czasu razem z najważniejszymi wydarzeniami z ostatnich 100 lat historii Polski.', 328, ''),
(7062, 'Uporządkowałem/am dokumentację i pamiątki rodzinne (świadectwa, dyplomy, listy, pamiątki). Najciekawsze z nich pokazałem/am znajomym/członkom zastępu/drużyny.', 328, ''),
(7063, 'Współorganizowałem/am i uczestniczyłem/am w rodzinnej wycieczce do miejsc ważnych dla rodziny (np. miejscowość, z której pochodzą przodkowie, kościół, w którym brali ślub, miejsce, gdzie poznali się rodzice, miejsce walki, w której uczestniczył ktoś z rodziny).', 328, ''),
(7064, 'Rozbudowałem/am drzewo genealogiczne swojej rodziny do przynajmniej czterech pokoleń wstecz.', 329, ''),
(7065, 'Wykonałem/am co najmniej jedno zadanie:\nWykorzystałem/am jeden z serwisów umożliwiający budowę drzewa genealogicznego do umieszczenia swojego w Internecie. Zaprosiłem/am innych członków rodziny do korzystania z niego.\nOpracowałem/am swoje drzewo genealogiczne w bogatej oprawie graficznej.', 329, ''),
(7066, 'Wykonałem/am co najmniej jedno zadanie:\nSkorzystałem/am z zasobów archiwum państwowego, kościelnego lub prywatnego, aby uzupełnić wiedzę zawartą w moim drzewie genealogicznym.\nSkorzystałem/am z zasobów internetowych, aby uzupełnić wiedzę zawartą w moim drzewie genealogicznym.', 329, ''),
(7067, 'Sporządziłem/am listę stron w Internecie przydatnych w pracach genealogicznych. Udostępniłem/am ją osobom zainteresowanym opracowaniem własnego drzewa genealogicznego.', 329, ''),
(7068, 'Dowiedziałem/am się, czym jest etnografia. Uzasadniłem/am, czemu badanie i zachowywanie kultury jest ważne.', 330, ''),
(7069, 'Poznałem/am kilka grup etnograficznych np. Kaszubów, Ślązaków, Podhalan, Mazurów. Dowiedziałem/am się, czym się wyróżniają i gdzie mieszkają.', 330, ''),
(7070, 'Byłem/am w skansenie lub muzeum etnograficznym. Porównałem/am wiedzę tam zdobytą z wcześniej poznaną grupą etnograficzną. Opowiedziałem/am o swoich wrażeniach zastępowi.', 330, ''),
(7071, 'Odszukałem/am elementy różnorodnego dziedzictwa kulturowego (np. język, architektura, organizacje społeczne, sztuka, elementy stroju) w Polsce z kręgu co najmniej czterech kultur, np. żydowskiej, niemieckiej, czeskiej, ukraińskiej, litewskiej.', 331, ''),
(7072, 'Odwiedziłem/am przynajmniej jedno miejsce/wydarzenie związane z kulturą ludową (np. skansen, muzeum, wydarzenie związane z doroczną obrzędowością), dowiadując się czegoś o sposobie życia i zwyczajach ludzi. Wyniki zaprezentowałem/am na zbiórce.', 331, ''),
(7073, 'Wykonałem/am określoną czynność metodą tradycyjną, np. haft, wyplecenie koszyka, wyrób masła.', 331, ''),
(7074, 'Rozmawiałem/am z etnologiem/antropologiem kultury na temat różnorodności kulturowej Polski i jej poznawania.', 331, ''),
(7075, 'Przeprowadziłem/am zwiad etnograficzny, odwiedzając wybraną społeczność i dowiadując się o jej sposobie życia.', 332, ''),
(7076, 'Zapoznałem/am się z gwarą/dialektem wybranego regionu lub językiem mniejszości zamieszkującej na tym terenie. Nauczyłem/am się wiersza lub piosenki w wybranej gwarze/dialekcie/języku i rozumiem o czym jest utwór.', 332, ''),
(7077, 'Dowiedziałem/am się, czym jest kultura i jakie są jej przejawy w sferze materialnej (np. strój, zdobnictwo) i symbolicznej (np. język, gesty, zwyczaje).', 332, ''),
(7078, 'Przeprowadziłem/am zajęcia dla wędrowników na temat kultury, jej poznawania i rozumienia.', 332, ''),
(7079, 'Nauczyłem/am się rozróżniać kilka rodzajów skał. Zaprezentowałem/am członkom zastępu swoje zbiory skał i minerałów.', 333, ''),
(7080, 'Skompletowałem/am sprzęt do prac terenowych związanych ze zbieraniem okazów skał i minerałów (m.in. młotek, dłuto, łopatka, lupa).', 333, ''),
(7081, 'Podczas rajdu/wycieczki drużyny wskazałem/am 3 charakterystyczne formy terenu, wraz z komentarzem na temat ich pochodzenia (formy erozji/akumulacji rzecznej, lodowcowej, eolicznej).', 333, ''),
(7082, 'Zlokalizowałem/am i odwiedziłem/am w swoim regionie min. 3 stanowiska geologiczne różnego typu. Przedstawiłem/am dokumentację zdjęciową na zbiórce drużyny lub zastępu, opowiadając o tym, jaką wiedzę o historii geologicznej regionu można czerpać z obserwacji danego stanowiska.', 334, ''),
(7083, 'Zalogowałem/am się w przynajmniej jednej skrytce geologicznej EarthCache ().', 334, ''),
(7084, 'Odwiedziłem/am przynajmniej 3 z wybranych miejsc:\nteren nadmorski (wydmowy lub klifowy),\nmeandry rzeczne lub pradolinę rzeki,\nformy krasowe (np. jaskinie)\nformy skalne,\nnieczynny, udostępniony kamieniołom lub kopalnię,\ngeopark lub Jurapark.', 334, ''),
(7085, 'Zgromadziłem/am własną, skatalogowaną kolekcję skał i minerałów lub skamieniałości. Zaprezentowałem/am ją na wystawie lub w Internecie.', 334, ''),
(7086, 'Zaplanowałem/am i odbyłem/am 2-dniową wycieczkę geologiczną po wybranym regionie, uwzględniającą ciekawe stanowiska geologiczne oraz możliwości pozyskania okazów skał i minerałów lub formy ochrony przyrody nieożywionej. Folder opisujący wycieczkę zilustrowałem/am wykonanymi samodzielnie zdjęciami.', 335, ''),
(7087, 'Przygotowałem/am prezentację dla zastępu/drużyny na temat wybranego zagadnienia geologicznego.', 335, ''),
(7088, 'Założyłem/am własną geologiczną skrytkę EarthCache ().', 335, ''),
(7089, 'Na zbiórce zastępu przedstawiłem/am święta, symbole, wydarzenia i najważniejsze postacie judaizmu, islamu, chrześcijaństwa wschodniego i zachodniego.', 336, ''),
(7090, 'Pomogłem/am zorganizować wycieczkę zastępu lub drużyny do świątyni wyznawców wybranej religii, wyznania lub obrządku (innej niż większości uczestników) i przedstawiłem/am informacje o niej. Przygotowałem/am członków zastępu lub drużyny pod względem właściwego ubioru i zachowania.', 336, ''),
(7091, 'Na podstawie materiałów udostępnionych przez wybrany kościół lub związek wyznaniowy zarejestrowany w Polsce przygotowałam/em prezentację na temat podobieństw i różnic w zakresie głównych zasad wiary i zwyczajów pomiędzy nim a rzymskim katolicyzmem.', 336, ''),
(7092, 'Rozmawiałem/am (np. na zlocie) lub korespondowałem/am ze skautem/skautką z zagranicy o tym, w jaki sposób spędza czas w „święty dzień” (np. niedzielę, sobotę, piątek).', 336, ''),
(7093, 'Na zbiórce zastępu w ciekawej formie (np. gra, quiz, turniej wiedzy) przedstawiłem/am historię i okoliczności powstania największych religii świata: ich wspólne wydarzenia, postacie i miejsca.', 337, ''),
(7094, 'W odniesieniu do 3 różnych związków wyznaniowych działających w Polsce: odszukałem/am najważniejsze informacje dotyczące tego, kim jest i ile jest osób boskich, jak powstał świat, skąd się wziął człowiek. Dowiedziałem/am się, gdzie są największe ośrodki wyznawców, jaka jest organizacja kościoła/wspólnoty wyznaniowej – czy jest hierarchiczny i jakie są najważniejsze zwyczaje i zasady działania. Zrobiłem/am to, realizując jedno z poniższych zadań:\n-Zapoznałem/am się ze stronami internetowymi trzech różnych związków wyznaniowych zarejestrowanych w Polsce, w tym co najmniej jednego należącego do religii niechrześcijańskiej (np. Związek Gmin Wyznaniowych Żydowskich, Muzułmański Związek Religijny w RP).\n-Wziąłem/wzięłam udział w Nocy Świątyń lub innym wydarzeniu, podczas którego odwiedziłem/am minimum 3 świątynie, wysłuchałem/am wykładu oraz rozmawiałem/am wyznawcami różnych wyznań/religii.', 337, ''),
(7095, 'Poznałem/am współczesne inicjatywy z zakresu dialogu międzyreligijnego i ekumenizmu. Przedstawiłem/am je koleżankom i kolegom z zastępu lub drużyny, wskazując najważniejsze zagadnienia i wyzwania w prowadzeniu dialogu.', 337, ''),
(7096, 'Brałem/am udział w dyskusji o wartościach na zbiórce zastępu lub drużyny, którą zainicjowałem/am przedstawieniem w ciekawej formie (np. gawęda, scenka) historii zainspirowanej dwiema opowieściami lub mitami z wierzeń kultur współczesnych i minionych.', 337, ''),
(7097, 'Poznałem/am główne dogmaty wiary i święta trzech największych religii świata (judaizm, chrześcijaństwo, islam). Wziąłem/wzięłam udział w święcie innego wyznania lub religii niż własna (np. na otwartych spotkaniach, podczas festiwali, u znajomych).', 338, ''),
(7098, 'Poznałem/am kilka największych wyznań w obrębie jednej wybranej religii monoteistycznej. Przeanalizowałem/am je pod względem różnic w zakresie prawd i zasad wiary, sposobów jej praktykowania, zasad organizacyjnych, struktury kościołów/związków wyznaniowych. Wziąłem/wzięłam udział w nabożeństwie przynajmniej jednego z nich, przygotowując się wcześniej pod kątem znaczenia każdego z elementów nabożeństwa.', 338, ''),
(7099, 'Przeprowadziłem/am w zastępie lub w drużynie dyskusję na temat tolerancji religijnej (m.in. idee, skutki braku tolerancji w społeczeństwie, potencjalne trudności). Wykorzystałem/am najważniejsze dokumenty na temat dialogu międzyreligijnego i ekumenizmu (np. Deklarację o stosunku Kościoła katolickiego do religii niechrześcijańskich, Deklarację o wolności religijnej, Wspólną Deklarację o Usprawiedliwieniu). Przygotowałem/am podsumowanie dyskusji dla jej uczestników i uczestniczek, uwzględniając różne opinie.', 338, ''),
(7100, 'Poznałem/am historię i zakres większych zmian dotyczących obrzędów, praktyk religijnych, świąt, zwyczajów i sposobu rozumienia wartości, jaką reprezentuje wybrana religia lub wyznanie. Podjąłem/podjęłam refleksję na temat ich źródeł, w tym uwarunkowań kulturowych i roli lokalnych tradycji. Przeprowadziłem/am w szczepie lub hufcu warsztaty lub dyskusję na ten temat.', 338, ''),
(7101, 'Rozpoznałem/am symbole i nazwy świętych ksiąg islamu, judaizmu i chrześcijaństwa. Dowiedziałem/am się, jak nazywa się Bóg w każdej z tych religii.', 339, ''),
(7102, 'Odwiedziłem/am (np. z rodzicami lub zastępem) świątynię, miejsce modlitwy lub cmentarz należące do innej religii lub wyznania niż moje własne.  Zastosowałem/am się do zasad ubioru i zachowania w miejscu kultu lub podczas ceremonii religijnej.', 339, ''),
(7103, 'Na zbiórce zastępu lub drużyny opowiedziałam/em lub pokazałam/em wybrany zwyczaj związany z Hanuką lub Ramadanem bądź innym dużym świętem religijnym religii innej niż chrześcijaństwo.', 339, ''),
(7104, 'Zaprezentowałem/am modlitwę lub piosenkę religijną innego niż moje własne wyznania lub religii.', 339, ''),
(7105, 'Przez co najmniej miesiąc zbierałem/am wybrane przedmioty i przechowywałem/am je w bezpiecznym miejscu tak, aby nie uległy zniszczeniu.', 340, ''),
(7106, 'Pogłębiłem/am wiedzę na temat wybranego przedmiotu z mojej kolekcji. Zrobiłem/am jego zdjęcie i opis.', 340, ''),
(7107, 'Przedstawiłem/am swoją kolekcję na zbiórce zastępu. Podzieliłem/am się z zastępem swoją wiedzą na temat zbioru.', 340, ''),
(7108, 'Stworzyłem/am własną kolekcję wybranych przedmiotów lub systematycznie powiększałem/am kolekcję, którą otrzymałem/am.', 341, ''),
(7109, 'Opracowałem/am system katalogowania własnych zbiorów i posługiwałem/am się nim, uzupełniając swoją kolekcję.', 341, ''),
(7110, 'Nawiązałem/am kontakt z co najmniej jedną osobą, posiadającą kolekcję zbliżoną do mojej. Wymieniłem/am się doświadczeniami dotyczącymi powiększania kolekcji, jej przechowywania i prezentowania itp.', 341, ''),
(7111, 'Zbudowałem/am zadbaną i wyróżniającą się w moim najbliższym otoczeniu kolekcję.', 342, ''),
(7112, 'Pozyskałem/am wiedzę na temat kolekcjonowanych przedmiotów. Stworzyłem/am zestawienie źródeł (książki, miejsca internetowe itp.), z których czerpię wiedzę na temat przedmiotów, które kolekcjonuję.', 342, ''),
(7113, 'Zorganizowałem/am zajęcia/warsztaty/prelekcję na temat zbieranych eksponatów.', 342, ''),
(7114, 'Zaprezentowałem/am najciekawsze elementy mojej kolekcji na wystawie lub w Internecie.', 342, ''),
(7115, 'Pozyskałem/am co najmniej jeden rzadki przedmiot do swojej kolekcji.', 343, ''),
(7116, 'Skompletowałem/am specjalistyczne publikacje na temat kolekcjonowanych przedmiotów.', 343, ''),
(7117, 'Przedstawiłem/am swoją kolekcję na zbiórce drużyny lub innym forum harcerskim. Podzieliłem/am się z innymi harcerzami/harcerkami wiedzą o korzyściach, jakie kolekcjonowanie daje mi w życiu codziennym.', 343, ''),
(7118, 'Nawiązałem/am kontakty ze społecznością kolekcjonerów. Brałem/am udział w spotkaniach/dyskusjach na forach/wystawach itp.', 343, ''),
(7119, 'Stworzyłem/am listę książek, które chciałbym/chciałabym przeczytać. Przeczytałem/am co najmniej dwie książki z mojej listy.', 344, ''),
(7120, 'Znalazłem/am książkę, którą polubiłem/am. Zachęciłem/am do jej przeczytania członków mojego zastępu lub kolegów/koleżanki z klasy.', 344, ''),
(7121, 'Samodzielnie wykonałem/am kilka zakładek do swoich książek.', 344, ''),
(7122, 'Zrobiłem/am spis alfabetyczny posiadanych książek.', 344, ''),
(7123, 'Przeczytałem/am książki z 3 różnych gatunków literatury, w tym przynajmniej jedną popularnonaukową.', 345, ''),
(7124, 'Zapisałem/am się do lokalnej biblioteki. Poznałem/am obowiązujące w niej zasady i się do nich stosuje. Regularnie (co najmniej raz w miesiącu) odwiedzałem/am bibliotekę i wypożyczałem/am książki.', 345, ''),
(7125, 'Wykonałem/am co najmniej jedno zadanie:\nWykonałem/am drobne naprawy introligatorskie kilku książek.\nPełniłem/am dyżur w bibliotece szkolnej lub publicznej.', 345, ''),
(7126, 'Sporządziłem listę książek, które chciałbym/chciałabym mieć w swojej biblioteczce (książek w wersji drukowanej lub/i elektronicznej). Zakupiłem je za własne oszczędności lub poprosiłem o sprezentowanie przez rodzinę/znajomych.', 345, ''),
(7127, 'Śledziłem/am nowości wydawnicze w wybranej dziedzinie i przeczytałem/am przynajmniej 2 z nich.', 346, ''),
(7128, 'Wzbogaciłem/am swoją prywatną biblioteczkę. Choć jedną książkę do mojej biblioteczki znalazłem /am w antykwariacie.', 346, ''),
(7129, 'Wykonałem/am co najmniej jedno zadanie:\nStworzyłem/am ex libris biblioteczki swojej lub harcerskiej. Umieściłem/am go w książkach moich lub należących do mojego środowiska harcerskiego.\nProwadziłem/am w czasie próby na sprawność bibliotekę drużyny (szczepu) lub obozu.', 346, ''),
(7130, 'Na podstawie wybranej książki o charakterze poradnikowym/naukowym itp. Wykonałem/am jedno z poniższych zadań:\n-Przygotowałem/am dla osoby prowadzącej zajęcia materiały na określony temat.\n-Przygotowałem/am miniporadnik na wybrany temat.', 346, ''),
(7131, 'Przedstawiłem/am i uzasadniłem/am propozycje książek, które warto dodać do biblioteczki drużyny lub przeczytać w ramach prób na stopnie.', 347, ''),
(7132, 'Wykonałem/am jedno z zadań:\nUrządziłem/am w drużynie/szkole itp. wieczór wspólnego czytania wybranej książki, spotkanie z autorem lub podobną imprezę literacką.\nUrządziłem/am w drużynie/szkole itp. wystawę ciekawych książek, prezentując na niej także własne zbiory.', 347, ''),
(7133, 'Wykonałem/am jedno z zadań:\nNawiązałem/am kontakt z antykwariatem, poznałem/am sposoby gromadzenia wydawnictw antykwarycznych i przechowywania oraz zabezpieczania starodruków.\nNawiązałem/am kontakt z muzeum lub archiwum, poznałem/am sposoby gromadzenia, przechowywania oraz zabezpieczania starodruków.', 347, ''),
(7134, 'Stworzyłem/am analogowy lub cyfrowy katalog swojego księgozbioru.', 347, ''),
(7135, 'Prowadziłem/am w okresie próby kronikę (zastępu, drużyny lub prywatną), posługując się kilkoma rodzajami pisma.', 348, ''),
(7136, 'Sporządziłem/am notatkę z wydarzeń na podstawie relacji świadków.', 348, ''),
(7137, 'Przez czas trwania próby dbałem/am o to, by kronika była zawsze tam, gdzie drużyna.', 348, ''),
(7138, 'Zapoznałem/am się z twórczością wybranego polskiego kronikarza.', 348, ''),
(7139, 'Zagrałem/am w 5 różnych gier stolikowych z rodziną, zastępem lub znajomymi.', 349, ''),
(7140, 'Na zbiórce zastępu zaproponowałem/am grę dobraną odpowiednio do wieku i doświadczenia graczy. Wyjaśniłem/am zasady i poprowadziłem/am rozgrywkę.', 349, ''),
(7141, 'Zagrałem/am w co najmniej jedną grę planszową lub karcianą o tematyce harcerskiej lub historycznej.', 349, ''),
(7142, 'Naprawiłem/am i zakonserwowałem/am grę stolikową (naprawa pudełka, wsadzenie kart w koszulki, uzupełnienie brakujących elementów).', 349, ''),
(7143, 'Poznałem/am przynajmniej jedną grę na refleks, strategię, rozwijającą logiczne myślenie, zdolności ekonomiczne, planowania, pamięć, wiedzę, kreatywne myślenie (mogą się pokrywać). Zaprezentowałem/am te gry, wplatając je w prace mojego zastępu.', 350, ''),
(7144, 'Wziąłem/wzięłam udział w turnieju gier planszowych lub karcianych.', 350, ''),
(7145, 'Zorganizowałem/am miniturniej gier stolikowych w zastępie lub drużynie.', 350, ''),
(7146, 'Znam przynajmniej jeden portal / blog / vlog o grach stolikowych.', 350, ''),
(7147, 'Zorganizowałem/am turniej gier stolikowych w dowolnym środowisku lub pracowałem/am na targach gier stolikowych, wyjaśniając gry.', 351, ''),
(7148, 'Poznałem/am mechaniki stosowane w grach i potrafię je zidentyfikować w grach, w które gram. Na zbiórce drużyny zaprezentowałem/am historię, twórców oraz rodzaje gier planszowych i karcianych.', 351, ''),
(7149, 'Zorganizowałem/am w swoim środowisku (drużyna, szkoła, kółko zainteresowań) akcję promującą gry planszowe i karciane. Zorganizowałem/am turniej gier w systemie szwajcarskim.', 351, ''),
(7150, 'Samodzielnie lub wraz z zespołem stworzyłem/am prostą grę planszową lub karcianą o tematyce harcerskiej i upowszechniłem/am ją w środowisku harcerskim.', 351, ''),
(7151, 'Określiłem/am potrzebę edukacyjną, docelową grupę wiekową i zakres wiedzy, który można przekazać za pomocą gry. Odpowiednio dobrałem/am mechanikę do projektowanej gry i stworzyłem/am fabułę.', 352, '');
INSERT INTO `zadaniasprawnosciharcerskich` (`ID`, `Tresc`, `Sprawnosc`, `Stopien`) VALUES
(7152, 'Zapoznałem/am się z materiałami o projektowaniu gier - np. Laboratorium Gier. Stworzyłem/am prototyp gry w kilku egzemplarzach i przeprowadziłem/am testy. Na bazie uzyskanej informacji zwrotnej poprawiłem/am grę i przeprowadziłem/am kolejną serię testów. Cykl powtarzałem/am do momentu, gdy gra stała się grywalna (uzyskała dobre oceny od testujących i w większości określali ją jako atrakcyjną).', 352, ''),
(7153, 'Stworzoną grę zaproponowałem/am wydawnictwu lub spopularyzowałem/am samodzielnie (np. upubliczniając materiały do samodzielnego wydruku).', 352, ''),
(7154, 'Razem z mistrzem gry stworzyłem/am swoją postać w dowolnym systemie RPG.', 353, ''),
(7155, 'Uczestniczyłem/am w kampanii RPG, przechodząc wraz z drużyną cały scenariusz.', 353, ''),
(7156, 'Grając w RPG wczułem/am się w swoją postać i odpowiednio ją odgrywałem/am, reagując na to, co działo się w rozgrywce. Uczestnicząc w grze, podejmowałem/am przemyślane strategiczne decyzje, przewidując ich skutki dla swojego bohatera.', 353, ''),
(7157, 'Poznałem/am jeden system RPG. Współtworzyłem/am jej klimat swoją grą aktorską, dobieraniem rekwizytów i interakcją ze światem gry.', 354, ''),
(7158, 'Jako Mistrz/yni Gry poprowadziłem/am krótki scenariusz (np. dla zastępu).', 354, ''),
(7159, 'Dowiedziałem/am się, jakie są najpopularniejsze systemy i settingi rozgrywki. Poznałem/am i zagrałem/am w co najmniej dwa z nich.', 354, ''),
(7160, 'Stworzyłem/am złożony scenariusz do przeprowadzenia w ciągu kilku sesji w co najmniej dwóch systemach i przeprowadziłem/am przynajmniej jeden.', 355, ''),
(7161, 'Stworzyłem/am własne materiały na sesje, dbając o ich estetykę. Zaaranżowałem/am przestrzeń w klimatyczny sposób.', 355, ''),
(7162, 'W czasie sesji odwoływałem/am się do bestiariuszy i dodatków, śledziłem/am zdolności posiadane przez graczy i reagowałem/am we właściwy sposób.', 355, ''),
(7163, 'Stworzyłem/am scenariusz i przeprowadziłem/am przynajmniej jednego LARPa.', 355, ''),
(7164, 'Wiem, co to jest granica i jakie są jej rodzaje (morska, lądowa, lotnicza). Wymieniłem/am kraje graniczące z Polską.', 356, ''),
(7165, 'Dowiedziałem/am się, co trzeba zrobić, żeby przekroczyć granicę. Wymieniłem/am podstawowe dokumenty uprawniające do przekroczenia granicy. Wiem, co to jest dowód osobisty, paszport i wiza. Wymieniłem/am i wskazałem/am na mapie przejścia graniczne w swoim województwie.', 356, ''),
(7166, 'Ćwiczyłem/am refleks, pamięć i spostrzegawczość w grach typu „znajdź odróżniające szczegóły”, KIM, memory, domino itp. (co najmniej 5 razy w trakcie trwania próby).', 356, ''),
(7167, 'Regularnie ćwiczę. Wykonałem/am po 10 brzuszków, pompek i przysiadów.', 356, ''),
(7168, 'Dowiedziałem/am się, jak dzieli się obszar granicy państwowej i czym różnią się jej poszczególne elementy (granica właściwa, pas drogi granicznej, strefa nadgraniczna). Wiem, czym jest oznaczana granica państwa na mapie i w terenie oraz na różnych odcinkach. Wyjaśniłem/am, na czym polega różnica między granicą zewnętrzną i granicą wewnętrzną strefy Schengen.', 357, ''),
(7169, 'Skrycie obserwowałem/am innych (samemu nie będąc widzianym/widzianą). Sporządziłem/am meldunek z zaobserwowanych zdarzeń.', 357, ''),
(7170, 'Uczestniczyłem/am w grze odtwarzającej działanie placówki Straży Granicznej lub strażnicy Korpusu Ochrony Pogranicza.', 357, ''),
(7171, 'Poznałem/am podstawowe zadania Straży Granicznej. Odróżniłem/am funkcjonariusza Straży Granicznej po wykonywanych zadaniach i mundurze od innych służb. Rozpoznałem/am oznaczenia stopni służbowych na mundurach funkcjonariuszy Straży Granicznej.', 357, ''),
(7172, 'W czasie ćwiczeń rozpoznałem/am ślady ludzi, określając przy tym: ich wiek, kierunek marszu i sposób poruszania się.', 358, ''),
(7173, 'Zdobyłem/am wiedzę na temat rozpoznawania i zabezpieczania śladów. Zdobytą wiedzę wykorzystałem/am przeprowadzając w terenie zajęcia dla zastępu.', 358, ''),
(7174, 'Opracowałem/am grę terenową, planszową lub komputerową dotyczącą ochrony granic, wykorzystując tematykę profilaktyki przestępczości granicznej (przemyt, fałszerstwa dokumentów, nielegalna migracja itp.).', 358, ''),
(7175, 'Poznałem/am strukturę Straży Granicznej. Wymieniłem/am Oddziały Straży Granicznej. Dowiedziałem/am się, gdzie znajduje się najbliższa mojemu miejscu zamieszkania jednostka Straży Granicznej.', 358, ''),
(7176, 'Odbyłem/am kilkudniową wędrówkę po wybranym odcinku granicy lub brałem/am udział w wycieczce na wybrane przejście graniczne. Zapoznałem/am się ze specyfiką poznanego odcinka granicy i związanymi z tym szczególnymi cechami pełnionej tu służby granicznej.', 359, ''),
(7177, 'Przeprowadziłem/am dla młodszych zajęcia dotyczące jednego z poniższych tematów:\nhistoria polskich formacji granicznych,\nnielegalna migracja,\nprzemyt narkotyków, broni i materiałów niebezpiecznych,\nnielegalny wwóz odpadów,\nnielegalny wywóz zabytków,\nhandel ludźmi,\nprzemyt egzotycznych zwierząt, roślin oraz produktów z nich pochodzących (wg konwencji CITES).\nWykonałem/am prostą grę edukacyjną (memory, domino, układanka itp.) dotyczącą wybranego tematu.', 359, ''),
(7178, 'Poznałem/am zasady rekrutacji do Straży Granicznej. Przeszedłem/Przeszłam pozytywnie test sprawnościowy, wymagany od kandydatów do SG.', 359, ''),
(7179, 'Pełniłem/am służbę na rzecz uchodźców lub uczestniczyłem/am w akcji profilaktycznej związanej z problematyką handlu ludźmi.', 359, ''),
(7180, 'Pokazałem/am kilka sposobów, jak unikać kontaktu z niebezpiecznymi osobami (udawanie rozmowy przez telefon, przyłączenie się do przechodniów itp.).', 360, ''),
(7181, 'Poprawnie poinformowałem/am policję przez telefon o niebezpieczeństwie podczas rzeczywistego wydarzenia lub symulacji.', 360, ''),
(7182, 'Wykonałem/am minimum trzy techniki obrony/ataku w bliskim kontakcie.', 360, ''),
(7183, 'Wykonałem/am pad w tył i przód.', 360, ''),
(7184, 'Obroniłem/am się przy użyciu improwizowanej tarczy znajdującej się w pobliżu (np. tornister) podczas rzeczywistego wydarzenia lub symulacji.', 360, ''),
(7185, 'Skutecznie uciekłem/am od zagrożenia (agresora, pożaru itp.), pokonując przeszkody terenowe (schody, ogrodzenie, tłum ludzi itp.) podczas rzeczywistego wydarzenia lub symulacji.', 360, ''),
(7186, 'Zdobyłem/am wiedzę i umiejętności na poziomie sprawności Obrońca/Obrończyni (*).', 361, ''),
(7187, 'Zastosowałem/am odpowiednie techniki i obroniłem/am się przed: atakami z przodu rąk i nóg oraz duszeniem przedramieniem.', 361, ''),
(7188, 'Wykonałem/am przewrót przez bark w przód i w tył.', 361, ''),
(7189, 'Ewakuowałem/am osobę z miejsca zagrożenia podczas rzeczywistego wydarzenia lub symulacji.', 361, ''),
(7190, 'Przeprowadziłem/am dla zastępu dwa treningi sprawnościowe na refleks, równowagę i koordynację ruchową.', 361, ''),
(7191, 'Wziąłem/Wzięłam udział w drużynowych lub sportowych zawodach z zakresu samoobrony (zapasy, walki kogutów itp.).', 361, ''),
(7192, 'Zdobyłem/am wiedzę i umiejętności na poziomie sprawności Obrońca/Obrończyni (**).', 362, ''),
(7193, 'Zastosowałem/am odpowiednie techniki i obroniłem/am się przed atakami rąk i nóg z różnych kierunków oraz atakami noża (z góry i z dołu).', 362, ''),
(7194, 'Wykazałem/am się umiejętnością stosowania trzech innych technik samoobrony.', 362, ''),
(7195, 'Przeprowadziłem/am ewakuację grupy z miejsca zagrożenia (pożar, napaść itp.) podczas rzeczywistego wydarzenia lub symulacji.', 362, ''),
(7196, 'Nauczyłem/am co najmniej dwie osoby kilku podstawowych technik obrony.', 362, ''),
(7197, 'Zorganizowałem/am dla drużyny zawody z zakresu samoobrony.', 362, ''),
(7198, 'W czasie zbiórek zastępów wykazałem/am się znajomością:\nstopni Państwowej Straży Pożarnej oraz stopni funkcyjnych Ochotniczej Straży Pożarnej,\nstruktury organizacyjnej Związku Ochotniczych Straży Pożarnych RP,\npodstawowych przyczyny pożarów,\nrodzajów węży oraz sprzętu do ich obsługi oraz zasad konserwacji.', 363, ''),
(7199, 'Wspólnie z zastępem zbudowałem/am linię wężową.', 363, ''),
(7200, 'Sprawnie ubrałem/am się w osobiste uzbrojenie strażaka.', 363, ''),
(7201, 'Wiem, jakimi środkami gaśniczymi należy gasić różnego rodzaju pożary.', 363, ''),
(7202, 'Podczas zbiórki drużyny opowiedziałem/am o szkodach dla środowiska, które niosą pożary traw i lasów.', 363, ''),
(7203, 'Wykazałem/am się znajomością budowy i zasady działania gaśnicy i hydronetki. W czasie ćwiczeń sprawnie posługiwałem/am się nimi oraz kocem gaśniczym, bosakiem, sprzętem burzącym i tłumicą.', 364, ''),
(7204, 'W czasie ćwiczeń wykazałem/am się znajomością budowy, zasad działania i obsługi motopompy.', 364, ''),
(7205, 'Poznałem/am środki transportowe używane przez straże pożarne. Potrafiłem/am rozwinąć skróty określeń samochodów strażackich. W czasie wizyty w remizach strażackich dokładnie poznałem/am co najmniej dwa typy pojazdów pożarniczych.', 364, ''),
(7206, 'W czasie ćwiczeń udzieliłem/am pierwszej pomocy w przypadku oparzenia, porażenia prądem oraz zatrucia gazami pożarowymi.', 364, ''),
(7207, 'Wziąłem/Wzięłam udział w zawodach Młodzieżowych Drużyn Pożarniczych.', 364, ''),
(7208, 'Zebrałem/am informacje o stanie zaopatrzenia wybranej miejscowości w wodę oraz ustaliłem/am najdogodniejsze miejsce na ustawienie motopompy.', 365, ''),
(7209, 'W czasie ćwiczeń:\nkierowałem/am budową linii wężowej oraz gaszeniem ognia przez zastęp podczas ćwiczeń,\ndostosowałem wodne prądy gaśnicze do odpowiedniego rodzaju pożaru.', 365, ''),
(7210, 'Ukończyłem/am co najmniej 15-godzinny kurs pierwszej pomocy.', 365, ''),
(7211, 'Prowadziłem/am służbową korespondencję radiową, używając kryptonimów.', 365, ''),
(7212, 'Poszerzyłem/am wiedzę z zakresu jednej ze specjalizacji ratowniczych (ratownictwo wodne, techniczne, chemiczne, wysokościowe itp.).', 365, ''),
(7213, 'Brałem/am udział w przygotowaniu harcerzy do udziału w zawodach Młodzieżowych Drużyn Pożarniczych.', 365, ''),
(7214, 'Poznałem/am zasady działania poczt harcerskich oraz ich historię. Przeczytałem/am książkę J. Kasprzaka „Tropami powstańczej przesyłki”.', 366, ''),
(7215, 'Poznałem/am zasady kolekcjonowania zbiorów filatelistycznych. Brałem/am czynny udział w przygotowaniu wystawy filatelistycznej.', 366, ''),
(7216, 'Wykonałem/am skrzynkę pocztową i byłem/am odpowiedzialny/a za przyjmowanie oraz rozprowadzanie korespondencji na obozie.', 366, ''),
(7217, 'Założyłem skrzynkę poczty elektronicznej (e-mail), nauczyłem/am się redagować i wysyłać listy pocztą elektroniczną.', 366, ''),
(7218, 'Uczestniczyłem/am w obsłudze pocztowej na zlocie, obozie, imprezie hufca albo imprezie publicznej.', 367, ''),
(7219, 'Wykonałem/am samodzielnie klisze lub pieczątkę dla poczty harcerskiej (np. linoryt).', 367, ''),
(7220, 'Przez okres próby prowadziłem/am współpracę z inną pocztą harcerską, realizując przynajmniej jeden wspólny projekt.', 367, ''),
(7221, 'Przygotowałem/am projekt wydawnictwa poczty harcerskiej do druku.', 367, ''),
(7222, 'Wykonałem co najmniej jedno z zadań:\nprzygotowałem/am własne zbiory wydawnictw poczty harcerskiej do wystawy;\nprzesłałem/am wydawnictwa swojej poczty do Centralnego Archiwum Poczt Harcerskich.', 367, ''),
(7223, 'Kierowałem/am kilkoma projektami tematycznymi poczty.', 368, ''),
(7224, 'Zorganizowałem/am sieć obsługi pocztowej na dużym obozie lub zlocie.', 368, ''),
(7225, 'Zrealizowałem/am projekt pocztowy we współpracy z Pocztą Polską, Polskim Związkiem Filatelistycznym lub odpowiednią organizacją, firmą spedycyjną lub logistyczną.', 368, ''),
(7226, 'Zrealizowałem/am dodatkowe zadanie mistrzowskie w porozumieniu z naczelnikiem macierzystej PH.', 368, ''),
(7227, 'Poznałem/am międzynarodowy podział pasm radiowych oraz służby korzystające z nich. Opowiedziałem/am o tym swojemu zastępowi.', 369, ''),
(7228, 'Poznałem/am zakresy amatorskich pasm KF i ich wewnętrzny podział oraz podstawowe wyrazy Q-kodu i podstawowe skróty służbowe.', 369, ''),
(7229, 'Poznałem/am zasady budowy znaków rozpoznawczych i prefiksy minimum pięciu państw. Dokonałem/am nasłuchów z co najmniej dziesięciu krajów.', 369, ''),
(7230, 'Obsługiwałem/am odbiornik komunikacyjny. Pełniłem/am dyżur nasłuchowy w pracy sieci i odebrałem/am kierowany doń radiogram.', 369, ''),
(7231, 'Przeprowadziłem/am na stacji klubowej co najmniej dwadzieścia łączności.', 370, ''),
(7232, 'Poznałem/am procedurę pracy amatorskiej, międzynarodowy system głoskowania oraz sposoby budowy znaków rozpoznawczych. Wykazałem/am się znajomością prefiksów krajów sąsiadujących z Polską, krajów Unii Europejskiej oraz USA.', 370, ''),
(7233, 'Poznałem/am procedurę pracy służbowej oraz sposób prowadzenia dokumentacji stacyjnej (radiogramy, log). Pracowałem/am w sieci służbowej łączności radiotelefonicznej i przekazałem/am co najmniej dwadzieścia radiogramów.', 370, ''),
(7234, 'Poznałem/am zasady rozchodzenia się fal KF i UKF, wybrałem/am właściwe miejsce w terenie dla usytuowania anteny oraz dobrałem/am odpowiedni typ anteny w zależności od wymaganego zasięgu.', 370, ''),
(7235, 'Przygotowałem/am do pracy przenośną radiostację i przeprowadziłem/am szkolenie zastępu w tym zakresie.', 370, ''),
(7236, 'Odebrałem/am i nadałem/am kluczem 60 znaków na minutę według PARIS.', 371, ''),
(7237, 'Poznałem/am minimum 20 znaków Q-kodu oraz znaki służbowe dotyczące korespondencji radiowej. Nawiązałem/am co najmniej 20 łączności telegrafią na radiostacji klubowej i przekazałem/am telegrafią co najmniej 10 radiogramów podczas pracy amatorskiej lub służbowej.', 371, ''),
(7238, 'Pracowałem/am co najmniej raz z terenowego stanowiska (obóz, biwak), utrzymując łączność z wybranym korespondentem w macierzystej lub innej miejscowości.', 371, ''),
(7239, 'Wykonałem/am proste urządzenie telegraficzne, np. generator do nauki telegrafii, wykorzystałem/am je podczas swoich zajęć z zastępem.', 371, ''),
(7240, 'Brałem/am udział w trzech meczach (np. siatkówki, piłki nożnej, koszykówki, footballu amerykańskiego, piłki ręcznej), pamiętając o zasadach fair play.', 281, ''),
(7241, 'Prawidłowo wykonałem/am minimum trzy podstawowe podania (np. strzał, podanie, chwyt bramkarski, dwutakt, przekazanie).', 281, ''),
(7242, 'Grałem/am na różnych pozycjach w czasie meczu, np. bramkarza, rozgrywającego, przyjmującego.', 281, ''),
(7243, 'Odpowiednio przygotowałem/am się do treningu oraz zadbałem/am o siebie po treningu.', 281, ''),
(7244, 'Nauczyłem/am innych zasad gry mojej ulubionej dyscypliny sportów zespołowych ( np. siatkówki, piłki nożnej, koszykówki, footballu amerykańskiego, piłki ręcznej).', 282, ''),
(7245, 'W czasie próby trenowałem/am systematycznie min. raz w tygodniu. Skompletowałem/am niezbędny sprzęt umożliwiający treningi, np. korki, kask.', 282, ''),
(7246, 'Wraz z drużyną wziąłem/wzięłam udział w zawodach (szkolnych/harcerskich) w wybranej dyscyplinie sportu zespołowego .', 282, ''),
(7247, 'W czasie rozgrywki wykazałem/am się dyscypliną taktyczną (wykonałem/am zalecenia trenera obejmujące pozycję oraz styl gry).', 282, ''),
(7248, 'Zorganizowałem/am zawody, podczas których pełniłem/am funkcję sędziego.', 283, ''),
(7249, 'Zapoznałem/am się z różnymi taktykami gry. W czasie dwóch rozgrywek przeprowadziłem/am taktykę, którą przekazałem/am innym członkom drużyny.', 283, ''),
(7250, 'Nauczyłem/am innych podstawowych podań z wybranej dyscypliny sportów zespołowych. Nauczyłem/am się kilku prostych trików z piłką, które przedstawiłem/am wśród rówieśników.', 283, ''),
(7251, 'Ułożyłem/am plan swojego rozwoju uwzględniający odpowiednią ilość snu, wypoczynku, ruchu, właściwe odżywianie, skonsultowałem/am go z trenerem i realizowałem/am przez miesiąc.', 283, ''),
(7252, 'Samodzielnie założyłem/am buty narciarskie. Wpiąłem/wpięłam i wypiąłem/wypięłam narty zjazdowe.', 375, ''),
(7253, 'Zademonstrowałem/am sposób bezpiecznego upadania podczas jazdy na nartach, a następnie samodzielnie się podniosłem/am.', 375, ''),
(7254, 'Wykonałem/am „pługiem” zjazd slalomem (między rozstawionymi kijkami/pachołkami) i zatrzymałem/am się „pługiem” w bezpiecznym miejscu na stoku.', 375, ''),
(7255, 'Podszedłem/podeszłam „dowolną techniką” pod stok przez co najmniej 10 m.', 375, ''),
(7256, 'Wykonałem/am jedno z poniższych zadań:\n-wjechałem/am na różne stoki co najmniej 2 typami wyciągów. Nazwałem/am je i wskazałem/am, czym się różnią;\n-pokonałem 100 m na płaskim terenie na nartach zjazdowych za pomocą odpowiedniej techniki jazdy (np. bezkrok, krok łyżwowy, itd.).', 376, ''),
(7257, 'Wykonałem/am zjazd slalomem wzdłuż postawionych słupków, zmieniając kierunek jazdy i zahamowałem/am, utrzymując narty równolegle do siebie (nie „pługiem”).', 376, ''),
(7258, 'Bezpiecznie zatrzymałem/am się, podjeżdżając pod stok „carvingiem” lub w miejscu „śmigiem” (tzw. „skręt stop”) w wyznaczonym miejscu, w którym nie przeszkadzałem/am innym narciarzom.', 376, ''),
(7259, 'Nauczyłem/am się i wykonałem/am poprawną rozgrzewkę przed jazdą na nartach, a także prawidłowe rozciąganie po niej.', 376, ''),
(7260, 'Wykonałem/am jedno z poniższych zadań:\n-wziąłem/wzięłam udział w wędrówce narciarskiej, używając do tego nart biegowych/śladowych/skiturowych lub telemarkowych. Przebyłem/am w ten sposób co najmniej 10 km;\n-wziąłem/wzięłam udział w zawodach narciarskich i zająłem/zajęłam satysfakcjonujące mnie miejsce.', 377, ''),
(7261, 'Nauczyłem/am się rozróżniać co najmniej trzy rodzaje śniegu. Przygotowałem/am narty do różnych warunków śniegowych.', 377, ''),
(7262, 'W trakcie zjazdu ze stoku, wykonałem/am półobrót, jechałem/am dalej tyłem przez co najmniej 10 m, a następnie wykonałem/am kolejny półobrót w celu kontynuacji jazdy przodem.', 377, ''),
(7263, 'Poznałem/am zasady bezpiecznego poruszania się na nartach i w trakcie wszystkich wycieczek narciarskich posiadałem/am przy sobie apteczkę, z której potrafię korzystać.', 377, ''),
(7264, 'Opracowałem/am i przeprowadziłem/am wycieczkę narciarską na podstawie mapy topograficznej o długości co najmniej 10 km. Wziąłem/wzięłam pod uwagę możliwości jej uczestników. Zaplanowałem/am ekwipunek indywidualny i zbiorowy na trasę wycieczki. Pomogłem/am jej uczestnikom w jego skompletowaniu.', 378, ''),
(7265, 'Wykonałem/am jedno z poniższych zadań:\n-pokonałem/am techniką łyżwową 50 m na nartach biegowych;\n-w trakcie jednego sezonu narciarskiego przebyłem/am na nartach zjazdowych co najmniej 100 km.', 378, ''),
(7266, 'Przeprowadziłem/am szkolenie narciarskie dla przynajmniej jednej osoby, którego celem było nauczenie jej m.in.: zakładania sprzętu narciarskiego, poruszania się po płaskim terenie na nartach, podchodzenia pod stok, bezpiecznego upadania, zasad bezpieczeństwa na stoku, podnoszenia się z przypiętymi nartami oraz jazdy pługiem i zatrzymania się w wyznaczonym miejscu.', 378, ''),
(7267, 'Wykonałem/am jedno z poniższych zadań:\n-zdaje sobie sprawę z niebezpieczeństwa, jakim jest lawina oraz co może ją spowodować. Korzystałem/am w warunkach symulacyjnych ze sprzętu lawinowego;\n-osiągnąłem/osiągnęłam minimum akceptowane dla Pomocnika Instruktorów PZN w zawodach regionalnych organizowanych przez SITN-PZN.', 378, ''),
(7268, 'Potrafię jeździć na rolkach do przodu, przejechałem/am jednorazowo min. 2 km.', 379, ''),
(7269, 'Zatrzymałem/am się samodzielnie, bez wykorzystania infrastruktury drogowej (znaki, słupki).', 379, ''),
(7270, 'Posiadam swoje rolki i trenowałem/am przez miesiąc co najmniej raz w tygodniu. Dbając o swoje bezpieczeństwo, wszystkie treningi odbywałem/am w kasku i ochraniaczach.', 379, ''),
(7271, 'Opanowałem/am skręcanie w obie strony.', 379, ''),
(7272, 'Podczas jazdy zachowywałem/am prawidłową pozycję rolkową (piszczele na języki rolek, ciało pochylone do przodu, proste plecy). Potrafię jeździć na rolkach tyłem, przejechałem/am kilkanaście metrów, bezpiecznie obracając się w obie strony.', 380, ''),
(7273, 'Zatrzymałem/am się bezpiecznie, wykorzystując wybraną technikę hamowania (np. grass stop, hamowanie beczką).', 380, ''),
(7274, 'Podczas jazdy wykonałem/am następujące ćwiczenia:\nprzysiad,\njazda na jednej nodze (obie nogi) co najmniej 4 m.', 380, ''),
(7275, 'Wraz z grupą chętnych rolkarzy (znajomych z klasy, drużyny itp.) zorganizowałem/am wspólne wyjście na rolki, prezentując swoje umiejętności.', 380, ''),
(7276, 'Znam różnice w budowie rolek oraz skompletowałem/am swój sprzęt odpowiedni do ulubionego stylu jazdy, pamiętając o ochraniaczach i kasku.', 381, ''),
(7277, 'Pokonałem/am (wjechałem/am i zjechałem/am) proste przeszkody podczas jazdy tj. krawężniki, progi zwalniające, studzienki, zmiany nawierzchni.', 381, ''),
(7278, 'Dowiedziałem/am się, gdzie w okolicy mogę bezpiecznie ćwiczyć różne dyscypliny jazdy na rolkach. Przez dwa miesiące min. raz w tygodniu ćwiczyłem/am w jednym z takich miejsc (skatepark, ścieżka rolkowa, miejsce na slalom itp.).', 381, ''),
(7279, 'Wykonałem/am poprawnie przeplatankę w obie strony.', 381, ''),
(7280, 'Wykonałem/am 2 podstawowe tricki w mojej ulubionej technice jazdy. np. slalom, slide, agressive, freestyle.', 381, ''),
(7281, 'Wykonałem/am jedno z poniższych zadań:\n-odbyłem/am min. jedną rolkową wycieczkę po mieście, jeżdżąc po różnych nawierzchniach, bezpiecznie i zgodnie z przepisami;\n-wziąłem/wzięłam udział w Nightskatingu lub innym wydarzeniu rolkowym (warsztaty, zawody itp.).', 382, ''),
(7282, 'Zaprezentowałem/am zastępowi/drużynie 5 różnych trików na rolkach, tłumacząc, jak należy je wykonać bezpiecznie.', 382, ''),
(7283, 'Zaplanowałem/am ścieżkę rozwoju swoich umiejętności rolkowych (np. kolejność nauki trików) i konsekwentnie zrealizowałem/am przynajmniej jeden jej etap.', 382, ''),
(7284, 'Zatrzymałem/am się podczas jazdy co najmniej 3 sposobami (hamownie beczką, T-stop, powerstop itp.).', 382, ''),
(7285, 'Nauczyłem/am się jeździć na łyżwach do przodu, przejechałem/am bez wywrotki kilka kółek na lodowisku.', 383, ''),
(7286, 'Zahamowałem/am skutecznie na łyżwach (nie wpadając w bandę).', 383, ''),
(7287, 'Przez co najmniej 1 miesiąc chodziłem/am na lodowisko min. raz w tygodniu.', 383, ''),
(7288, 'Przejechałem/am kilka metrów na łyżwach tyłem, z asekuracją drugiej osoby z przodu.', 384, ''),
(7289, 'Wykonałem/am kilka ćwiczeń na równowagę, np. zrobiłem/am jaskółkę, przysiad w czasie jazdy. Przejechałem/am kilka metrów na jednej nodze (na prawej i lewej) oraz ustawiłem/am nogi w jednej linii podczas jazdy.', 384, ''),
(7290, 'Przejechałem/am kilka kółek przodem, nie odrywając łyżew od lodu.', 384, ''),
(7291, 'Przez co najmniej 2 miesiące chodziłem/łam na lodowisko min. raz w tygodniu.', 384, ''),
(7292, 'Przejechałem/am przekładanką przodem i tyłem po okręgu na łyżwach (demonstrując umiejętność skręcania oraz zwracania uwagi na osoby znajdujące się za moimi plecami), zwracając uwagę na inne osoby na tafli lodowiska.', 385, ''),
(7293, 'Skompletowałem/am sprzęt odpowiedni do mojego stylu jazdy, przez cały sezon uczęszczałem/am na lodowisko min. raz w tygodniu (w zależności od pogody: listopad-marzec).', 385, ''),
(7294, 'Zorganizowałem/am wyjście zastępu/drużyny na lodowisko. Przed wycieczką zapoznałem/am uczestników z podstawami jazdy i zasadami, a w trakcie pomagałem/am innym w doskonaleniu ich umiejętności.', 385, ''),
(7295, 'Wykonałem/am kilka trików/figur na łyżwach, zaprezentowałem/am je zastępowi/drużynie, zachęcając do aktywności na łyżwach.', 385, ''),
(7296, 'Brałem/am udział w min. 5 zajęciach dotyczących wybranej sztuk walki.', 386, ''),
(7297, 'Zapoznałem/am się z zasadami panującymi na zajęciach, np. znam etykietę, zasady bezpieczeństwa w trakcie zajęć i stosuję się do nich.', 386, ''),
(7298, 'Poznałem/am 2 podstawowe pozycje w wybranej sztuce walki. Wykonałem/am, nazwałem/am i wyjaśniłem/am proste uderzenia lub techniki (min. 6 przykładów) charakterystyczne dla mojego stylu walki (np. mae-geri, mae-keage, itd.).', 386, ''),
(7299, 'Przeszedłem/przeszłam test sprawnościowy: 10 pompek, 20 brzuszków, 5 podciągnięć.', 386, ''),
(7300, 'Zaprezentowałem/am i objaśniłem/am swojemu zastępowi albo rówieśnikom spoza drużyny kilka chwytów, kopnięć, bloków (min. 10 przykładów) charakterystycznych dla mojego stylu walki.', 387, ''),
(7301, 'Wykonałem/am 3 dowolne pady (ćwiczyłem/am je pod okiem instruktora/nauczyciela), dodatkowo przeszedłem/przeszłam test sprawnościowy: 20 pompek na kościach, 40 brzuszków, 10 podciągnięć.', 387, ''),
(7302, 'Skompletowałem/am strój i akcesoria potrzebne do ćwiczeń (np. gi, dobok, ochraniacze, rękawiczki). Nauczyłem/am się o niego dbać, np. prawidłowo składać, zakładać, itd.', 387, ''),
(7303, 'Przez okres próby regularnie ćwiczyłem/am min. raz w tygodniu. Wykonywałem/am odpowiednią rozgrzewkę przed treningami oraz rozciąganie po treningu. Przed treningiem wyciszałem/am swój umysł, poświęcając się treningowi.', 387, ''),
(7304, 'Zorganizowałem/am wyjście zastępu/drużyny na zajęcia sportowe dotyczące sztuki walki.', 388, ''),
(7305, 'Brałem/am udział w min. 5 zajęciach dotyczących wybranej sztuk walki.', 386, ''),
(7306, 'Zapoznałem/am się z zasadami panującymi na zajęciach, np. znam etykietę, zasady bezpieczeństwa w trakcie zajęć i stosuję się do nich.', 386, ''),
(7307, 'Poznałem/am 2 podstawowe pozycje w wybranej sztuce walki. Wykonałem/am, nazwałem/am i wyjaśniłem/am proste uderzenia lub techniki (min. 6 przykładów) charakterystyczne dla mojego stylu walki (np. mae-geri, mae-keage, itd.).', 386, ''),
(7308, 'Przeszedłem/przeszłam test sprawnościowy: 10 pompek, 20 brzuszków, 5 podciągnięć.', 386, ''),
(7309, 'Zorganizowałem/am wyjście zastępu/drużyny na zajęcia sportowe dotyczące sztuki walki.', 388, ''),
(7310, 'Przez okres próby regularnie ćwiczyłem/am min. dwa razy w tygodniu. Zademonstrowałem/am min. 10 poprawnych pozycji. Zademonstrowałem/am poprawne poruszanie się do przodu, do tyłu, w bok. Zademonstrowałem/am min. 25 różnych uderzeń, technik, poddań.', 388, ''),
(7311, 'Znalazłem/am min. dwa sposoby, dzięki którym mogę rozładować napiętą sytuację tak, aby nikogo nie skrzywdzić oraz aby nie powstrzymywać swoich emocji. Przedstawiłem/am znalezione sposoby na zbiórce zastępu/drużyny.', 388, ''),
(7312, 'Wykonałem/am jedno z poniższych zadań:\n-wziąłęm/wzięłam udział w zawodach i zająłem/zajęłam satysfakcjonujące mnie miejsce;\n-wziąłem/wzięłam udział w egzaminie i zdobyłem/am wyższy stopień wtajemniczenia.', 388, ''),
(7313, 'Wykonałem/am jedno z poniższych zadań:\n-przez 2 miesiące w czasie treningu pod okiem instruktora brałem/am udział w walkach. Stoczyłem/am w sumie min. 50 walk, ewidencjonowałem/am je, w większości walk zwyciężyłem/am;\n-przeszedłem/przeszłam „test kumite” – 5 walk pod rząd.', 388, ''),
(7314, 'Wykonałem/am jedno z poniższych zadań:\n-przeprowadziłem/am warsztaty dotyczące samoobrony/mojej sztuki walki dla wędrowników w szczepie/w hufcu;\n-zorganizowałem/am dla szczepu/hufca zawody związane ze sztukami walki.\nW obu przypadkach pamiętałem/am o zachowaniu niezbędnych zasad bezpieczeństwa.', 389, ''),
(7315, 'Zorganizowałem/am debatę na temat przemocy fizycznej dla moich rówieśników. Wspólnie zastanowiliśmy się, jak możemy jej przeciwdziałać.', 389, ''),
(7316, 'Uczestniczyłem/am w seminarium ze znanym trenerem/nauczycielem/senseiem.', 389, ''),
(7317, 'Poznałem/am zasady bezpieczeństwa, nauczyłem/am się udzielać pierwszej pomocy i wiem, jaka jest procedura postępowania w nagłych wypadkach. Uczestniczyłem/am min. w 3-godzinnym szkoleniu, kładącym nacisk na umiejętności potrzebne w uprawianej przeze mnie sztuce walki.', 389, ''),
(7318, 'Wykonałem/am rozgrzewkę przed bieganiem oraz rozciąganie po bieganiu.', 390, ''),
(7319, 'Posiadam wygodne obuwie sportowe (niekoniecznie profesjonalne), z którego korzystałem/am podczas biegania.', 390, ''),
(7320, 'Biegałem/am krótkie dystanse ok. 1 km - 3 km, przynajmniej 2 razy w tygodniu przez miesiąc.', 390, ''),
(7321, 'Dowiedziałem/am się, jakie są zalety biegania, jak wpływa ono na mój organizm. Zdobytą wiedzę przedstawiłem/am klasie lub swojemu zastępowi.', 390, ''),
(7322, 'Poznałem/am zasady odpowiedniego przygotowania się do treningu biegowego (obowiązkowo rozgrzewka, odpowiednie ubranie do panującej pogody i temperatury). Biegałem/am w min. dwa dni o różnych warunkach atmosferycznych.', 391, ''),
(7323, 'Razem z zastępem lub koleżankami/kolegami z klasy zorganizowaliśmy zawody związane z biegami na krótki lub długi dystans.', 391, ''),
(7324, 'Systematycznie zwiększam swoje dystanse. Biegałem/am na dystanse ok. 4 km - 7 km systematycznie co 2-3 dzień przez min. półtora miesiąca.', 391, ''),
(7325, 'Interesuję się wydarzeniami związanymi z biegami. Brałem/am w minimum jednym wydarzeniu jako widz, ewentualnie jako uczestnik.', 391, ''),
(7326, 'Wykonałem/am jedno z poniższych zadań:\n-wziąłem/wzięłam udział w akcji sportowej, podczas której przebiegłem/am dystans (bez zatrzymywania się) min. 10 km;\n-wziąłem/wzięłam udział w marszobiegu o długości min. 10 km.', 392, ''),
(7327, 'Dbam w czasie biegu o odpowiednie nawodnienie, sporządziłem/am napój izotoniczny wg własnego przepisu. Przed i po treningu przygotowałem/am odpowiedni posiłek dostarczający mi niezbędnych wartości odżywczych.', 392, ''),
(7328, 'Poznałem/am własne tempo biegania i utrzymywałem/am je przez większość czasu. Korzystałem/am z aplikacji biegowej (np. Activy, Strava, itd.) w celu monitorowania swoich osiągnięć min. przez miesiąc.', 392, ''),
(7329, 'Wykonałem/am jedno z poniższych zadań:\n-razem z zastępem/rówieśnikami braliśmy udział w wydarzeniu sportowym związanym z biegami;\n-wziąłem/wzięłam udział w biegach drużynowych lub w biegach bazujących na współpracy z innymi uczestnikami, np. Runmagedon.', 392, ''),
(7330, 'Zgromadziłem/am profesjonalny sprzęt, np. odpowiednie buty biegowe, plecak do biegania, itd.', 392, ''),
(7331, 'Ułożyłem/am plan treningowy, uwzględniając: treningi siłowe, treningi na wytrzymałość, odpowiednie posiłki oraz odpowiednią ilość snu i wypoczynku. Po skończonym planie treningowym byłem/am w stanie przebiec 20 km.', 393, ''),
(7332, 'Wykonałem/am jedno z poniższych zadań:\n-ukończyłem/am półmaraton - 21 km;\n-wziąłem/wzięłam udział w min. 4 akcjach sportowych, w których przebiegłem/am -dystans min. 10 km bez zatrzymywania się.', 393, ''),
(7333, 'Odpowiednio przygotowałem/am się i wziąłem/wzięłam udział w biegach górskich min. ok. 10 km.', 393, ''),
(7334, 'Pomagałem/am przy organizacji biegu/maratonu jako wolontariusz.', 393, ''),
(7335, 'Przepłynąłem/przepłynęłam 100 metrów, w tym połowę na plecach.', 394, ''),
(7336, 'Wytrzymałem/a 20 sekund pod wodą.', 394, ''),
(7337, 'Położyłem/am się na wodzie i utrzymałem/am się na niej przez 30 sekund.', 394, ''),
(7338, 'Przepłynąłem/przepłynęłam 200 metrów, w tym połowę na plecach.', 395, ''),
(7339, 'Skoczyłem/am do wody z wysokości 1 metra i przepłynąłem/przepłynęłam pod wodą 10 metrów.', 395, ''),
(7340, 'Wyciągnąłem/wyciągnęłam trzy przedmioty znajdujące się 1,5 metra pod wodą.', 395, ''),
(7341, 'Przepłynąłem/przepłynęłam między nogami kolegi/koleżanki pod wodą.', 395, ''),
(7342, 'Przepłynąłem/przepłynęłam 400 metrów co najmniej trzema różnymi stylami.', 396, ''),
(7343, 'Bezpiecznie skoczyłem/am do wody na kilka sposobów (np. skokiem ratowniczym, w kamizelce asekuracyjnej itp.)', 396, ''),
(7344, 'Pracowałem/am nad techniką pływania wybranym stylem. Z pomocą instruktora lub Internetu poznałem/am dokładnie wybraną technikę i przygotowałem/am dla siebie zestaw ćwiczeń, które powtórzyłem/am podczas min. 3 treningów.', 396, ''),
(7345, 'Wykonałem/am poprawnie nawroty – odkryty (np. w żabce) i koziołkowy (np. w kraulu).', 396, ''),
(7346, 'Przepłynąłem/am 800 m, dowolnie wybierając style płynięcia, bez przerw.', 397, ''),
(7347, 'Przepłynąłem/am 100 metrów stylem zmiennym (25 m stylem motylkowym, 25 m grzbietem, 25 m żabką i 25 m kraulem).', 397, ''),
(7348, 'Przepłynąłem/am pod wodą 20 metrów.', 397, ''),
(7349, 'Pracowałem/łam nad techniką pływania wybranym stylem (innym niż w sprawności ***) – z pomocą instruktora lub Internetu poznałem dokładnie wybraną technikę i przygotowałem/łam dla siebie zestaw ćwiczeń, które powtórzyłem/łam podczas min. 3 treningów.', 397, ''),
(7350, 'W czasie zajęć na wodzie zawsze pływałem/am w kamizelce asekuracyjnej lub ratunkowej.', 398, ''),
(7351, 'Opowiedziałem/am, jak oznacza się kąpieliska w Polsce, co oznaczają poszczególne flagi oraz w jakich miejscach należy unikać kąpieli.', 398, ''),
(7352, 'Skompletowałem/am apteczkę zastępu/wachty na biwak, rejs lub obóz.', 398, ''),
(7353, 'Prawidłowo wezwałem/am pomoc w przypadku wypadku nad wodą lub w czasie pozoracji wypadku.', 398, ''),
(7354, 'Zawiązałem/am węzeł ratowniczy.', 398, ''),
(7355, 'Odwiedziłem/am z drużyną/zastępem/wachtą stację ratownictwa wodnego lub sekcję wodną straży pożarnej.', 399, ''),
(7356, 'Udzieliłem/am pomocy w przypadku skaleczenia i oparzenia słonecznego.', 399, ''),
(7357, 'Zapobiegłem/am wystąpieniu udaru słonecznego, w czasie zajęć na wodzie zawsze nosiłem/am czapkę.', 399, ''),
(7358, 'Wskazałem/am na mapie, gdzie na najbliższych zbiornikach wodnych zlokalizowane są służby mogące nieść pomoc na wodzie.', 399, ''),
(7359, 'Zawiązałem/am węzeł ratowniczy w wodzie oraz na lądzie z zawiązanymi oczami.', 399, ''),
(7360, 'Rozpoznałem/am oznaki hipotermii i opowiedziałem/am, jak postąpić w przypadku jej wystąpienia.', 400, ''),
(7361, 'Ustawiłem/am odpowiednio jednostkę (jacht, kajak, łódź motorową itp.) w przypadku pozorowanego pożaru na jednostce.', 400, ''),
(7362, 'Prawidłowo podałem/am koło ratunkowe, założyłem/am pas ratunkowy i kamizelkę asekuracyjną.', 400, ''),
(7363, 'Ukończyłem/am przynajmniej 9-godzinny kurs pierwszej pomocy, w którym wykonywano ćwiczenia RKO na fantomie.', 400, ''),
(7364, 'Zaprezentowałem/am przynajmniej pięć sposobów wzywania pomocy na wodach śródlądowych.', 400, ''),
(7365, 'Skoczyłem/am do wody na nogi w kamizelce ratunkowej z wysokości 2 metrów.', 400, ''),
(7366, 'Opisałem/am procedurę wzywania pomocy przez radiotelefon UKF, odebrałem/am pozorowany komunikat MAYDAY, PAN-PAN i SECURITE.', 401, ''),
(7367, 'Posługiwałem/am się szelkami asekuracyjnymi na pokładzie.', 401, ''),
(7368, 'Opowiedziałem/am, jak działają i do czego służą: tyczka z chorągiewką oraz pława świetlna.', 401, ''),
(7369, 'Zaprezentowałem/am użycie (bez odpalania) środków pirotechnicznych środków wzywania pomocy: flary, rakiety i pławki dymnej.', 401, ''),
(7370, 'Posiadam odznakę Ratownik ZHP lub ukończyłem/am inny 15-godzinny kurs pierwszej pomocy.', 401, ''),
(7371, 'Wie, czym się różni kamizelka asekuracyjna od ratunkowej, poprawnie założyłem/am jedną z nich.', 402, ''),
(7372, 'Przepłynąłem/przepłynęłam 200 metrów dowolnym stylem.', 402, ''),
(7373, 'Wyciągnąłem/wyciągnęłam przedmiot z wody z głębokości 2 metrów.', 402, ''),
(7374, 'Utrzymałem/am się na powierzchni wody w pozycji pionowej przez minutę, pracując tylko nogami.', 402, ''),
(7375, 'Pełniłem/am funkcję sanitariusza na wycieczce lub rejsie.', 402, ''),
(7376, 'Przed zajęciami na wodzie sprawdziłem/am poprawność założenia środków asekuracyjnych u innych członków zastępu.', 403, ''),
(7377, 'Opowiedziałem/am zastępowi, jak oznaczane są kąpieliska w Polsce i w jakich miejscach powinno się unikać kąpieli.', 403, ''),
(7378, 'Przepłynąłem/przepłynęłam 600 metrów, w tym min. 200 na plecach.', 403, ''),
(7379, 'Przepłynąłem/przepłynęłam pod wodą 15 metrów.', 403, ''),
(7380, 'Prawidłowo podałem/am koło ratunkowe.', 403, ''),
(7381, 'Przepłynąłem/am 25 metrów w ubraniu.', 403, ''),
(7382, 'Poznałem/am min. jeden sposób holowania tonącego i przeholowałem/am kogoś na dystansie 25 metrów.', 403, ''),
(7383, 'Skoczyłem/am do wody sposobem ratowniczym.', 404, ''),
(7384, 'Przepłynąłem/przepłynęłam 50 metrów kraulem ratowniczym i 50 metrów żabką ratowniczą.', 404, ''),
(7385, 'Poznałem/am min. dwa sposoby holowania tonącego i przeholowałem/am kogoś na dystansie 50 metrów.', 404, ''),
(7386, 'Podczas symulacji ułożyłem/am poszkodowanego z podejrzeniem urazu kręgosłupa na desce ratowniczej.', 404, ''),
(7387, 'Rzuciłem/am rzutką ratowniczą trzy razy na odległość min. 15 metrów. Po każdym rzucie poprawnie ją sklarowałem/am.', 404, ''),
(7388, 'Podczas ćwiczeń odpaliłem/am pławkę dymną, flarę ratowniczą lub rakietę spadochronową.', 405, ''),
(7389, 'Przepłynąłem/przepłynęłam 400 metrów dowolnym sposobem w czasie poniżej osiem minut.', 405, ''),
(7390, 'Przepłynąłem/przepłynęłam 25 metrów pod wodą.', 405, ''),
(7391, 'Holowałem/am osobę udającą tonącego na dystansie 150 metrów z zastosowaniem trzech sposobów holowania. Wyciągnąłem/wyciągnęłam tę osobę na brzeg i ułożyłam/am w pozycji bezpiecznej.', 405, ''),
(7392, 'Przepłynąłem/przepłynęłam ratowniczą łodzią wiosłową za pomocą dwóch wioseł dystans 75 metrów w linii prostej do boi, dopłynąłem/dopłynęłam do niej rufą i powrócił/a do miejsca startu w czasie krótszym niż 2:40 minut.', 405, ''),
(7393, 'Brałem/am udział w symulowanej akcji ratowniczej polegającej na przepłynięciu dystansu co najmniej 20 metrów, wydobyciu manekina położonego na dnie i holowaniu go w pasie ratowniczym do brzegu.', 405, ''),
(7394, 'Przepłynąłem/przepłynęłam prawidłowo w płetwach i w masce dystans 25 metrów, oddychając przez fajkę.', 406, ''),
(7395, 'Zanurkowałem/am na głębokość 2 metrów i wydobyłem/am przedmiot z dna kąpieliska/basenu.', 406, ''),
(7396, 'Wytrzymałem/am pod wodą bez oddechu przez 30 sekund.', 406, ''),
(7397, 'Wytłumaczyłem/am, jak wygląda i co oznacza flaga A MKS oraz jak wygląda flaga nurkowa.', 406, ''),
(7398, 'Skompletowałem/am swoje wyposażenie ABC (płetwy, maska, fajka).', 407, ''),
(7399, 'Nurkowałem/am w ABC. Brałem/am udział w wyławianiu zagubionych przedmiotów na wodzie otwartej lub lustracji dna np. przystani.', 407, ''),
(7400, 'Kontrolowałem/am swoje ciało pod wodą, wykonałem/am tzw. korek.', 407, ''),
(7401, 'Pokazałem/am podstawowe znaki nurkowe stosowane do komunikacji pod wodą.', 407, ''),
(7402, 'Założyłem/am maskę pod wodą i wydmuchałem/am z niej wodę.', 407, ''),
(7403, 'Przepłynąłem/przepłynęłam w płetwach dystans 50 metrów oddychając przez fajkę.', 407, ''),
(7404, 'Zdobyłem/am podstawowy stopień płetwonurka (OWD) w uznanej federacji nurkowej (PADI, PSAI, SSI, SDI, NAUI, CMAS)', 408, ''),
(7405, 'Zdobyłem/am stopień zaawansowanego płetwonurka (AOWD) w uznanej federacji nurkowej (PADI, PSAI, SSI, SDI, NAUI, CMAS)', 409, ''),
(7406, 'Opowiedziałem/am, co to jest wiatr.', 410, ''),
(7407, 'Pokazałem/am szkwał na jeziorze.', 410, ''),
(7408, 'Wyjaśniłem/am, jak powstaje rosa, deszcz i śnieg.', 410, ''),
(7409, 'Przed wycieczką zastępu/wachty wysłuchałem/am prognozy pogody w radio/ telewizji i na jej podstawie dobrałem/am odpowiednią odzież.', 410, ''),
(7410, 'Wytłumaczyłem/am, ile stopni Celsjusza oznacza ciepło, ile zimno.', 410, ''),
(7411, 'Narysowałem/am i wytłumaczyłem/am, na czym polega zjawisko bryzy nocnej i dziennej.', 411, ''),
(7412, 'Będąc na wycieczce, spływie lub rejsie rozpoznałem/am, z jaką siłą wieje wiatr w skali Beauforta.', 411, ''),
(7413, 'Zaobserwowałem/am chmurę cumulonimbus. Opowiedziałem/am, jakie niesie ona zagrożenia.', 411, ''),
(7414, 'Wskazałem/am cztery oznaki, na podstawie których można przewidzieć dobrą lub złą pogodę.', 411, ''),
(7415, 'Opowiedziałem/am, jaką wartość ma ciśnienie normalne oraz jakie wartości ma ciśnienie niskie i wysokie.', 411, ''),
(7416, 'Zaprezentowałem/am skalę Beauforta w zakresie od 0 do 7 stopni. W czasie zajęć nad wodą pięć razy wskazałem/am, z jaką siłą wieje wiatr.', 412, ''),
(7417, 'Posługiwałem/am się skalą stanu morza do określenia warunków na jeziorze.', 412, ''),
(7418, 'Rozpoznałem/am cztery rodzaje chmur na niebie. Opowiedziałem/am, kiedy te chmury występują.', 412, ''),
(7419, 'Omówiłem/am powstawanie i działanie podstawowych zjawisk atmosferycznych (szkwał, deszcz, mgła, wiatr).', 412, ''),
(7420, 'Wytłumaczyłem/am zasadę działania systemu ostrzegania meteorologicznego na Wielkich Jeziorach Mazurskich.', 412, ''),
(7421, 'Przed zajęciami sprawdziłem/am prognozę pogody w serwisie internetowym. Przekazałem/am ją wachcie/zastępowi.', 412, ''),
(7422, 'Zastosowałem/am numeryczne prognozy pogody z przynajmniej trzech serwisów.', 413, ''),
(7423, 'Przynajmniej raz ściągnąłem/ściągnęłam i zastosowałem/am griby.', 413, ''),
(7424, 'Na podstawie mapy synoptycznej przewidziałem/am pogodę na najbliższą dobę.', 413, ''),
(7425, 'Wysłuchałem/am ze zrozumieniem prognozy pogody w języku angielskim.', 413, ''),
(7426, 'Przynajmniej raz odebrałem/am i zanotowałem/am morską prognozę pogody dla rybaków i żeglarzy.', 413, ''),
(7427, 'Zaprezentowałem/am pełną skalę Beauforta.', 413, ''),
(7428, 'Znalazłem/am w swoim otoczeniu 10 produktów, które mogły dotrzeć do Polski drogą morską oraz nazwałem/am przynajmniej trzy gatunki ryb żyjących w Bałtyku.', 414, ''),
(7429, 'Rozpoznałem/am Banderę Marynarki Wojennej RP i Proporzec Marynarki Wojennej RP, prawidłowo zachowałem/am się w czasie podniesienia Flagi Polski lub Bandery ZHP.', 414, ''),
(7430, 'Wymieniłem/am i wskazałem/am na mapie przynajmniej dwa duże polskie porty morskie, dwa znane ośrodki wypoczynkowe i dwa porty rybackie.', 414, ''),
(7431, 'Nazwałem/am oznaczenia funkcji na statku stosowane w marynarce handlowej.', 414, ''),
(7432, 'Zaśpiewałem/am przynajmniej jedną pieśń morską.', 414, ''),
(7433, 'Opowiedziałem/am o przynajmniej pięciu zwyczajach i przesądach żeglarskich.', 414, ''),
(7434, 'Poznałem/am pochodzenie marynarskiego munduru. Wytłumaczyłem/am genezę kroju kołnierza, trzech pasków na kołnierzu, czarnych krawatów i wstążek na czapce, rozszerzanych spodni i sznurków na rozcięciach munduru.', 415, ''),
(7435, 'Poznałem/am historię Władysława Wagnera i jachtów Zjawa.', 415, ''),
(7436, 'Wiem, kim byli Mariusz Zaruski, Witold Bublewski i Bolesław Romanowski. Opowiedziałem/am o życiu i dokonaniach wybranej postaci związanej z harcerskim ruchem wodnym.', 415, ''),
(7437, 'Dowiedziałem/am się, jakie ryby poławiane są na Morzu Bałtyckim oraz jakie produkty przyjmują polskie porty. Byłem/am w minimum jednym polskim porcie morskim.', 415, ''),
(7438, 'Rozpoznałem/łam Banderę ZHP, Yacht Klubu Polski, Ligi Morskiej i Rzecznej oraz Jacht Klubu Marynarki Wojennej Kotwica. Rozpoznałem/am wygląd proporca drużynowego, retmana, pilota chorągwi i Naczelnika ZHP.', 415, ''),
(7439, 'Wybiłem/am prawidłowo szklanki na dzwonie ze wskazaniem godzin.', 415, ''),
(7440, 'Poznałem/am nazwy przynajmniej pięciu polskich żaglowców, pokazałem/am je na zdjęciach.', 415, ''),
(7441, 'Poznałem/am historię żaglowca Zawisza Czarny oraz Leonida Teligi i jego jachtu Opty.', 416, ''),
(7442, 'Poznałem/am położenie przynajmniej jednej polskiej platformy wiertniczej na Morzu Bałtyckim.', 416, ''),
(7443, 'Opowiedziałem/am przynajmniej jeden epizod z dziejów Polskiej Marynarki Wojennej w czasie II wojny światowej (np. ucieczka ORP Batory, internowanie i ucieczka ORP Orzeł, udział niszczycieli w Bitwie o Narwik, ochrona konwojów walki dywizjonu lotniczego z Pucka itp).', 416, ''),
(7444, 'Poznałem/am genezę i przebieg Bitwy pod Oliwą (1627).', 416, ''),
(7445, 'Dowiedziałem/am się, czym zajmuje się Stacja Morska na Helu i jakie są problemy ochrony fok i morświnów.', 416, ''),
(7446, 'Rozpoznałem/am różnicę pomiędzy szantą oraz piosenką żeglarską, zaśpiewałem/am cztery szanty i cztery pieśni kubryku.', 416, ''),
(7447, 'Prawidłowo zachowałem/am się wchodząc na jacht morski, prawidłowo oddałem/łam salut Banderze, opisałem/am wygląd Wielkiej i Małej Gali Flagowej.', 416, ''),
(7448, 'Przeczytałem/am przynajmniej jedną książkę Karola Borchardta.', 416, ''),
(7449, 'Poznałem/am najważniejsze porty Polski i ich rolę dla polskiej gospodarki. Poznałem/am podstawowe fakty o wybranym polskim porcie morskim (np. wartość przeładowanych towarów, ilość przeładowanych towarów, rodzaje towarów przeładowywanych w polskich portach). Przedstawiłem/am zdobyte informacje w ciekawej formie.', 417, ''),
(7450, 'Rozpoznałem/am bandery wszystkich państw i obszarów leżących nad Morzem Bałtyckim Wskazałem/am, gdzie jacht nosi banderę odwiedzanego kraju.', 417, ''),
(7451, 'Zgłębiłem/am problematykę występowania gatunków obcych w Morzu Bałtyckim i opowiedziałem/am o tych gatunkach i problemach z tym związanych swojemu zastępowi lub drużynie.', 417, ''),
(7452, 'Prowadziłem/am uroczystość podniesienia bandery na obozie lub rejsie, prawidłowo oddawałem/am honory Banderą.', 417, ''),
(7453, 'Brałem/am udział w rejsie lub odwiedziłem/am flagowy jacht ZHP s/y Zawisza Czarny.', 417, ''),
(7454, 'Za pomocą systemów opartych o AIS wskazałem/am, gdzie aktualnie znajduje się s/y Zawisza Czarny, STS Dar Młodzieży.', 417, ''),
(7455, 'Podczas żeglugi obsługiwałem/am szoty foka, balastowałem/am oraz odpowiadałem/am na komendy żeglarskie.', 418, ''),
(7456, 'Podczas żeglugi wyczuwałem/am wiatr, pokazywałem/am, skąd wieje, rozróżniałem/am halsy oraz bajdewind, półwiatr, baksztag i fordewind.', 418, ''),
(7457, 'Sterowałem/am na punkt oraz ostrzyłem/am i odpadałem/am na żaglach i w przód oraz wstecz na silniku, a następnie na podstawie obserwacji wyjaśniłem/am zasadę działania steru.', 418, ''),
(7458, 'Obsługiwałem/am cumę dziobową i rufową oraz odbijacze podczas manewrów portowych.', 418, ''),
(7459, 'Pagajowałem/am lub wiosłowałem/am na komendę w przód, wstecz i hamowałem/am pagajami lub wiosłami.', 418, ''),
(7460, 'Sterując awaryjnie, zatrzymałem/am jacht w linii wiatru.', 418, ''),
(7461, 'Podczas żeglugi obsługiwałem/am jednocześnie ster i szot grota oraz obsługiwałem/am szoty foka aktywnie, wybierając i luzując żagiel do granicy łopotu.', 419, ''),
(7462, 'Wyjaśniłem/am konieczność halsowania pod wiatr i określenia kąt martwy oraz zaobserwowałem/am zależność pomiędzy kursem względem wiatru, przechyłem jachtu a pozorną prędkością wiatru.', 419, ''),
(7463, 'Jachtem typu ket lub slup o długości kadłuba poniżej 6 metrów wykonałem/am skuteczne i bezpieczne zwroty na wiatr i z wiatrem.', 419, ''),
(7464, 'Jachtem o długości kadłuba poniżej 6 metrów sterowałem/am przy podejściu do kei.', 419, ''),
(7465, 'Jednocześnie sterując i obsługując silnik, wykonałem/am cyrkulację i porównałem/am promienie skrętu w lewo i w prawo z silnikiem ustawionym na wprost oraz ze skrętem silnika podążającym za sterem.', 419, ''),
(7466, 'Awaryjnie stanąłem/stanęłam jachtem w dryf oraz pełniłem/am funkcję obserwatora w czasie ćwiczenia alarmu „człowiek za burtą”.', 419, ''),
(7467, 'Podczas żeglugi kierowałem/am pracą załogi poprzez zapowiedź manewru, przydzielanie stanowisk i zadań oraz wydawanie i egzekwowanie komend we właściwym momencie.', 420, ''),
(7468, 'Porównałem/am przechył, prędkość i opór na sterze przed i po zarefowaniu żagli oraz wyjaśniłem/am innym siły działające na żagle, miecz i ster jachtu.', 420, ''),
(7469, 'Jachtem o długości kadłuba powyżej 5 metrów wykonałem/am skuteczne i bezpieczne manewry na żaglach: zwroty przez sztag i rufę, dojście i odejście od boi, żegluga na biegu wstecznym.', 420, ''),
(7470, 'Jachtem o długości kadłuba powyżej 5 metrów wykonałem/am skuteczne i bezpieczne manewry portowe: zatrzymania i dryfowania w basenie portowym (lub ograniczonym akwenie), podejścia rufą pod wiatr równolegle i prostopadle do kei.', 420, ''),
(7471, 'Jachtem o długości kadłuba powyżej 5 metrów wykonałem/am skuteczne i bezpieczne manewry na silniku: kotwiczenia i podejścia do boi w różnych warunkach.', 420, ''),
(7472, 'Jachtem o długości kadłuba powyżej 5 metrów wykonałem/am próbne manewry awaryjne: stanięcie w dryf oraz podejście do człowieka za burtą.', 420, ''),
(7473, 'Podczas żeglugi kierowałem/am pracą załogi poprzez omówienie manewru planowego, zapasowego i awaryjnego, przydzielanie stanowisk i zadań oraz ustalenie zasad komunikacji w trakcie.', 421, ''),
(7474, 'Wyjaśniłem/am zasady optymalnego ustawienie żagli do panujących warunków oraz zademonstrowałem/am prawidłowy trym w różnych kursach względem wiatru, wskazując wiatr rzeczywisty i pozorny.', 421, ''),
(7475, 'Jachtem kabinowym o długości kadłuba powyżej 7,5 metrów wykonałem/am skuteczne i bezpieczne manewry na żaglach: zwroty przez sztag i rufę, dojście i odejście od boi, żegluga na biegu wstecznym.', 421, ''),
(7476, 'Jachtem kabinowym o długości kadłuba powyżej 7,5 metrów wykonałem/am skuteczne i bezpieczne manewry portowe: wejście między Y bomy rufą i dziobem do kei, cumowanie rufą do kei z użyciem mooringu lub boi.', 421, ''),
(7477, 'Jachtem mieczowym wykonałem/am skuteczny i bezpieczny manewr sztrandowania w celu postoju przy dzikim brzegu.', 421, ''),
(7478, 'Jachtem o długości kadłuba powyżej 7,5 metrów wykonałem/am próbne manewry awaryjne: stanięcie w dryf oraz podejście do człowieka za burtą na żaglach i na silniku.', 421, ''),
(7479, 'Zawiązałem/am węzły: knagowy, ósemka, płaski, ratowniczy przed sobą i na sobie oraz zbuchtowałem/am linę.', 422, ''),
(7480, 'Sklarowałem/am żagle przed i po żeglowaniu.', 422, ''),
(7481, 'Wskazałem/am i nazwałem/am elementy jachtu: dziób, rufa, burty, grot, fok, maszt, szoty, bom, miecz, rumpel.', 422, ''),
(7482, 'Zmieniałem/am obroty i biegi oraz odstawiłem/am (wyłączyłem/am) silnik zaburtowy.', 422, ''),
(7483, 'Zawiązałem/am węzły: rożkowy, wyblinka, cumowy żeglarski oraz z zakrytymi oczami zawiązałem/am węzły: knagowy, ósemka, płaski.', 423, ''),
(7484, 'Samodzielnie przygotowałem/am mały jacht do żeglugi i sklarowałem/am po pływaniu — zarefowałem/am i rozrefowałem/am żagiel, użyłem/am cum i szpringów oraz założyłem/am odbijacze tak, żeby skutecznie pracowały.', 423, ''),
(7485, 'Wskazałem/am i nazwałem/am elementy jachtu: kokpit, skrzynka mieczowa, sztagi, wanty, pieski, kabestan.', 423, ''),
(7486, 'Samodzielnie sprawdziłem/am poziom oleju w silniku benzynowym czterosuwowym lub Diesla oraz asystowałem/am podczas tankowania paliwa do zbiornika.', 423, ''),
(7487, 'Nauczyłem/am inną osobę wiązać węzły z zakresu pierwszej gwiazdki (jungi) oraz wykonałem/am opaskę z juzingu na końcówce liny miękkiej.', 424, ''),
(7488, 'Wraz z załogą przesunąłem/przesunęłam i obróciłem/am na cumach jacht przy kei. Zabezpieczyłem/am łódź zacumowaną wzdłuż kei (alongside) przy wietrze dopychającym z użyciem cum, szpringów i odbijaczy.', 424, ''),
(7489, 'Wyjaśniałem/am załodze zasady bezpiecznego korzystania z instalacji jachtowych oraz asystowałem/am przy wymianie akumulatora i butli gazowej na jachcie.', 424, ''),
(7490, 'Zademonstrowałem/am załodze czynności przy uruchamianiu i odstawianiu silnika zaburtowego oraz zatankowałem/am odpowiedni rodzaj paliwa do zbiornika jachtu.', 424, ''),
(7491, 'Wraz z załogą położyłem/am i postawiłem/am maszt.', 424, ''),
(7492, 'Wyjaśniłem/am zasady użycia oraz nauczyłem/am inną osobę wiązać węzły z zakresu pierwszej i drugiej gwiazdki oraz samodzielnie wykonałem/am dodatkowe prace linowe, np. opaskę turecką, kulkę bosmańską, sploty na linach miękkich.', 425, '');
INSERT INTO `zadaniasprawnosciharcerskich` (`ID`, `Tresc`, `Sprawnosc`, `Stopien`) VALUES
(7493, 'Wyjaśniałem/am, demonstrowałem/am czynności oraz kierowałem/am pracą załogi podczas przygotowania jachtu do żeglugi i postoju.', 425, ''),
(7494, 'Samodzielnie wymieniłem/am butlę gazową i akumulator na jachcie oraz podłączyłem/am akumulator do ładowania z lądu lub na lądzie.', 425, ''),
(7495, 'Wyjaśniłem/am i zademonstrowałem/am sprawdzanie oleju, paliwa i chłodzenia w silniku zaburtowym lub stacjonarnym oraz samodzielnie wykręciłem/am świecę w silniku benzynowym w celu sprawdzenia iskry.', 425, ''),
(7496, 'Uczestniczyłem/am w taklowaniu jachtu przed pierwszym pływaniem lub w zabezpieczeniu jachtu po sezonie, lub w slipowaniu/wodowaniu jachtu.', 425, ''),
(7497, 'Wskazałem/am z pomocą kompasu północ i południe oraz wschód i zachód.', 426, ''),
(7498, 'Wskazałem/am na mapie i opowiedziałem/am o zbiornikach wodnych w mojej okolicy.', 426, ''),
(7499, 'Odszukałem/am na mapie nazwę cieku lub rzeki, która przepływa w mojej najbliższej okolicy oraz sprawdziłem/am, dokąd ona płynie i do której zlewni należy.', 426, ''),
(7500, 'Zaplanowałem/am podróż do innego miasta środkami komunikacji publicznej za pomocą internetowego serwisu mapowego (Google Maps, Jak Dojadę, itp.).', 426, ''),
(7501, 'Podczas wędrówki obejrzałem/am budowlę hydrotechniczną: śluzę, tamę lub jaz.', 426, ''),
(7502, 'Wyznaczyłem/am azymut na mapie i w terenie, posługując się kompasem magnetycznym.', 427, ''),
(7503, 'Ustaliłem/am współrzędne geograficzne swojego domu/namiotu oraz kilku wybranych punktów w mojej okolicy przy użyciu odbiornika nawigacji satelitarnej (np. aplikacja GPS w telefonie).', 427, ''),
(7504, 'Zorientowałem/am mapę papierową na podstawie obiektów w terenie.', 427, ''),
(7505, 'Podczas poruszania się po wodzie wskazałem/am obiekty na brzegu na podstawie mapy lub opisu.', 427, ''),
(7506, 'Wskazałem/am na mapie jezioro rynnowe, morenowe i sztuczny zalew oraz omówiłem/am ich cechy istotne dla żeglarzy.', 427, ''),
(7507, 'W aplikacji lub odbiorniku nawigacji satelitarnej (GPS) zapisałem/am przebytą drogę podczas wędrówki. Wyświetliłem/am na mapie cyfrowej, sprawdziłem/am i omówiłem/am kompletność oraz dokładność swojego śladu.', 428, ''),
(7508, 'Zorientowałem/am się w przestrzeni i wskazałem/am kierunek północny na podstawie położenia słońca i gwiazd.', 428, ''),
(7509, 'Zmierzyłem/am drogę na papierowej mapie turystycznej oraz, znając swoją prędkość, obliczyłem/am czas dotarcia do celu (ETA).', 428, ''),
(7510, 'Podczas rejsu lub spływu korzystałem/am z przewodników (locji) i opisów dróg wodnych, portów itp.', 428, ''),
(7511, 'Omówiłem/am elementy regulujące nurt rzeki: opaska, ostroga, tama równoległa oraz żółte romby na przęsłach mostu.', 428, ''),
(7512, 'W aplikacji lub chartploterze do nawigacji jachtowej ustawiłem/am i aktywowałem/am punkty (waypoints) oraz trasę (route) żeglugi.', 429, ''),
(7513, 'Określiłem/am pozycję jachtu na papierowej mapie morskiej na podstawie obserwacji dwóch punktów oraz na podstawie obserwacji jednego punktu i pomiaru głębokości.', 429, ''),
(7514, 'Wyznaczyłem/am na papierowej mapie morskiej pozycję jachtu na podstawie kursu, prędkości, czasu itd. od ostatniej pozycji obserwowanej.', 429, ''),
(7515, 'Przeczytałem/am w locji opisy i obejrzałem/am na mapie podejścia do trzech portów morskich i zatokowych w Polsce. Zrobiłem/am notatki, na podstawie których opowiedziałem/am innym o każdym z nich.', 429, ''),
(7516, 'Podczas żeglugi zidentyfikowałem/am na mapie mijane i widoczne obiekty na wodzie i na brzegu.', 429, ''),
(7517, 'Rozróżniałem/am napędy mijanych jednostek na wodzie i stosowałem/am prawo drogi: (1) żagle (2) wiosła (3) silnik.', 430, ''),
(7518, 'Dowiedziałem/am się, jak mijają się dwie łodzie wiosłowe płynące z przeciwka, aby uniknąć zderzenia (lewymi burtami).', 430, ''),
(7519, 'Rozpoznałem/am na wodzie „duże” statki, który mają prawo drogi przed jednostkami rekreacyjnymi.', 430, ''),
(7520, 'Zwracałem/am uwagę na mijane znaki na drodze wodnej i dowiedziałem/am się, co one oznaczają.', 430, ''),
(7521, 'Śmieci powstające na jachcie podczas żeglugi zabrałem/am do odpowiedniego śmietnika na lądzie.', 430, ''),
(7522, 'Zastosowałem/am na wodzie zasadę „wyprzedany ma prawo drogi przed wyprzedającym”.', 431, ''),
(7523, 'Ustaliłem/am hals mijanych jachtów na żaglach. Zastosowałem/am odpowiednio zasadę „prawy hals przed lewym halsem”.', 431, ''),
(7524, 'W trakcie żeglugi regularnie zaglądałem/am pod żagiel, aby sprawdzić, czy po zawietrznej znajduje się jacht, któremu należy ustąpić drogi.', 431, ''),
(7525, 'Płynąc na silniku, przy spotkaniu z motorówką, zastosowałem/am zasadę „prawej strony” do ustalenia prawa drogi.', 431, ''),
(7526, 'Opowiedziałem/am, jakie są prawne ograniczenia prowadzenia jachtów żaglowych i motorowych po wodach śródlądowych bez patentu.', 431, ''),
(7527, 'Wyjaśniłem/am negatywny wpływ odpadków spożywczych (biodegradowalnych) oraz innych śmieci na środowisko wodne.', 431, ''),
(7528, 'Prowadziłem/am jacht pod żaglami przy spotkaniu z innym jachtem oraz przy wyprzedzaniu.', 432, ''),
(7529, 'Prowadziłem/am jacht żaglowy na silniku przy spotkaniu z innym jachtem oraz przy wyprzedzaniu.', 432, ''),
(7530, 'Wyjaśniłem/am znaczenie czerwonego, zielonego lub białego światła poruszającego się nocą po wodzie.', 432, ''),
(7531, 'Rozpoznałem/am typ oraz znaczenie wskazanych znaków żeglugowych.', 432, ''),
(7532, 'Dowiedziałem/am się, jakie uprawnienia na wodach śródlądowych posiadają posiadacze patentów żeglarskich.', 432, ''),
(7533, 'Zdałem/am śmieci z jachtu na ląd oraz zabezpieczyłem/am używane i zużyte płyny (paliwo, oleje, rozpuszczalniki, farby itp.) przed przedostaniem się do wody.', 432, ''),
(7534, 'Podczas żeglugi oceniłem/am ryzyko zderzenia na podstawie obserwacji namiaru burtowego oraz podjąłem/podjęłam działanie zgodne z prawidłami MPZZM (colreg).', 433, ''),
(7535, 'Podczas żeglugi prowadzałem/am obserwację m.in. znaków i sieci, innych jednostek, śmieci w wodzie, głębokości oraz nasłuch radiowy.', 433, ''),
(7536, 'Wyjaśniłem/am, jak rozpoznać statek: (1) nieodpowiadający za swoje ruchy, (2) wyprzedzany, (3) o ograniczonej zdolności manewrowej, (4) ograniczony swoim zanurzeniem, (5) zajęty połowem, (6) żaglowy i o napędzie mechanicznym w dzień i w nocy.', 433, ''),
(7537, 'Wyjaśniłem/am różnice pomiędzy oznaczeniem kierunku oznakowania szlaku żeglownego na wodach śródlądowych i morskich.', 433, ''),
(7538, 'Dowiedziałem/am się, jakie uprawnienia na wodach morskich posiadają posiadacze patentów żeglarskich.', 433, ''),
(7539, 'Przed żeglowaniem wyjaśniłem/am oraz podczas rejsu nadzorowałem/am przestrzegania zasad ochrony środowiska przez załogę.', 433, ''),
(7540, 'Poznałem/am zasady bezpieczeństwa panujące na jachcie motorowodnym.', 434, ''),
(7541, 'Poznałem/am podstawową budowę łodzi motorowodnej oraz małego silnika zaburtowego.', 434, ''),
(7542, 'Zawiązałem/am 10 węzłów.', 434, ''),
(7543, 'Przepłynąłem/am łodzią o napędzie motorowym jako załogant.', 434, ''),
(7544, 'Poznałem/am szczegółową budowę łodzi motorowodnej oraz zasady działania silnika dwu- i czterosuwowego.', 435, ''),
(7545, 'Przygotowałem/am silnik do uruchomienia. Sklarowałem/am go po pływaniu.', 435, ''),
(7546, 'Sprawdziłem/am poziom oleju i stan paliwa w silniku.', 435, ''),
(7547, 'Korzystając z pomocy innych, prawidłowo zamontowałem/am silnik przyczepny na pawęży/pantografie.', 435, ''),
(7548, 'Poznałem/am budowę mocniejszych silników zaburtowych oraz stacjonarnych, zasady ich działania i metody przenoszenia napędu.', 436, ''),
(7549, 'Przygotowałem/am samodzielnie łódź i silnik do wypłynięcia. Sklarowałem/am i zabezpieczyłem/am go po pływaniu.', 436, ''),
(7550, 'Wykonałem/am prostą naprawę lub konserwację silnika zaburtowego lub jego elementu.', 436, ''),
(7551, 'Nauczyłem/am inną osobę budowy małego silnika zaburtowego.', 436, ''),
(7552, 'Oceniłem/am stan techniczny pędnika, mechanizmu przenoszenia napędu oraz mechanizm sterujący. Wymieniłem/am uszkodzony pędnik w silniku stacjonarnym.', 437, ''),
(7553, 'Samodzielnie wymieniłem/am olej, filtr paliwa i oleju w silniku stacjonarnym.', 437, ''),
(7554, 'Dokonałem/am zabiegów regulacyjnych silnika.', 437, ''),
(7555, 'Kontrolowałem/am i oceniłem/am pracę silnika, poziom ciśnienia oleju, działanie układu chłodniczego oraz ładowania akumulatora.', 437, ''),
(7556, 'Prawidłowo uruchomiłem/am mały silnik zaburtowy.', 438, ''),
(7557, 'Byłem/am załogantem na łodzi motorowodnej.', 438, ''),
(7558, 'Poznałem/am budowę łodzi motorowodnej.', 438, ''),
(7559, 'Trzymałem/am kurs i zmieniałem/am biegi.', 438, ''),
(7560, 'Prowadziłem/am jacht motorowy, gdy na akwenie znajdowały się też inne jednostki.', 439, ''),
(7561, 'Wykonałem/am manewry: odejście od nadbrzeża, dojście do nadbrzeża, alarm „człowiek za burtą”, kotwiczenia.', 439, ''),
(7562, 'Poznałem/am prawo drogi, szczególnie dotyczące jachtów motorowych. Stosowałem/am zasady podczas pływania.', 439, ''),
(7563, 'Poznałem/am niebezpieczeństwa, które mogą pojawić się na łódce oraz zapobiegałem/am im.', 439, ''),
(7564, 'Poznałem/am prawo panujące na wodach śródlądowych - oznaczenie szlaków żeglownych, podstawy locji, sygnały dźwiękowe. Wskazałem/am kilka znaków i boi oraz opowiedziałem/am, co one oznaczają.', 440, ''),
(7565, 'Brałem/am udział w akcji ratunkowej z wykorzystaniem łodzi motorowodnej.', 440, ''),
(7566, 'W trudnych warunkach pogodowych wykonałem/am manewry: odejście od nadbrzeża, dojście do nadbrzeża, alarm „człowiek za burtą”, kotwiczenia, holowania.', 440, ''),
(7567, 'Kierowałem/am załogą jachtu motorowego, wydawałem/am komendy i egzekwowałem/am ich wykonanie we właściwym momencie.', 440, ''),
(7568, 'Pełniłem/am funkcję osoby odpowiedzialnej za maszynownię i instalacje jachtowe w czasie rejsu morskiego lub śródlądowego.', 441, ''),
(7569, 'Manewrowałem/am jachtem motorowym o długości większej niż 8 metrów, wykonałem/am manewry podejścia i odejścia od kei, cyrkulacji, człowiek za burtą.', 441, ''),
(7570, 'Sterowałem/am jachtem wyposażonym w napęd motorowy za pomocą kierownicy lub koła sterowego.', 441, ''),
(7571, 'Odbyłem/am dwa rejsy po wodach morskich w łącznym czasie co najmniej 200 godzin żeglugi.', 441, ''),
(7572, 'Uczestniczyłem/am w co najmniej jednym spływie kajakowym.', 442, ''),
(7573, 'W czasie spływów zachowałem/am się bezpiecznie, zawsze używałem/am kamizelki.', 442, ''),
(7574, 'Wsiadłem/am do kajaka z brzegu i/lub z pomostu/kei.', 442, ''),
(7575, 'Wskazałem/am i nazwałem/am części kajaka - dziób, rufę, prawą i lewą burtę oraz kokpit.', 442, ''),
(7576, 'Bezpiecznie pokonałem/am przeszkodę podczas spływu.', 442, ''),
(7577, 'Potrafię pływać. Zdobyłem/am kartę pływacką.', 442, ''),
(7578, 'Uczestniczyłem/am w co najmniej 10 spływach kajakowych na różnych akwenach w tym co najmniej w dwóch wielodniowych.', 443, ''),
(7579, 'Wiem, co to jest kabina. Zaprezentowałem/am, jak ją wykonać.', 443, ''),
(7580, 'Opowiedziałem/am, jak pomóc wywróconemu kajakowi, holowałem/am kajakarza na dziobie swojego kajaka.', 443, ''),
(7581, 'Pełniłem/am funkcję osoby zamykającej spływ lub prowadzącej.', 443, ''),
(7582, 'Wziąłem/wzięłam udział w spływie w charakterze obsługi spływu.', 443, ''),
(7583, 'Napisałem/am plan spływu. Zrobiłem/am to w oparciu o przepisy państwowe jak i związkowe.', 443, ''),
(7584, 'Brałem/am udział w zwiadzie jeziora lub stawu (wody stojącej).', 444, ''),
(7585, 'Rozpoznałem/am pospolite okazy flory i fauny wodnej (np. trzcina, tatarak, moczarka kanadyjska, kaczka krzyżówka, rybitwa).', 444, ''),
(7586, 'Rozpoznałem/am na ilustracji lub w terenie zaporę, śluzę, jaz i opisałem/am do czego służą.', 444, ''),
(7587, 'Scharakteryzowałem/am różnice między kanałem a rzeką. Przedstawiłem/am przykład kanału i rzeki.', 444, ''),
(7588, 'Wskazałem/am na mapie zbiorniki wodne znajdujące się w okolicy miejsca zamieszkania.', 444, ''),
(7589, 'Rozpoznałem/am na ilustracji lub w terenie faszynę, opaskę, ostrogę i opisałem/am do czego służą.', 445, ''),
(7590, 'Narysowałem/am z pamięci mapę konturową Polski i naniosłem/am na nią najważniejsze rzeki, kanały, jeziora, zalewy, porty śródlądowe i morskie.', 445, ''),
(7591, 'Przedstawiłem/am definicje dorzecza, działu wodnego, zlewiska. Podałem/am kilka przykładów rzek Polski z przyporządkowaniem do odpowiednich dorzeczy.', 445, ''),
(7592, 'Rozpoznałem/am trzy chronione gatunki roślin wodnych, np. grążel żółty, grzybienie białe, kosaciec syberyjski.', 445, ''),
(7593, 'Podałem/am klasyfikację rzek Polski pod względem malowniczości wraz z przykładami.', 445, ''),
(7594, 'Przeprowadziłem/am zwiad rzeki na długości 100 metrów, oceniając napotkane trudności i przedstawiając bezpieczne sposoby ich pokonania.', 446, ''),
(7595, 'Przedstawiłem/am na schemacie lub modelu budowę rzeki. Poprawnie rozpoznałem/am zjawiska zachodzące na powierzchni rzeki (bystrze, mielizna itp.).', 446, ''),
(7596, 'Przedstawiłem/am zasadę działania śluzy oraz zasady obowiązujące podczas śluzowania.', 446, ''),
(7597, 'Scharakteryzowałem/am klasyfikację trudności rzek.', 446, ''),
(7598, 'Scharakteryzowałem/am rzeki, jeziora, zbiorniki wodne z terenu swojego powiatu pod kątem walorów turystycznych.', 446, ''),
(7599, 'Przedstawiłem/em zasady organizacji spływu i przepisy obowiązujące podczas spływu.', 447, ''),
(7600, 'Opracowałem/am trasę spływu, uwzględniając wiek i umiejętności uczestników.', 447, ''),
(7601, 'Wytłumaczyłem/am pojęcia: zlewnia, zlewisko, dorzecze, dział wodny, ciek wodny, rzeka uregulowana/skanalizowana, rzeka spławna/żeglowna, spadek/spad rzeki, rzeka stała/okresowa/epizodyczna, rzeka wodospadowa/górska/nizinna, melioracja, irygacja, retencja, eutrofizacja.', 447, ''),
(7602, 'Scharakteryzowałem/am szlaki wodne swojego województwa pod względem stopnia trudności, klasy czystości oraz walorów turystycznych.', 447, ''),
(7603, 'Sporządziłem/am listę niezbędnego ekwipunku spływowego, wyposażyłem/am skrzynkę bosmańską na spływ.', 447, ''),
(7604, 'Wziąłem/wzięłam udział w przynajmniej kilkugodzinnej wycieczce wodnej jako wioślarz na kajaku, kanadyjce lub czółnie (np. wzdłuż brzegu jeziora albo na łatwej rzece).', 448, ''),
(7605, 'Brałem/am udział w regatach na kanadyjce lub łodzi wiosłowej.', 448, ''),
(7606, 'Zamieniłem/am się miejscem ze sternikiem nie wychodząc z łodzi.', 448, ''),
(7607, 'Bezpiecznie przeszedłem/przeszłam z łódki do łódki na wodzie.', 448, ''),
(7608, 'Wziąłem/wzięłam udział w kilkudniowym spływie jako sternik łodzi wiosłowej lub kanadyjki na rzece o niskim stopniu trudności.', 449, ''),
(7609, 'Uczestniczyłem/am w ćwiczeniach z wywrotek kanadyjek. Przygotowałem/am wywróconą łódź do pływania.', 449, ''),
(7610, 'Wsiadłem/am do kanadyjki na płytkiej i głębokiej wodzie.', 449, ''),
(7611, 'Wziąłem/wzięłam udział w trwającym min. tydzień spływie na rzece o średnim stopniu trudności z min. jedną przenoską.', 450, ''),
(7612, 'Zarządzałem/am załogą podczas przenoski na spływie.', 450, ''),
(7613, 'Dowodziłem/am czółnem na rzece.', 450, ''),
(7614, 'Zorganizowałem/am biwak dla zastępu na spływie, pamiętając o poprawnym rozbiciu namiotu, zabezpieczeniu sprzętu kuchennego i żywności, klarowaniu i zabezpieczeniu łodzi.', 450, ''),
(7615, 'Dowodziłem/am patrolem podczas spływu. Przygotowałem/am niezbędne dokumenty, zatwierdziłem/am spływ w komendzie hufca.', 451, ''),
(7616, 'Wziąłem/wzięłam udział w spływie rzeką o charakterze górskim.', 451, ''),
(7617, 'Zorganizowałem/am regaty wioślarskie dla drużyny.', 451, ''),
(7618, 'Brałem/am udział w wodowaniu łodzi i wyciąganiu jej na brzeg.', 452, ''),
(7619, 'Sklarowałem/am łódź przed i po pływaniu.', 452, ''),
(7620, 'Skompletowałem/am wyposażenie łodzi do pływania (odpowiednie wiosła, kamizelki, klarszmata, cuma).', 452, ''),
(7621, 'Zawiązałem/am węzły: ósemka, ratowniczy, cumowniczy.', 452, ''),
(7622, 'Poprawnie wskazałem/am nazwy części łodzi wiosłowej (dziób, rufa, prawa i lewa burta, dno, kokpit, bakista/luk). Nazwałem/am osprzęt jachtowy używany na kajaku lub kanadyjce (ucho cumownicze, kipa, knaga).', 452, ''),
(7623, 'Dowodziłem/am zastępem podczas wodowania i wyciągania łodzi.', 453, ''),
(7624, 'Uczestniczyłem/am w zabezpieczeniu sprzętu pływającego na zimę.', 453, ''),
(7625, 'Dokonałem/am przeglądu pagajów/wioseł. Pod okiem bosmana naprawiłem/am zauważone uszkodzenia – szlifowałem/am, lakierowałem/am.', 453, ''),
(7626, 'Zbuchtowałem/am linę, zakończyłem/am linę opaską.', 453, ''),
(7627, 'Zastosowałem/am węzły: ósemkę, sztyk, żeglarski, rybacki, kotwiczny, knagowy, wyblinkę, flagowy, rożkowy i ratowniczy.', 453, ''),
(7628, 'Uczestniczyłem/am w naprawach łodzi laminatowych – czyściłem/am, szlifowałem/am, malowałem/am elementy kadłuba pod nadzorem.', 454, ''),
(7629, 'Posługiwałem/am się piłami ręcznymi, tarnikiem, pilnikiem, dłutem, heblem, wiertarką, szlifierką kątową i oscylacyjną podczas zbiórki bosmańskiej.', 454, ''),
(7630, 'Podczas prac szkutniczych stosowałem/am zasady BHP i odpowiednią odzież ochronną (rękawice, maseczka).', 454, ''),
(7631, 'Opracowałem/am sposób doraźnej naprawy kajaka lub kanadyjki na spływie. Przygotowałem/am skrzynkę bosmańską z niezbędnym sprzętem, który zabrałem/am na spływ.', 454, ''),
(7632, 'Samodzielnie dokonałem/am naprawy uszkodzenia łodzi laminatowej – oczyściłem/am uszkodzone miejsce, laminowałem/am, szlifowałem/am łatę, malowałem/am powierzchnię, odpowiednio zadbałem/am o narzędzia.', 455, ''),
(7633, 'Opracowałem/am zasady BHP podczas posługiwania się narzędziami tradycyjnymi i elektrycznymi.', 455, ''),
(7634, 'Samodzielnie wykonałem/am pagaj/wiosło.', 455, ''),
(7635, 'Poprawnie wiosłowałem/am pagajem na czółnie lub kanadyjce lub wiosłem na kajaku i odpowiednio reagowałem/am na komendy sternika', 456, ''),
(7636, 'Wsiadłem/am i wysiadłem/am z/do kanadyjki lub kajaka z brzegu i pomostu.', 456, ''),
(7637, 'Odbiłem/am i dobiłem/am do brzegu oraz podawałem/am cumę na wodzie stojącej.', 456, ''),
(7638, 'Wraz z zastępem zbudowałem/am tratwę z materiałów, które są pod ręką. Wprawiłem/am tratwę w ruch i pokonałem/am na niej co najmniej 50 metrów.', 456, ''),
(7639, 'Pokonałem/am tor slalomowy na wodzie stojącej, płynąc jako sternik kajaka lub kanadyjki.', 457, ''),
(7640, 'Odbiłem/am i dobiłem/am do brzegu w wybranym miejscu na rzece.', 457, ''),
(7641, 'Poprawnie wydawałem/am wioślarzowi komendy na wiosła oraz podczas odchodzenia i podchodzenia do kei.', 457, ''),
(7642, 'Poprawnie wiosłowałem/am dwoma wiosłami w dulkach (na bączku lub wędkarskiej lub szalupie ratowniczej).', 457, ''),
(7643, 'Przeprawiłem/am się przez rzekę lub zatokę jeziora na belce, zabezpieczając ubrania przed zamoknięciem.', 457, ''),
(7644, 'Wiosłowałem/am długim wiosłem w dulce na wydrze, trenerze, DZ lub smoczej łodzi.', 458, ''),
(7645, 'Pokonałem/am wyznaczoną trasę śrubkując z rufy do przodu i wstecz.', 458, ''),
(7646, 'Poprawnie piórkowałem/am z burty.', 458, ''),
(7647, 'Wiosłowałem/am \"na pych\" wiosłem pychowym lub żerdzią.', 458, ''),
(7648, 'Płynąłem/płynęłam w szyku podczas defilady.', 458, ''),
(7649, 'Dowodziłem/am wioślarzami na szalupie. Poprawnie wydawałem/am komendy wioślarzom i sternikowi (wiosła, wiosła basta, naprzód, prawa naprzód, lewa wstecz) itd.', 459, ''),
(7650, 'Wiosłowałem/am sposobem rybackim \"z ręki\" jednym wiosłem.', 459, ''),
(7651, 'Wykonałem/am na łodzi wiosłowej podejście do człowieka za burtą. Podjąłem/podjęłam człowieka na pokład.', 459, ''),
(7652, 'Zorganizowałem/am naukę wiosłowania pagajem z burty, jednym i dwoma wiosłami w dulkach, piórkowania, śrubkowania, wiosłowania na pych.', 459, ''),
(7653, 'Wytłumaczyłem/am młodszym i bezbłędnie zademonstrowałem/am elementy prowadzenia łodzi spływowej (dowodzenie, służba oka, promowanie, jeleni skok, przejście przez bystrze, przejście przez przeszkodę, przenoska, dobicie do brzegu, zachowanie po wywrotce).', 459, ''),
(7654, 'Opowiedziałem/am 5 legendarnych i baśniowych opowieści lotniczych. Wykazałem/am się wiedzą, z jakiego kraju i okresu pochodzą. Wskazałem/am wydawnictwa, w których są zamieszczone.', 460, ''),
(7655, 'Opowiedziałem/am trzy życiorysy ludzi lotnictwa. Wykazałem/am się wiedzą, w jakim celu opowiada się gawędy, i jak mogą być one realizowane przez harcerzy, przestawiłem/am różne formy gawęd.', 460, ''),
(7656, 'Wyszukałem/am w książkach lub czasopismach trzy fragmenty, które następnie przeczytałem/am jako gawędy i wskazałem/am ich główne wątki. Wykazałem/am się umiejętnością płynnego czytania oraz recytowania wierszy.', 460, ''),
(7657, 'Wykorzystałem/am swe umiejętności gawędziarskie podczas zajęć lotniczych w drużynie lub zastępie albo dla popularyzacji lotnictwa wśród harcerzy lub kolegów i koleżanek spoza ZHP.', 460, ''),
(7658, 'Prowadziłem/am notatnik z informacjami, w jakich wydawnictwach (książkach, czasopismach) można znaleźć materiały do gawęd ukazujące cechy potrzebne lotnikowi (silna wola, wytrwałość, dzielność, systematyczność, dokładność, koleżeńskość, odwaga).', 460, ''),
(7659, 'Przekazałem/am swoją wiedzę o lotnictwie w inny sposób niż gawęda tj. napisałem/am artykuł do gazetki, wykonałem/am plansze, kilka ilustracji na określony temat.', 460, ''),
(7660, 'Objaśniłem/am lotniczą treść swego zbioru.', 461, ''),
(7661, 'Wykazałem/am się wiedzą, gdzie i jakie zbiory lotnicze tego rodzaju można obejrzeć oraz gdzie zrzeszeni są ich zbieracze.', 461, ''),
(7662, 'Prawidłowo, według ustalonego systemu (porządku) prowadziłem/am swoją kolekcję lotniczą. Wyjaśniłem/am przyjętą zasadę porządkowania zbioru.', 461, ''),
(7663, 'Znalazłem/am źródła informacji dotyczących samolotów przedstawionych na eksponatach zawartych w zbiorze.', 461, ''),
(7664, 'Zebrałem/am ponad 50 eksponatów do swego zbioru lotniczego i prawidłowo je przechowuję (np. prowadząc albumy, teczki, kartotekę).', 461, ''),
(7665, 'Opowiedziałem/am na zbiórce zastępu czy drużyny o najciekawszych eksponatach ze zbioru i dziejach samolotów, które te eksponaty przedstawiają.', 461, ''),
(7666, 'Zorganizowałem/am pokaz zbioru dla kolegów i koleżanek.', 461, ''),
(7667, 'Przeczytałem/am kilka publikacji (książek lub artykułów) z wybranej dziedziny awiatyki.', 462, ''),
(7668, 'Wykazałem/am się znajomością oznakowania i kolorystyki samolotów oraz znajomością odznak lotniczych zgodnie z zasadami estetyki, perspektywy i kompozycji kształtów i barw, a zarazem poprawnie z punktu widzenia techniki lotniczej.', 462, ''),
(7669, 'Wykonałem/am lub zaprojektowałem/am awiatyk (oprócz ilustracji).', 462, ''),
(7670, 'Wykonałem/am 5 projektów lub przedmiotów z wybranej dziedziny awiatyki', 462, ''),
(7671, 'Zebrałem/am kolekcję z wybranej dziedziny awiatyki i sporządziłem/am album, kartotekę (lub Inny rodzaj zbiorów) oraz uzasadniłem/am przyjęty podział zbioru.', 462, ''),
(7672, 'Urządziłem/am wystawę (np. dla drużyny) swych prac lub zbioru, bądź zorganizowałem/am konkurs na temat awiatyki.', 462, ''),
(7673, 'Wykazałem/am się znajomością w zarysie polskiej i międzynarodowej historii lotnictwa: legendy lotnicze, pierwsze próby i pierwsze wzloty, pierwsze sukcesy i współczesne rekordy, rozwój baloniarstwa, spadochroniarstwa, szybownictwa, lotnictwa silnikowego i pocisków rakietowych.', 463, ''),
(7674, 'Rozpoznałem/am najważniejsze historyczne maszyny latające polskie i zagraniczne.', 463, ''),
(7675, 'Zorganizowałem/am pogadankę o dowolnie wybranych postaciach i faktach okresu legendarnego lub pionierskiego albo z tradycji polskiego lotnictwa. Podkreśliłem/am cechy charakteru osób, o których opowiadałem/am.', 463, ''),
(7676, 'Zebrałem/am wiadomości i ilustracje z historii lotnictwa. Prowadziłem/am album poświęcony twórcom lotnictwa, wybranym jego bohaterom lub konstruktorom albo wykonałem/am model samolotu historycznego.', 463, ''),
(7677, 'Zorganizowałem/am wśród kolegów grę z historii lotnictwa (np. obrazkowe kino, konkurs wiedzy).', 463, ''),
(7678, 'Przeczytałem/am, co najmniej trzy książki z działu historii lotnictwa.', 463, ''),
(7679, 'Rozpoznałem/am różne rodzaje latawców. Wykazałem/am się wiedzą, jaka jest konstrukcja każdego z nich i jakie miały zastosowanie dawniej i dziś.', 464, ''),
(7680, 'Wyjaśniłem/am, jakie siły działają na latawiec w locie i od czego zależy prawidłowy lot.', 464, ''),
(7681, 'Wyregulowałem/am latawiec, aby prawidłowo wzbijał się w powietrze i latał.', 464, ''),
(7682, 'Samodzielnie zbudowałem/am latawiec płaski.', 464, ''),
(7683, 'Wspólnie z zastępem zbudowałem/am latawiec skrzynkowy. Zbudowałem/am wyciąg do latawca i go zastosowałem/am.', 464, ''),
(7684, 'Uczestniczyłem/am ze swym latawcem w zawodach latawcowych np. z okazji Święta Latawca.', 464, ''),
(7685, 'Opowiedziałem/am o pierwszych próbach wzlotów szybowcowych.', 465, ''),
(7686, 'Wyjaśniłem/am, jakie cechy charakteru musi mieć dobry lotnik.', 465, ''),
(7687, 'Wyjaśniłem/am, jakie siły działają na szybowce w locie.', 465, ''),
(7688, 'Wyważyłem/am i wyregulowałem/am latający kartonowy model szybowca, aby wykonywał lot prosty.', 465, ''),
(7689, 'Na modelu zademonstrowałem/am działanie sterów i lotek.', 465, ''),
(7690, 'Wykonałem/am z papieru strzałę i gołębia oraz model latający szybowca z kartonu (bez użycia kleju).', 465, ''),
(7691, 'Brałem/am udział w zawodach kartonówek.', 465, ''),
(7692, 'Poznałem/am i stosuję zasady bezpieczeństwa używania modeli rakiet.', 466, ''),
(7693, 'Wyjaśniłem/am zasady poruszania się rakiety oraz budowę wyrzutni i urządzenia do startu modeli rakiet.', 466, ''),
(7694, 'Opowiedziałem/am kolegom i koleżankom o ważniejszych wydarzeniach z dziejów astronautyki oraz zasługach Polaków w jej rozwoju lub brałem/am udział w konkursie wiedzy kosmicznej.', 466, ''),
(7695, 'Zbudowałem/am model rakiety z silniczkiem rakietowym i zorganizowałem/am pokaz startu rakiety wśród swoich kolegów i koleżanek lub brałem/am udział w zawodach modeli rakiet.', 466, ''),
(7696, 'Nauczyłem/am kolegów i koleżanki budowy modeli rakiet.', 466, ''),
(7697, 'Rozpoznałem/am 3 typy polskich szybowców i 12 typów używanych w Polsce samolotów lub śmigłowców.', 467, ''),
(7698, 'Wykazałem/am się znajomością nazw części samolotu, wyjaśniłem/am działanie sterów.', 467, ''),
(7699, 'Zbudowałem/am 3 modele z drewna lub kartonu wiernie oddające wygląd samolotów lub skleiłem/am 5 modeli plastikowych, estetycznie i prawidłowo je malując.', 467, ''),
(7700, 'Wziąłem/wzięłam udział w konkursie modeli samolotów z tworzyw sztucznych lub zademonstrowałem/am własne modele w zastępie.', 467, ''),
(7701, 'Poznałem/am przynajmniej dwa systemy zdalnego sterowania samolotem; wiem, czym one się charakteryzują. Dowiedziałem/am się, jaką rolę w urządzeniu zdalnie sterowanym spełniają przekaźniki elektromechaniczne, czujniki i silniki elektryczne.', 468, ''),
(7702, 'Wyjaśniłem/am, gdzie i na jakich warunkach można uzyskać zezwolenie na budowę urządzeń nadawczo-odbiorczych do sterowania radiomodelarskiego.', 468, ''),
(7703, 'Narysowałem/am i objaśniłem/am schemat blokowy jednokanałowego nadajnika i odbiornika sterowania falami radiowymi.', 468, ''),
(7704, 'Zademonstrowałem/am na zbiórce zastępu lub drużyny funkcjonowanie zestawu zdalnego sterowania modeli samolotów, wyjaśniłem/am kolegom i koleżankom, na czym ono polega.', 468, ''),
(7705, 'Przeprowadziłem/am pokaz akrobacji lotniczej z wykorzystaniem zestawu zdalnego sterowania.', 468, ''),
(7706, 'Wskazałem/am i nazwałem/am na koniu ważne struktury kości (np. grzebień łopatki, guz kulszowy i inne), które są wyczuwalne, jak również mięśnie, na których stosuje się chwyty masażu.', 510, ''),
(7707, 'Oceniłem/am u konia prawidłowość kopyt w kontekście fizjoterapeutycznym (np. niska-wysoka piętka, kopyto sztorcowe czy wąska-szeroka strzałka).', 510, ''),
(7708, 'Wytłumaczyłem/am zastępowi, czym jest zoofizjoterapia pod kątem bezpieczeństwa ludzi i koni, zaprezentowałem/am wskazania i przeciwwskazania, zwróciłem/am szczególną uwagę, że osoba ucząca się może wykonywać zabiegi tylko pod okiem specjalisty.', 510, ''),
(7709, 'Przygotowałem/am pokaz ćwiczeń stretchingowych czynnych i biernych bezpiecznych dla laików, nauczyłem/am poprawności wykonywania ćwiczeń z koniem swój zastęp/drużynę. Przy okazji wytłumaczyłem/am, na jaki problem ćwiczenie odpowiada , jakie mięśnie aktywuje oraz na co należy zwracać szczególną uwagę.', 510, ''),
(7710, 'Poznałem/am podstawową budowę siodła, poznałem/am pojęcie terlica oraz wymieniłem/am i opisałem/am typy siodeł. Poznałem/am podstawową budowę ogłowia, rozróżniłem/am i opisałem/am nachrapniki.', 511, ''),
(7711, 'Prawidłowo założyłem/am siodło i ogłowie na trzech koniach o różnej budowie, jeśli koń w swoim sprzęcie posiadał dodatkowe elementy: wytok, podogonie, podkładki, również założyłem/am je prawidłowo.', 511, ''),
(7712, 'Wziąłem/Wzięłam udział w czyszczeniu i konserwacji sprzętu skórzanego (siodło, ogłowie).', 511, ''),
(7713, 'W trakcie siodłania i po jeździe kontrolowałem/am grzbiet konia. Rozpoznałem/am podstawowe problemy związane ze źle dopasowanym siodłem (otarcia, białe włosy, opuchlizny, zanik mięśni, blizny lub zgrubienia).', 511, ''),
(7714, 'Wyznaczyłem/am na koniu granice, w których powinno leżeć siodło, tak aby nie wchodziło na łopatki oraz nie wychodziło poza odcinek piersiowy konia.', 512, ''),
(7715, 'Wytłumaczyłem/am zastępowi, czym jest terlica, jak jest zbudowana oraz jakie ma wady i zalety.', 512, ''),
(7716, 'Poznałem/am rodzaje wędzideł i krótko je scharakteryzowałem/am zastępowi. Oceniłem/am, czy wędzidło jest odpowiednio długie.', 512, ''),
(7717, 'Złożyłem/am ogłowie po konserwacji oraz poprawnie dopasowałem/am na nowo do konia.', 512, ''),
(7718, 'Przedstawiłem/am w dowolnej formie, dlaczego należy regularnie sprawdzać jakość siodła oraz jego dopasowanie do konia.', 512, ''),
(7719, 'Wymieniłem/am i pokazałem/am na koniu, na jakich mięśniach leży siodło.', 513, ''),
(7720, 'Wykonałem/am podstawową ocenę siodła: test terlicy, symetria siodła, stan paneli (np. jakość wypełnienia), szerokość kanału. Dowiedziałem/am się, dlaczego szerokość kanału min. 6 cm jest ważna dla grzbietu konia.', 513, ''),
(7721, 'Wykonałem/am podstawową ocenę siodła leżącego na grzbiecie konia: czy leży w odpowiednich granicach anatomicznych konia (nie jest za długie), ochrona kłębu, ochrona kręgosłupa, przyleganie paneli - czy terlica jest odpowiedniej szerokości.', 513, ''),
(7722, 'Pokazałem/am mniej doświadczonym kolegom, jak poprawnie siodłać i kiełznać konia.', 513, ''),
(7723, 'Poznałem/am rodzaje wodzy pomocniczej. W zastępie/ drużynie przeprowadziłem/am dyskusję na temat tego, czy są one potrzebne, do czego i dla kogo.', 513, ''),
(7724, 'Wskazałem/am i scharakteryzowałem/am etapy życia konia.', 514, ''),
(7725, 'Rozróżniłem/am na pastwisku czy źrebak jest noworodkiem, odsadkiem i roczniakiem. Przedstawiłem/am w dowolnej formie podobieństwa i różnice pomiędzy nimi, zwracając uwagę na budowę ciała oraz charakterystyczne zachowania.', 514, ''),
(7726, 'Przy pomocy osoby doświadczonej czyściłem/am źrebaka zgrzebłem plastikowym i szczotką z miękkim włosiem.', 514, ''),
(7727, 'Uczestniczyłem/am w sesji treningowej źrebiąt jako pomocnik. Następnie wykonałem/am mapę myśli dotyczącą wczesnego szkolenia źrebiąt, czyli dotyczącą tego, czego źrebak powinien się nauczyć w pierwszych miesiącach życia.', 514, ''),
(7728, 'Przedstawiłem/am zastępowi, co to jest imprinting zwierząt, kto opisał tę metodę dla koni oraz jakie są jej główne cele.', 515, ''),
(7729, 'Wziąłem/Wzięłam udział w sesji treningowej źrebaka, gdzie przy specjaliście uczyłem/am konia podawania nóg, chodzenia na uwiązie lub odczulania na jakiś „straszny” bodziec.', 515, ''),
(7730, 'Wskazałem/am, do ilu godzin po porodzie należy wykonać pierwszą sesję imprintingu.', 515, ''),
(7731, 'Zaprezentowałem/am i nauczyłem/am mniej doświadczone osoby, jak należy trzymać źrebaka w pozycji bezpiecznej (na sztucznym/pluszowym koniu).', 515, ''),
(7732, 'Obserwując zabawy źrebiąt na pastwisku, wskazałem/am i nazwałem/am zabawy źrebiąt np. przyszczypywanie, skakanie, podskoki, wspinanie się itd.', 516, ''),
(7733, 'Poznałem/am zasady planowania sesji imprintingowych, dowiedziałem/am się, co w której sesji można wprowadzić, oraz ile powinna trwać taka sesja. Pod opieką specjalisty zaplanowałem/am jedną sesję treningową.', 516, ''),
(7734, 'Przedstawiłem/am w dowolnej formie zalety i wady wczesnego szkolenia źrebiąt.', 516, ''),
(7735, 'Pod opieką specjalisty zaprezentowałem/am zastępowi zasady bezpiecznego podstępowania przy źrebakach, pokazałem/am, jak prawidłowo przyuczać do zakładania kantara, jak odczulać na dotyk człowieka, jak prowadzić na uwiązie oraz jak odczulić źrebię na różne przedmioty, np. folia, parasolka.', 516, ''),
(7736, 'Nauczyłem/am się, czym jest hipoterapia. Wytłumaczyłem/am zastępowi, na czym polega.', 517, ''),
(7737, 'Pomogłem/am przygotować konia do hipoterapii pod okiem specjalisty.', 517, ''),
(7738, 'Opisałem/am, jakimi cechami powinien charakteryzować się odpowiedni koń do hipoterapii oraz jaki powinien być hipoterapeuta.', 517, ''),
(7739, 'Zaprezentowałem/am w stępie podstawowe ćwiczenia wykonywanie w hipoterapii.', 517, ''),
(7740, 'Poznałem/am rodzaje hipoterapii, w dowolny sposób swoją wiedzę na ten temat przedstawiłem/am zastępowi.', 518, ''),
(7741, 'Wymieniłem/am min. po 5 wskazań i przeciwwskazań do hipoterapii.', 518, ''),
(7742, 'Pomagałem/am w hipoterapii, jako osoba prowadząca konia.', 518, ''),
(7743, 'Wymyśliłem/am po dwa ćwiczenia odpowiednie dla dziecka MPD, z zespołem Downa oraz upośledzeniem umysłowym. Swoje propozycje pokazałem/am specjaliście.', 518, ''),
(7744, 'Nauczyłem/am się, jaki sprzęt jest wymagany w hipoterapii dla konia i pacjenta, odpowiednio zadbałem/am przed terapią o prawidłowe dopasowanie sprzętu.', 518, ''),
(7745, 'Poznałem/am kanony pracy hipoterapeuty i przedstawiłem/am je zainteresowanym kolegom z drużyny.', 519, ''),
(7746, 'Pod okiem specjalisty dobrałem/am konia do pacjenta, a także wybrałem/am miejsce, czas zajęć oraz plan zmiany kierunków.', 519, ''),
(7747, 'Wymieniłem/am wskazania, przeciwwskazania względne i bezwzględne. Wytłumaczyłem/am powyższe pojęcia. Zaklasyfikowałem/am, czy dana jednostka chorobowa jest wskazaniem, przeciwskazaniem względnym czy bezwzględnym.', 519, ''),
(7748, 'Poznałem/am objawy i charakterystykę podstawowych jednostek chorobowych: MPD, Zespół Downa, upośledzenie umysłowe oraz autyzm.  Pod okiem specjalisty brałem/am udział w przeprowadzeniu wywiadu hipoterapeutycznego z nowym pacjentem.', 519, ''),
(7749, 'Poznałem/am, w jaki sposób oddziałuje koń na jeźdźca. Oceniałem/am postawę jeźdźca: przodopochylenie, tyłopochylenie i prawidłowe ustawienie miednicy.', 519, ''),
(7750, 'Wyjaśniłem/am zasady unoszenia się balonu w powietrzu oraz budowę balonu sportowego.', 478, ''),
(7751, 'Rozpoznałem/am rodzaje balonów i wykazałem/am się znajomością ich zastosowania.', 478, ''),
(7752, 'Stosowałem/am przepisy bezpiecznego wypuszczania balonów na ogrzane powietrze.', 478, ''),
(7753, 'Wraz z zastępem zbudowałem/am z bibułki balon na ogrzane powietrze.', 478, ''),
(7754, 'Brałem/am udział w pokazie wypuszczania modelu balonu na ogrzane powietrze lub w zawodach modeli balonów.', 478, ''),
(7755, 'Opowiedziałem/am kolegom i koleżankom o sukcesach Polaków w zawodach Gordon-Bennetta oraz o najważniejszych faktach z dziejów balonu i historii baloniarstwa w Polsce.', 479, ''),
(7756, 'Wykazałem/am się znajomością cech charakteru i wymagań zdrowotnych, jakie powinien spełniać pilot balonowy. Przedstawiłem/am, w jaki sposób systematycznie je ćwiczyć.', 479, ''),
(7757, 'Rozpoznałem/am różne rodzaje balonów oraz wykazałem/am się znajomością ich budowy, teorii lotu oraz sposobów kierowania nimi.', 479, ''),
(7758, 'Pomagałem/am w organizacji pokazu balonowego lub zawodów balonowych.', 479, ''),
(7759, 'Jako pasażer wykonałem/am wzlot na uwięzi, balonem.', 479, ''),
(7760, 'Opowiedziałem/am o dziejach baloniarstwa w Polsce i na świecie, wykazując się ich dobrą znajomością.', 480, ''),
(7761, 'Wykazałem/am się znajomością wymagań do uzyskania Harcerskiego Znaku Balonowego.', 480, ''),
(7762, 'Wykazałem/am się umiejętnością podstawowej obsługi balonu na ogrzane powietrze, przy starcie i po lądowaniu.', 480, ''),
(7763, 'Brałem/am czynny udział w przygotowaniach do wzlotu balonu.', 480, ''),
(7764, 'Jako pasażer wykonałem/am lot balonem na ogrzane powietrze.', 480, ''),
(7765, 'Poznałem/am podstawowe zasady filmowania. Zgodnie z nimi udokumentowałem wydarzenie w drużynie, szkole lub rodzinie.', 28, ''),
(7766, 'Rozróżniam gatunki filmowe. Dopasowałem/am je do odbiorcy proponując repertuar stosownie do upodobań moich znajomych.', 28, ''),
(7767, 'Nakręciłem/am film (telefonem komórkowym, aparatem, kamerą) według własnego scenariusza i przygotowałam/em go samodzielnie do prezentacji.', 28, ''),
(7768, 'Zorganizowałem/am w drużynie lub szczepie wieczór filmowy, wybierając wg własnego kryterium filmy, których treścią chce się podzielić z innymi.', 28, ''),
(7769, 'Odwiedziłem/am w mojej miejscowości lub podczas wyjazdu dwa muzea poświęcone dawnym czasom. Opowiedziałem/am o jednej z obejrzanych wystaw.', 74, ''),
(7770, 'Na podstawie różnych (minimum dwóch) dostępnych źródeł (pamiętniki, dokumenty, encyklopedia, przekaz ustny, itd.) sporządziłem/am notatkę o patronie drużyny/szkoły/ulicy. Wykorzystałem/am ją na zbiórce lub w szkole. ', 74, ''),
(7771, 'Stworzyłem/am plansze historyczne ukazujące epoki historyczne lub okresy z historii harcerstwa (wraz z ich ramami czasowymi i charakterystycznymi elementami). Udostępniłem/am je w szkole/harcówce/Internecie lub ozdobiłem/am swój pokój. ', 74, ''),
(7772, 'Przeczytałem/am beletrystyczną historyczną książkę dla dzieci (np. z serii: „Kroniki Archeo”, „Wojna oczami dzieci”, „Pan Samochodzik”, „Polscy superbohaterowie”, „Strażnicy historii”, „Zdarzyło się w Polsce”), najciekawszymi fragmentami podzieliłem/am się z zastępem. ', 74, ''),
(7773, 'Posiadam stały adres e-mailowy, regularnie sprawdzam swoją skrzynkę odbiorczą. Napisałem/am i wysłałem/am list tradycyjny i e-mailowy. ', 160, ''),
(7774, 'Poznałem/am podstawowe rodzaje szyfrów – wiem, czym się różnią. Zaszyfrowałem/am i odszyfrowałem/am wiadomość w co najmniej pięciu różnych szyfrach. Posłużyłem/am się szyfrem w grach harcerskich. ', 160, ''),
(7775, 'Poznałem/am metody utrzymywania łączności w terenie. Podczas gry terenowej utrzymywałem/am kontakt z innym zespołem. ', 160, ''),
(7776, 'Pobożnie i aktywnie uczestniczyłem/am w nabożeństwie/mszy świętej/liturgii, starając się stanowić wzór dla innych. ', 249, ''),
(7777, 'Poznałem/am podstawowe postawy i gesty liturgiczne. Wykazałem/am się ich znajomością asystując we mszy świętej. ', 249, ''),
(7778, 'Przeprowadziłem/am dla zastępu zajęcia z musztry harcerskiej, zwracając uwagę na poprawność wykonywanych postaw i gestów. ', 249, ''),
(7779, 'Poznałem/am strukturę roku liturgicznego. Wiem, kiedy się rozpoczyna i kończy i jak się nazywają jego główne okresy. Stworzyłem/am swój kalendarz liturgiczny. Zaznaczyłem/am na nim najważniejsze święta mojego wyznania. Wiem, które z tych świąt mają stały termin obchodów, a które są „ruchome”. ', 250, ''),
(7780, 'Poznałem/am możliwości zaangażowania w przygotowanie liturgii. Pomogłem/am w organizacji wydarzenia religijnego (np. polowa msza święta) w hufcu/drużynie. ', 250, ''),
(7781, 'Uczestniczyłem/am w budowie ołtarza polowego na obozie drużyny. Zadbałem/am o urządzenie tego miejsca tak, by atmosfera sprzyjała modlitwie. ', 250, ''),
(7782, 'Poznałem/am strukturę mszy świętej/nabożeństwa/liturgii. Umiem wymienić jej części i dowiedziałem/am się, jakie postawy należy przyjąć w poszczególnych jej elementach. Przekazałem tę wiedzę młodszej osobie. ', 251, ''),
(7783, 'Poznałem/am nazwy podstawowych paramentów liturgicznych. Przygotowałem/am ołtarz do sprawowania Eucharystii. ', 251, ''),
(7784, 'Poznałem/am ceremoniał harcerski dotyczący uczestnictwa pocztu sztandarowego i drużyny reprezentacyjnej we mszy świętej/nabożeństwie/liturgii. Wystąpiłem/am w poczcie sztandarowym lub byłem proporcowym/proporcową w trakcie mszy świętej/nabożeństwa/liturgii. ', 251, ''),
(7785, 'Obejrzałem/am wspólnie z rodziną/drużyną/zastępem film pt. „Największy z cudów” (2011), a następnie porozmawialiśmy o przesłaniu, jakie niesie film. ', 251, ''),
(7786, 'Przygotowałem/am hufcowy/drużynowy „niezbędnik liturgiczny” dla mojego wyznania w formie broszury, który zawiera, np. opis obrzędu mszy, pieśni, piosenki harcerskie, które można wykorzystać w liturgii, sposoby zaangażowania w liturgię. ', 252, ''),
(7787, 'Zadbałem/am wspólnie z chętnymi osobami o urządzenie na obozie/biwaku miejsca modlitwy (wyciszenia, skupienia), w którym każdy może realizować swoje potrzeby duchowe (np. ołtarz polowy, kapliczkę obozową, leśną \"czytelnię\" wartościowych książek dotyczących rozwoju duchowego). ', 252, ''),
(7788, 'Przygotowałem/am wspólnie z kapelanem/duszpasterzem chorągwi/hufca wydarzenie religijne. ', 252, ''),
(7789, 'Wykazałem/am się wiedzą na temat bojowego ratownictwa i poszukiwań (CSAR), w szczególności zastosowania w tym celu spadochroniarstwa. ', 259, ''),
(7790, 'Rozpoznałem/am oznaczenia stopni służbowych obowiązujących w formacjach działających na rzecz bezpieczeństwa powszechnego (Wojsko, Policja, Straż Pożarna). ', 259, ''),
(7791, 'Wykazałem/am się wiedzą na temat możliwych zagrożeń aktami terroryzmu oraz algorytmu postępowania w przypadku zauważenia podejrzanych przedmiotów porzuconych w miejscach publicznych, znalezienia niewypałów lub niewybuchów ', 259, ''),
(7792, 'Ukończyłem/am szkolenie z czerwonej taktyki lub inny kurs pierwszej pomocy w warunkach polowych. ', 259, ''),
(7793, 'Brałem/am udział w akcji poszukiwawczej. ', 259, ''),
(7794, 'Sporządziłem/am plan, a następnie przeprowadziłem/am z drużyną ćwiczenia ewakuacji biwaku lub obozu w bezpieczne miejsce na wypadek zaistnienia zagrożeń naturalnych. ', 259, ''),
(7795, 'Przeprowadziłem/am zbiórkę dla drużyny lub zastępu z udziałem przedstawiciela wybranej służby mundurowej na temat zachowania się w sytuacji klęski żywiołowej lub katastrofy (budowlanej, komunikacyjnej, naturalnej). ', 259, ''),
(7796, 'Brałem/am udział w trzech meczach (np. siatkówki, piłki nożnej, koszykówki, footballu amerykańskiego, piłki ręcznej), pamiętając o zasadach fair play. ', 372, ''),
(7797, 'Prawidłowo wykonałem/am minimum trzy podstawowe podania (np. strzał, podanie, chwyt bramkarski, dwutakt, przekazanie). ', 372, ''),
(7798, 'Grałem/am na różnych pozycjach w czasie meczu, np. bramkarza, rozgrywającego, przyjmującego. ', 372, ''),
(7799, 'Odpowiednio przygotowałem/am się do treningu oraz zadbałem/am o siebie po treningu. ', 372, ''),
(7800, 'Nauczyłem/am innych zasad gry mojej ulubionej dyscypliny sportów zespołowych ( np. siatkówki, piłki nożnej, koszykówki, footballu amerykańskiego, piłki ręcznej). ', 373, ''),
(7801, 'W czasie próby trenowałem/am systematycznie min. raz w tygodniu. Skompletowałem/am niezbędny sprzęt umożliwiający treningi, np. korki, kask. ', 373, ''),
(7802, 'Wraz z drużyną wziąłem/wzięłam udział w zawodach (szkolnych/harcerskich) w wybranej dyscyplinie sportu zespołowego . ', 373, ''),
(7803, 'W czasie rozgrywki wykazałem/am się dyscypliną taktyczną (wykonałem/am zalecenia trenera obejmujące pozycję oraz styl gry). ', 373, ''),
(7804, 'Zorganizowałem/am zawody, podczas których pełniłem/am funkcję sędziego. ', 374, ''),
(7805, 'Zapoznałem/am się z różnymi taktykami gry. W czasie dwóch rozgrywek przeprowadziłem/am taktykę, którą przekazałem/am innym członkom drużyny. ', 374, ''),
(7806, 'Nauczyłem/am innych podstawowych podań z wybranej dyscypliny sportów zespołowych. Nauczyłem/am się kilku prostych trików z piłką, które przedstawiłem/am wśród rówieśników. ', 374, ''),
(7807, 'Ułożyłem/am plan swojego rozwoju uwzględniający odpowiednią ilość snu, wypoczynku, ruchu, właściwe odżywianie, skonsultowałem/am go z trenerem i realizowałem/am przez miesiąc. ', 374, ''),
(7808, 'Opowiedziałem/am o kilku najnowszych osiągnięciach techniki lotniczej, różnych doświadczalnych rodzajach samolotów i układach aerodynamicznych. Objaśniłem/am zasady lotu różnego rodzaju statków latających. ', 469, ''),
(7809, 'Wyjaśniłem/am, jaką rolę spełniają poszczególne elementy konstrukcyjne i urządzenia aerodynamiczne samolotów. ', 469, ''),
(7810, 'Objaśniłem/am, do czego służy wirnik ogonowy śmigłowca oraz jak steruje się śmigłowcem. Wyważyłem/am i wyregulowałem/am model o układzie nietypowym: kaczki lub bezogonowca. ', 469, ''),
(7811, 'Zbudowałem/am model latający o układzie nietypowym: kaczki lub bezogonowca. ', 469, ''),
(7812, 'Zbudowałem/am model doświadczalny własnego pomysłu dowolnego statku powietrznego (np. śmigłowiec, pionowzlot, samolot skróconego startu, poduszkowiec) lub ze specjalnymi urządzeniami aerodynamicznymi (sploty, zaburzacze, hamulce aerodynamiczne, interceptory zamiast lotek itp.) i zademonstrowałem/am jego działanie. ', 469, ''),
(7813, 'Pełniłem/am obowiązki pomocnika/asystenta sędziego-chronometrażysty w zawodach i otrzymałem/am pozytywną ocenę sędziego. ', 470, ''),
(7814, 'Wykazałem/am się znajomością przepisów Kodeksu Sportowego FAI (w szczególności część 4a i 4b), dotyczących zawodów modelarskich. Wykazałem/am się znajomością organizacji pracy komisji sędziowskiej. ', 470, ''),
(7815, 'Wykazałem/am się znajomością kategorii, klas i konkurencji w zawodach modeli latających - lotniczych i kosmicznych. ', 470, ''),
(7816, 'Wykazałem/am się znajomością zasad posługiwania się stoperem i pomiaru czasu lotu modeli. ', 470, ''),
(7817, 'Pod nadzorem sędziego prowadziłem/am zorganizowany trening zawodników ubiegających się o zdobycie modelarskich odznak sportowych. ', 470, ''),
(7818, 'Wyjaśniłem/am i zademonstrowałem/am zasady lotu i pilotażu lotni/paralotni, korzystając z samodzielnie zbudowanego latawca płaskiego. ', 471, ''),
(7819, 'Wykazałem/am się znajomością podstawowych zasad wykonywania lotów m.in. w jakich warunkach można je wykonywać. ', 471, ''),
(7820, 'Opowiedziałem/am o najważniejszych faktach z historii lotniarstwa/paralotniarstwa i sportu lotniowego/paralotniowego. ', 471, ''),
(7821, 'Uczestniczyłem/am (jako widz) w zawodach lub pokazach lotniowych/paralotniowych. ', 471, ''),
(7822, 'Zademonstrowałem/am i wyjaśniłem/am kolegom i koleżankom zasady sterowania lotnią/paralotnią. ', 472, ''),
(7823, 'Brałem/am udział w składaniu i rozkładaniu lotni/paralotni i przygotowaniu jej do lotów. Prawidłowo założyłem/am i przymocowałem/am uprząż do lotni/paralotni. ', 472, ''),
(7824, 'Rozróżniłem/am przynajmniej 3 rodzaje lotni/paralotni oraz uprzęży dla pilotów lotniowych/paralotniowych i ich zastosowanie. ', 472, ''),
(7825, 'Wykazałem/am się wiedzą, jak prawidłowo udzielić pierwszej pomocy w prostych przypadkach. Wykazałem/am się umiejętnością bezpiecznego podejścia do poszkodowanego oraz prawidłowo udrożniłem/am drogi oddechowe. Wykazałem/am się wiedzą z zasad higieny lotniczej. ', 472, ''),
(7826, 'Wykazałem/am się znajomością historii lotniarstwa/paralotniarstwa w Polsce i na świecie. ', 472, ''),
(7827, 'Określiłem/am kierunek i prędkość wiatru względem zbocza oraz możliwości latania w danych warunkach. ', 473, ''),
(7828, 'Wykonałem/am ćwiczenia przygotowawcze do wstępnego szkolenia w pilotażu lotni (rozbieg i dobieg z lotnią/paralotnią, utrzymanie lotni/paralotni w łożu wiatru, lot latawcowy na uwięzi). ', 473, ''),
(7829, 'Wykazałem/am się wiedzą, jak prawidłowo udzielić pierwszej pomocy. Prawidłowo zabezpieczyłem/am poszkodowanego z podejrzeniem uszkodzenia kręgosłupa lub złamaniami kończyn. Założyłem/am kołnierz ortopedyczny i ułożyłem/am poszkodowanego na desce ortopedycznej. ', 473, ''),
(7830, 'Wykazałem/am się znajomością podstawowych zasady dotyczące startów za wyciągarką. ', 473, ''),
(7831, 'Wykazałem/am się znajomością wymagań do uzyskania Harcerskiego Znaku Lotniczego. ', 473, ''),
(7832, 'Wyjaśniłem/am i zademonstrowałem/am zasady lotu i pilotażu szybowca, korzystając z samodzielnie zbudowanego prostego modelu szybowca. ', 474, ''),
(7833, 'Wykazałem/am się wiedzą, w jakich warunkach można wykonywać loty oraz znajomością podstawowych zasad wykonywania lotów. ', 474, ''),
(7834, 'Opowiedziałem/am o najważniejszych faktach z historii szybownictwa i sportu szybowcowego. ', 474, ''),
(7835, 'Uczestniczyłem/am (jako widz) w zawodach lub startach szybowcowych. ', 474, ''),
(7836, 'Wykazałem/am się wiedzą, jakie siły działają na szybowiec w locie i jakie warunki sprzyjają lotom szybowcowym. W terenie lub na mapie wskazałem/am, gdzie powstają prądy wznoszące. ', 475, ''),
(7837, 'Wykazałem/am się wiedzą dot. zasad bezpieczeństwa obowiązujące na starcie szybowcowym. Pełniłem/am funkcję dyżurnego i chronometrażysty na starcie. ', 475, ''),
(7838, 'Uczestniczyłem/am w wykładaniu, wyhangarowaniu i transporcie szybowca, wykładaniu znaków startowych i przygotowywaniu szybowca do lotu – samodzielnie podpinałem/am linę holowniczą i wypuszczałem/am szybowiec przy starcie. ', 475, ''),
(7839, 'Zapoznałem/am się z przynajmniej jedną książką o tematyce szybowcowej. ', 475, ''),
(7840, 'Wykonałem/am lot pasażerski szybowcem. ', 475, ''),
(7841, 'Wymieniłem/am wymagania zdrowotne i psychiczne stawiane lotnikom. Wykazałem/am się wiedzą dot. warunków przyjęcia na szkolenia lotnicze (wiek, wykształcenie) i główne rodzaje pracy w lotnictwie. ', 476, ''),
(7842, 'Wykazałem/am się znajomością nazw głównych części samolotu. ', 476, ''),
(7843, 'Rozpoznałem/am polskie odznaki lotnicze cywilne i wojskowe. ', 476, ''),
(7844, 'Poruszałem/am się po lotnisku zgodnie z obowiązującymi zasadami i rozpoznałem/am znaki startowe. ', 476, ''),
(7845, 'Zbudowałem/am poprawnie latający model na uwięzi lub zdalnie sterowany. Zademonstrowałem/am start modelu, lot sterowany i lądowanie. ', 476, ''),
(7846, 'Określiłem/am nawigacyjną prędkość, meteorologiczny kierunek i prędkość wiatru oraz możliwości latania w danych warunkach. ', 477, ''),
(7847, 'Wykazałem/am się znajomością przeznaczenia dźwigara, kesonu, wręgi, żebra, okucia i pokrycia. ', 477, ''),
(7848, 'Scharakteryzowałem/am różnice w konstrukcji kratownicowej i półskorupowej. ', 477, ''),
(7849, 'Zademonstrowałem/am ruchy sterami samolotu i opisałem/am główne przyrządy pokładowe. ', 477, ''),
(7850, 'Wskazałem/am podstawowe typy silników lotniczych oraz najpopularniejszych producentów. ', 477, ''),
(7851, 'Odbyłem/am lot pasażerski samolotem sportowym lub turystycznym. ', 477, ''),
(7852, 'Wykazałem/am się wiedzą, gdzie znajduje się najbliższe lotnisko, na którym odbywają się skoki spadochronowe. ', 481, ''),
(7853, 'Odwiedziłem/am lotnisko, gdy odbywały się na nim skoki spadochronowe i wie jak bezpiecznie poruszać się po lotnisku. ', 481, ''),
(7854, 'Przeprowadziłem/am wywiad ze skoczkiem spadochronowym. ', 481, '');
INSERT INTO `zadaniasprawnosciharcerskich` (`ID`, `Tresc`, `Sprawnosc`, `Stopien`) VALUES
(7855, 'Uczestniczyłem/am (jako widz) w zawodach lub pokazach spadochronowych. ', 481, ''),
(7856, 'Wzięła/wziął udział w zajęciach popularyzujących spadochroniarstwo. ', 481, ''),
(7857, 'Wykazałem/am się wiedzą z zakresu podstawowych pojęć związanych ze skokami spadochronowymi (lina desantowa, piguła, sonda, wysokościomierz, rękaw, czasza, pilocik itd.). ', 481, ''),
(7858, 'Zdobyłem/am wiedzę i umiejętności na poziomie sprawności Młody spadochroniarz (*). ', 482, ''),
(7859, 'Wykazałem/am się wiedzą z wybranego fragmentu historii spadochroniarstwa. ', 482, ''),
(7860, 'Wykazałem/am się wiedzą nt budowy spadochronu szybującego. Rozpoznałem/am różne typy spadochronów (szkolny, celnościowy, zapasowy itp.). ', 482, ''),
(7861, 'Wykazałem/am się wiedzą nt podstawowego wyposażenia skoczka spadochronowego. ', 482, ''),
(7862, 'Odwiedziłem/am (np. na lotnisku, w hangarze) różne statki powietrzne, z których można wykonywać skoki spadochronowe. Opisałem/am ich przystosowanie do skoków. ', 482, ''),
(7863, 'Brałem/am udział w pracach pomocniczych na starcie spadochronowym. Pomagałem/am w rozstawieniu rękawa, znaków ziemia-powietrze, stołów spadochronowych itp. ', 482, ''),
(7864, 'Wyszukałem/am ciekawe informacje ze stron internetowych dotyczących spadochroniarstwa. ', 482, ''),
(7865, 'Zdobyłem/am wiedzę i umiejętności na poziomie sprawności Aspirant spadochronowy (**). ', 483, ''),
(7866, 'Zdobyłem/am wiedzę i umiejętności na poziomie sprawności Lotnika tunelowego (**) lub wykonałem/am skok ze spadochronem (samodzielny lub w tandemie). ', 483, ''),
(7867, 'Wykazałem/am się wiedzą z historii spadochroniarstwa krajowego i światowego. ', 483, ''),
(7868, 'Wykazałem/am się wiedzą na poziomie kursu spadochronowego (przepisy Prawa Lotniczego w zakresie spadochroniarstwa, zasady aerodynamiki, podstawy meteorologii, higiena lotnicza itp.) ', 483, ''),
(7869, 'Prawidłowo zabezpieczyłem/am poszkodowanego skoczka z podejrzeniem uszkodzenia kręgosłupa lub złamaniami kończyn. ', 483, ''),
(7870, 'Poprawnie zgasiłem/am czaszę spadochronu na wietrze. ', 483, ''),
(7871, 'Przeanalizowałem/am nagrania video skoków spadochronowych. W trzech nagraniach wskazałem/am na potencjalnie niebezpieczne zachowanie wymagające poprawy. ', 483, ''),
(7872, 'Nawiązałem/am kontakt z harcerskim środowiskiem spadochronowym. Poznałem/am harcerskie przepisy dotyczące specjalności lotniczej i spadochroniarstwa. ', 483, ''),
(7873, 'Przeprowadziłem/am dla drużyny lub zastępu grę o tematyce spadochronowej. ', 483, ''),
(7874, 'Wykazałem/am się znajomością podstawowych zasad przechowywania, konserwacji i transportu sprzętu spadochronowego. ', 484, ''),
(7875, 'Dokonałem/am wizualnej oceny spadochronu szybującego i kontenera oraz wskazałem/am ewentualne uszkodzenia. ', 484, ''),
(7876, 'Pod nadzorem osoby uprawnionej poprawnie ułożyłem/am spadochron szybujący na dwa sposoby (tak jak do skoku i magazynowo). ', 484, ''),
(7877, 'Sprawdziłem/am poprawność przygotowania zestawu spadochronowego do skoku (karta spadochronu, plomba, AAD, zawleczka, itp.). ', 484, ''),
(7878, 'Poprawnie podczepiłem/am czaszę spadochronu do kontenera za pomocą systemu trzech kółek. Wyjaśniłem/am sposób działania tego systemu. ', 484, ''),
(7879, 'Przeprowadziłem/am dla drużyny lub zastępu zajęcia lub konkurs na temat budowy i działania spadochronu szybującego. ', 484, ''),
(7880, 'Odczytałem/am i zinterpretowałem/am komunikat pogodowy. ', 485, ''),
(7881, 'Na podstawie danych meteo określiłem/am kierunek do zrzutu i punkt rozpoczęcia zrzutu skoczków spadochronowych z samolotu. ', 485, ''),
(7882, 'Na planie lotniska wykreśliłem/am rundę do lądowania spadochronu, znając jego doskonałość, prędkość postępową oraz prędkość i kierunek wiatru w kilku wariantach. ', 485, ''),
(7883, 'Wykazałem/am się znajomością wszystkich obowiązujących znaków i sygnałów używanych podczas prowadzenia skoków spadochronowych. Wykładałem/am znaki na lotnisku przed rozpoczęciem skoków. ', 485, ''),
(7884, 'Wykazałem/am się znajomością alfabetu ICAO. Prowadziłem/am nasłuch komunikacji radiowej na lotnisku podczas zrzutu skoczków. ', 485, ''),
(7885, 'Wyjaśniłem/am zasady dot. kolejności wyrzutu skoczków w zależności od rodzaju skoku. ', 485, ''),
(7886, 'Przeprowadziłem/am zbiórkę dla zastępu lub drużyny na lotnisku, w trakcie której wyjaśniłem/am sposób zorganizowania i wykonywania skoków (lądowisko, runda do lądowania, komunikacja radiowa i naziemna). ', 485, ''),
(7887, 'Wykonałem/am stabilne skoki na strugi relatywne. ', 487, ''),
(7888, 'Utrzymałem/am kierunek spadania i otworzyłem/am spadochron na kierunku wyrzutu. ', 487, ''),
(7889, 'Po otwarciu spadochronu pierwszy zakręt wykonałem/am używając wyłącznie tylnych taśm (bez użycia sterówek). ', 487, ''),
(7890, 'Poprawnie zbudowałem/am rundę do lądowania zachowując wysokości i kąt skrętów. ', 487, ''),
(7891, 'Wylądowałem/am w ograniczonym terenie o wymiarach 100x100 metrów. ', 487, ''),
(7892, 'Wylądowałem/am na nogi bez podparcia innymi częściami ciała. ', 487, ''),
(7893, 'Przeprowadziłem/am ćwiczenia dla zastępu lub drużyny rozwijające refleks, precyzję i koordynację ruchową. ', 487, ''),
(7894, 'Wykazałem/am się znajomością historii polskich wojskowych jednostek specjalnych. Rozpoznałem/am znaki identyfikacyjne tych formacji oraz wymieniłem/am ich główne zadania. ', 488, ''),
(7895, 'Wykazałem/am się wiedzą, jakimi cechami winien odznaczać się wojskowy zwiadowca oraz wskazałem/am, które z umiejętności wojskowych zwiadowców zostały przeniesione na grunt skautingu. ', 488, ''),
(7896, 'Samodzielnie wykonałem/am elementy maskujące odzież potrzebne do skrytego przemieszczania się w terenie. Uwzględniłem/am przy tym różne pory roku. ', 488, ''),
(7897, 'Wykonałem/am samodzielnie nocny marsz na odległość około 10 km wykorzystując techniki skrytego przemieszczania się, a następnie prowadziłem/am obserwację rejonu wskazanego na mapie przez opiekuna próby. W trakcie obserwacji wykonałem/am szkic obserwowanego rejonu. ', 488, ''),
(7898, 'Będąc w terenie, za pomocą radiowych środków łączności przekazałem/am meldunek załodze statku powietrznego o miejscu położenia wybranego obiektu. Wykazałem/am się znajomością sposobu określania położenia obowiązującego w nawigacji lotniczej. ', 488, ''),
(7899, 'Przez dwie doby przetrwałem/am samodzielnie w wyznaczonym terenie leśnym. Przez cały czas pozostawałem/am niezauważony/a i żywiłem/am się wyłącznie „darami lasu”. ', 488, ''),
(7900, 'Przeprowadziłem/am zbiórkę w terenie dla zastępu lub drużyny rozwijającą umiejętności zwiadowcy. ', 488, ''),
(7901, 'Przeprowadziłem/am zbiórkę historyczną zastępu lub drużyny poruszającą tematykę działalności Cichociemnych Spadochroniarzy Armii Krajowej. ', 488, ''),
(7902, 'Odczytałem/am i zinterpretowałem/am komunikat pogodowy. ', 486, ''),
(7903, 'Na podstawie danych meteo określiłem/am kierunek do zrzutu i punkt rozpoczęcia zrzutu skoczków spadochronowych z samolotu. ', 486, ''),
(7904, 'Na planie lotniska wykreśliłem/am rundę do lądowania spadochronu, znając jego doskonałość, prędkość postępową oraz prędkość i kierunek wiatru w kilku wariantach. ', 486, ''),
(7905, 'Wykazałem/am się znajomością wszystkich obowiązujących znaków i sygnałów używanych podczas prowadzenia skoków spadochronowych. Wykładałem/am znaki na lotnisku przed rozpoczęciem skoków. ', 486, ''),
(7906, 'Wykazałem/am się znajomością alfabetu ICAO. Prowadziłem/am nasłuch komunikacji radiowej na lotnisku podczas zrzutu skoczków. ', 486, ''),
(7907, 'Wyjaśniłem/am zasady dot. kolejności wyrzutu skoczków w zależności od rodzaju skoku. ', 486, ''),
(7908, 'Przeprowadziłem/am zbiórkę dla zastępu lub drużyny na lotnisku, w trakcie której wyjaśniłem/am sposób zorganizowania i wykonywania skoków (lądowisko, runda do lądowania, komunikacja radiowa i naziemna). ', 486, ''),
(7909, 'Rozpoznawałem/am podstawowe pasze objętościowe i treściwe w żywieniu koni. ', 490, ''),
(7910, 'Poznałem/am i zaprezentowałem/am zasady karmienia i pojenia koni.  ', 490, ''),
(7911, 'Wziąłem/Wzięłam czynny udział w karmieniu koni pod okiem specjalisty. ', 490, ''),
(7912, 'Kontrolowałem/am stan poideł i żłobów. Przynajmniej raz wziąłem/wzięłam udział w ich czyszczeniu. ', 490, ''),
(7913, 'Poznałem/am podstawową budowę układu pokarmowego koni, wskazałem/am różnice między nim a układem pokarmowym ludzi. ', 490, ''),
(7914, 'Oceniłem/am organoleptycznie jakość podawanych pasz treściwych i objętościowych. ', 491, ''),
(7915, 'Poznałem/am, co to jest skala oceny BCS. Krótko opisałem/am zastępowi, na czym ona polega. ', 491, ''),
(7916, 'Przedstawiłem/am zastępowi w ciekawej formie, w jakich aspektach/momentach należy zmienić paszę bądź ilości podawania (np. klacz wysokoźrebna). ', 491, ''),
(7917, 'Przygotowałem/am mesz dla koni, pod okiem specjalisty zadałem/am go koniom w stajni. ', 491, ''),
(7918, 'Poznałem/am i scharakteryzowałem/am dokładną budowę układu pokarmowego konia. Dowiedziałem/am się, w jaki sposób koń pobiera pasze. ', 491, ''),
(7919, 'Byłem/am obecny/a przy układaniu dawki pokarmowej przez specjalistę żywienia koni. Następnie przedstawiłem/am zastępowi, na co należy zwracać uwagę przy układaniu zbilansowanej dawki, a także opowiedziałem/am, jak taki specjalista pracuje. ', 492, ''),
(7920, 'Pod opieką specjalisty dokonałem/am oceny BCS u min. 5 różnych koni. Wskazałem/am, który koń jest w normie, który ma nadwagę lub niedowagę. ', 492, ''),
(7921, 'Poznałem/am i opisałem/am objawy podstawowych chorób układu pokarmowego dowiedziałem/am się, jakie są wskazania dietetyczne w takich przypadkach.  ', 492, ''),
(7922, 'Wymieniłem/am kilka metod oceny wagi koni, wziąłem/wzięłam udział w takiej ocenie wybraną przez siebie metodą. ', 492, ''),
(7923, 'Kontrolowałem/am pobieranie paszy przez konie, dowiedziałem/am, o czym mogą świadczyć pozostawione resztki paszy w żłobie. Byłem/am obecny podczas kontroli uzębienia koni w stajni. Zaprezentowałem/am zastępowi, dlaczego tak ważna jest regularna kontrola stomatologiczna u koni. ', 492, ''),
(7924, 'Prawidłowo odczytywałem/am mowę ciała konia (ogólny wygląd, ustawienie zadu, głowy, mowa nóg, mowa ogona). Na podstawie odczytanych sygnałów konia bezpiecznie podchodziłem/am do konia. Sygnały mowy końskiej wskazałem/am zastępowi.   ', 493, ''),
(7925, 'Dowiedziałem/am się, jak konie odczytują mowę ciała człowieka, podczas pracy z koniem stosowałem/am się do tych zasad', 493, ''),
(7926, 'Za zgodą właściciela i pod okiem osoby doświadczonej nauczyłem/am konia wykonywać buziaka.', 493, ''),
(7927, 'Obserwowałem/am pokaz końskich sztuczek.', 493, ''),
(7928, 'Przygotowałem/am w formie graficznej informacje, jakie sztuczki potrafi wykonywać koń.', 493, ''),
(7929, 'Pod okiem specjalisty nauczyłem/am konie trzech dowolnych sztuczek.', 494, ''),
(7930, 'Wskazałem/am zalety i wady konkretnych sztuczek, np. leżenie, wyrzut nogi, wyrzut z zadnich nóg', 494, ''),
(7931, 'Obserwowałem/am przez min. 3h zachowania koni na łące, poznałem/am ich naturalne reakcje. Zauważyłem/am, jak naturalne zachowania łączą się w wyuczone sztuczki.', 494, ''),
(7932, 'Wymieniłem/am min. 3 autorytety, które pracują z końmi na zasadzie sztuczek. ', 494, ''),
(7933, 'Pod okiem specjalisty przygotowałem/am konia (nauczyłem/am sztuczek) do swojego przedstawienia.', 495, ''),
(7934, 'Zaprezentowałem/am swój autorski program końskich sztuczek.', 495, ''),
(7935, 'Nauczyłem/am min. 2 osoby, jak poprawnie i bezpiecznie wykonać z koniem min. 3 sztuczki.', 495, ''),
(7936, 'Wziąłem/Wzięłam udział w sesji zdjęciowej, w której były przedstawione końskie sztuczki.', 495, ''),
(7937, 'Prawidłowo odczytywałem/am mowę ciała konia (ogólny wygląd, ustawienie zadu, głowy, mowa nóg, mowa ogona). Na podstawie odczytanych sygnałów konia bezpiecznie podchodziłem/am do konia. Sygnały mowy końskiej wskazałem/am zastępowi. ', 496, ''),
(7938, 'Prawidłowo podszedłem/podeszłam do konia na wolności, założyłem/am kantar, prowadziłem/am konia w ręku, odwrażliwiałem/am konia na swój dotyk po całym ciele, nauczyłem/am konia ustępować od nacisku, zatrzymałem/am konia, prowadząc go w ręku.', 496, ''),
(7939, 'Nauczyłem/am się, jak konie odczytują mowę ciała człowieka, podczas pracy z koniem stosowałem/am się do tych zasad. Przygotowałem/am instrukcję porozumiewania się z koniem za pomocą mowy ciała człowieka.', 496, ''),
(7940, 'Poznałem/am 7 gier Parelliego. Zagrałem/am z koniem w grę w zaprzyjaźnianie, w jeża oraz w prowadzenie.', 496, ''),
(7941, 'Grałem/am w 7 gier Parelliego', 497, ''),
(7942, 'Dowiedziałem/am się, na czym polega odczulanie koni oraz jak je prawidłowo przeprowadzić. Odczuliłem/am konia na min. jedną rzecz.', 497, ''),
(7943, 'Przygotowałem/am i przeszedłem/przeszłam z koniem zgodnie z założeniami bezstresowej pracy: przechodzenie przez drągi ułożone w \"L\" w przód i tył, wejście na plandekę, skoki w ręku oraz inne wedle możliwości.', 497, ''),
(7944, 'W trakcie swojej edukacji jeździeckiej oprócz jazdy konnej regularnie pod okiem specjalisty pracowałem/am z koniem z ziemi jako element treningowy.', 497, ''),
(7945, 'Poznałem/am prawidłowe zasady lonżowania konia, pod okiem specjalisty lonżowałem/am konia zgodnie z biomechaniką ruchu oraz potrzebami ruchowymi i psychicznymi konia', 497, ''),
(7946, 'Przeczytałem/am książkę dotyczącą pracy z koniem z ziemi lub komunikowania się z koniem, zaprezentowałem/am „złote myśli” i pomysły na zadania swojemu zastępowi.', 498, ''),
(7947, 'Wyszukałem/am w Internecie lub innych źródłach różne osobistości, które wypracowały swoją metodę pracy z ziemi. Przeanalizowałem/am je, a następnie stworzyłem/am porównanie sposobów pracy, na sam koniec wybrałem/am metodę najbardziej mi odpowiadającą.', 498, ''),
(7948, 'Nawiązałem/am kontakt z koniem, tak aby po puszczeniu konia luzem, podążał za mną. W wersji na wolności wykonałem/am z koniem podstawowe ćwiczenia pracy z ziemi.', 498, ''),
(7949, 'Pracowałem/am z koniem z ziemi na ujeżdżalni oraz w terenie, w swojej pracy używałem/am naturalnych pomocy, stosowałem/am się do zasad bezpiecznej komunikacji z koniem.', 498, ''),
(7950, 'Przygotowałem/am film instruktażowy o tym, jak pracuje się z koniem z ziemi w porozumieniu i harmonii. Przedstawiłem/am swój filmik na zbiórce.', 498, ''),
(7951, 'Wymieniłem/am podstawowe  wyposażenie  lonżującego  oraz  konia,  którego \r\nlonżujemy. Zabezpieczyłem/am strzemiona w siodle na czas lonżowania, dlaczego należy to robić.', 499, ''),
(7952, 'Obserwując prowadzenie  lonży  przez specjalistę, zauważałem/am i wskazałem/am mowę ciała konia \r\n(ogólny wygląd, głowa, ustawienie zadu, mowa nóg, mowa ogona) oraz mowę ciała osoby prowadzącej \r\n(ustawienie rąk, kroki). ', 499, ''),
(7953, 'Nauczyłem/am się prawidłowo trzymać bat oraz prawidłowo trzymałem/am i zwijałem/am lonże. \r\nPoznałem/am podstawowe błędy przy posługiwaniu się tymi pomocami.', 499, ''),
(7954, 'Przeprowadziłem/am dyskusję  na  temat  tego, dlaczego  ważny  jest  ton  głosu  w  trakcie  prowadzenia \r\nlonży oraz jak go dostosować.', 499, ''),
(7955, 'W  dowolny  sposób  przedstawiłem/am zalety i wady poszczególnych wodzy \r\npomocniczych. Nauczyłem/am się, kiedy należy ich stosować. Poznałem/am kilka sposobów \r\nprzypinania lonży, zaprezentowałem/am je na zbiórce.', 500, ''),
(7956, 'Wymieniłem/am  min.  5  celów,  dla  których  lonżujemy  konia.  Pod okiem \r\nosoby doświadczonej lonżowałem/am  konia  min.  dwa  razy,  stosując  zasady  prawidłowego \r\nlonżowania.', 500, ''),
(7957, 'Dowiedziałem/am się, jaka  musi  być  minimalna  średnica  koła,  po  którym  porusza się koń oraz \r\nminimalny i maksymalny czas pracy konia w danych chodach oraz co ile powinno się zmieniać chody.', 500, ''),
(7958, 'Przygotowałem/am i zaprezentowałem/am kilka ćwiczeń dla konia, aby uatrakcyjnić sesję treningową \r\npoprzez zaangażowanie psychiczne. Swoje pomysły na ćwiczenia zapisałem/am w notatniku.', 500, ''),
(7959, 'Prawidłowo  dopasowałem/am  wodze  pomocnicze:  wypinacze,  chambon,  gogue,  wodze Pessoa, \r\nnauczyłem/am się, na co zwracać uwagę przy dopasowaniu tych pomocy.', 501, ''),
(7960, 'Zaplanowałem/am 3 sesje treningowe dla różnych koni (młody koń, koń wracający po kontuzji nogi, \r\nkoń w podeszłym wieku), odpowiednio dostosowałem/am poziom zajęć i atrakcyjność ćwiczeń dla \r\nkonia, tak aby poruszał się w harmonii i w spokoju psychicznym.', 501, ''),
(7961, 'Pod okiem specjalisty lonżowałem/am  konia  na  podwójnej  lonży,  odpowiednio  dobierając \r\nwyposażenie, a w trakcie sesji zadbałem/am o bezpieczeństwo pracy (nauczyłem/am się posługiwać \r\nbezpiecznie dwoma lonżami). ', 501, ''),
(7962, 'Przygotowałem/am  i  przedstawiłem/am  pokaz  dla  zastępu/drużyny  dotyczący  tego,  jak  należy \r\nlonżować konia, pokazałem/am sposób na rozluźnienie konia, poprawę pracy mięśni brzucha oraz \r\nzaangażowania kończyn. Nauczyłem/am gości podstaw lonżowania.', 501, ''),
(7963, 'Prowadziłem/am  pod  okiem  specjalisty  jazdę  dla  osoby  początkującej  na  lonży,  podczas  zajęć \r\npanowałem/am nad koniem, a także uważałem/am na jeźdźca na grzbiecie, tłumaczyłem/am jeźdźcowi \r\npodstawowe ćwiczenia gimnastyczno-rozluźniające na koniu.', 501, ''),
(7964, 'Poznałem/ma budowę konia.  Wskazałem/am na koniu przebieg kręgosłupa,  łopatkę, nadgarstek, biodro, kolano, staw skokowy.', 502, ''),
(7965, ' Zmierzyłem/am  temperaturę  konia  pod  opieką  specjalisty,  wskazałem/am  prawidłowy  zakres temperatur.', 502, ''),
(7966, 'Pomagałem/am w odrobaczaniu konia, dowiedziałem/am się, czemu należy odrobaczać konie (co jaki czas należy to robić, jakie są sposoby podawania).', 502, ''),
(7967, 'Wykonałem/am  wywiad  z  lekarzem  weterynarii  na  temat  układu  pokarmowego,  diety  oraz najczęstszych chorób u koni.', 502, ''),
(7968, 'Poznałem/am budowa ogólną, budowę anatomiczną konia, układ szkieletowy oraz położenie najważniejszych narządów.', 503, ''),
(7969, 'Prawidłowo  zmierzyłem/am temperaturę, tętno i liczbę oddechów konia, poznałem/am ich prawidłowe wartości.', 503, ''),
(7970, 'Poznałem/am objawy najczęściej występujących chorób u koni (gruda, kolka, ochwat, obtarcie kłębu, zagwożdżenie),  nauczyłem/am  się,  jak  postępować,  kiedy  pojawią  się  określone objawy.  Zapoznałem/am się z procedurą wzywania pomocy w nagłych wypadkach.', 503, ''),
(7971, 'Poznałem/am  skład  apteczki, skontrolowałem/am jej stan, wszelkie ubytki uzupełniłem/am. Zastosowałem/am wcierki i założyłem/am opatrunek na kopyto. ', 503, ''),
(7972, 'Poznałem/am anatomię konia (szkielet, podstawowe układy  pokarmowy, krwionośny, mięśniowy, oddechowy). Zaprezentowałem/am, gdzie znajdują się poszczególne narządy, wskazując je na koniu (anatomia topograficzna).', 504, ''),
(7973, 'Poznałem/am  postępowanie  teoretyczne  i nauczyłem/am  się,  jak  udzielić  pierwszej  pomocy  w przypadku zranienia, ochwatu, kolki, odwodnienia, podbicia i mięśniochwatu.  W  razie  nagłego przypadku swoją wiedzę teoretyczną wykorzystałem/am w praktyce. ', 504, ''),
(7974, 'Nauczyłem/am mniej doświadczone osoby, jak prawidłowo zmierzyć temperaturę, liczbę oddechów, tętno, nawodnienie oraz stan błon śluzowych. ', 504, ''),
(7975, 'Poznałem/am cykl podstawowych szczepień i odrobaczeń, nauczyłem/am się, na co się szczepi konie i kiedy należy to  zrobić,  a także  czym różni  się  odrobaczanie  jedno- i wieloskładnikowe. Przeanalizowałem/am wykaz szczepień znajdujący się w paszporcie konia, wyznaczyłem/am, kiedy powinno być następne szczepienie, p pomagałem lekarzowi weterynarii podczas zabiegu iniekcji. ', 504, ''),
(7976, 'Poznałem/am budowę anatomiczną końskiego kopyta.', 505, ''),
(7977, 'Poznałem/am rodzaje podków i ich przeznaczenie oraz zasady prawidłowego podkuwania koni.', 505, ''),
(7978, 'Pomagałem/am podczas rozczyszczania kopyta konia, wykonując polecenia podkuwacza (kowala). Prawidłowo trzymałem/am nogi konia. ', 505, ''),
(7979, 'Pielęgnowałem/am kopyta w podstawowym zakresie.', 505, ''),
(7980, 'Rozpoznałem/am wady budowy kopyt', 506, ''),
(7981, 'Poznałem/am zasady pielęgnacji kopyt (postępowania w przypadku gnicia strzałki, obluzowania podków, codziennej pielęgnacji) i stosowałem/am je w praktyce.', 506, ''),
(7982, 'Uczestniczyłem/am  w  podkuwaniu  koni,  zdejmując  stare  podkowy,  prawidłowo  rozczyszczając kopyto (w obecności specjalisty). ', 506, ''),
(7983, 'Nauczyłem/am mniej  doświadczonych  harcerzy  zasad  bezpiecznego  podnoszenia  kończyn i codziennej pielęgnacji kopyt. ', 506, ''),
(7984, 'Poznałem/am terminarz rozczyszczania i kucia koni.', 506, ''),
(7985, 'Poznałem/am i  zastosowałem/am  w  praktyce  naturalne  sposoby  strugania  koni  w  obecności specjalisty.', 507, ''),
(7986, 'Postąpiłem/am prawidłowo w przypadku zagwożdżenia.', 507, ''),
(7987, 'Nauczyłem/am mniej doświadczonych kolegów zasad trzymania nóg do podkuwania i pielęgnacji kopyt.', 507, ''),
(7988, 'Poznałem/am różne podkowy, dobrałem/am i oceniłem/am je w stosunku do wady.', 507, ''),
(7989, 'Wyjaśniłem/am, czym jest zoofizjoterapia, a  także  co  wchodzi  w  jej  skład,  podałem/am kilka przykładów działań zoofizjoterapeuty.', 508, ''),
(7990, 'Wskazałem/am na żywym koniu i nazwałem/am takie struktury jak: czaszka, przebieg kręgosłupa, klatka  piersiowa, łopatka, guz krzyżowy, guz biodrowy, guz kulszowy, mięsień najdłuższy grzbietu, mięsień czworoboczny. ', 508, ''),
(7991, 'Opowiedziałem/am zastępowi, po co masuje się konie, kiedy można, a  kiedy nie można masować, pokazałem/am zastępowi  podstawowe  chwyty  do  masaży:  głaskanie,  uciskanie,  ugniatanie, oklepywanie, rozcieranie.', 508, ''),
(7992, 'Dowiedziałem/am się,  dlaczego  ruch  fizyczny  jest  ważny  zarówno  u  ludzi,  jak  i  zwierząt. Codziennie ćwiczyłem/am  stretching, aby utrzymać zdrowie w pojęciu fizjoterapeutycznym. ', 508, ''),
(7993, 'Zaprezentowałem/am w ciekawej formie zalety i wady: masażu, laseroterapii, magnetoterapii, leczenie ruchem, stretching. ', 509, ''),
(7994, 'Wykonałem/am na  koniu  masaż  „wzdłuż kręgosłupa\", nauczyłem/am się, kiedy powinien  być stosowany. ', 509, ''),
(7995, 'Wskazałem/am na koniu poszczególne struktury anatomiczne i je scharakteryzowałem/am: odcinki kręgosłupa w tym atlas, kończyna piersiowa i miednicza (poszczególne kości i stawy), m. płatowaty, m. ramienno-głowowy, m. trójkątny ramienia, mm. pośladkowe, m. czworogłowy uda.', 509, ''),
(7996, 'Opisałem/am, czym jest więzadło karkowe, gdzie leży oraz jakie ma znaczenie w funkcjonowaniu koni.', 509, ''),
(7997, 'Nauczyłem/am się,  jak  wygląda  reakcja  na  ból  oraz  jak  unikać  własnych  kontuzji  podczas masażu. Oceniałem/am, czy mięsień jest spięty czy rozluźniony.', 509, ''),
(7998, 'Stworzyłem/am prostą statyczną stronę WWW, wykorzystując język HTML i arkusz stylów CSS oraz zewnętrzne zasoby (np. obrazki).', 521, ''),
(7999, 'Postawiłem/am serwer WWW (np. Apache) na komputerze lub maszynie wirtualnej.', 521, ''),
(8000, 'Poznałem/am terminologię webową (np. protokół, serwer, host, ftp, domena, api) i sposób działania Internetu.', 521, ''),
(8001, 'Samodzielnie postawiłem/am stronę internetowym na wybranym systemie zarządzania treścią (Wordpress, Joomla!, PrestaShop, Drupal, Magento, itp.) i skonfigurowałem/am ją odpowiednio do swoich potrzeb.', 521, ''),
(8002, 'Poznałem/am metody bezpiecznego poruszania się po Internecie. Zapoznałem/am się z technologią VPN. Przeprowadziłem/am zbiórkę dla zastępu/drużyny na temat bezpiecznego korzystania z Internetu, omawiając przy tym zalety sieci VPN.', 521, '');
INSERT INTO `zadaniasprawnoscizuchowych` (`ID`, `Sprawnosc`, `Zadanie`, `Poziom`) VALUES
(1, 1, 'Opowiedziałem/am innym zuchom, czym zajmują się charakteryzatorzy i gdzie pracują.', 1),
(2, 1, 'Narysowałem/am (lub zaprezentowałem/am w Kręgu Rady) przedmioty potrzebne w pracy charakteryzatora.', 1),
(3, 1, 'Ucharakteryzowałem/am innego zucha do teatrzyku.', 1),
(4, 1, 'Wykonałem/am jeden prosty element charakteryzacji twarzy.', 1),
(5, 1, 'Opowiedziałem/am innym zuchom, czym zajmują się charakteryzatorzy i gdzie pracują oraz jakie są dziedziny ich pracy.', 2),
(6, 1, 'Zaprezentowałem/am w Kręgu Rady przedmioty potrzebne w pracy charakteryzatora.', 2),
(7, 1, 'Ucharakteryzowałem/am moją szóstkę do teatrzyku.', 2),
(8, 1, 'Własnoręcznie wykonałem/am elementy charakteryzacji twarzy do wybranej postaci.', 2),
(9, 1, 'Przygotowałem/am naturalne farby do charakteryzacji i zaprezentowałem/am je w praktyce.', 2),
(10, 1, 'Zmyłem/am makijaż po charakteryzacji.', 2),
(11, 1, 'Opowiedziałem/am innym zuchom, czym zajmują się charakteryzatorzy i gdzie pracują oraz jakie są dziedziny ich pracy. Zaprezentowałem/am sylwetkę ulubionego charakteryzatora oraz najlepsze, moim zdaniem, efekty jego pracy.', 3),
(12, 1, 'Zaprezentowałem/am w Kręgu Rady przedmioty potrzebne w pracy charakteryzatora. Pokazałem/am chętnym zuchom, jak się nimi posługiwać.', 3),
(13, 1, 'Ucharakteryzowałem/am moją szóstkę do teatrzyku. Czuwałem/am nad charakteryzacją zuchów z gromady, służąc innym radą i wskazówkami.', 3),
(14, 1, 'Własnoręcznie wykonałem/am elementy charakteryzacji dla wybranej postaci.', 3),
