INSERT INTO `zuchowezadaniaindywidualne` (`ID`, `Rozwoj-Emocjonalny`, `Rozwoj-Duchowy`, `Tresc`, `Gwiazdki`) VALUES
(1, 1, 0, 'Przez tydzień będę codziennie zapisywał/a 3 dobre rzeczy, które przydarzyły mi się w danym dniu. \r\nWskazówka: takie zadanie będzie bardzo odpowiednie dla zucha nadmiernie narzekającego. ', 'Zuch ochoczy\r\nZuch sprawny\r\nZuch zaradny'),
(2, 1, 0, 'Znajdę jedną pozytywną cechę w każdym z mojej szóstki. O swoich przemyśleniach opowiem drużynowemu.', 'Zuch zaradny'),
(3, 1, 0, 'Wraz z gromadą wezmę udział w pieszej wycieczce i postaram się swoje trudności pokonywać w milczeniu. \r\nWskazówka: takie zadanie będzie bardzo odpowiednie dla zucha nadmiernie narzekającego.', 'Zuch ochoczy\r\nZuch sprawny\r\nZuch zaradny'),
(4, 1, 0, 'Przez okres wyjazdu będę zachowywać się z uprzejmością i szacunkiem względem innych osób.', 'Zuch ochoczy\r\nZuch sprawny\r\nZuch zaradny'),
(5, 1, 1, 'Przez tydzień będę spisywać moje dobre i złe uczynki. Przeproszę osobę, którą skrzywdziłem/am złym uczynkiem.', 'Zuch sprawny\r\nZuch zaradny'),
(6, 0, 1, 'Wiem, kto jest moim idolem/autorytetem, potrafię podać 3 powody.', 'Zuch ochoczy\r\nZuch sprawny\r\nZuch zaradny'),
(7, 0, 1, 'Wytłumaczę wybranemu zuchowi z gromady Prawo Zucha. \r\nWskazówka: Można ograniczyć tłumaczenie tylko do kilku punktów z Prawa Zucha (np. dwóch). Wskazane, aby zuch, któremu będzie tłumaczone Prawo Zucha był nowy w gromadzie.', 'Zuch sprawny\r\nZuch zaradny'),
(8, 0, 1, 'Przez tydzień ograniczę korzystanie z telefonu/komputera/oglądanie telewizji. \r\nWskazówka dla drużynowego: w tym miejscu można ująć także inną aktywność, której ograniczenie będzie trudnością dla zucha. Należy ustalić z zuchem dokładny czas, do jakiego ogranicza daną aktywność każdego dnia wybranego tygodnia.', 'Zuch ochoczy\r\nZuch sprawny\r\nZuch zaradny'),
(9, 0, 1, 'Przez okres biwaku będę mówił/a prawdę. Jeśli powiedzenie prawdy mogłoby kogoś zranić, poradzę się drużynowego, czy lepiej byłoby w tym wypadku przemilczeć sprawę', 'Zuch ochoczy\r\nZuch sprawny\r\nZuch zaradny'),
(10, 0, 1, 'Przez tydzień nie będę jadł/a słodyczy i pił/a gazowanych napojów, dowiem się, czym mogę je zastąpić. Zamiast niezdrowej przekąski wezmę do szkoły owoc.', 'Zuch zaradny'),
(11, 0, 1, 'Własnoręcznie zasadzę roślinę. Zapoznam się dokładnie z informacjami na temat posadzonego gatunku, by wiedzieć, jak najlepiej ją pielęgnować. ', 'Zuch zaradny'),
(12, 0, 1, 'Będę dobrze wypełniać obowiązki dyżurnego w klasie przez okres tygodnia.\r\nWskazówka dla drużynowego: należy porozmawiać z zuchem o zakresie wykonywanych przez niego obowiązków np. wspólnie przygotować listę zadań po konsultacji z wychowawcą zucha. ', 'Zuch sprawny'),
(13, 0, 1, 'Posprzątam zuchówkę. \r\nWskazówka dla drużynowego: należy wyzwanie dostosować/zmodyfikować do wieku oraz możliwości zucha np. przez okres zdobywania gwiazdki/przez okres trzech miesięcy będę odpowiedzialny za utrzymanie porządku i posprzątanie: biblioteczki gromady/miejsca na przybory plastyczne.', 'Zuch ochoczy'),
(14, 0, 1, 'Pod opieką osoby dorosłej samodzielnie zrobię zakupy według sporządzonej listy.', 'Zuch zaradny'),
(15, 0, 1, 'Uzgodnię z rodzicami 2 prace domowe, które będę wykonywał/a regularnie przez dwa tygodnie. ', 'Zuch ochoczy');
